INSERT INTO `category` (`id`, `parent_id`, `title`, `slug`, `intro`, `image`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted`, `deleted_at`, `menu_order`) VALUES
(3, 4, 'Công trình tiêu biểu', 'cong-trinh-xay-dung-tieu-bieu', '<p><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Giới thiệu những công trình xây dựng tiêu biểu đã và đang được thực hiện bởi Công ty thiết kế và xây dựng An Gia Khang.</span></p>', '1574812796_50bd917208a7438396c9.jpg', 0, 1, 1, 1573658398, 1590065758, 0, NULL, 0),
(4, 0, 'Nhà thầu xây dựng', 'nha-thau', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Để xây dựng được bất kỳ một công trình nào, ngoài chủ đầu tư thì nhà thầu xây dựng là nhân tố không thể thiếu. Vậy nhà thầu xây dựng là gì? Làm thế nào để bạn chọn được một nhà thầu xây dựng uy tín theo đúng tiêu chí đánh giá và quy định của nhà nước về luật đấu thầu? </span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%; color: rgb(65, 65, 65); background: white;\"><img src=\"/uploads/contents/1576122525_7c38753c42694ae648a5.png\" style=\"width: 652px;\" class=\"fr-fic fr-dii\"></span></span></span></p><p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/nha-thau\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng</span></span></a></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng<strong> </strong>góp phần rất quan trọng cho các dự án chủ đầu tư yêu cấu, nhà thầu có năng lực thật sự sẽ giúp cho dự án thành công và phát triển bền vững. </span></span></p>', 'ao-cap-cho-gia-dinh-4_1615803392_1618286090.jpg', 0, 1, 4, 1573658398, 1618286090, 0, NULL, 0),
(7, 4, 'Năng Lực Nhà Thầu', 'nang-luc-nha-thau', '<p style=\"text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Công ty Cổ Phần Thiết Kế và Xây Dựng An Gia Khang (An Gia Khang Construction and Design Corporation) được thành lập từ năm 2016 nhưng được biết đến là một trong những công ty thiết kê và thi công xây dựng dân dụng (biệt thự, nhà phô\" thương mại, villas shop,...) uy tín và chất lượng tại Việt Nam đặc biệt là khu vực tỉnh Kiên Giang, Phú Quốc.</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1574609760_db1e9b857d7fd794e2ca.png\" style=\"width: 613px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/nang-luc-nha-thau\">Năng lực nhà thầu</a></p>\n', '1574340376_031122757943c7813437.jpg', 0, 1, 1, 1573658398, 1592467551, 0, NULL, 0),
(8, 0, 'Thiết Kế Kiến Trúc', 'thiet-ke-kien-truc', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Công việc thiết kế là giai đoạn ban đầu của dự án. Thiết kế kiến trúc đòi hỏi một quá trình gồm nhiều công đoạn khác nhau. Tại sao một công trình cần phải thiết kế kiến trúc và nó mang lại những giá trị gì? Mời các bạn tham khảo bài viết dưới đây để có thêm   những kinh nghiệm khi thiết kế kiến trúc.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575817675_1a20adef0470d30b0e4c.jpg\" style=\"width: 613px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/thiet-ke-kien-truc\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Thiết kế kiến trúc</span></a></p>\n', '1575817508_493919d67c1cab3371a6.png', 0, 1, 1, 1573658398, 1590065155, 0, NULL, 0),
(9, 8, 'Thiết Kế Biệt Thự', 'thiet-ke-biet-thu', '<p style=\"text-align: left;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Ngày nay chất lượng cuộc sống  ngày một nâng cao chính vì thế mà nhu cầu nhà ở được xây dựng phải đảm bảo đầy đủ các yếu tố như hiện đại, thoáng mái và tiện nghi. Nhằm đáp ứng nhu cầu trên hàng loạt các dạng thiết kế nhà ở được ra đời trong đó có thiết kế biệt thự đang rất được ưa chuộng hiện nay.</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1574338247_9169983f0adbf3d644d3.png\" style=\"width: 607px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:150%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";text-align:center;\'><a href=\"https://www.angiakhang.com/thiet-ke-biet-thu\"><span style=\"font-size: 14px; line-height: 150%; font-family: Arial, Helvetica, sans-serif;\">Thiết kế biệt thự</span><strong><span style=\"font-size: 14px; line-height: 150%; font-family: Arial, Helvetica, sans-serif;\"> </span></strong></a></p>\n', '1574338510_bf8053ab5fb4b23e6570.png', 0, 1, 1, 1573658398, 1590065087, 0, NULL, 0),
(10, 8, 'Thiết Kế Nhà Phố', 'thiet-ke-nha-pho', '<p style=\"margin: 0in 0in 10pt; line-height: 150%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\'font-size:17px;line-height:150%;font-family:\"Times New Roman\",\"serif\";\'>Bạn đang có nhu cầu tìm kiếm một không gian gần các trục đường phố chính và trung tâm thương mại. Tuy nhiên với sự phát triển không ngừng như hiện nay thì giá nhà đất càng trở nên đắt đỏ. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 150%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\'font-size:17px;line-height:150%;font-family:\"Times New Roman\",\"serif\";\'><img src=\"/uploads/contents/1574339203_f8e8774c13877c00593c.png\" style=\"width: 618px;\" class=\"fr-fic fr-dib\"></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:150%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";text-align:center;\'><a href=\"https://www.angiakhang.com/thiet-ke-nha-pho\"><em><span style=\'font-size:17px;line-height:150%;font-family:\"Times New Roman\",\"serif\";\'>Thiết kế nhà phố</span></em></a></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:150%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;\'><span style=\'font-size:17px;line-height:150%;font-family:\"Times New Roman\",\"serif\";\'>Để giải quyết được những nhu cầu này và tạo ra nhiều hơn không gian sống cho con người các <strong>thiết kế nhà phố</strong> được ra đời nhằm tiết kiệm được diện tích nhưng cũng đảm bảo được các điều kiện tiện nghi nhất.</span></p>\n', '1574339398_d0b19eb5235889665995.png', 0, 1, 1, 1573658398, 1590065006, 0, NULL, 0),
(11, 8, 'Thiết Kế nhà cao tầng', 'thiet-ke-nha-cao-tang', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><strong>Thiết kế nhà cao tầng</strong> là một thể loại thiết kế công trình kiến trúc có chiều cao lớn, đòi hỏi phải đáp ứng được những tiêu chuẩn và quy định theo yêu cầu của pháp luật. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577861533_31d18d3f0102a31f9d50.png\" style=\"width: 620px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></p>\n<p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/thiet-ke-nha-cao-tang\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế nhà cao tầng</span></span></a></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Bên cạnh đó cũng phải thể hiện được vẻ đẹp độc đáo riêng của tòa nhà. Bài viết dưới đây sẽ cung cấp cho bạn những thông tin hữu ích về thiết kế nhà cao tầng, mời các bạn tham khảo.</span></p>\n', '1577866920_9b4902ddf5fe1d168b13.png', 0, 1, 1, 1573658398, 1590064962, 0, NULL, 0),
(12, 8, 'Thiết Kế Khách Sạn', 'thiet-ke-khach-san', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Thiết kế khách sạn đẹp, hoàn hảo là nhiệm vụ nhưng cũng là mong muốn của bất cứ đơn vị nào khi muốn kinh doanh loại hình dịch vụ này.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575301766_22b9df6cba9a49d2fcd0.jpg\" style=\"width: 633px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><a href=\"https://www.angiakhang.com/thiet-ke-khach-san\">Thiết kế khách sạn</a></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Đặt trong bối cảnh khi mà có quá nhiều khách sạn đủ loại mọc lên thì khách hàng cũng thông thái hơn, kỹ tính hơn ngoài việc quan tâm đến sự tiện nghi, phong cách phục vụ nhiều người còn quan tâm tới nội thất, kiến trúc và không gian của khách sạn. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Vậy thiết kế khách sạn cần lưu ý những điều gì? Những thông tin mà chúng tôi giới thiệu trong bài viết dưới đây chắc chắn có thể giúp bạn hình dung rõ hơn và có những tư liệu hữu ích về vấn đề này.</span></p>\n', '1575301782_4725a4fec91ad234559c.png', 0, 1, 1, 1573658398, 1590064871, 0, NULL, 0),
(13, 0, 'Thi Công Xây Dựng', 'thi-cong-xay-dung', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Để hoàn thành một dự án xây dựng, một kế hoạch hiệu quả là cần thiết. Một dự án xây dựng hoàn thiện phải đảm bảo thi công xây dựng đúng chương trình, ngân sách, an toàn xây dựng tại công trường cùng nhiều vấn đề khác.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576023849_302d3d868d9a5112c677.png\" style=\"width: 586px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><a href=\"https://www.angiakhang.com/thi-cong-xay-dung\">Thi công xây dựng</a></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Vậy, <strong>thi công xây dựng là gì? </strong>Quy trình thi công xây dựng nào là chuẩn nhất? Mời bạn tham khảo bài viết dưới đây.</span></p>\n', '1577667497_79cf1a18de5e866ad79a.jpg', 0, 1, 1, 1573658398, 1590064816, 0, NULL, 0),
(14, 13, 'Xây nhà phố', 'xay-dung-nha-pho', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Công ty xây dựng nhà phố An Gia Khang, chúng tôi luôn tự hào là một trong những công ty xây dựng nhà phố uy tín, chuyên nghiệp nhất, luôn mang đến sự hài lòng cho tất cả các quý khách hàng dù là khách hàng khó tính nhất. Xây dựng nhà phố là một trong những loại nhà được nhiều khách hàng chọn lựa hiện nay. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586755990_b83c2b3b471dc926d542.jpg\" style=\"width: 609px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><a href=\"https://www.angiakhang.com/xay-dung-nha-pho\">Xây dựng nhà phố</a></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Với chúng tôi sự hài lòng của quý khách hàng về công trình xây dựng còn hơn cả lợi nhuận. Mọi thông tin tư vấn về thiết kế và thi công xây dựng nhà phố, quý khách vui lòng liên hệ: <span style=\"color: rgb(226, 80, 65);\">0937 181 181</span></span></p>\n', '1586756337_b9be8f5cafe239dbc214.jpg', 0, 1, 1, 1573658398, 1590571348, 0, NULL, 2),
(15, 13, 'Xây biệt Thự', 'xay-biet-thu', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;\'><span style=\"font-size: 14px; line-height: 115%; font-family: Arial, Helvetica, sans-serif;\">Xây nhà biệt thự từ lâu đã là lựa chọn của những gia chủ có tiềm lực về kinh tế đồng thời ưa thích sự sang trọng, có chút cầu kỳ. Các mẫu biệt thự đẹp với sự đầu tư từ thiết kế thi công tới các tiểu tiết chính là lựa chọn hàng đầu nếu bạn thích sự sang trọng quý phái. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%;\"><img src=\"/uploads/contents/1584033211_af4b672f4189d9166b24.jpg\" style=\"width: 651px;\" class=\"fr-fic fr-dib\"></span></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"http://www.angiakhang.com/xay-biet-thu\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây nhà biệt thự</span></span></a></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;\'><span style=\"font-size: 14px; line-height: 115%; font-family: Arial, Helvetica, sans-serif;\">Vậy xây dựng biệt thự cần phải chú ý điều gì? Bài viết dưới đây chúng tôi giúp các bạn tìm hiểu những thông tin chi tiết về vấn đề này.</span></p>\n', '1584033932_80072b43b7271ed4184c.jpg', 0, 1, 1, 1573658398, 1590571782, 0, NULL, 0),
(16, 13, 'Xây nhà hàng', 'xay-dung-nha-hang', '<p style=\"text-align: justify;\"><span style=\"font-size: 14px; line-height: 115%; font-family: Arial, Helvetica, sans-serif;\">Kinh doanh nhà hàng hiện nay đang là một loại hình dịch vụ mới được nhiều người lựa chọn. Tuy nhiên với những người mới bắt đầu thì tất cả đều rất bỡ ngỡ và lạ lẫm. </span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%;\"><img src=\"/uploads/contents/1575820368_4d48560068a384723fef.jpg\" style=\"width: 643px;\" class=\"fr-fic fr-dib\"></span></span></span></p>\n<p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/xay-dung-nha-hang\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây dựng nhà hàng</span></span></a></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><span style=\"line-height: 115%;\">Vậy xây dựng nhà hàng cần phải lưu ý những điều gì? Bài viết dưới đây chúng tôi giới thiệu đến các bạn những thông tin chi tiết về vấn đề này để tiện tham khảo</span></span></p>\n', '1578970203_0016a1c95d06a186fcab.png', 0, 1, 1, 1573658398, 1590733726, 0, NULL, 6),
(18, 0, 'Xây Nhà Trọn Gói', 'xay-nha-tron-goi', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Xây dựng nhà trọn gói hiện nay đang là một hu hướng mới được nhiều người ưa chuộng và lựa chọn cho công trình của mình. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Thế nhưng bạn có từng thắc mắc tại sao xây dựng nhà trjọn gói được lòng nhiều gia chủ đến vậy? Và liệu rằng nó có tiềm ẩn rủi ro hay hạn chế nào không? </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575448757_03efd4febc549692c38c.jpg\" style=\"width: 599px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><a href=\"http://www.angiakhang.com/xay-nha-tron-goi\"><strong>Xây nhà trọn gói</strong></a></span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Này nay, nhiều gia chủ chọn dịch vụ xây nhà trọn gói để có thể sở hữu một ngôi nhà chất lượng mà không mất nhiều công sức và thời gian.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tuy nhiên nhiều người vẫn băn khoăn không biết, <em><strong>có nên xây dựng nhà trọn gói không</strong></em><strong>?</strong> Bài viết dưới đây chúng tôi giúp các bạn hiểu rõ hơn về loại hình dịch vụ này.</span></span></p>\n', '1576851875_bbaef2dc8f6a506037de.jpg', 0, 1, 1, 1573658398, 1590049742, 0, NULL, 0),
(21, 4, 'Công ty An Gia Khang', 'gioi-thieu', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:7.5pt;margin-left:0in;line-height:normal;font-size:15px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;background:white;\'><span style=\"font-size: 14px; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51);\">Công ty xây dựng An Gia Khang là một trong những công ty xây dựng uy tín và chất lượng tại Tphcm và Phú Quốc hiện nay. Với sự phát triển mạnh mẻ với đội ngũ nhân sự là những kiến trúc sư chuyên nghiệp nhiều năm kinh nghiệm, cùng với đội ngũ anh em công nhân đầy nhiệt quyết, gắn bó lâu dài trên những công trình dự án.</span></p>\n<p style=\"margin: 0in 0in 7.5pt; line-height: normal; font-size: 15px; font-family: Calibri, sans-serif; text-align: center; background: white;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\"><img src=\"/uploads/contents/1574590166_8557ab499e4a56e25539.png\" style=\"width: 661px;\" class=\"fr-fic fr-dib\"></span></span></span></p>\n<p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/gioi-thieu\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Đội ngũ công nhân An Gia Khang</span></a></p>\n', '1574155533_0e7d0874a79da67eb716.jpg', 0, 1, 1, 1573658398, 1592224987, 0, NULL, 0),
(23, 18, 'Xây nhà cấp 4 mái thái', 'xay-nha-cap-4-mai-thai', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Có thể nói rằng, <strong>mẫu nhà cấp 4</strong> đã gắn liền với người dân Việt Nam, nếp sống và văn hóa Việt Nam bao lâu nay. Chính vì thế đến nay, các mẫu xây dựng nhà cấp bốn chưa bao giờ lỗi thời, thế nhưng tiêu chí và quy mô qua các năm đều khác nhau. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575603927_10756910b668fa5d2cb1.jpg\" style=\"width: 607px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/xay-nha-cap-4-mai-thai\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây nhà cấp 4 mái thái</span></span></a></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Chính vì thế để có được một căn nhà cấp 4 hiện đại hợp xu hướng khách hàng hãy cùng công ty xây dựng nhà cấp 4 An Gia Khang tìm hiểu nhé.</span></p>\n', '1574350777_20222d2ccf75025438b9.jpg', 0, 1, 1, 1573658398, 1590049531, 0, NULL, 4),
(25, 13, 'Xây khách sạn', 'xay-dung-khach-san', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Kinh doanh lĩnh vực khách sạn là một trong những kênh đầu tư mang lại tiềm năng lợi nhuận cao đang được quan tâm. Trong đó một trong những vấn đề khởi đầu kinh doanh lĩnh vực này chính là xây khách sạn tạo nên không gian tuyệt vời , dịch vụ chất lượng và giá cả hợp ý giúp cho khách hàng lưu trú cảm thấy hoài lòng.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1584981565_c0f9cc5d0169321c27c1.jpg\" style=\"width: 621px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><a href=\"http://www.angiakhang.com/xay-dung-khach-san\">Xây dựng khách sạn</a></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Chính vì thế mà hiện nay nhiều nhà đầu tư đang phân vân không biết nên chọn đơn vị xây dựng khách sạn trọn gói nào để có chất lượng công trình và độ uy tín cao – góp phần tạo nên sự thành công trong việc kinh doanh.</span></p>\n', '1582303439_81152b8f4397f6e80607.jpg', 0, 1, 1, 1573658398, 1590572217, 0, NULL, 1),
(26, 13, 'Xây nhà xưởng', 'xay-dung-nha-xuong', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Xây dựng nhà xưởng công nghiệp đạt chất lượng sẽ giúp nâng cao năng lực sản xuất của công ty, mang đến nhiều lợi nhuận. Bên cạnh việc định hình và lên kế hoạch thì khi xây dựng nhà xưởng cần chú ý đến sự bền vững cho công trình. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1587359480_07c421451da4ffaaed0e.png\" style=\"width: 629px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><a href=\"https://www.angiakhang.com/xay-dung-nha-xuong\">Xây dựng nhà xưởng công nghiệp</a></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><span style=\"color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important;\">Công ty thiết kế và xây dựng An Gia Khang là một trong những đơn vị thi công xây dựng nhà xưởng công nghiệp uy tín tại tphcm hiện nay.</span> </span></p>\n', '1575555367_bc40ede7ca36ed921d32.png', 0, 1, 1, 1573658398, 1590570749, 0, NULL, 5),
(35, 35, 'Công trình 1', 'cong-trinh-1', '', '1574787897_b86b74c92d1a5f583551.jpg', 0, 1, 1, 1573658398, 1573658398, 0, NULL, 0),
(36, 3, 'Biệt thự Villas Shop SV2A', 'biet-thu-villas-shop-sv2a', '<p>Công ty xây dựng Biệt thự Villas Shop SV2A tại Phú Quốc. Công ty xây dựng An Gia Khang là đơn vị thi công xây dựng.</p>\n<p style=\"text-align: center;\"><img src=\"/uploads/contents/1576294259_a829ab4cf25ebb7dacec.jpg\" style=\"width: 632px;\" class=\"fr-fic fr-dib\"></p>\n<p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/biet-thu-villas-shop-sv2a\">Công trình xây dựng tại Phú Quốc</a></p>\n', '1576294407_1ad84542257e4e135949.jpg', 0, 1, 1, 1573658398, 1590065731, 0, NULL, 0),
(37, 3, 'Xây nhà phố SS 23 tại Phú Quốc', 'xay-nha-pho-ss23-tai-phu-quoc', '<p>Dự án xây dựng nhà phố SS23 công ty xây dựng An Gia Khang thực hiện . Nhà phố được thiết kế 1 trệt 3 tầng được An Gia Khang thiết kế và thi công xây dựng.</p>\n<p style=\"text-align: center;\"><img src=\"/uploads/contents/1576295242_767f0e0fe51ab32553e9.jpg\" style=\"width: 521px;\" class=\"fr-fic fr-dib\"></p>\n<p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/xay-nha-pho-ss23-tai-phu-quoc\">Đang thi công xây dựng nhà phố tại Phú Quốc</a></p>\n', '1576295488_77f181daa781e69e67f7.jpg', 0, 1, 1, 1573658398, 1590065679, 0, NULL, 0),
(38, 3, 'Xây biệt thự Paris Villas tại Đảo Ngọc Phú Quốc', 'xay-dung-biet-thu-paris-villas-tai-phu-quoc', '<p>Biệt thự Paris Villas tại Đảo Ngọc Phú Quốc, tỉnh Kiên Giang là một trong những công trình chủ thầu xây dựng An Gia Khang là đơn vị thiết kế và thi công xây dựng.</p>\n<p style=\"text-align: center;\"><img src=\"/uploads/contents/1576296223_1cf1c2105d93ea689224.jpg\" style=\"width: 619px;\" class=\"fr-fic fr-dib\"></p>\n<p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/xay-dung-biet-thu-paris-villas-tai-phu-quoc\">Xây biệt thự Paris Villas tại Phú Quốc</a></p>\n', '1576296748_46f915544f0edcd93e32.jpg', 0, 1, 1, 1573658398, 1590065628, 0, NULL, 0),
(39, 3, 'Biệt thự Sonasea Villas tại Đảo Ngọc Phú Quốc', 'xay-biet-thu-sonasea-sv1a-phu-quoc', '<p>Xây biệt thự SV1A Đảo Ngọc Phú Quốc một trong những biệt thự đẳng cấp bậc nhất tại Phú Quốc, tỉnh Kiên Giang. Chủ thầu xây dựng Công ty xây dựng An Gia Khang.</p>\n<p style=\"text-align: center;\"><img src=\"/uploads/contents/1576297634_589adfdd1b01014fd72f.jpg\" style=\"width: 636px;\" class=\"fr-fic fr-dib\"></p>\n<p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/xay-biet-thu-sonasea-sv1a-phu-quoc\">Xây biệt thự Sonasea Villas Đảo ngọc Phú Quốc</a></p>\n', '1576297897_09db9e3b0a0f813160be.png', 0, 1, 1, 1573658398, 1590065579, 0, NULL, 0),
(40, 3, 'Xây dựng công viên Bình Hưng - Bình Chánh', 'xay-dung-cong-vien-binh-chanh', '<p>Thi công xây dựng công viên Bình Hưng, Bình Chánh tại TPHCM. Công trình do công ty xây dựng An Gia Khang thực hiện.</p>\n<p style=\"text-align: center;\"><img src=\"/uploads/contents/1576501997_0e198a0c22c9b74e9ad7.jpg\" style=\"width: 626px;\" class=\"fr-fic fr-dib\"></p>\n<p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/xay-dung-cong-vien-binh-chanh\">Xây dựng công viên cây xanh</a></p>\n', '1576502497_11c911616b3e2aba10d6.png', 0, 1, 1, 1573658398, 1590065502, 0, NULL, 0),
(42, 35, 'Công trình 1', 'cong-trinh-1-1', '<p>Công trình 1</p>', '1574812589_8f9e1274e4deac573307.jpg', 0, 1, NULL, 1573658398, 1573658398, 0, NULL, 0),
(43, 3, 'Xây dựng nhà phố Sonasea ss23 - s07', 'xay-dung-nha-pho-sonasea-ss23-07', '<p><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Công ty cổ phần xây dựng An Gia khang là đơn vị thi công xây dựng nhà phố Sonasea 23 - 07 tại Phú Quốc, tỉnh Kiên Giang.</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576505326_389e1cf20edc1fc6dd09.png\" style=\"width: 636px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/xay-dung-nha-pho-sonasea-ss23-07\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Thi công xây nhà phố Sonasea resort ss23</span></a></p>\n', '1576505545_3f7b1e3cae0625f04b5a.png', 0, 1, 1, 1573658398, 1590065448, 0, NULL, 0),
(44, 14, 'Nhà phố 1 tầng 1 trệt', 'nha-pho-1-tang-1-tret', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Bạn đang lên kế hoạch thiết kế và xây dựng cho mình một ngôi nhà riêng. Bạn loay hoay với không gian mình đang có và không biết phải lên ý tưởng cũng như lựa chọn kiểu nhà như thế nào cho phù hợp. Không gian thành phố không mấy rộng rãi, dưới đây là một số mẫu nhà phố đẹp 1 tầng để bạn tham khảo và tự mình tạo nên một cơ ngơi riêng nhé!</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1584588970_79cfe49e3101c4b3a9c5.jpg\" style=\"width: 589px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/nha-pho-1-tang-1-tret\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nhà phố 1 tầng đẹp</span></a></p>\n', '1574813664_40910d6aed02e5c9e4ec.jpg', 0, 1, 1, 1573658398, 1590064685, 0, NULL, 0),
(45, 14, 'Nhà phố 2 tầng mặt tiền', 'nha-pho-2-tang', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><strong><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nhà phố</span></strong><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> là một không gian sống lý tưởng được nhiều gia đình lựa chọn. Với không gian sống riêng tư, tiện nghi và yên tĩnh càng khiến nhiều người muốn tìm cho mình một ngôi nhà phố.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575709996_50d9c51c68bf3923eadb.jpg\" style=\"width: 564px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><a href=\"https://www.angiakhang.com/nha-pho-2-tang\">Mẫu nhà phố đẹp 2 tầng</a></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Nhà phố 2 tầng mái thái</strong> hiện nay đang được nhiều người quan tâm thế nhưng không ít người không biết nhà mái thái 2 tầng là gì và những đặc trưng của loại hình kiến trúc này là gì. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Bài viết dưới đây chúng tôi giới thiệu đến các bạn một số những thông tin, đặc điểm về nhà mái thái 2 tầng để các bạn tiện tham khảo.</span></p>\n', '1574813650_86b2ffdd339a14b5175c.jpg', 0, 1, 1, 1573658398, 1590064630, 0, NULL, 0),
(46, 0, 'Báo giá', 'bao-gia', '<p><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Tư vấn thiết kế và báo giá xây nhà trọn gói tại Tp.hcm và Phú Quốc. Công ty Cổ Phần thiết kế và xây dựng An Gia Khang tự hào là một trong những nhà thầu xây dựng uy tín và chất lượng hiện nạy.</span></p>', '1582304481_a3778a2bd56a400095e5.jpg', 0, 1, 1, 1573658398, 1590048966, 0, NULL, 0),
(47, 46, 'Báo giá xây nhà tại tphcm', 'bao-gia-xay-nha-tron-tai-tphcm', '<p style=\"text-align: left;\">Xin gửi đến quý khách hàng bảng báo giá xây nhà tại Tp.HCM. Công ty Cổ Phần Thiết Kế và Xây Dựng An Gia Khang.</p>', '1575369585_dcb7b0e4b1e92f019bc1.png', 0, 1, 1, 1573658398, 1590045959, 0, NULL, 0),
(49, 46, 'Báo giá xây nhà tại Phú Quốc', 'bao-gia-xay-nha-tai-phu-quoc', '<p style=\"text-align: left;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Xin gửi đến quý khách hàng báo giá xây nhà tại Phú Quốc tỉnh Kiên Giang. Công ty Cổ Phần Thiết kế và Xây Dựng An Gia Khang.</span></p>', '1575370700_94c48f4ceb0d9282e30a.png', 0, 1, 1, 1573658398, 1592465492, 0, NULL, 0),
(52, 15, 'Mẫu biệt thự đẹp 1 tầng', 'mau-biet-thu-dep-1-tang', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu biệt thự đẹp 1 tầng đang là loại hình được lựa chọn xây dựng khá phổ biến hiện nay. </span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhờ vào ưu điểm phù hợp cho họ gia đình có nhiều người cùng sinh sống, dễ dàng sắp xếp, bố trí không gian, quá trình thi công không quá phức tạp, thời gian xây dựng không quá dài ngày, không cần thiết kế cầu thang, không gian thoáng đãng và thoải mái hòa mình cùng thiên nhiên,....</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1580561401_e144f5c391eb9988cad7.jpg\" style=\"width: 632px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/mau-biet-thu-dep-1-tang\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu biệt thự 1 tầng</span></a></p>\n', '1580561445_f02e1a2ba66ad2b552e2.jpg', 0, 1, 1, 1573658398, 1590053344, 0, NULL, 0),
(53, 15, 'Mẫu biệt thự 2 tầng', 'mau-biet-thu-2-tang', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu biệt thự 2 tầng thực chất là gì? Hiện nay bao gồm những loại nào? Và giá cả, chi phí xây dựng cũng như những việc lưu ý khi xây dựng các mẫu biệt thự hiện nay là gì?</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1585232070_ca30faf51a889a85dc0a.jpg\" style=\"width: 627px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><a href=\"https://www.angiakhang.com/mau-biet-thu-2-tang\">Mẫu biệt thự 2 tầng</a><br></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"> Hãy cùng tìm câu trả lời qua những thông tin từ bài viết dưới đây nhé!</span></p>\n', '1585232440_c4c4c05ec9d21ddc9d83.jpg', 0, 1, 1, 1573658398, 1590053285, 0, NULL, 0),
(54, 15, 'Mẫu biệt thự 3 tầng', 'mau-biet-thu-3-tang', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu biệt thự 3 tầng là một trong những mẫu thiết kế phức tạp, đòi hỏi đơn vị thi công thiết kế và xây dựng cần có nhiều kinh nghiệm. Biệt thự 3 tầng thể hiện được sự giàu có và sang trọng của gia chủ, vì vậy trong thiết kế cần chú ý để họa tiết và cách phối màu phù hợp.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1585234249_8bf5bc1c2826a46c6c6c.jpg\" style=\"width: 605px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/mau-biet-thu-3-tang\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự 3 tầng</span></span></a></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Bạn mong muốn được sống trong một ngôi nhà sang trọng? Bạn đang tìm hiểu về xu hướng biệt thự 3 tầng hiện nay.</span></p>\n', '1585234699_85ebcc68fe5f7c4e1023.jpg', 0, 1, 1, 1573658398, 1590053161, 0, NULL, 0),
(55, 15, 'Mẫu biệt thự kiểu pháp đẹp', 'mau-biet-thu-kieu-phap', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Hiện nay khi nói đến các mẫu biệt thự kiểu Pháp đẹp thì người ta sẽ liên tưởng ngay đến hình ảnh một không gian sống hiện đại và sang trọng bậc nhất. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1585235164_15ad5e5af02d0aad9108.jpg\" style=\"width: 619px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/mau-biet-thu-kieu-phap\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự kiểu Pháp</span></span></a></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nếu bạn đang có nhu cầu tìm hiểu các thông tin liên quan đến các mẫu biệt thự kiểu Pháp đẹp thì đừng bỏ qua những thông tin từ bài viết dưới đây nhé!</span></p>\n', '1575695136_41c014359a94a0e5da6e.jpg', 0, 1, 1, 1573658398, 1590053127, 0, NULL, 0),
(56, 15, 'Mẫu biệt thự kiểu hiện đại', 'mau-biet-thu-kieu-hien-dai', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu biệt thự hiện đại là sự sáng tạo trong thiết kế biệt thự, để có được những mẫu biệt thự theo phong cách hiện đại, cần có những đơn vị nhiều kinh nghiệm trong xây dựng.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bạn đang có nhu cầu thiết kế xây dựng cho mình một không gian sống đơn giản nhưng không kém sự hiện đại và tinh tế. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1580565750_b647df9e1d5138a742c8.jpg\" style=\"width: 634px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><a href=\"https://www.angiakhang.com/mau-biet-thu-kieu-hien-dai\">Mẫu biệt thự hiện đại</a></span></span></p>\n<p><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Vậy tại sao bạn không chọn các mẫu biệt thự kiểu hiện đại đang được cho là “hot” được nhiều người đánh giá cao hiện nay.</span></p>\n', '1580566204_d7cc968d8bc820c11fcb.jpg', 0, 1, 1, 1573658398, 1590053079, 0, NULL, 0),
(57, 15, 'Mẫu biệt thự kiểu ấn độ', 'mau-biet-thu-kieu-an-do', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Một không gian riêng tư để bạn có thể hoàn toàn thư giãn, không vướng bận, tách biệt với những ồn ào bên ngoài sẽ là mong ước của nhiều người. Nhưng không quá khó để thực hiện mong ước thành hiện thực. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1580568894_6166b760ecbd66985c75.jpg\" style=\"width: 602px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><a href=\"https://www.angiakhang.com/mau-biet-thu-kieu-an-do\">Mẫu biệt thự kiểu ấn độ</a></span></span></p>\n<p><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Những mẫu biệt thự kiểu Ấn Độ sẽ là một gợi ý cực kỳ tốt nếu bạn đang có ý định tạo cho mình một không gian riêng đấy nhé.</span></p>\n', '1580569138_d416669d5318fe770593.jpg', 0, 1, 1, 1573658398, 1590053051, 0, NULL, 0),
(58, 14, 'Nhà phố 4 tầng', 'nha-pho-4-tang', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Hiện nay tại các thành phố lớn, mẫu nhà phố nhiều tầng hiện đại và biệt thự sang trọng đang là xu hướng kiến trúc của các hộ gia đình. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575711699_58e5a671ddd175cc5fc0.jpg\" style=\"width: 544px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><a href=\"https://www.angiakhang.com/nha-pho-4-tang\">Nhà phố 4 tầng</a></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nhận biết được nhu cầu xây dựng nhà đó, bài viết sau đây sẽ giúp bạn hiểu rõ hơn về mẫu nhà phố đẹp 4 tầng, mẫu nhà phố rất được các hộ gia đình chọn lựa bởi sự hiện đại, tiện nghi mà không cần quá nhiều diện tích đất.</span></p>\n', '1575711394_5f76771d868b3dc9135b.jpg', 0, 1, 1, 1573658398, 1590064559, 0, NULL, 0),
(59, 18, 'Xây nhà trọn gói tại tphcm', 'xay-nha-tron-goi-tai-tphcm', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Xây nhà trọn gói là một trong những dịch vụ rất phổ biến hiện nay, vậy bạn cần biết nội dung của dịch vụ “xây nhà trọn gói” là gì không?. </span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1583767119_040485277b5ec3bf5bc0.jpg\" style=\"width: 659px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><a href=\"https://www.angiakhang.com/xay-nha-tron-goi-tai-tphcm\"><strong>Xây nhà trọn gói tại tphcm</strong></a></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Công ty thiết kế và thi công xây dựng An Gia Khang, trước tiên xin gửi lời cảm ơn quý khách hàng đã quan tâm đến AN GIA KHANG nhà thầu xây dựng uy tín tại tphcm.</span></p>\n', '1583768277_e9ed83309dfe0707c3aa.jpg', 0, 1, 1, 1573658398, 1590049312, 0, NULL, 0),
(60, 18, 'Xây nhà trọn gói tại Phú Quốc', 'xay-nha-tron-goi-tai-phu-quoc', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Phú Quốc hiện nay là một mảnh đất màu mỡ thu hút nhiều nhà đầu tư, không những xây dựng các công trình phục vụ đời sống dân sinh mà cả các công trình phục vụ mục đích sản xuất đều được chú trọng quan tâm. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576421564_fa478489ae6247a7bc7f.jpg\" style=\"width: 605px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/xay-nha-tron-goi-tai-phu-quoc\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công ty xây dựng tại Phú Quốc</span></span></a></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Vậy các công ty xây dựng ở Phú Quốc có ưu điểm và có những đặc trưng gì? Bài viết dưới đây chúng tôi giới thiệu đến các bạn những thông tin chi tiết về vấn đề này để tiện tham khảo.</span></p>\n', '1575797303_8f612202ab2399730d1d.png', 0, 1, 1, 1573658398, 1590049156, 0, NULL, 0);
INSERT INTO `category` (`id`, `parent_id`, `title`, `slug`, `intro`, `image`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted`, `deleted_at`, `menu_order`) VALUES
(61, 14, 'Nhà phố 5 tầng đẹp', 'nha-pho-5-tang', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Với sự phát triển không ngừng hiện nay thì đất đai ở các thành phố ngày càng trở nên khan hiếm. Chính vì thế với mục đích tận dụng tối đa diện tích đất có tại các đô thị cũng như mang đến cho người dùng không gian sống tiện nghi và thoải mái nhất các <strong>mẫu nhà phố đẹp 5 tầng</strong> đã được ra đời.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/nha-pho-5-tang-7_1592201634.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span><br></p>\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><a href=\"https://www.angiakhang.com/nha-pho-5-tang\">Mẫu nhà phố 5 tầng đẹp</a></span></p>\n', 'nha-pho-5-tang-7_1592201698.jpg', 0, 1, 1, 1577677896, 1592205035, 0, NULL, 0),
(62, 13, 'Xây nhà cao tầng', 'xay-nha-cao-tang', '<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:10.0pt;line-height:12.95pt;background:white;\'><span style=\'font-size:14px;font-family:\"Arial\",\"sans-serif\";color:#414141;\'>Xây dựng nhà cao tầng</span><span style=\" border-box;-webkit-tap-highlight-color: transparent;-webkit-font-smoothing: antialiased;\"><span style=\" border-box;-webkit-tap-highlight-color: transparent;-webkit-font-smoothing: antialiased;\"><span style=\'font-size:14px;font-family:\"Arial\",\"sans-serif\";color:#414141;\'> là một hạng mục công trình lớn, vì vậy đơn vị thi công phải là nhà thầu xây dựng có năng lực và kinh nghiệm trong xây dựng công nghiệp.</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:10.0pt;line-height:12.95pt;text-align:center;background:white; border-box;-webkit-tap-highlight-color: transparent;-webkit-font-smoothing: antialiased;font-variant-ligatures: normal;font-variant-caps: normal;orphans: 2;widows: 2;-webkit-text-stroke-width: 0px;text-decoration-style: initial;text-decoration-color: initial;word-spacing:0px;\'><img src=\"/uploads/contents/1582986301_c5de8311ddb548697326.jpg\" style=\"width: 628px;\" class=\"fr-fic fr-dib\"></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:10.0pt;line-height:12.95pt;text-align:center;background:white; border-box;-webkit-tap-highlight-color: transparent;-webkit-font-smoothing: antialiased;font-variant-ligatures: normal;font-variant-caps: normal;orphans: 2;widows: 2;-webkit-text-stroke-width: 0px;text-decoration-style: initial;text-decoration-color: initial;word-spacing:0px;\'><a href=\"https://www.angiakhang.com/xay-nha-cao-tang\"><span style=\" border-box;-webkit-tap-highlight-color: transparent;-webkit-font-smoothing: antialiased;\"><span style=\" border-box;-webkit-tap-highlight-color: transparent;-webkit-font-smoothing: antialiased;\"><span style=\'font-size:14px;font-family:\"Arial\",\"sans-serif\";color:#414141;\'><span style=\"color:#9C27B0;text-decoration:none;\">Xây dựng nhà cao tầng</span></span></span></span></a></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:10.0pt;line-height:12.95pt;background:white; border-box;-webkit-tap-highlight-color: transparent;-webkit-font-smoothing: antialiased;font-variant-ligatures: normal;font-variant-caps: normal;orphans: 2;widows: 2;-webkit-text-stroke-width: 0px;text-decoration-style: initial;text-decoration-color: initial;word-spacing:0px;\'><span style=\" border-box;-webkit-tap-highlight-color: transparent;-webkit-font-smoothing: antialiased;\"><span style=\'font-size:14px;font-family:\"Arial\",\"sans-serif\";color:#414141;\'>Nếu chủ đầu tư không chú tâm có thể xảy ra những rủi ro lớn hay tạo ra các tình huống không mong muốn. Vậy quá trình xây dựng nhà cao tầng cần lưu ý những điều gì? Bài viết dưới đây chúng tôi giới thiệu đến các bạn những thông tin chi tiết về vấn đề này để tiện tham khảo.</span></span></p>\n', '1582986350_0d1d1c3a881a2cd988c4.jpg', 0, 1, 1, 1580457276, 1590571233, 0, NULL, 6),
(63, 18, 'Hợp đồng xây nhà trọn gói', 'hop-dong-xay-nha-tron-goi', '<p style=\"text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Công ty An Gia Khang là một trong những đơn vị thi công xây dựng nhà uy tín và chất lượng. Với ưu điểm về đội ngũ kiến trúc sư kinh nghiệm, dịch vụ chất lượng,..Xin chia sẽ với quý khách mẫu hợp đồng xây nhà.</span></p>\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><img src=\"/uploads/contents/1590046373_e3db6683388cfa15dcf9.jpg\" style=\"width: 614px;\" class=\"fr-fic fr-dib\"></span><a href=\"https://www.angiakhang.com/hop-dong-xay-nha-tron-goi\"></a></p>\n<p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/hop-dong-xay-nha-tron-goi\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu hợp đồng xây nhà trọn gói</span></a></p>\n', '1590046421_3600184a1523ffbebb02.jpg', 0, 1, 1, 1580458518, 1590046446, 0, NULL, 0),
(64, 14, 'Nhà phố 3 tầng', 'nha-pho-3-tang', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Hiện nay, khi dân số ngày càng tăng cao thì nhu cầu về đất đai và nhà ở ngày càng trở nên quan trọng hơn, đặc biệt là ở những thành phố lớn. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu bạn và gia đình đang tìm cho mình một mẫu nhà phố đẹp 3 tầng rộng rãi, tiện nghi và vừa túi tiền thì bài viết dưới đây sẽ đưa cho bạn một số thông tin về các mẫu nhà phố đẹp 3 tầng hiện nay.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1580460609_46e4dca586a97ac61cff.png\" style=\"width: 626px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/nha-pho-3-tang\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nhà phố 3 tầng đẹp</span></a></p>\n', '1580460836_ffe66b6dcb6f6d5447c1.png', 0, 1, 1, 1580460010, 1590064460, 0, NULL, 0),
(65, 4, 'Quy định đấu thầu xây dựng', 'cac-quy-dinh-dau-thau-trong-xay-dung', '<p style=\"text-align: justify;\"><span style=\"color: rgb(65, 65, 65); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important;\">Đấu thầu xây dựng là quá trình lựa chọn nhà thầu đáp ứng được các điều kiện về: Xây dựng và lắp đặt các thiết bị công trình, các hạng mục công trình của bên mời thầu. Đấu thầu xây dựng diễn ra trên cơ sở có sự cạnh tranh công khai, minh bạch giữa các nhà thầu</span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> </span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1582438869_c2c625fc794e6060f628.jpg\" style=\"width: 594px;\" class=\"fr-fic fr-dii\"><a href=\"http://www.angiakhang.com/cac-quy-dinh-dau-thau-trong-xay-dung\"></a></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><a href=\"https://www.angiakhang.com/cac-quy-dinh-dau-thau-trong-xay-dung\">Các quy định trong thầu xây dựng</a></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nghị định 85/2009/NĐ-CP ngày 15 tháng 10 năm 2009, hướng dẫn thi hành luật đấu thầu và lựa chọn nhà thầu xây dựng theo luật xây dựng</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Căn cứ Luật Tổ chức Chính phủ ngày 25 tháng 12 năm 2001;</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Căn cứ Luật Đấu thầu số 61/2005/QH11 ngày 29 tháng 11 năm 2005, Luật Xây dựng số 16/2003/QH11 ngày 26 tháng 11 năm 2003 của Quốc hội;</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Căn cứ Luật sửa đổi, bổ sung một số điều của các luật liên quan đến đầu tư xây dựng cơ bản số 38/2009/QH12 ngày 19 tháng 6 năm 2009 của Quốc hội;</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Xét đề nghị của Bộ trưởng Bộ Kế hoạch và Đầu tư, Bộ trưởng Bộ Xây dựng,</span></p>\n', '1582439134_6ef1a9aa1a2f31928f53.jpg', 0, 1, 1, 1582430434, 1590065246, 0, NULL, 3),
(66, 53, 'Mẫu biệt thự nhà vườn 2 tầng đẹp', 'mau-biet-thu-nha-vuon-2-tang', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Trong xu hướng thiết kế kiến trúc hiện đại các mẫu nhà vườn được nhiều gia chủ ưa thích lựa chọn. Nhiều người trải qua những năm tháng vất vả nơi phố thị phồn hoa với dân cư đông đúc xô bồ có xu hướng tìm mua đất ở ngoại thành hay về quê xây dựng nhà tiện nghi để an hưởng tuổi già. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1585495404_501d26612cd4b5969c3b.jpg\" style=\"width: 617px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/mau-biet-thu-nha-vuon-2-tang\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự nhà vườn 2 tầng</span></span></a></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu biệt thự nhà vườn 2 tầng là kiểu kiến trúc được nhiều người ưa thích lựa chọn…Vậy biệt thự nhà vườn có những tính năng ưu việt gì? Hiện nay có những mẫu biệt thự nhà vườn 2 tầng nào đẹp? Bài viết dưới đây chúng tôi giới thiệu đến các bạn những thông tin chi tiết về vấn đề này để tiện</span></p>\n', '1585495856_9afbede66b2b831a58fc.jpg', 0, 1, 1, 1585495264, 1590053246, 0, NULL, 0),
(67, 25, 'Mẫu khách sạn 3 sao', 'mau-khach-san-3-sao', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Khách sạn 3 sao hiện nay là một trong số các loại hình dịch vụ lưu trú được nhiều người lựa chọn bởi có giá cả phải chăng, chất lượng dịch vụ tiện ích. Vậy có bao giờ bạn từng băn khoăn không biết khách sạn 3 sao cần có những tiêu chuẩn gì mới được phép hoạt động <span style=\"color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important;\">kinh doanh? Bài viết dưới đây chúng tôi giúp các bạn giải đáp thắc mắc về vấn đề này để tiện tham khảo.</span> </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1585725082_add89c9d390c300acf65.jpg\" style=\"width: 593px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/mau-khach-san-3-sao\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu khách sạn 3 sao đẹp</span></a></p>\n', '1585725873_c342c288bed5cf6b5635.jpg', 0, 1, 1, 1585725747, 1590050036, 0, NULL, 0),
(68, 25, 'Mẫu khách sạn 4 sao', 'mau-khach-san-4-sao', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu khách sạn 4 sao nếu được xây dựng đúng tiêu chuẩn có thể mang đến cho du khách một không gian nghỉ dưỡng cao cấp, sang trọng và có được những trải nghiệm tuyệt vời. Nắm được các tiêu chuẩn xây dựng cũng giúp cho chủ đầu tư tạo ra các công trình tiện nghi và đẳng cấp. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1585736558_6d4cdf1221576e4f9874.jpg\" style=\"width: 616px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/mau-khach-san-4-sao\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu khách sạn 4 sao</span></span></a></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Ngoài việc đề cập tới các tiêu chuẩn bắt buộc của một khách sạn 4 sao, bài viết dưới đây chúng tôi giới thiệu đến các bạn các mẫu khách sạn 4 sao đẹp.</span></p>\n', '1585736613_f11b0699968a97ef3f8a.jpg', 0, 1, 1, 1585736613, 1590049975, 0, NULL, 0),
(69, 53, 'Mẫu biệt thự 2 tầng mini', 'mau-biet-thu-2-tang-mini', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Bên cạnh các kiểu kiến trúc khác biệt thự mini ngày càng được nhiều gia đình ưa chuộng lựa chọn. Biệt thự mini có thể mang tới cho gia đình bạn một không gian đẹp, diện tích đất sử dụng không quá lớn mà chi phí xây dựng cũng phải chăng. Vậy quá trình xây dựng biệt thự 2 tầng mini cần lưu ý điểm gì để có được một công trình hoàn hảo. Bài viết dưới đây sẽ giúp các bạn giải đáp thắc mắc về vấn đề này.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><img src=\"/uploads/contents/1586015950_2e5b2ac117ccdddfa759.jpg\" style=\"width: 602px;\" class=\"fr-fic fr-dib\"></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/mau-biet-thu-2-tang-mini\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu biệt thự 2 tầng mini</span></a></p>\n', '1586016158_8ff096e604b8a46e9420.jpg', 0, 1, 1, 1586014687, 1590053204, 0, NULL, 0),
(70, 14, 'Mẫu nhà phố 6 tầng', 'mau-nha-pho-6-tang', '<p style=\"text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Xin giới thiệu quý khách hàng mẫu nhà phố 6 tầng đẹp nhất 2020. Công ty thiết kế và xây dựng An Gia Khang, chuyên thực hiện xây dựng các dự án nhà phố, biệt thự cao cấp và nhà cao tầng tại tphcm và Phú Quốc.</span></p>\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><img src=\"/uploads/contents/1587471544_6f2fbe5faa1ec2317ba6.jpg\" style=\"width: 549px;\" class=\"fr-fic fr-dib\"></span></p>\n<p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/mau-nha-pho-6-tang\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nhà phố 6 tầng</span></a></p>\n', '1587471576_88baa4f9521a6bfe136c.jpg', 0, 1, 1, 1587471576, 1590064421, 0, NULL, 0),
(71, 25, 'Mẫu khách sạn 5 sao', 'mau-khach-san-5-sao', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Những mẫu khách sạn 5 sao đẹp và sang trọng, khi có nhu cầu nghỉ dưỡng nhiều người thường chọn khách sạn 5 sao vì những tiện ích mà nó mang lại. Chính bởi vậy nhiều chủ đầu tư cũng quyết định xây dựng khách sạn 5 sao để đáp ứng nhu cầu này. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><img src=\"/uploads/contents/1587821251_81092ec4a581c72cdc72.jpg\" style=\"width: 602px;\" class=\"fr-fic fr-dib\"></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/mau-khach-san-5-sao\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu khách sạn 5 sao</span></a></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Tuy vậy bạn có biết rằng khách sạn 5 sao có những tiêu chuẩn cụ thể và khắt khe? Hãy cùng tham khảo bài viết dưới đây của chúng tôi để tìm hiểu những mẫu khách sạn 5 sao đẹp và tiêu chuẩn xây dựng khách sạn 5 sao là gì nhé.</span></p>\n', '1587821541_0dae1ee974b29200fbd5.jpg', 0, 1, 1, 1587820615, 1590049891, 0, NULL, 3),
(72, 26, 'Mẫu nhà xưởng công nghiệp', 'mau-nha-xuong-cong-nghiep', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nhà thầu xây dựng nhà xưởng công nghiệp An Gia Khang với nhiều năm trong lĩnh vực thiết kế và xây dựng tại Tphcm. Hãy liên hệ ngay với chúng tôi đề được tư vấn và báo giá chi tiết các hạng mục xây dựng nhà xưởng công nghiệp.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1591626658_5b8c58b5b205aee932df.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/mau-nha-xuong-cong-nghiep\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu nhà xưởng công nghiệp</span></a></p>\n', '1591626687_2ae5c51616fd25d2568f.jpg', 0, 1, 1, 1591626527, 1591627600, 0, NULL, 1),
(73, 16, 'Mẫu nhà hàng ăn uống đẹp', 'mau-nha-hang-an-uong-dep', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Kinh doanh nghiệp vụ nhà hàng, khách sạn đang được nhiều gia chủ chọn lựa hiện nay. Xã hội ngày càng phát triện, chất lượng cuộc sống cũng được nâng lên không ngừng, các dịch vụ phục vụ nhu cầu ngày càng đa dạng hơn như: Khách sạng, nhà hàng, spa…. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1591629083_5fcb676b7a5ba35af73d.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/mau-nha-hang-an-uong-dep\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà hàng ăn uống</span></span></a></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trong đó loại hình dịch vụ kinh doanh nhà hàng, đươc nhiều khách hàng lựa chọn, và đang phát triển mạnh mẽ tại tphcm. Thành Phố Hồ Chí Minh với ăn hóa ẩm thực nổi tiếng, thì loại hình kinh doanh nhà hàng đang thu hút được nhiều chủ đầu tư quan tâm. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mọi thông tin tư vấn về thiết kế và xây dựng nhà hàng, hãy gọi ngay Hotline: 0937 181 181 để được nhân viên chúng tôi tư vấn chi tiết.</span></p>\n', '1591631512_508d9585bffa28d4c00f.jpg', 0, 1, 1, 1591628593, 1591631512, 0, NULL, 1),
(74, 16, 'Mẫu nhà hàng tiệc cưới', 'mau-nha-hang-tiec-cuoi', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Dịch vụ cưới hỏi ngày nay được nhiều cặp đôi chọn lựa tại những nhà hàng tiệc cưới sang trọng với không gian thiết kế đẹp, ấn tượng, ấm cúm. Đây sẽ là nơi để lưu lại những kỷ niệm đẹp nhất của những cặp đôi, vì vậy loại hình kinh doanh nhà hàng tiệc cưới đang được nhiều gia chủ chọn lựa. Mọi thông tin tư vấn và thiết kế vui lòng liên hệ: 0937 181 181.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1591631285_ae13a7a742436e42c602.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/mau-nha-hang-tiec-cuoi\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu nhà hàng tiệc cưới</span></a></p>\n', '1591631404_909161f5bb9c0c10469a.jpg', 0, 1, 1, 1591631043, 1591631404, 0, NULL, 2),
(75, 62, 'Mẫu nhà cao tầng ', 'mau-nha-cao-tang', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Xây dựng nhà cao tầng là một trong  những công trình dự án lớn, vì vậy chủ đầu tư cần tìm hiểu kỹ lưỡng những nhà thầu xây dựng lớn có uy tín và thương hiệu trên thị trường hiện nay. An Gia Khang tự tin là một trong những nhà thầu xây dựng uy tín và có nhiều kinh nghiệm trong lĩnh vực xây dựng nha cao tầng, nhiều tầng, cao ốc..</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1591673466_bee1dafbac3a57e29fbf.jpg\" style=\"width: 641px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/mau-nha-cao-tang\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà cao tầng</span></span></a></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Xin được chia sẽ những mẫu nhà cao tầng đẹp nhất hiện nay, mọi thông tin tư vấn và thiết kế nhà cao tầng, quý khách vui lòng liên hệ :0937 181 181 hoặc đăng ký form để được nhân viên tư vấn gọi lại quý khách.</span></p>\n', '1591673642_23bdd70fa67cb0a719d0.jpg', 0, 1, 1, 1591673168, 1591674499, 0, NULL, 1);
INSERT INTO `content` (`id`, `title`, `slug`, `intro`, `content`, `is_lock`, `image`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(13, 'Chính sách và quy định sử dụng', 'quy-trinh-lam-viec', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><strong>CHÍNH SÁCH VÀ QUY ĐỊNH SỬ DỤNG </strong></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG </strong>xin gửi lời cảm ơn đến quý khách hàng đã tin tưởng và sử dụng sản phẩm và dịch vụ AN GIA KHANG cung cấp..</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Nhằm mục đích giúp khách hàng dễ dàng sử dụng các dịch vụ trên website <strong>www.angiakhang.com</strong> thuộc <strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG </strong>”AN GIA KHANG  CO.LTD”, quý khách hàng khi tham gia website đã đồng ý với các điều khoản và điều kiện sử dụng dịch vụ tại công ty. Công ty có thể toàn quyền thay đổi, chỉnh sửa hoặc bổ sung thêm hoặc bở bớt các điều khoản và điều kiện sử dụng trên website mà không cần thông báo trước quý khách hàng. Khi khách hàng thực hiện các giao dịch trên website cần kiểm tra kỷ thông tin và những điều khoản dịch vụ trước khi chấp nhận thanh toán.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><strong><em>Quý khách hàng thường xuyên đăng nhập vào website để cập nhật những thông tin chính sách mới nhất.</em></strong></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><strong>1. VỀ QUY ĐỊNH SỬ DỤNG</strong></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG </strong>rất cảm ơn tất cả quý khách hàng đã đang sử dụng dịch vụ tại công ty. Điều kiện quý khách hàng tham gia website từ 18 tuổi trở lên và không bị mất năng lực hành vi dân sư, còn khách hàng dưới 18 tuổi phải có sự giám sát của người quản lý đại diện hợp pháp.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG </strong>cấp giấy phép sử dụng để quý khách hàng có thể mua sắm trên trang web trong những quy định và điều kiện sử dụng đã đề ra.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Chúng tôi nghiêm cấm sử dụng bất kỳ phần nào của trang web này với mục đích thương mại hoặc nhân danh bất kỳ đối tác thứ ba nào, nếu không được <strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG </strong>cho phép trước bằng văn bản. Nếu vi phạm bất cứ điều nào trong quy định và điều khoản, chúng tôi sẽ hủy bỏ giấy phép của quý khách hàng mà không cần báo trước.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Quý khách hàng không cần đăng ký tài khoản khi mua sản phảm, dịch vụ trên trang web những sẽ được yêu cầu cung cấp thông tin cá nhân để đơn hàng được chấp nhận và xử lý hợp lệ.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><strong> 2.  ĐƠN HÀNG VÀ GIÁ CẢ</strong></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG </strong>có thể yêu cầu quý khách hàng cung cấp địa chỉ, số điện thoại và email trước khi chấp nhận đơn   hàng. Chúng tôi cam kết sẽ cung cấp thông tin giá cả chính xác nhất cho người tiêu dùng. Nếu có những sai sót xảy ra, chúng tôi sẽ liên hệ trực tiếp để hướng dẫn hoặc thông báo hủy đơn hàng cho quy khách hàng.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Chúng tôi cũng có quyền từ chối hoặc hủy bỏ bất kỳ đơn hàng nào của quý khách hàng, đơn hàng đó đã được xác nhận hoặc đã được thanh toán. Trong trường hợp chúng tôi hủy bỏ đơn hàng sau khi đơn hàng đã được thanh toán vì lỗi của quý khách hàng. Chúng tôi  sẽ hoàn trả lại cho quý khách hàng khoản tiền đã thanh toán của quý khách hàng.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><strong>3. QUYỀN SỞ HỮU TRÍ TUỆ</strong></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Mọi quyền sở hữu trí tuệ(đã đăng ký hoặc chưa đăng ký), bao gồm nhưng không hạn chế quyền đối với các nội dung thông tin và tất cả các thiết kể hình ảnh, văn bản, đồ họa, phần mềm, video, âm nhạc, âm thanh, biên dịch, mã nguồn và phần mền cơ bản được sử dụng trên trang web này đều là tài sản của <strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG</strong>. Toàn bộ nội dung và hình thức của trang web được bảo hộ bởi pháp luật sở hữu trí tuệ Việt Nam. </p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><strong>4. THANH TOÁN AN TOÀN VÀ BẢO MẬT</strong></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG </strong>hoạt động kết hợp với nhiều ngân hàng trong nước chấp nhận thanh toán qua thể VISA, MASTERCARD, AMEX, JCB và thể tín dụng trong nước như : Ngân hàng Vietcombank, Techcombank, ACB và nhiều ngân hàng khác. Mọi thông tin thực hiện thanh toán sẽ được chuyển trực tiếp vào tài khoản ngân hàng của <strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG.</strong></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Sau khi hoàn tất thanh toán, quy khách sẽ nhân được thông tin thanh toán từ các ngân hàng qua (email hoặc số điện thoại). Trong vòng tối đa 2 ngày làm việc kể từ khi khách hàng thực hiện thanh toán vào tài khoản <strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG</strong>, quý khách hàng sẽ nhận được thông báo quá mail hoặc điện thoại từ Công ty  để xác  nhận việc thanh toán của quý khách hàng.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Mọi thông tin giao dịch của khách hàng tại <strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG </strong>qua thẻ tín dụng đều được bảo mật thông tin bằng mã hóa( bạn có thể xem tại điều khoản “quy định về bảo mật”)</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Lưu ý: Khi khách hàng sử dụng trang web thanh toán qua mang cần có chứng chỉ thanh toán an toàn.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><strong>5. THAY ĐỔI, HỦY BỎ, CHẤM DỨT GIAO DỊCH </strong></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Nếu không hài lòng với sản phẩm dịch vụ trên trang web hoặc bất kỳ điều khoản, điều kiện, quy tăc, chính sách, hướng dẫn của <strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG</strong>, quý khách hàng có thể chấm dứt mọi hoạt động và giao dịch đã thực hiện trên web <strong>www.angiakhang.com</strong></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Xin lưu ý: đơn hàng đã mua tại <strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG </strong>là không thể đổi trả và các khoản thanh toán đã được thực hiện thông qua VISA CARD, MASTERCARD, thẻ tín dụng là không thể hủy bỏ hoặc hoàn lại dưới bất kỳ hình thức nào trừ trường hợp quy định tại điều 2 của quy định và điều kiện sử dụng này.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><strong>6. LUẬT ĐIỀU CHỈNH VÀ  TRANH CHẤP</strong></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Với tất cả nội dung của website bao gồm quy định và điều kiện sử dụng này, và tất cả các tranh chấp và khiếu kiện phát sinh hoặc liên quan đến quy định, và điều khoản sử dụng này và việc sử dụng trang web hoặc các giao dịch tại <strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG </strong>sẽ được điều chỉnh và giải thích theo pháp luật Việt Nam. Mọi tranh chấp phát sinh liên quan đến quy định và điều kiện sử dụng này hoặc sử dụng trang web, hoặc các giao dịch tại Công ty trước hết sẽ được giải quyết bằng hình thức thương lượng, hòa giải. Nếu trong trường hợp tranh chấp không giải quyết được bằng thỏa thuận, quý khách hàng hoặc Công ty có thể đưa tranh chấp ra giải quyết tại tòa án có thẩm quyền tại Việt Nam.</p>\n', '', 0, NULL, 1, 1, 1575024674, 1575024920, NULL),
(14, 'Chính sách bảo hành và đổi trả', 'bao-hanh-doi-tra', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>CHÍNH SÁCH BẢO HÀNH VÀ ĐỔI TRẢ</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>    1) Trong vòng 24h kể từ khi giao hàng, <strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG </strong>chấp nhận đổi hàng/trả lại tiền cho quý khách trong trường hợp sau:</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>       a) Không đúng chủng loại, mẫu mã như quý khách đặt hàng.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>       b) Không đủ số lượng, không đủ bộ như trong đơn hàng.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>       c) Tình trạng bên ngoài bị ảnh hưởng như bong tróc, hư hỏng xảy ra trong quá trình vận chuyển,…</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>       d) Không đạt chất lượng như: quá hạn sử dụng, hết bảo hành, hư hỏng khách quan trong phạm vi bảo hành của nhà cung cấp, nhà sản xuất,…</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>    Quý khách vui lòng kiểm tra hàng hóa và ký nhận tình trạng với Nhân viên giao hàng ngay khi nhận được hàng. Khi phát hiện một trong các trường hợp trên, quý khách có thể trao đổi trực tiếp với Nhân viên giao hàng hoặc phản hồi cho chúng tôi trong vòng 24h theo đường dây nóng. <strong>Hotline: 0937 181 181</strong></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>    2) <strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG </strong>không chấp nhận đổi/trả hàng khi:</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>       a) Quý khách muốn thay đổi chủng loại, mẫu mã nhưng không thông báo trước.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>       b) Quý khách tự làm ảnh hưởng tình trạng bên ngoài như rách bao bì, bong tróc, bể vỡ,…</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>       c) Quý khách bảo quản không đúng chỉ dẫn gây hư hỏng sản phẩm.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>       d) Quý khách đã kiểm tra và ký nhận tình trạng hàng hóa nhưng không có phản hồi trong vòng 24h kể từ lúc ký nhận hàng.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Xin cảm ơn quý khách hàng đã sử dụng sản phẩm tại công ty chúng tôi.</p>\n', '', 0, NULL, 1, NULL, 1575025173, 1575025173, NULL),
(15, 'Hình thức thanh toán', 'hinh-thuc-thanh-toan', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>1. <strong>Thanh toán bằng tiền mặt</strong></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>    Hình thức giao hàng – Trả tiền mặt hiện tại chỉ áp dụng đối với những khu vực trong nội thành TPHCM.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Quý khách có thể đặt hàng trên Website hoặc đặt hàng qua điện thoại. Nhân viên sẽ tiến hành xuất hàng và xác nhận ngày giờ giao hàng cho quý khách.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>    Quý khách có trách nhiệm thanh toán đầy đủ giá trị đơn hàng cho nhân viên giao nhận của ngay khi hoàn tất kiểm tra hàng hóa và nhận hóa đơn thanh toán. Quý khách thanh toán đúng số tiền trên hóa đơn, Mọi thông tin vui lòng liên hệ hotline: <strong>0937 181 181 </strong> để được hỗ trợ.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>    2. <strong>Thanh toán bằng chuyển khoản qua Ngân hàng hoặc thẻ ATM</strong></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>    Quý khách vui lòng chuyển khoản trước giá trị đơn hàng vào tài khoản ngân hàng của <strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG</strong>. Sau khi nhận được tiền chuyển khoản, Công ty sẽ gọi điện thông báo và chuyển hàng cho quý khách.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>    Lưu ý quý khách vui lòng thông báo (bằng điện thoại, email, SMS, …) khi quý khách đã thực hiện chuyển tiền, chuyển khoản. Hoặc Quý khách vui lòng liên hệ với Bộ phận Chăm sóc khách hàng www.angiakhang.com  theo đường dây nóng <strong>0937 181 181</strong>  để thông báo đã chuyển tiền.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>    Sau khi nhận được tiền chuyển khoản, <strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG </strong>sẽ gọi điện thông báo và chuyển hàng cho quý khách.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Cảm ơn quý khách hàng đã tin tưởng và sử dụng sản phẩm của chúng tôi.</p>\n', '', 0, NULL, 1, NULL, 1575025349, 1575025349, NULL),
(16, 'Chính sách vận chuyển', 'van-chuyen-giao-nhan-lap-dat-hang', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'> </p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Chính sách vận chuyển / giao nhận /</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG </strong>giao hàng tận trong giời hành chính, thời gian giao hàng từ 07h00 đến 22h00 hàng ngày.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>    *** Giao hàng nhanh chóng và đảm bảo.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>    1. Trách nhiệm xử lý đơn hàng và vận chuyển sản phẩm đến khách hàng do <strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG</strong> đảm nhận và phụ thuộc vào hình thức thanh toán và vận chuyển cho các sản phẩm mà khách hàng khai báo khi tạo đơn hàng.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>    2. <strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG </strong>sẽ chịu trách nhiệm đảm bảo khách hàng nhận được hàng hóa đúng như đã đặt mua trong thời gian giao hàng như cam kết. Trường hợp hàng hóa bị lỗi, hư hại trong quá trình vận chuyển www.angiakhang.com sẽ chấp nhận việc đổi hàng mới cho quý khách hàng.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>    3. Trong trường hợp vì lý do bất khả kháng, thời gian giao hàng bị chậm so với thời gian quy định, Công ty sẽ thông báo đến khách hàng. Trong trường hợp khách hàng muốn hủy đơn hàng, <strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG</strong> sẽ hoàn trả tiền lại cho khách hàng (nếu đã thanh toán trước).</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>    – Trong thời gian chờ đợi nhận hàng, Quý khách có bất cứ thắc mắc gì về thông tin vận chuyển xin vui lòng liên hệ tổng đài toàn chăm sóc khách hàng <strong>0937 181 181</strong> để nhận trợ giúp.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>    – Đối với đơn hàng giao hàng ở xa thông qua dịch vụ vận chuyển thuê ngoài, để an toàn cho hàng hóa của quý khách,www.angiakhang.com sẽ đóng gói hàng hóa trong các bao bì hoặc thùng hàng chuyên dụng lớn hơn kích thước hàng. Nếu trong quá trình vận chuyển có xảy ra rủi ro không đáng kể, quý khách hàng vui  lòng thông cảm. Xin cảm ơn !</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'> </p>\n', '', 0, NULL, 1, NULL, 1575025531, 1575025531, NULL),
(17, 'Chính sách bảo mật', 'chinh-sach-bao-mat', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>CHÍNH SÁCH BẢO MẬT THÔNG TIN</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>1-Mục đích và phạm vi thu thập thông tin</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Mục đích của việc thu thập thông tin hay thuật ngữ”thông tin cá nhân” trong điều khoản chính sách bảo mật này nghĩa là thông tin nhận diện hoặc có khả năng nhận diện danh tính cá nhân của khách hàng. Những loại thông tin cá nhân mà chúng tôi thu thập xử lý(có thể khác  nhau theo pham vị quyền hạn dựa trên pháp luật hiện hành tại Việt Nam) bào gồm:</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Họ và Tên khách hàng, địa chỉ liên lạc, Email, số điện thoại di động, số điện thoại bàn. Chúng tôi luôn thông báo cho khách hàng biết về mục đích cụ thể của việc thu thập thông tin khách hàng. Những thông tin được thu thập từ khách hàng, đều được sự đồng ý và tự nguyện của khách hàng cung cấp.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>2- Phạm vi sử dụng thông tin</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Thông tin khách hàng được thu thập sẽ chỉ được sử dụng trong nội bộ <strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG . </strong>Thông tin cá nhân khách hàng được bảo mật tuyệt đối không cung cấp cho bên thứ 3, ngoài nội bộ <strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG</strong>. Trừ trường hợp được cơ quan có thẩm quyền yêu cầu theo các quy định của pháp luật.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Khi cần thiết, chúng tôi có thể sử dụng những thông tin này để liên hệ trực tiếp với khách hàng dưới các hình thức như: xác nhận thông tin đơn hàng,xác chuyển sản phẩm cho khách hàng,xác nhận thời gian giao nhận sản phẩm theo thông tin khách hàng,xác nhận địa điểm giao nhận sản phẩm</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>3-Thời gian lưu trữ thông tin</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG </strong>sẽ lưu trữ các thông tin khách hàng cung cấp trên các hệ thống nội bộ của chúng tôi, trong quá trình khách hàng cung cấp đơn hàng hoặc khi khách hàng có yêu cầu hủy thông tin đơn  hàng.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Thời gian lữu trữ thông tin khách hàng trên đơn hàng được xử lý trong vòng 24h, sau đó sẽ được lưu trữ trên hệ thống bảo mật nội bộ của công ty.  Khi khách hàng có yêu cầu hủy thông tin chúng tôi sẽ hủy ngay thông tin lưu trữ của khách hàng trong vòng 1-2 ngày.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>4- Địa chỉ của đơn vị thu thập và quản lý thông tin cá nhân</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Tất cả thông tin của khách hàng sẽ được  <strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG </strong>thu thập và lưu trữ tại địa chỉ : 340/46 Quang Trung, P. 10, Q.Gò Vấp, TP. HCM ,Việt nam. Hotline <strong>0937 181 181</strong></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>5- Phương tiện và công cụ để người dùng tiếp cận và chỉnh sửa dữ liệu cá nhân của mình:</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Khi khách hàng muốn thay đổi và chỉnh sửa thông tin cá nhân của mình có thể liên hệ với bộ phân chăm sóc khách hàng của công ty theo số hotline: <strong>0937 181 181</strong></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Bộ phân nhân viên sẽ tiếp nhận và xử lý cập nhật thông tin trong vòng 24h.</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>6- Cam kết bảo mật thông tin cá nhân khách hàng</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>Trang web của chúng tôi coi trọng việc bảo mật thông tin và sử dụng các biện pháp tốt nhất bảo vệ thông tin của quý khách hàng. Thông tin quý khách hàng trong quá trình thanh toán sẽ được mã hóa để đảm bảo an toàn.  Chúng tôi cam kết bảo mật tuyệt đối thông tin khách hàng, khi đăng ký sử dụng dịch vụ tại <strong>CÔNG TY CỔ PHẦN THIẾT KẾ VÀ XÂY DỰNG AN GIA KHANG</strong></p>\n', '', 0, NULL, 1, 1, 1575025754, 1575025779, NULL);
