(158, 'Nguyễn Văn còn', 'cuasat.duyphuong1988@gmail.com', '0969906624', 'Kính chào quý anh chị bên em tổ đội làm về sắt nhà tiền chế cổng rào lan can mái hiên cửa sắt rất mong được hợp tác', 'https://www.angiakhang.com/lien-he', 1, '171.253.134.59', 1590214272, 1590282107, NULL),
(160, 'Phạm Tiến Trường', 'imlangdi2108@gmail.com', '0349521698', 'Bên em có tổ đội khoảng 20 người nhận khoán thi công lĩnh vực xây trét, điện Nước,  vệ sinh công nghiệp,  sơn Nước, bốc vác,  thu mua phế liệu công trình,  cty có nhu cầu xin liên hệ Trương: 0349521698 ae tổ đội làm việc nhiệt tình chu đáo, uy tín bảo hành trọn gói công việc. ', 'https://www.angiakhang.com/xay-nha-tron-goi-tai-phu-quoc', 1, '171.253.137.94', 1590474432, 1590557738, NULL),
(161, 'Cao văn Chất ', 'chatcaovan60@icluod.com', '0945475579', 'Tôi muốn biết giá xây dựng trọn gói ở Phú quốc dự định xây dựng 9/2020 dt sàn 350m2x10t địa điểm xd đường Trần Hưng đạo.', 'https://www.angiakhang.com/xay-nha-tron-goi-tai-phu-quoc', 1, '116.109.80.78', 1591101744, 1591358151, NULL),
(162, 'Thiếu Lê', 'thinh@tanthanhthinh.com', '0909548888', 'test', 'https://www.angiakhang.com/xay-dung-nha-pho', 1, '14.161.23.139', 1591604412, 1591679067, NULL),
(163, 'Đặng Ngọc Trương', 'truongngoc1189@yaho.com', '0903516595', 'Chúng tôi đang dự định xây Tòa nhà Khách sạn và TT Thương mại\r\nTòa nhà Khách sạn 5 sao khoảng 13 tầng, DT sử dụng11.000m2, khoảng 155 phòng, có hồ bơi, nhà hàng, spa...\r\nTT thương mại 3 tầng + tum. DT sử dụng khoảng 12.000m2\r\nKính nhờ Quý công ty tư vấn giúp Chúng tôi suất đầu tư xây dựng hoàn thiện khoảng bao nhiêu để chúng tôi dự toán nguồn kinh phí. Xin cảm ơn!', 'https://www.angiakhang.com/xay-dung-khach-san', 1, '112.109.92.110', 1591608170, 1591679075, NULL),
(164, 'Nguyễn văn Thắng', 'thangaka2222@gmail.com', '0933233100', 'Báo giá nhà trọn gói diện tích 6x17 5 tầng, 1 sân thượng tại thị trấn Dương Đông. ', 'https://www.angiakhang.com/xay-nha-tron-goi-tai-phu-quoc', 1, '171.226.237.178', 1591791672, 1591835231, NULL),
(165, 'Nguyễn Ngọc Quân', 'Quan.nn2203@gmail.com', '0903467979', 'Báo giá xây dựng từ phần móng căn nhà  diện tích 8m nhân 15m! Xây 6 tầng tại Bãi Trường Phú quốc Kiên giang', 'https://www.angiakhang.com/xay-nha-tron-goi-tai-phu-quoc', 1, '118.70.68.24', 1591888785, 1591931721, NULL),
(166, 'Trần quân', 'quan3888@gmail.com', '0981055844', 'Báo giá trọn gói nhà mái tôn 4,5x16, 2pn\r\nDiện tích đất 5x20', 'https://www.angiakhang.com/xay-nha-tron-goi-tai-phu-quoc', 1, '171.253.142.156', 1591975977, 1592052991, NULL);
