INSERT INTO `news` (`id`, `title`, `slug`, `image`, `intro`, `is_hot`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(9, 'Top 5 nhà thầu xây dựng lớn nhất thế giới', 'top-5-nha-thau-xay-dung-lon-nhat-the-gioi', '1576121487_8a80bdbbeb324ed50057.jpg', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nếu bạn đang hoạt động trong lĩnh vực xây dựng hoặc đam mê về lĩnh vực này hoặc đơn giản chỉ muốn tìm hiểu về nhà thầu hoặc những nhà thầu xây dựng lớn nhất thế giới thì những thông tin mà chúng tôi đưa ra bên dưới đây chắc chắn sẽ khiến bạn hài lòng và cảm thấy hấp dẫn.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576124637_18a30d5120ab86730eeb.png\" style=\"width: 617px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/top-5-nha-thau-xay-dung-lon-nhat-the-gioi\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">top 5 nhà thầu xây dựng lớn nhất thế giới</span></a></p>\n', 0, 0, 1, 1, 1576121487, 1590679626, NULL),
(10, 'Thiết kế nội thất theo phong cách nào thì đẹp?', 'thiet-ke-noi-that-dep-theo-phong-cach-nao', '1576731302_741a3a4e7345ae3aceda.png', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Xã hội ngày càng phát triển, cuộc sống của con người được nâng cao do đó nhu cầu làm đẹp cho bản thân hay cho không gian sống đang từng bước được chú trọng. Tuy nhiên <strong>quy trình thiết kế nội thất như thế nào?</strong></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576730719_4e889a976e75d8b2c9d0.png\" style=\"width: 619px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><a href=\"https://www.angiakhang.com/thiet-ke-noi-that-dep-theo-phong-cach-nao\">Thiết kế nội thất theo phong cách nào thì đẹp</a></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Cần đảm bảo yêu cầu gì trong thiết kế nội thất để có một công trình đẹp? Bài viết dưới đây chúng tôi giới thiệu đến các bạn những thông tin chi tiết về vấn đề này để tiện tham khảo.</span></p>\n', 0, 0, 1, 1, 1576731226, 1590678696, NULL),
(11, 'Những mẫu thiết kế nhà gác lửng đẹp 1 tầng ở nông thôn', 'nhung-mau-nha-thiet-ke-gac-lung-dep', '1577029592_5b812797a2c2dc9a72e1.jpg', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Hiện nay các <strong>m</strong><strong>ẫu thiết kế nhà gác lửng đẹp</strong> được nhiều gia chủ lựa chọn. Vừa là để mở rộng vừa tạo điểm nhấn cho không gian sống của gia đình bạn. Tham khảo ngay những kinh nghiệm thiết kế tầng lửng ấn tượng và vẫn tiết kiệm được chi phí nhé.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577030213_7149755334e1038c54a6.jpg\" style=\"width: 618px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/nhung-mau-nha-thiet-ke-gac-lung-dep\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu thiết kế nhà gác lửng đẹp</span></a></p>\n', 0, 0, 1, 1, 1577029592, 1590678781, NULL),
(12, 'Những mẫu nhà vườn nông thôn đẹp thiết kế theo phong cách hiện đại', 'nhung-mau-nha-vuon-nong-thon-dep', '1577100657_7eef6f89c78ffcd222f0.jpg', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><strong><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu nhà vườn nông thôn</span></strong><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> với một khoảng sân rộng có lối đi nhỏ dẫn vào nhà khiến nhiều người thích thú. Mẫu nhà này rất thích hợp với các gia đình có diện tích đất vừa phải, nhất là các gia đình ở nông thôn với diện tích sân vườn rộng rãi. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577100417_e8daed8a9ffdda1a5979.jpg\" style=\"width: 620px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/nhung-mau-nha-vuon-nong-thon-dep\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà vườn nông thôn đẹp</span></span></a></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Bài viết dưới đây hãy cùng chúng tôi tìm hiểu xem những mẫu nhà vườn đẹp ở nông thôn có gì độc đáo.</span></p>\n', 0, 0, 1, 1, 1577100657, 1590043323, NULL),
(13, 'Khách sạn được xây dựng sớm nhất ở Sài Gòn', 'khach-san-duoc-xay-dung-som-nhat-o-sai-gon', '1578500992_866f43df1bfdb205f953.jpg', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Danh sách 3 khách sạn được xây dựng sớm nhất ở Sài Gòn. Khách sạn Continental(Grand)  hay Majestic đều có lịch sử hàng trăm năm và từng đón tiếp nhiều người quan trọng trên thế giới.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif;\"><img src=\"/uploads/contents/1578500878_3c7caab32394a7fe55dc.jpg\" style=\"width: 606px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/khach-san-duoc-xay-dung-som-nhat-o-sai-gon\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Khách sạn được xây dựng lâu đời nhất ở Sài gòn</span></a></p>\n', 0, 0, 1, 1, 1578500992, 1590043506, NULL),
(14, 'Tiêu chuẩn xây dựng khách sạn 4 sao ', 'tieu-chuan-xay-dung-khach-san-4-sao', '1579172394_bab42d366f3310b13c6e.png', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><strong>Thiết kế khách sạn đạt tiêu chuẩn 4 sao</strong> là một trong những tiêu chí rất quan trọng khi chủ đầu tư và kiến trúc sư phải thật sự nắm rỏ các quy định. </span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tiềm lực kinh tế mạnh cho phép gia chủ đầu tư trong các dịch vụ khách sạn và khu nghỉ dưỡng cao cấp.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579172504_2448a5226befec8eaf8c.png\" style=\"width: 619px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/tieu-chuan-xay-dung-khach-san-4-sao\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tiêu chuẩn thiết kế khách sạn 4 sao</span></span></a></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tuy nhiên, việc để sở hữu mẫu thiết kế khách sạn 4 sao cá tính, thiết kế đẳng cấp và mang phong cách riêng thì việc đầu tư kinh phí trong thiết kế và thi công xây dựng khách sạn là một trong những bài toán cần có sự nghiên cứu kỹ lưỡng và chiến lược phát triển lâu dài.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Chúng tôi xin được chia sẽ đến các bạn những tiêu chuẩn thiết kế khách sạn 4 sao được chúng tôi biên soạn và tổng hợp lại từ những bộ tài liệu chuyên ngành, từ khách hàng và những trải nghiệm trong qua trính triển khai các dự án khách sạn từ quy mô nhỏ đến lớn.</span></p>\n', 0, 0, 1, 1, 1579172394, 1590043722, NULL),
(15, 'Tiêu chuẩn xây dựng khách sạn 3 sao mới nhất 2020', 'tieu-chuan-xay-dung-khach-san-3-sao', '1579193638_5aae47c6e6b7edd111bd.jpg', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Không ít chủ đầu tư khi có ý định đầu tư vào khách sạn chú ý tới mô hình khách sạn 3 sao. Tuy nhiên nhiều người không biết thế nào là khách sạn 3 sao đạt tiêu chuẩn. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579193946_3bdaf2f9ed8b7d30c1df.jpg\" style=\"width: 588px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/tieu-chuan-xay-dung-khach-san-3-sao\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tiêu chuẩn xây dựng khach sạn 3 sao</span></span></a></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hơn nữa làm cách nào để vận dụng các tiêu chuẩn đó trong xây dựng nhằm tạo nên một công trình đẳng cấp, sang trọng thu hút mọi khách hàng và mang tới lợi nhuận cho nhà đầu tư? </span></span></p>\n', 0, 0, 1, 1, 1579193638, 1590043858, NULL),
(16, 'Tiêu chuẩn xây dựng khách sạn 5 sao mới cập nhật 2020', 'tieu-chuan-xay-dung-khach-san-5-sao', '1579247369_5c7e1fc6c985a96ddcc7.jpg', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Xây dựng thiết kế khách sạn 5 sao yêu cầu phải đáp ứng theo đúng những tiêu chí khắt khe chung về vị trí, kiến trúc, nội thất, công năng... </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bộ tiêu chuẩn này được ban hành với mục đích giúp làm nên những công trình khách sạn sang trọng đạt tiêu chuẩn nhà nước và quốc tế. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1587861077_26f1923862fc25cc4766.jpg\" style=\"width: 616px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/tieu-chuan-xay-dung-khach-san-5-sao\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Tiêu chuẩn xây dựng khách sạn 5 sao</span></a></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Vậy tiêu chuẩn thiết kế xây dựng khách sạn 5 sao là gì? Mời bạn tham khảo bài viết dưới đây.</span></p>\n', 0, 0, 1, 1, 1579246634, 1590096966, NULL),
(17, 'Những rủi ro có thể gặp phải nếu chọn nhà thầu giá rẻ', 'nhung-rui-ro-co-the-gap-phai-neu-chon-nha-thau-gia-re', '1579569969_7d9d5e035846c2b5e0df.jpg', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Trong quá trình chọn thầu xây dựng, chủ đầu tư nên biết một số thông tin quan trọng để quyết định đặt niềm tin và nhà thầu mình chọn.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><img data-fr-image-pasted=\"true\" src=\"http://www.angiakhang.com/uploads/contents/1576123642_ed698e8b9ae197417f90.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; border-style: none; -webkit-font-smoothing: antialiased; cursor: pointer; padding: 0px 1px; max-width: calc(100% - 10px); color: rgb(65, 65, 65); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: center; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; width: 620px;\" class=\"fr-fic fr-dii\"></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/nhung-rui-ro-co-the-gap-phai-neu-chon-nha-thau-gia-re\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Những rủi ro gặp phải khi chọn nhà thầu giá rẻ</span></span></a></p>\n', 0, 0, 1, 1, 1579569969, 1590044163, NULL),
(18, 'Quy trình nhà thầu xây dựng thực hiện dự án cho nhà đầu tư', 'quy-trinh-nha-thay-xay-dung-du-an-cu-the', '1579570779_c9206f3e175d409a761a.png', '<p style=\"text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Các bạn có thể tham khảo thông tin và quy trình nhà thầu xây dựng và thiết kế các công trình dự án như sau:</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1574588514_1fca9c3c1514f33f1ef7.png\" style=\"width: 614px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/quy-trinh-nha-thay-xay-dung-du-an-cu-the\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Quy trình nhà thầu thực hiện dự án</span></a></p>\n', 0, 0, 1, 1, 1579570779, 1590044290, NULL),
(19, '10 kinh nghiệm khi chọn xây nhà trọn gói', '10-kinh-nghiem-khi-chon-xay-nha-tron-goi', '1579571711_f4e5c3be65459edd722f.png', '<p style=\"text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nếu bạn đang phải băn khoăn tìm kiếm thông tin về kinh nghiệm xây nhà trên mạng internet rồi hỏi bạn bè,người thân hay đồng nghiệp mà vẫn chưa biết được kinh nghiệm xây dựng nhà trọn gói gồm những vấn đề gì thì bạn có thể tham khảo các thông tin như:</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif;\"><img src=\"/uploads/contents/1576851318_8fd587cfa2e7b95a8937.png\" style=\"width: 609px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><a href=\"http://Kinh%20nghi%E1%BB%87m%20khi%20ch%E1%BB%8Dn%20x%C3%A2y%20nh%C3%A0%20tr%E1%BB%8Dn%20g%C3%B3i\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Kinh nghiệm khi chọn xây nhà trọn gói</span></a></p>\n', 0, 0, 1, 1, 1579571711, 1590644923, NULL),
(20, 'Khách sạn Bộ xây dựng Sầm Sơn Thanh Hóa', 'khach-san-bo-xay-dung-sam-son', '1581588205_ca516d07a8c1a9583cbc.jpg', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Sầm sơn Thanh Hóa được biết đến là một trong những thành phố có biển đẹp nhất vùng đồng bằng Bắc Bộ. Một trong những địa điểm du lịch lý tưởng cho các gia đình vào dịp hè, dịp lễ…</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1581587973_6298549889a19b9526bb.jpg\" style=\"width: 597px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><a href=\"https://www.angiakhang.com/khach-san-bo-xay-dung-sam-son\">Khách sạn sầm sơn</a></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Một trong những yếu tố quan trọng để lưu trú đó là khách sạn Sầm Sơn được rất nhiều khách hàng ưu thích.</span></p>\n', 0, 0, 1, 1, 1581588205, 1590044567, NULL),
(21, 'Bảng xếp hạng top 100 công ty xây dựng tại Việt Nam', 'bang-xep-hang-top-100-cong-ty-xay-dung-viet-nam', '1582617711_520b57ef7296fea90275.jpg', '<p style=\'margin-top:12.0pt;margin-right:0in;margin-bottom:12.0pt;margin-left:0in;line-height:150%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;\'><span style=\"font-size: 14px; line-height: 150%; font-family: Arial, Helvetica, sans-serif; color: black;\">Khi có nhu cầu xây dựng công trình hoặc đơn giản muốn tìm hiểu về linh vực xây dựng và các nhà thầu xây dựng tại Việt Nam hiện nay chắc chắn bạn không thể bỏ qua bài viết này. </span></p>\n<p style=\"margin: 12pt 0in; line-height: 150%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\"><img src=\"/uploads/contents/1582617662_84bc2d959cd888634d72.jpg\" style=\"width: 598px;\" class=\"fr-fic fr-dib\"></span></span></span></p>\n<p style=\"margin: 12pt 0in; line-height: 150%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/bang-xep-hang-top-100-cong-ty-xay-dung-viet-nam\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bảng xếp hạng top 100 công ty xây dựng ở Việt Nam</span></span></a></p>\n<p style=\'margin-top:12.0pt;margin-right:0in;margin-bottom:12.0pt;margin-left:0in;line-height:150%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;\'><span style=\"font-size: 14px; line-height: 150%; font-family: Arial, Helvetica, sans-serif; color: black;\">Bảng xếp hạng top 100 công ty xây dựng Việt Nam và các công ty xây dựng có vốn đầu tư nước ngoài tại Việt Nam mà chúng tôi giới thiệu dưới đây chắc chắn sẽ là những thông tin hữu ích cho các bạn.</span></p>\n', 0, 0, 1, 1, 1582608398, 1592226746, NULL),
(22, '4 kinh nghiệm xây nhà bạn nên biết', '4-kinh-nghiem-xay-nha', '1582818165_48786cadf2dc602435cf.jpg', '<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:12.0pt;margin-bottom:12.0pt;line-height:107%;text-align:justify;\'><span style=\"font-size: 14px; color: black; font-family: Arial, Helvetica, sans-serif;\">Ngôi nhà không chỉ là nơi để ở mà ngày nay nó còn thể hiện tính cách, sở tính hay khả năng tài chính của gia chủ. Do đó nhiều người khi muốn xây nhà đều băn khoăn tìm hiểu về kinh nghiệm xây nhà đều không biết như thế nào mới thực sự đúng. </span></p>\n<p style=\"margin: 12pt 0in; font-size: 15px; font-family: Calibri, sans-serif; line-height: 107%; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: black;\"><img src=\"/uploads/contents/1582819893_f1848ae0a5ce8aff5cd2.jpg\" style=\"width: 596px;\" class=\"fr-fic fr-dib\"></span></span></span><a href=\"http://www.angiakhang.com/4-kinh-nghiem-xay-nha\"></a></p>\n<p style=\"margin: 12pt 0in; font-size: 15px; font-family: Calibri, sans-serif; line-height: 107%; text-align: center;\"><a href=\"https://www.angiakhang.com/4-kinh-nghiem-xay-nha\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kinh nghiệm xây nhà</span></span></a></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:12.0pt;margin-bottom:12.0pt;line-height:107%;text-align:justify;\'><span style=\"font-size: 14px; color: black; font-family: Arial, Helvetica, sans-serif;\">Nếu bạn cũng đang có ý định xây nhà cấp 4 hay nhà 2 tầng thì đừng bỏ qua bài viết dưới đây nhé.</span></p>\n', 0, 0, 1, 1, 1582816634, 1590678591, NULL),
(23, 'Những mẫu nhà có màu sơn đẹp và kinh nghiệm hoàn thiện xây thô', 'nhung-mau-nha-co-mau-son-dep', '1584607285_d9a4109c2a005a0ecd43.jpg', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nhà của người Việt thường chịu nhiều ảnh hưởng bởi văn hóa và lối kiến trúc phương Đông. Ngôi nhà dù được thiết kế và xây dựng theo phong cách nào thì việc lựa chọn màu sơn tường nhà vẫn cân tuân theo các nguyên tắc cơ bản là đẹp, ấm cúng và hợp phong thủy.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1584606985_d9ed3604e7d1eabcc492.jpg\" style=\"width: 623px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/nhung-mau-nha-co-mau-son-dep\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Những mẫu nhà có màu sơn đẹp</span></span></a></p>\n<p><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Bởi vì mục đích chính khi lựa chọn màu sơn nhà là mang lại nhiều may mắn, tài lộc và bình an cho gia chủ. Xin chia sẽ với quý bạn đọc những mẫu nhà thiết kế có màu sơn đẹp</span></p>\n', 0, 0, 1, 1, 1584606576, 1590678298, NULL),
(24, 'Có nên xây nhà trọn gói', 'co-nen-xay-nha-tron-goi', '1586186716_2ad9f44d247d7679c391.jpg', '<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Quá trình thiết kế và thi công xây dựng nhà ở chúng tôi nhận được yêu cầu từ nhiều khách hàng liên quan tới vấn đề thi công trọn gói căn hộ như thế nào. Đặc biệt rất nhiều người quan tâm đến vấn đề có nên xây dựng nhà trọn gói không?</span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: center; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586186283_7810233d164aa7a334be.jpg\" style=\"width: 592px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: center; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><a href=\"https://www.angiakhang.com/co-nen-xay-nha-tron-goi\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Có nên xây nhà trọn gói?</span></a></p>\n', 0, 0, 1, 1, 1586185620, 1590645042, NULL),
(25, 'Khách sạn Bộ Xây Dựng Cửa Lò', 'khach-san-bo-xay-dung-cua-lo', '1586408479_fbdee6b02ccd04830701.jpg', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nếu bạn và gia đình đang có ý định đi du lịch Cửa Lò năm nay mà chưa tìm được địa điểm nghỉ ngơi lý tưởng thì đừng bỏ qua bài viết dưới đây nhé.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586407887_b3ecacf9960ea3d01179.jpg\" style=\"width: 623px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/khach-san-bo-xay-dung-cua-lo\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn Bộ Xây Dựng Cửa Lò</span></span></a></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Khách sạn Bộ Xây dựng Cửa Lò tọa lạc tại số 216, đường Bình Minh, thị xã Cửa Lò và trực thuộc trung tâm điều dưỡng phục hồi chức năng của Bộ Xây dựng, tỉnh Nghệ An. Khách sạn được xây dựng trên khuôn viên rộng lớn tới 8.300m2 vừa thoáng đãng, nhiều cây xanh bao phủ và nằm ngay ở trung tâm bãi tắm Cửa Lò.</span></p>\n', 0, 0, 1, 1, 1586407493, 1590677896, NULL),
(26, 'Kinh nghiệm ký hợp đồng xây nhà', 'kinh-nghiem-ky-hop-dong-xay-nha', '1586594385_f4c1b2608099804669f0.jpg', '<p><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Kinh nghiệm ký hợp đồng xây nhà, hợp đồng xây dựng nhà ở nghe có vẻ khá không cần thiết nhưng thủ tục này lại cần thiết không tưởng trong khi xây dựng cũng như đưa vào sử dụng nhà ở. Những kinh nghiệm ký hợp đồng xây nhà ở sau đây chắc chắn giúp bạn có được cơ sở phát triển tốt, sử dụng đồng tiền của mình đúng chỗ và nhận được những hỗ trợ cần thiết trong quá trình hợp tác với nhà thầu xây dựng.</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586593780_62f5cdf0d1190b241936.jpg\" style=\"width: 596px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/kinh-nghiem-ky-hop-dong-xay-nha\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Kinh nghiệm ký hợp đồng xây dựng</span></a></p>\n', 0, 0, 1, 1, 1586592266, 1590677819, NULL),
(27, 'Xu hướng thiết kế biệt thự', 'xu-huong-thiet-ke-biet-thu', '1587550675_e1d0c4d1be486f14e835.jpg', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Sở hữu căn biệt thự đẹp luôn là mong muốn của gia chủ khi xây nhà và đặc biệt xu hướng thiết kế biệt thự theo đúng phong cách gia chủ ưng ý. Hiện nay với những thay đổi trong quan điểm thiết kế hiện đại thì việc thiết kế và xây dựng biệt thự đẹp cũng có những thay đổi nhất định. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><img data-fr-image-pasted=\"true\" src=\"https://www.angiakhang.com/uploads/contents/1587551280_5cc422ce84b1bf0f5348.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; border-style: none; -webkit-font-smoothing: antialiased; cursor: pointer; padding: 0px 1px; max-width: 100%; color: rgb(65, 65, 65); font-family: Calibri, sans-serif; font-size: 15px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: center; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; width: 580px;\" class=\"fr-fic fr-dib\"> <br></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><a href=\"https://www.angiakhang.com/xu-huong-thiet-ke-biet-thu\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Xu hướng thiết kế biệt thự</span></a></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Bài viết dưới đây sẽ chia sẻ những kinh nghiệm trong quá trình thiết kế biệt thự để gia chủ sở hữu được ngôi nhà sang trọng, cuốn hút hơn.</span></p>\n', 0, 0, 1, 1, 1587549177, 1590677632, NULL);
