INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(55, 'category', 4, 0, '1. Nhà thầu xây dựng là gì?', '1-nha-thau-xay-dung-la-gi', '<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng được xem là một đơn vị hay tổ chức có đầy đủ các năng lực hoặc chức năng xây dựng để có thể kí kết hợp đồng xây dựng với chủ đầu tư để nhận thầu toàn bộ các công việc của toàn dự án xây dựng mà được nhận.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><strong>NHÀ THẦU XÂY DỰNG UY TÍN AN GIA KHANG</strong></p><p><iframe width=\"100%\" height=\"360\" src=\"https://www.youtube.com/embed/3fQu0tIbMvQ\" frameborder=\"0\" allowfullscreen=\"\"></iframe></p><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thuật ngữ nhà thầu xây dựng có lẽ không quá xa lạ với mỗi người. Nhà thầu xây dựng dùng để chỉ các cá nhân, tổ chức có đủ năng lực trong hoạt động xây dựng. Đủ năng lực hành nghề xây dựng khi tham gia ký kết hợp đồng với chủ  đầu tư trong hoạt động xây dựng.</span></span></p><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có bao nhiều loại nhà thầu trong xây dựng hiện nay?</span></span></p><h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>a)  Nhà thầu xây dựng chính</strong></span></span></h3><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây là nhóm nhà thầu thực hiện ký kết hợp đồng trực tiếp với chủ đầu tư xây dựng. Thực hiện công việc chính của toàn bộ công trình xây dựng đã nhận.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><img src=\"/uploads/contents/1582432030_6d0bf9e5127de2d33dcc.jpg\" style=\"width: 649px;\" class=\"fr-fic fr-dib\"></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Thi công xây dựng </span></p><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng chính sau khi nhận thầu sẽ phải chịu toàn bộ trách nhiệm trước chủ đầu tư và pháp luật về chất lượng công trình cũng như chất lượng của từng hạng mục. Hoặc có bất kỳ sự cố liên quan nào xảy ra nhà thầu chính cũng cần chịu trách nhiệm về mình nếu như không có sự ký kết của bên liên quan thứ ba.</span></span></p><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chính vì vậy một nhà thầu xây dựng uy tín đòi hỏi phải đáp ứng được các điều kiện cần có như:</span></span></p><ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giấy phép kinh doanh.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chứng chỉ hành nghề.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đội ngũ kỹ sư, kiến trúc sư.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đội ngũ giám sát, chỉ huy công trình.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đội ngũ công nhân hành nghề.</span></span></li></ul><h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>b) Nhà thầu xây dựng phụ</strong></span></span></h3><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Là nhóm nhà thầu thực hiện ký kết hợp đồng với nhà thầu chính để thực hiện một phần công việc trong số công việc mà nhà thầu chính đã nhận hoặc trong tổng thầu xây dựng. Nhà thầu xây dựng phụ thường đảm nhiệm các công việc như:</span></span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1574588137_e21f715a22c568d9e8ce.png\" style=\"width: 652px;\" class=\"fr-fic fr-dii\"></span></span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu phụ</span></span></p><ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thi công đào móng.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thi công điện nước</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thi công thạch cao.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thi công cửa.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hoặc thi công các công việc khác được nhà thầu chính giao khoán.</span></span></li></ul><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trong suốt quá trình thi công xây dựng nhà thầu xây dựng phải chịu trách nhiệm về các vấn đề như: Phương tiện và thiết bị thi công, toàn bộ cơ sở vật chất, vật tư, vật liệu thi công….. Sau khi kết thúc công trình cần bàn giao lại đầy đủ cho chủ đầu tư.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577287000_97e6c952e05c5f70a45a.png\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; border-style: none; -webkit-font-smoothing: antialiased; cursor: pointer; padding: 0px 1px; max-width: calc(100% - 10px); color: rgb(65, 65, 65); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: center; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; width: 652px;\" class=\"fr-fic fr-dii\"></span></span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đội ngũ công nhân An Gia Khang</span></span></p><h3><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>c) Các loại nhà thầu xây dựng khác</strong></span></span></h3><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Tổng thầu xây dựng</strong> và nhà thầu phụ không phải là một, do tổng thầu chịu trách nhiệm về việc thực hiện kết hợp đồng nếu được chấp thuận , còn thầu phụ chỉ ký kết hợp đồng với nhà thầu chính, tổng thầu để nhận một phần công việc nào đó được giao và không chịu trách nhiệm về việc tham gia đấu thầu. </span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1574601137_0c36bf479dccc69125f1.png\" style=\"width: 652px;\" class=\"fr-fic fr-dii\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu An Gia Khang tại Phú Quốc</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%; color: rgb(65, 65, 65); background: white;\"><strong>Ngoài ra cò có các nhà thầu khác như:</strong></span></span></span></p><ol><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%; color: rgb(65, 65, 65); background: white;\">Nhà thầu độc lập là nhà thầu tham gia đấu thầu độc lập</span></span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%; color: rgb(65, 65, 65); background: white;\">Nhà thầu liên doanh là loại hình nhiều nhà thầu tham gia đấu thầu trong một đơn thầu </span></span></span></li></ol><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một nhà thầu xây dựng Phú Quốc thì cần phải có đầy đủ các pháp lý, có đủ các chức năng và năng lực liên quan tới chuyên ngành về xây dựng và đã được nhà nước cấp phép chẳng hạn như các chứng chỉ hành nghề, giấy phép kinh doanh, dội ngữ kiến trúc sư, kĩ sư, chỉ huy công trình và đội ngũ công nhân chuyên nghiệp, dày dặn kinh nghiệm. </span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1574601348_c7580eb2572fe7f17c02.png\" style=\"width: 648px;\" class=\"fr-fic fr-dii\"></span></span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chất lượng công trình phụ thuộc vào năng lực nhà thầu</span></span></p><p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu công trình được chuyển giao cho các <strong>nhà thầu xây dựng công nghiệp</strong> thì chủ đầu tư sẽ an tâm hơn một phần  Chính bởi những đặc điểm này mà mà khi các chủ đầu tư muốn tiến hành một công trình hay dự án, nếu được chuyển giao cho các nhà thầu xây dựng chuyên nghiệp thì chủ đầu tư sẽ an tâm hơn một phần nào đó. </span></span></p><ul><li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Quy định về nhà thầu phụ trong xây dựng</strong></span></span></li></ul><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kinh tế thị trường phát triển người ta thường nhắc tới các thuật ngữ như hoạt động đấu thầu hay đấu thầu. </span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hiểu một cách cơ bản nhất thì nó giống như mua bán hàng hóa nhưng với hình thức khá đặc biệt đó là người mua chính là bên mời thầu có thể yêu cầu nhiều bên tham gia.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576122576_30683d6b82cb70ae5ac4.png\" style=\"width: 652px;\" class=\"fr-fic fr-dii\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các quy định về nhà thầu trong xây dựng</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Từ đó chọn người bán chính là bên nhà thầu tốt nhất cho mình qua một quá trình và hình thức cụ thể công khai, dân chủ, cạnh tranh công bằng. </span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trong quan hệ đấu thầu bên cạnh bên mời thầu với nhà thầu chính; ở một số trường hợp có cả nhà thầu phụ tham gia phiên đấu thầu. Vậy quy định về nhà thầu phụ trong phiên đấu thầu như thế nào.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576122606_5e713f3f3ef729c3ee32.jpg\" style=\"width: 650px;\" class=\"fr-fic fr-dii\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công trình đang đổ bê tông</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Dưới đây chúng tôi giới thiệu về hợp đồng thầu phụ và việc dùng nhà thầu phụ trong đấu thầu theo như quy định của pháp luật hiện hành</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Theo quy định của pháp luật hiện nay quy định về hợp đồng thầu phụ cũng như dùng nhà thầu phụ trong đấu thầu được quy định cụ thể ở Luật đấu thầu năm 2013 Nghị định 37/2015/NĐ-CP, Thông tư 01/2015/TT-BKHĐT .</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thực tế cho thấy ở trong quan hệ đấu thầu thường có nhà thầu chính và nhà thầu phụ. Nếu như nhà thầu chính là đơn vị trực tiếp tham gia đấu thầu, có tên dự thầu cũng là người trực tiếp ký kết hợp đồng với chủ đầu tư khi được lựa chọn trúng thầu sau khi thực hiện đấu thầu thì khái niệm nhà thầu phụ có thể hiểu theo nghĩa khác. </span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576122664_bc3484fba2c616317c1f.jpg\" style=\"width: 652px;\" class=\"fr-fic fr-dii\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quy định về nhà thầu phụ</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cụ thể là căn cứ theo quy định ở khoản 36 điều 4 Luật đấu thầu năm 2013 nhà thầu phụ là nhà thầu không trực tiếp tham gia đấu thầu hay dự thầu như lại tham gia thực hiện gói thầu dựa trên cơ sở nội dung thỏa thuận trong hợp đồng được ký giữa đơn vị này và nhà thầu chính.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576122687_feb222fbbd69b9f05b46.jpg\" style=\"width: 652px;\" class=\"fr-fic fr-dii\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công nhân đang thực hiện đổ trụ nhà</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ở quy định về nhà thầu phụ còn có cả quy định về nhà thầu phụ đặc biệt. Khái niện này sử dụng để chỉ nhà thầu phụ được nhà thầu chính đề xuất trong hồ sơ dự thầu. Bản hồ sơ đề xuất với nội dung thường thực hiện các công việc quan trọng, đặc biệt của gói thầu.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quy định về việc sử dụng “nhà thầu phụ” trong hoạt động đấu thầu: Hiện nay, căn cứ theo quy định tại Điều 12 Chương VI, Phần thứ nhất của Thông tư 01/2015/TT-BKHĐT thì nội dung về nhà thầu phụ được quy định như sau:</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576122766_d7cffd0841cf8bac72ca.jpg\" style=\"width: 648px;\" class=\"fr-fic fr-dii\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">công nhân xây dựng cán bê tông</span></span></p><ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu phụ được ký kết hợp đồng với nhà thầu chính để thực hiện một phần công việc được nêu trong hồ sơ dự thầu được xác định là những nhà thầu nằm trong danh sách nhà thầu phụ nêu tại phần điều kiện cụ thể của hợp đồng nằm trong hồ sơ dự thầu.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Việc có sử dụng nhà thầu phụ hay không sẽ không làm thay đổi, cũng như không ảnh hưởng đến quyền, nghĩa vụ của nhà thầu.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Dù việc thực hiện công việc có hiệu quả hay không thì nhà thầu vẫn phải chịu trách nhiệm trước chủ đầu tư về khối lượng, chất lượng, tiến độ cũng như các quyền và nghĩa vụ khác đối với phạm vi công việc mà nhà thầu phụ thực hiện.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chỉ khi được chủ đầu tư chấp thuận, nếu không, nhà thầu không được phép thay thế, hay bổ sung nhà thầu phụ nằm ngoài danh sách các nhà thầu phụ được nêu tại Điều kiện cụ thể của Hợp đồng.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giá trị công việc mà các nhà thầu phụ phải thực hiện theo nội dung hồ sơ dự thầu thì không được vượt quá tỷ lệ % (phần trăm) theo giá hợp đồng được nêu tại Điều kiện cụ thể của hợp đồng.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ngoài các công việc đã được kê khai về việc sử dụng nhà thầu phụ được thể hiện trong hồ sơ dự thầu thì nhà thầu không được yêu cầu hay sử dụng nhà thầu phụ cho các công việc khác.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Những yêu cầu khác đối với nhà thầu phụ phải được quy định cụ thể tại Điều kiện cụ thể của hợp đồng trong Hồ sơ dự thầu.</span></span></li></ul><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trên đây là những quy định chung về việc sử dụng nhà thầu phụ trong đấu thầu. Điều kiện, quyền và nghĩa vụ của nhà thầu phụ trong đấu thầu còn được thể hiện rõ hơn thông qua nội dung về Điều kiện cụ thể của hợp đồng.</span></span></p><ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Quy định về hợp đồng thầu phụ</strong></span></span></li></ul><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mặc dù trong Luật Đấu thầu năm 2013 không đề cập đến khái niệm hợp đồng thầu phụ, tuy nhiên tại quy định tại điểm b khoản 3 Điều 4 Nghị định 37/2015/NĐ-CP lại có quy định về một trong những loại hợp đồng xây dựng được phân loại dựa theo mối quan hệ của các bên tham gia trong hợp đồng là hợp đồng thầu phụ.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576122819_7c12f3461046ea2a55ef.png\" style=\"width: 652px;\" class=\"fr-fic fr-dii\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">San lấp mặt bằng thi công</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cụ thể, theo đó, hợp đồng thầu phụ được xác định là khái niệm dùng để chỉ hợp đồng xây dựng được ký kết giữa nhà thầu chính hoặc tổng thầu với nhà thầu phụ. Trong khi đó, hợp đồng thầu chính lại là khái niệm dùng để chỉ loại hợp đồng được ký kết giữa chủ đầu tư với nhà thầu chính/tổng thầu.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trên cơ sở khái niệm hợp đồng xây dựng là hợp đồng thầu phụ được quy định tại điểm b khoản 3 Điều 4 Nghị định 37/2015/NĐ-CP nêu trên, kết hợp với khái niệm “nhà thầu phụ” được quy định tại khoản 36 Điều 4 Luật Đấu thầu năm 2013 được trích dẫn ở trên, có thể hiểu:</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hợp đồng thầu phụ là loại hợp đồng được ký kết giữa nhà thầu chính/tổng thầu với nhà thầu phụ, để nhằm mục đích thỏa thuận về việc thực hiện gói thầu mà nhà thầu đã có được sau quá trình đấu thầu.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hợp đồng thầu phụ là cơ sở để xác định phạm vi công việc, phần công việc, tỷ lệ phần trăm công việc mà nhà thầu phụ được thực hiện khi tham gia thực hiện gói thầu này, và là cơ sở để xác định quyền và nghĩa vụ của nhà thầu/tổng thầu và nhà thầu phụ trong việc thực hiện gói thầu.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ngoài ra, do hợp đồng thầu phụ là căn cứ ghi nhận sự thỏa thuận cũng như căn cứ xác lập quan hệ giữa nhà thầu chính và nhà thầu phụ nên mọi nội dung trong các hợp đồng thầu phụ đều phải đáp ứng yêu cầu là thống nhất, đồng bộ với hợp đồng thầu chính đã ký với chủ đầu tư.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576122847_7a520018abd23c69f7a1.jpg\" style=\"width: 648px;\" class=\"fr-fic fr-dii\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các quy định về nhà thầu</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đồng thời, tùy vào từng lĩnh vực công việc thực hiện trong gói thầu mà hợp đồng thầu phụ cũng có những yêu cầu khác. Có thể ví dụ như đối với hợp đồng thầu phụ là hợp đồng xây dựng thì căn cứ theo quy định tại Điều 47 Nghị định 37/2015/NĐ-CP, hợp đồng thầu phụ cần đáp ứng một số quy định sau:</span></span></p><ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hợp đồng thầu phụ phải được ký kết với nhà thầu phụ có năng lực hành nghề, năng lực hoạt động phù hợp với yêu cầu khi thực hiện gói thầu.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu nhà thầu chính là nhà thầu nước ngoài khi thực hiện hợp đồng xây dựng trên lãnh thổ việt Nam chỉ được ký hợp đồng thầu phụ với các nhà thầu phụ nước ngoài nếu sau khi đã xác định được các nhà thầu phụ trong nước Việt Nam đã không đáp ứng được yêu cầu của gói thầu.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chỉ khi được chủ đầu tư chấp thuận thì những nhà thầu phụ nằm ngoài danh sách thầu phụ kèm theo hợp đồng mới được ký kết hợp đồng thầu phụ, tham gia thực hiện gói thầu.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trong hợp đồng thầu phụ, nhà thầu chính hoặc Tổng thầu không được giao lại toàn bộ công việc theo hợp đồng cho nhà thầu phụ thực hiện.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tổng thầu, nhà thầu chính phải chịu trách nhiệm trước chủ đầu tư về phần công việc do nhà thầu phụ thực hiện.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu phụ có tất cả quyền và nghĩa vụ của bên nhận thầu…</span></span></li></ul><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Như vậy, nhà thầu phụ đóng một vai trò quan trọng trong quan hệ đấu thầu. Mặc dù không trực tiếp tham gia dự thầu nhưng nhà thầu phụ là điều kiện giúp cho nhà thầu chính thực hiện hiệu quả gói thầu đối với những phần công việc mà nhà thầu chính không có năng lực thực hiện.</span></p>', NULL, 4, NULL, 1574346066, 1618286090, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(63, 'category', 23, 0, '1. Xây nhà cấp 4 mái thái', '1-xay-nha-cap-4-mai-thai', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Ngày xưa người ta quen thường gọi nhà ở nông thôn, do những ngôi nhà có thiết kế kết cấu được xây dựng bằng những vật liệu thô sơ như gỗ, tre, rơm rạ thì được gọi là nhà cấp 4. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cung theo quan điểm người xưa những goi nhà cấp 4 là để chỉ những đối tượng nhà nghèo, xây những ngôi nhà tạm bợ, ..</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Và sau này, nhà cấp 4 còn được dùng để nói về những ngôi nhà ống 1 tầng, đơn giản, có thiết kế không được đẹp, xấu xí.</span></span></p>\n<ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Nhà cấp 4 thông thường.</strong></span></span></li></ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Còn theo từ điển Tiếng Việt “nhà cấp 4” là những công trình xây dựng có mái, có tường vách để ở hay để sử dụng vào một mục đích khác, được phân loại theo một số tiêu chí về kết cấu, diện tích,mục đích sử dụng…</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Còn theo thông tư 03/2016/TT-BXD của bộ xây dựng 10/3/2016 thì quy định  về nhà cấp công trình xây dựng và hướng dẫn trong quản lý xây dựng . Nhà cấp 4 được xác định đựa trên các tiêu chí sau:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575604242_23ab164a4f1d727c6e14.png\" style=\"width: 600px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thi công xây dựng nhà</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Nhà cấp 4</strong> là những công trình xây dựng có mái, có tường vách để ở hoặc sử dụng vào một múc đích khác có diện tích nhỏ hơn 1000m2, 1 tầng, có chiều cao nhỏ hơn hoặc bằng 6m và kết cấu nhịp lớn nhất nhỏ hơn 15m.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có thể nói rằng, mẫu hình nhà cấp 4 đã gắn liền với người dân Việt Nam, nếp sống và văn hóa Việt Nam bao lâu nay. Chính vì thế đến nay, các mẫu xây dựng nhà cấp bốn chưa bao giờ lỗi thời, thế nhưng tiêu chí và quy mô qua các năm đều khác nhau. </span></span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chính vì thế để có được một căn nhà cấp 4 hiện đại hợp xu hướng khách hàng hãy cùng công ty xây dựng nhà cấp 4 An Gia Khang tìm hiểu nhé.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mục đích <strong>xây dựng nhà cấp 4</strong> có thể là để ở, cho thuê hoặc kinh doanh buôn bán</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Để có thể tiến hành xây dựng nhà cấp 4 khách hàng cần phải xác định rõ ràng mục tiêu xây dựng ngôi nhà cấp 4 này là để làm gì. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chẳng hạn khách hàng xây dựng ngôi nhà này nhằm mục đích là ở, hoặc là cho thuê cũng có thể là sử dụng làm nơi kinh doanh,… </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575604433_5e3eacd9cb5ee7b75eb6.jpg\" style=\"width: 600px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà cấp 4 xây để ở </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cùng là một không gian trong ngôi nhà cấp 4 khi khách hàng đã xác định mục đích sử dụng khác nhau thì sẽ dẫn đến việc các thiết kế khác nhau để có thể tận dụng tối đa mục đích sử dụng của ngôi nhà. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mục đích sử dụng ngôi nhà cũng ảnh hưởng đến quy mô xây dựng nhà</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mục đích sử dụng ngôi nhà cấp 4 cũng sẽ ảnh hưởng đến quy mô, thiết kế nhà chính vì vậy khi quyết định lựa chọn mẫu nhà cấp 4, quý khách hàng hãy cẩn trọng lựa chọn kĩ càng và xác định trước mục tiêu xây dựng nhà để lựa chọn mẫu mã nhà cấp 4 cho phù hợp.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575605025_5b3d2d80066840953364.jpg\" style=\"width: 611px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chi phí cần chuẩn bị cho việc xây dựng nhà cấp 4</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chi phí xây nhà còn tùy thuộc vào quy mô, diện tích, mẫu mã,… xây dựng nhà</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đối với mỗi dự án xây dựng nhà cấp 4 ngân sách bạn sẽ chuẩn bị là khác nhau tùy thuộc vào diện tích xây dựng, vị trí thi công công trình nhà cấp 4, vật liệu thi công, và thời điểm xây dựng,… </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có rất nhiều yếu tố ảnh hưởng đến chi phí xây dựng một dự án xây dựng nhà cấp 4 , thế nhưng hầu hết đó là những yếu tố phụ, và thông thường các yếu tố chính ảnh hưởng nhiều nhất đến chi phí xây dựng ngôi nhà cấp 4 sẽ có các tiêu chí chính sau:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiểu mẫu mã thiết kế ngồi nhà. Ví dụ nhà cấp 4 mà khách hàng dự kiến xây là dạng ống hay thông thường.</span></span></p>\n<ol>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Diện tích sàn, phần đổ móng , mái tôn là bao nhiêu, lớn hay nhỏ.</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các giấy tờ liên quan xin phép xây dựng, bản thiết kế nhà.</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phần thuê nhân công hoàn thiện công trình xây dựng ngôi nhà cấp 4.</span></span></li>\n</ol>\n<ul><li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Nhà cấp 4 mái thái</strong></span></span></li></ul>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hiện nay các nhà cấp 4 được yêu thích hơn không chỉ bởi tính thẩm mỹ mà còn đảm bảo tốt các công năng giúp tối ưu khi sử dụng. Nhiều người hỏi, nhà cấp 4 mái thái là gì? Thì có thể định nghĩa một cách đơn giản như sau: Nhà cấp 4 mái thái là kiểu nhà kiến trúc thấp tầng, chủ yếu là 1 trệt và 1 tầng. </span></span></p>\n<ul><li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Đặc điểm của nhà cấp 4 mái thái</strong></span></span></li></ul>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Để giúp bạn hình dung nhà cấp 4 mái thái là gì bạn hãy chú ý các đặc điểm sau đây:</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Thiết kế phần mái (mái che được thiết kế theo kiến trúc mái của Thái Lan)</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Có cửa chính, to</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Có nhiều cửa sổ xung quanh nhà</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiểu nhà cấp 4 mái thái du nhập từ Thái Lan với phần mái có thiết kế độ dốc khá lớn. Sóng của mái thường là song nhỏ, sóng to hay mái giả đá vô cùng công phu. Đây là kiểu nhà mái chữ A khá phổ biến ở các nước châu Á.</span></span></p>\n<ul><li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>So sánh nhà cấp 4 và nhà cấp 4 mái thái</strong></span></span></li></ul>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Từ khái niệm cho tới đặc điểm thì bạn hoàn toàn có thể so sánh nhà cấp 4 với nhà cấp 4 mái thái một cách rõ nét rồi phải không. Chúng tôi tóm tắt những đặc điểm giống và khác nhau giữa hai loại hình nhà cấp 4 dưới đây để bạn có cái nhìn tổng quát:</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>- Giống:</strong></span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đều là nhà cấp 4 với đặc điểm duy nhất 1 tầng</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>- Khác:</strong> </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Về công năng: Nhà cấp 4 mái thái chống ẩm thấp, che chắn tốt hơn nhà cấp 4.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Về tính thẩm mỹ: Nhà cấp 4 mái thái đẹp hơn, đa dạng thiết kế.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Về chi phí xây dựng: Nhà cấp 4 mái thái có chi phí xây dựng khá lớn (tùy vào năng lực tài chính của gia chủ). Trong khi đó bạn chỉ mất từ 100 đến 400 triệu để xây dựng nhà cấp 4.</span></span></p>\n<ul><li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Tại sao nhà cấp 4 mái thái ưu việt hơn?</strong></span></span></li></ul>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà cấp 4 mái thái ngày càng được nhiều gia đình Việt lựa chọn thiết kế bởi những đặc điểm vượt trội hơn hẳn so với nhà cấp 4 bình thường của chúng ta. Ưu điểm của thiết kế nhà này có thể nêu như sau: </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>- Về công năng: </strong>Nhà cấp 4 mái thái có tính năng tản nhiệt nên khả năng chống nóng rất tốt. Do phần mái có độ dốc từ vừa phải đến lớn nên khi trời mưa rớt nước xuống rất nhanh, tránh hiện tượng bị ứ đọng. Ở trong ngôi nhà cấp 4 mái thái thì bạn không lo bị thấm dột, ngấm nước.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>- Về thiết kế:</strong> Bạn hoàn toàn có thể yêu cầu kỹ sư thiết kế phần mái tùy theo nhu cầu miễn đảm bảo được công năng. Hiện nay có rất nhiều biệt thự được xây dựng theo kiểu nhà cấp 4 mái thái như này với nhiều kiểu cách kiến trúc khác nhau. Các chất liệu để xây dựng nhà cũng hết sức đa dạng, chủ nhân có thể quyết định.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>- Về tính thẩm mỹ: </strong>Nhà cấp 4 mái thái có lối kiến trúc về phần chung tương đối đơn giản nhưng vẫn đảm bảo được tính thẩm mỹ trong từng chi tiết của bộ phận căn nhà. Kiến trúc nhà mái thái khéo léo tôn vinh lên được vẻ đẹp thanh thoát và cao ráo của ngôi nhà. Nếu bạn muốn thiết kế nhà vườn thì đây chính là loại nhà phù hợp mà bạn nên cân nhắc.</span></span></p>\n<p style=\'margin: 0in 0in 10pt; line-height: 150%; font-size: 16px; font-family: \"Times New Roman\", serif; text-align: justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%;\">Để có một thiết kế nhà cấp 4 mái thái hoàn hảo thì bạn cần phải hiểu rõ được những yêu nhược điểm của thiết kế này từ đó có thể khắc phục điểm yếu cũng như phát huy điểm mạnh của kiến trúc.</span></span></span></p>\n<p style=\'margin: 0in 0in 10pt; line-height: 150%; font-size: 16px; font-family: \"Times New Roman\", serif; text-align: center;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%;\"> <img src=\"/uploads/contents/1575605165_1691a1ee09dfd08ef39f.jpg\" style=\"width: 613px;\" class=\"fr-fic fr-dii\"></span></span></span></p>\n<p style=\'margin: 0in 0in 10pt; line-height: 150%; font-size: 16px; font-family: \"Times New Roman\", serif; text-align: center;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%;\">Các tiêu chỉ để xây nhà cấp 4 đẹp</span></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:150%;font-size:16px;font-family:\"Times New Roman\",\"serif\";text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">Đặc điểm nổi bật của mẫu nhà mái thái là kiến trúc thấp tầng, phần mái được tạo nên từ các lớp ngói xếp chồng lên nhau theo chiều dốc. Phần tường được trang trí với của chính cửa sổ và mái che đều được thiết kế theo nét đặc trưng của kiến trúc thái.</span></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:150%;font-size:16px;font-family:\"Times New Roman\",\"serif\";text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong><span style=\"line-height: 150%; color: black;\">- Ưu điểm của mẫu nhà</span><em><span style=\"line-height: 150%; color: black;\">:</span></em></strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:150%;font-size:16px;font-family:\"Times New Roman\",\"serif\";text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">+ Những mẫu nhà cấp 4 mái thái thường có chi phí rẻ hơn các mẫu thiết kế nhà khác cũng như dễ dàng tìm kiếm được các nhà thầu thi công nhờ vào kêt cấu nhà đơn giản, cách thức thi công không quá công phu cần nhiều kỹ thuật.</span></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:150%;font-size:16px;font-family:\"Times New Roman\",\"serif\";text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">+ Thời gian thi công nhanh chóng. </span></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:150%;font-size:16px;font-family:\"Times New Roman\",\"serif\";text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">+ Là mẫu nhà có thiết kế hiện đại hơn so với các ngồi nhà cấp 4   với kiểu mái truyền thống nhưng vẫn sang trọng và đầy đủ tiện nghi.</span></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:150%;font-size:16px;font-family:\"Times New Roman\",\"serif\";text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">+ Ngôi nhà được thiết kế với hệ thống mái thái sẽ có lợi thế rất lớn trong mùa hè nhờ vào khả năng tản nhiệt và chống nắng rất tốt. Và nó cũng không hè làm gia chủ thất vọng vào mùa mưa nhờ vào khả năng thoát nước tốt không hề có hiện tượng ứ đọng nhờ vào hệ thống mái dốc xếp tầng.</span></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:150%;font-size:16px;font-family:\"Times New Roman\",\"serif\";text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">+ Mẫu thiết kế nhà này là lựa chọn sáng suốt của những người thích sự phóng khoáng và thoải mái, luôn muốn gần gũi và hòa nhập với thiên nhiên. </span></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:150%;font-size:16px;font-family:\"Times New Roman\",\"serif\";text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">+ Thiết kế nhà cấp 4 mái thái phù hợp với những gia đình có ít thành viên và rất thuận tiện với người già vì hạn chế được việc leo cầu thang. Thật là</span><span style=\"line-height: 150%;\"> lãng<span style=\"color:black;\"> phí nếu bạn xây dựng một ngôi nhà mà không sử dụng hết không gian của nó .</span></span></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:150%;font-size:16px;font-family:\"Times New Roman\",\"serif\";text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong><span style=\"line-height: 150%; color: black;\">- Nhược điểm của thiết kế:</span></strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:150%;font-size:16px;font-family:\"Times New Roman\",\"serif\";text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\"> + Thiết kế này thường chỉ phù hợp với những khuôn viên nhà rộng có thể thiết kế sân vườn vì vậy bạn thường chỉ thấy chúng xuất hi</span><span style=\"line-height: 150%;\">ệ<span style=\"color:black;\">n phổ biến ở khu vực nông thôn và ít được lựa chọn và xây dựng tại thành phố.</span></span></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:150%;font-size:16px;font-family:\"Times New Roman\",\"serif\";text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">+ Một nhược điểm khác của thiết kế đó là ít tạo ra sự riêng tư so với các mẫu thiết kế tầng và chỉ không phù hợp với những hộ gia đình có nhiều thành viên.</span></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:150%;font-size:16px;font-family:\"Times New Roman\",\"serif\";text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">+ Chi phí xây dựng mẫu nhà này tuy chi phí rẻ hơn so với các mẫu thiết kế chồng t</span></span></span><span style=\"font-size: 14px; line-height: 150%;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">ầ</span><span style=\"color: black; font-family: Arial, Helvetica, sans-serif;\">ng phức tạp khác nhưng lại là cao hơn so với nhà mái bằng hay mái tôn.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n', NULL, 1, NULL, 1574350777, 1590049531, NULL),
