INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(80, 'category', 25, 0, '1. Xây dựng khách sạn', '1-xay-dung-khach-san', '<p><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Xây khách sạn</span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> là công trình kiến trúc kiên cố với nhiều tầng, nhiều phòng ngủ. Khách sạn thường được trang bị nhiều vật dụng tiện nghi phục vụ cho mục đích kinh doanh nghỉ dưỡng, dịch vụ ăn uống, lưu trú hay dịch vụ khác.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1584981600_45cec65975888cd4bae1.jpg\" style=\"width: 624px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thi công xây dựng khách sạn 4 sao</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Còn tùy vào mục đích cũng như đối tượng sử dụng mà sẽ có các loại khách sạn khác nhau như: khách sạn nghỉ dưỡng, khách sạn du lịch, khách sạn tạm trú hay khách sạn hội nghị,… </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Căn cứ vào tiện nghi và quy mô của khách sạn sẽ có thứ hạng từ 1 đến 5 sao. Khách sạn càng nhiều sao thì quy mô càng lớn và đầy đủ các dịch vụ để phục vụ khách hàng. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nói một cách chính xác có thể hiểu khách sạn là một loại hình doanh nghiệp được thành lập và đăng ký kinh doanh theo quy định của pháp luật nhằm mục đích sinh lời.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>10 mẫu khách sạn đẹp</strong></span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><iframe width=\"100%\" height=\"360\" src=\"https://www.youtube.com/embed/awzDsYdl-CA\" frameborder=\"0\" allowfullscreen=\"\"></iframe></span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>a) Giới thiệu về Nhà thầu xây dựng khách sạn An Gia Khang</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với nhiều năm hoạt động trong lĩnh vực xây dựng nói chung và xây khách sạn nói riêng, Công ty xây dựng An Gia Khang được đánh giá là công ty xây dựng uy tín nhất trên thị trường Việt Nam hiện nay.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1584981702_f6384de2d305bf8077c2.jpg\" style=\"width: 648px;\" class=\"fr-fic fr-dib\"><br></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây dựng khách sạn An Gia Khang</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chúng tôi luôn đặt ra các phương châm hoạt động, sứ mệnh và tầm nhìn chiến lược cụ thể, rõ ràng làm kim chi nam cho mọi hoạt động của doanh nghiệp. Cụ thể:</span></span></p>\n<ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Phương châm hoạt động</strong></span></span></li></ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Luôn đặt lợi ích của khách hàng song hành cùng lợi ích của doanh nghiệp, từ đó biến khách hàng thành những người marketing miễn phí – hiệu quả nhất cho doanh nghiệp.</span></span></p>\n<ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Sứ mệnh của Công ty</strong></span></span></li></ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mục tiêu hoạt động doanh nghiệp luôn hướng tới đó chính là tạo nên nền tảng tốt – vững mạnh để từ đó có thể hỗ trợ, đáp ứng tất cả các yêu cầu của khách hàng trong mọi dự án xây dựng (kể cả những khách hàng khó tính nhất) để mang lại không gian tuyệt vời đúng theo ý muốn của nhà đầu tư, góp phần tạo nên thắng lợi trong các dự án kinh doanh của khách hàng. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1584981688_1ad3b8201b727fcd3ebd.jpg\" style=\"width: 627px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây khách sạn 5 sao</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bên cạnh đó, đội ngũ nhân viên công ty không ngừng học hỏi, trau dồi chuyên môn cũng như tích lũy kinh nghiệm để vươn ra xa hơn, mang lại sự an tâm với những công trình xây dựng lớn trong và ngoài nước.</span></span></p>\n<ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Tầm nhìn chiến lược</strong></span></span></li></ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công ty xây dựng An Gia Khang luôn phấn đấu để giữ vị thế là công ty xây dựng công nghiệp uy tín, chất lượng dịch vụ cao hàng đầu tại Việt Nam. Đồng thời, chú trọng mở rộng và thiết lập nhiều các mối quan hệ để hợp tác lâu dài. Từng bước khẳng định vị trí và tầm quan trọng trong lĩnh vực đấu thầu xây dựng.</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>b) Các dịch vụ An Gia Khang cung cấp</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hiện nay chúng tôi đang cung cấp nhiều dịch vụ xây dựng nhằm đáp ứng mọi nhu cầu của khách hàng cũng như mang lại cho khách hàng nhiều sự lựa chọn tốt nhất.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1584981741_94ea5c84e783ff3cf238.jpg\" style=\"width: 637px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế khách sạn đẹp</span></span></p>\n<ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Dịch vụ thiết kế công trình xây dựng</strong></span></span></li></ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cung cấp các bản vẽ thiết kế nên không gian bạn mong muốn chính là một trong những dịch vụ Công ty xây dựng An Gia Khang đang cung cấp và được nhiều khách hàng đánh giá cao. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không những thế, chúng tôi còn có một kho tư liệu các bản thiết kế không gian tuyệt đẹp theo nhiều phong cách từ cổ điển cho đến hiện đại để bạn có thể tham khảo.</span></span></p>\n<ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Dịch vụ xây dựng công trình</strong></span></span></li></ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây là dịch vụ trọng yếu của chúng tôi, hỗ trợ bạn xây dựng nên những công trình hoàn mỹ. Bạn có thể lựa chọn 02 phương thức dịch vụ xây dựng công trình do chúng tôi cung cấp.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thực hiện xây dựng những hạn mục theo yêu cầu của khách hàng, chịu trách nhiệm thi công đảm bảo chất lượng xây dựng tốt nhất.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1584981808_4ce2e322d0fdb0937190.jpg\" style=\"width: 626px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế khách sạn hiện đại</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mọi dịch vụ xây dựng luôn được thực hiện với quy trình làm việc khoa học cùng đội ngũ chuyên nghiệp bậc nhất tạo nên giá trị cốt lõi của Công ty xây dựng An Gia Khang.</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>c) Lý do bạn nên chọn nhà thầu xây dựng An Gia Khang</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Như bạn đã biết, xây dựng khách sạn là một trong những công trình xây dựng lớn đòi hỏi nhà thầu xây dựng cần có kỹ thuật máy móc hiện đại, đội ngũ nhân viên chuyên môn sâu và độ uy tín cao. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1584981848_760fa6bb319dd831a9e7.jpg\" style=\"width: 627px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thi công xây dựng khách sạn uy tín</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chính vì thế mà Công ty xây dựng An Gia Khang là công ty thầu xây dựng được nhắc đến nhiều trong lĩnh vực xây dựng khách sạn tại Việt Nam.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không phải hiển nhiên mà chúng tôi lại nhận được sự đánh giá cao từ các chuyên gia xây dựng và khách hàng. Dưới đây là 5 lý do bạn nên chọn Công ty xây dựng An Gia Khang để xây dựng khách sạn, bạn có thể tham khảo trước khi đưa ra quyết định lựa chọn của mình.</span></span></p>\n<ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Chất lượng công trình cao</strong></span></span></li></ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chất lượng công trình xây dựng chính là yếu tố luôn được chúng tôi đặt lên hàng đầu, đây cũng là một trong những vấn đề quan trọng được nhiều chủ đầu tư quan tâm (đặc biệt là trong xây dựng khách sạn).</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đến với Công ty xây dựng An Gia Khang bạn có thể hoàn toàn yên tâm về chất lượng xây dựng, đảm bảo 100% theo cam kết ban đầu giữa công ty và chủ đầu tư.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1584981894_aab8209a1bb95c487dc4.jpg\" style=\"width: 638px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhiều kinh nghiệm thị công xây dựng khách sạn</span></span></p>\n<ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Đội ngũ chuyên nghiệp – đưa ra giải pháp toàn diện</strong></span></span></li></ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quy trình làm việc khoa học cùng đội ngũ nhân viên chuyên nghiệp sẽ hỗ trợ bạn đưa ra giải pháp toàn diện nhất, phù hợp nhất với nhu cầu, mục đích đầu tư xây dựng khách sạn của mình.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bạn sẽ được hỗ trợ xây dựng hoàn chỉnh ý tưởng không gian khách sạn mang phong cách riêng biệt của riêng mình, nắm được tiến độ xây dựng – thời gian hoàn thành cũng như kinh phí cần chi trong các hạng mục công trình.</span></span></p>\n<ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Minh bạch tài chính – báo cáo tiến độ theo đúng thỏa thuận</strong></span></span></li></ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chúng tôi luôn minh bạch tài chính ngay từ đầu trong giai đoạn ký kết hợp đồng, đảm bảo sẽ không có những khoản kinh phí phát sinh ngoài dự kiến nếu không có sự thay đổi yêu cầu của chủ đầu tư.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bên cạnh đó, chế độ giám sát và báo cáo tiến độ thi công công trình cụ thể giúp chủ đầu tư nắm được từng hạng mục của dự án.</span></span></p>\n<ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Bảng giá xây dựng khách sạn vô cùng hợp lý</strong></span></span></li></ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đưa ra mức giá xây dựng hợp lý chính là một trong các yếu tố khiến Công ty xây dựng An Gia Khang được nhiều chủ đầu tư tin chọn. Tùy vào từng hạng mục cụ thể với các yêu cầu chi tiết về xây dựng mà chúng tôi sẽ đưa ra các mức giá chi phí xây dựng cụ thể. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tuy nhiên, chúng tôi tự tin khi nói rằng mức giá xây dựng do chúng tôi đưa ra hợp lý nhất trên thị trường hiện nay với chất lượng công trình tuyệt đỉnh.</span></span></p>\n<ul><li><strong>Đơn giá xây dựng khách sạn</strong></li></ul>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Theo tiêu chuẩn quy định khách san 3 sao</span></span></p>\n<ul>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Số phòng ít nhất 50 phòng</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Buồng 1 giường đơn 18m2</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Buồng 1 giường đôi hoặc 2 giường đơn 22m2</span></span></li>\n</ul>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Và nhiều quy định khách, quý khách có thể xem thêm: </span></span><a href=\"http://www.angiakhang.com/tieu-chuan-xay-dung-khach-san-3-sao\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(184, 49, 47);\">Tiêu chuẩn xây dựng khách sạn 3 sao</span></span></a></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đơn giá xây khách sạn 3 sao gồm những gì và chi phí như thế nào?</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giá xây dựng phần thô: từ 2.600.000đ đến 3.500.0000đ/m2 xây dựng. Mức giá dao động tùy vào yêu cầu, quy mô công trình.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giá xây dựng và hoàn thiện trọn gói: từ 4.000.000đ đến 7.000.000đ/m2. Mức giá dao động căn cứ vào quy mô và chủng loại vật tư theo yêu cầu</span></span></li>\n</ul>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(184, 49, 47);\">Lưu ý:</span><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Đơn giá xây dựng khách sạn này chỉ mang tính tham khảo, vì quá trình thi công xây dựng khách sạn, cần có sự khảo sát, thay đổi theo từng thời điểm, phụ thuộc vào giá nhân công, nguyên vật liệu, diện tích, Vì vậy để nhận được bảng báo giá chi phí xây dựng khách sạn chi tiết và mới nhất hãy gọi ngay chúng tôi:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1584982155_154a62f17fd515e044f3.jpg\" style=\"width: 644px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu khách sạn 3 sao</span></span></p>\n<ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Cam kết hoàn trả 100% nếu chất lượng công trình kém</strong></span></span></li></ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chúng tôi cam kết hoàn trả 100% phí xây dựng nếu chất lượng công trình kém so với cam kết ban đầu. Bên cạnh đó, chúng tôi còn cung cấp dịch vụ bảo dưỡng sửa chữa công trình xây dựng theo định kỳ và theo yêu cầu của khách hàng với chi phí thấp nhằm tri ân khách hàng đã tin tưởng lựa chọn chúng tôi.</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>d)  Cam kết chất lượng</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Dưới đây là những vấn đề Công ty xây dựng An Gia Khang cam kết đảm bảo cho nhà đầu tư nhằm tăng độ tin tưởng, tạo cảm giác an tâm khi lựa chọn dịch vụ xây dựng khách sạn do chúng tôi cung cấp.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các hạng mục cam kết của Công ty xây dựng An Gia Khang đối với khách hàng bao gồm:</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cam kết thực hiện đúng tiến độ công trình 100%.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cam kết mức chi phí được đưa ra thỏa thuận ngay ban đầu khi ký hợp đồng (100% công trình đảm bảo không phát sinh thêm một khoản chi phí nào so với kế hoạch dự kiến ban đầu).</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cam kết minh bạch không chỉ về tài chính mà còn về giao tiếp thương mại trong mọi giai đoạn (từ khâu tiếp nhận các dự án, tư vấn dự án xây dựng, đấu thầu đến khâu thực hiện thi công công trình và bàn giao công trình).</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Cam kết luôn là người đồng hành với khách hàng trong từng giai đoạn (không chỉ trong các giai đoạn trước và trong thi công công trình xây dựng khách sạn mà còn cả giai đoạn sau thi công – bảo hành và bảo trì).</span></li>\n</ul>\n<p><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><span style=\"color: rgb(65, 65, 65); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; display: inline !important; float: none;\">[[contact_short_code]]</span> <br></span></p>\n', NULL, 1, NULL, 1574424941, 1590572217, NULL),
(82, 'category', 27, 0, '1. Xây nhà cao tầng', '1-xay-nha-cao-tang', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Những lưu ý khi thi công xây dựng nhà cao tầng. Để có một công trình hoàn chỉnh, đạt tối đa công năng sử dụng và tránh được rủi ro bạn cần phải lưu ý đến một số vấn đề như:</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576507581_651a9e4f0f4a1e03cd27.jpg\" style=\"width: 605px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây dựng chung cư</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1 Đơn vị thi công xây dựng nhà cao tầng</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đơn vị thi công phải là nhà thầu xây dựng lớn và có kinh nghiệm, có sự phân công nhiệm vụ rõ ràng cho các đội thi công, sao cho có thể đẩy nhanh tiến độ thi công. Đồng thời cử ban chỉ huy giám sát công trình nhằm đảm bảo sát sao tới các công đoạn để đạt được hiệu quả cao nhất.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576507791_4ef482d8d4fe6198b0c2.jpg\" style=\"width: 566px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">nhà thầu xây dựng An Gia Khang</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2 Chọn thiết bị vận chuyển nguyên vật liệu lên cao</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quá trình xây dựng nhà cao tầng với kết cấu bê tông cốt thép nhằm đảm bảo chất lượng hàng đầu và hiệu quả kinh tế thì việc vận chuyển nguyên vật liệu lên cao là rất quan trọng. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bạn cần phải căn cứ vào từng kết cấu và quy mô công trình mà lựa chọn các thiết bị thi công khác nhau. Có thể tham khảo bơm bê tông nhà cao tầng, vận thăng hay các biện pháp kỹ thuật thi công như: dầm, sàn, cột, vách thang máy, cầu thang,… </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576507851_c379a18bd7074024d42f.jpg\" style=\"width: 571px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">thiết bị hỗ trợ xây nhà cao tầng</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phải lưu ý thật kỹ khi chọn thiết bị vận chuyển nguyên liệu lên cao</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Nên lựa chọn thiết bị vận chuyển nguyên vật liệu phù hợp với quy mô công trình và để đảm bảo đúng tiến độ đã đề ra trước đó.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3 Thực hiện công tác cốp pha</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nên chọn giải pháp thi công cốp pha cốt thép an toàn trong khi xây dựng nhà cao tầng, đảm bảo đúng tiến độ giúp mang đến chất lượng công trình đạt chuẩn. Do đó cần phải kết hợp cốp pha với cây chống để lắp dụng cho kết cấu nhỏ lẻ. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576507888_7674157629f83863b9d5.jpg\" style=\"width: 586px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">sắt thép xây dựng phải đảm bảo</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ngoài ra đà giáo và cốp pha cũng cần đảm bảo sự ổn định, độ cứng và dễ tháo lắp nhằm mang đến sự thuận tiện cho công tác sau này. Cốp pha cần được ghép kín và kín để không làm mất nước xi măng khi đổ, đầm bê tông. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Lưu ý ghép cốp pha đúng chuẩn</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quá trình lắp dựng cốp pha cần đảm bảo chất lượng công trình hàng đầu đồng thời tối ưu mong muốn của chủ đầu tư.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>4 Lắp dựng cột, dầm và sàn</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công đoạn này trước tiên cần đổ mầm cột cao khoảng 50mm để tạo được đường dựng bán khuôn. Thế nhưng cũng cần phải lưu ý đặt sẵn các thép chờ trên sàn để tạo chỗ neo cho cốp pha cột. Sau đó gia công thành từng mảng với kích thước bằng kích thước của 1 mặt cột, ghép các mảng theo kích thước cụ thể từng cột đồng thời đừng quên để cửa sổ đổ bê tông, chân cột chừa lỗ để vệ sinh trước khi đổ bê tông.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576507936_c3873ebd05d07e174ca8.jpg\" style=\"width: 590px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà cao tầng là công trình phức tạp nên cần đặc biệt chú trọng</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cần phải lắp dựng dầm, cột, sàn đúng tiêu chuẩn và kỹ thuật mang đến hiệu quả cao trong quá trình thi công xây dựng nhà cao tầng mà vẫn đảm bảo được đúng tiến độ đề ra.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với việc lắp dựng dầm thì trước hết cần phải xác định tim dầm và rải ván lót để đặt chân cột. Đặt cây chống chữ T, đặt 2 cây chống sát với cột rồi cố định 2 cột chống. Tiếp theo đặt thêm một số cột theo dọc tim dầm. Rải ván đáy dầm trên xà đỡ cột chống T rồi cố định hai đầu bằng hệ thống giằng.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sau đó đặt những tấm ván khuôn thành dầm và đóng đinh liên kết với đáy dầm rồi cố định mép trên bằng cây chống xiên, gông, bu lông. Sau cùng kiểm tra lại tim dầm, đồng thời chỉnh độ cao đáy dầm sao cho đúng với bản vẽ thiết kế.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">5 Lắp dựng cốt thép sàn đạt chuẩn</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sau cùng với phần sàn bạn cần phải sử dụng ván khuôn thép định hình rồi đặt trên hệ giàn giáo chữ A chịu lực bằng thép với hệ xà gồ đỡ sàn và  xà gồ thép.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1576507977_9c1083383b77c675233c.jpg\" style=\"width: 598px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sử dụng cốt thép đạt chuẩn để tránh rủi ro</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nên dùng tối đa diện tích ván khuôn thép định hình với những diện tích khó thi công còn lại thì nên dùng kết hợp với ván khuôn gỗ. Tính theo chu vi sàn có ván diềm sẽ được liên kết đinh con đỉa với thành ván khuôn dầm, dầm đỡ ván khuôn dầm.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong> 6 Thực hiện công tác cốt thép</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đầu tiên cần phải gia công cốt thép như thiết kế ở kho công trường theo như tiến độ thi công. Điều này có thể khắc phục các sai sót và đảm bảo gia công chính xác như bản vẽ thiết kế và tiến độ đề ra. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công việc uốn, cắt thép và hàn cốt thép phải được đảm bảo theo yêu cầu công việc. Trong đó quá trình gia công cần được sắp xếp theo các chủng loại và cấu kiện riêng nhằm tránh nhầm lẫn một cách tối đa.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576508289_e49f0af132bf8d1d6de8.jpg\" style=\"width: 595px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><a href=\"http://www.angiakhang.com/thi-cong-xay-dung\">thi công xây dựng</a></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thực hiện công tác cốt thép đúng tiến độ đồng thời vẫn phải đảm bảo yêu cầu giúp tối ưu mong muốn của chủ đầu tư.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>7 Đổ bê tông công trình</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trước khi đổ bê tông cần phải kiểm tra kích thước, hình dáng cũng như khe hở của ván khuôn. Tiếp đến kiểm tra giàn giáo, cốt thép và sàn thao tác. Cuối cùng phải chuẩn bị hệ thống ván gỗ để làm sàn công tác.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Chiều cao rơi tự do của bê tông thường không quá 1,5-2m nhằm tránh bị phân tầng bê tông. Khi đổ bê tông cũng phải chú ý theo trình tự đã định, từ trong ra ngoài, từ xa tới gần. Bắt đầu ở chỗ thấp trước sau đó đổ từng lớp, xong lớp nào cần dầm ngay lớp đó. Cần phải dùng đầm bàn cho sàn, dầm và tường, đầm dùi cho cột.</span></p>\n', NULL, 1, NULL, 1574425263, 1579259014, NULL),
(83, 'category', 21, 1, '2. Chính sách chất lượng', '2-chinh-sach-chat-luong', '<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><strong><span style=\"font-size: 14px; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51);\">An Gia Khang cam kết chính sách chất lượng thông qua các việc:</span></strong></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Công ty tuyển chọn và đào tạo đội ngũ kiến trúc sư, kỹ sư, quản lý dự án có năng lực và chuyên môn cao đảm bảo tuân thủ việc thực hiện theo chính sách chất lượng của công ty trong quá trình thực hiện dự án.</span></span></span></p>\n<p style=\"margin: 0in 0in 7.5pt; font-size: 15px; font-family: Calibri, sans-serif; line-height: 115%; text-align: center; background: white;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\"><img src=\"/uploads/contents/1574590280_8ebd5766c3597b40c5d1.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">công trình An Gia Khang thực hiện cam kết chất lượng bền vững</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Hợp tác với các nhà cung cấp và nhà thầu phụ có uy tín và năng lực thi công. Kiểm soát chặt chẽ việc cung cấp vật liệu xây dựng và vật tư kỹ thuật có chất lượng cao nguồn gốc rõ ràng đáp ứng nhu cầu của nhà đầu tư.</span></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\"><img src=\"/uploads/contents/1574590401_3ecd6f4eac7a129fb38d.png\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công trình đang thực hiện</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Không ngừng cải tiến hệ thống quản lý chất lượng, tuân thủ và kiểm soát nghiêm khắc công trình thi công các biện pháp kỹ thuật, an toàn lao động, vệ sinh môi trường và các biện pháp nhằm tránh rủi ro.</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Tổ chức lớp tập huấn nâng cao kiến thức chuyên môn cho đội ngũ nhân sự quản lý và nhân sự thi công.</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Tuân thủ các quy định của pháp luật và các tiêu chuẩn của ngành nghề hoạt động.</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51);\">Nỗ lực thực hiện các cam kết về chất lượng, tiến độ và an toàn của công trình.</span></p>\n<p style=\"margin: 0in 0in 7.5pt; font-size: 15px; font-family: Calibri, sans-serif; line-height: 115%; text-align: center; background: white;\"><strong>Giới thiệu về Công ty xây dựng An Gia Khang</strong></p>\n<p><iframe width=\"100%\" height=\"360\" src=\"https://www.youtube.com/embed/atUEemufouk\" frameborder=\"0\" allowfullscreen=\"\"></iframe></p>\n<p><br></p>\n', NULL, 1, NULL, 1574589250, 1592224987, NULL),
(84, 'category', 21, 2, '3. Sơ đồ tổ chức công ty', '3-so-do-to-chuc-cong-ty', '<p style=\"text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><img src=\"/uploads/contents/1574589310_c7b37a2481778f751829.png\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></p>\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Sở đồ tổ chức công ty An Gia Khang</span></p>\n', NULL, 1, NULL, 1574589331, 1592224987, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(85, 'category', 21, 3, '4. Lịch sử hình thành và phát triển', '4-lich-su-hinh-thanh-va-phat-trien', '<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51);\">Ngày 28 tháng 1 năm 2016 – Tại thành phố Hồ Chí Minh, <strong>An Gia Khang </strong>chính thức ghi tên trên bản đồ các doanh nghiệp trong lĩnh vực THIẾT KẾ VÀ XÂY DỰNG.</span></p>\n<p style=\"margin: 0in 0in 7.5pt; font-size: 15px; font-family: Calibri, sans-serif; line-height: 115%; text-align: center; background: white;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\"><img src=\"/uploads/contents/1574590558_0c086c63abb24f496700.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đổi ngũ nhân sự Công ty An Gia Khang</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Năm 2016, tại Việt Nam cũng ghi dấu sự kiện quan trọng: Đảo ngọc Phú Quốc chuyển mình đón du khách và các nhà đầu tư với tham vọng trở thành Thiên đường du lịch. Với tầm nhìn chiến lược, đón đầu xu hướng và sự đầu tư kỹ càng về nguồn lực, về con người – An Gia Khang tự tin đặt tên trên những công trình xây dựng thiên đường du lịch Phú Quốc.</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Những người thợ An Gia Khang trong niềm hân hoan xây dựng những công trình. Từ đây, với thế mạnh xây dựng, 48 căn <strong>biệt thự</strong> sang trọng và đẳng cấp trong khu tổ hợp du lịch Sonasea Villas &amp; Resort từng bước vươn mình ra biển lớn.</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Dần định danh thương hiệu, An Gia Khang bắt đầu chuyển mình mạnh mẽ với hình ảnh của một chiến binh trong lĩnh vực thiết kế và thi công xây dựng. Những <strong>nhà phố thương mại, Villas Shop</strong> trong khu 80ha thuộc dự án Sonasea Villas &amp; Resorts 01 Phú Quốc chính thức do An Gia Khang thi công.</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Chất lượng công trình chính là thước đo của niềm tin và sự lựa chọn khi An Gia Khang trở thành đơn vị được chọn để hoàn thiện nội thất căn biệt thự mẫu khu Sonasea Golden Villas - một trong những khu biệt thự có vị trí đắc địa nhất trên đảo Phú Quốc với nội thất hiện đại, sang trọng xứng tầm đẳng cấp “Giá trị của cuộc sống vương giả”.</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Chưa dừng lại ở đó, cùng với năng lực thi công xây dựng,  một lần nữa An Gia Khang đã dành trọn niềm tin của khách hàng khi là một trong những đơn vị thi công <strong>Paris Villas</strong> trong khu 52ha thuộc dự án Sonasea Villas &amp; Resort 02 – sản phẩm bất động sản được khẳng định bởi tầm vóc lẫn kiến trúc.</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Trong sự cạnh tranh khốc liệt của hàng ngàn doanh nghiệp cùng lĩnh vực, sau một chặng hành trình, liên tiếp được đứng trong hàng ngũ những đơn vị xây dựng dự án bất động sản đẳng cấp và sang trọng mang tầm thế giới, chiến binh An Gia Khang đã khẳng định:</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">- An Gia Khang là đối tác uy tín và chất lượng trong ngành xây dựng tại Việt Nam, đặc biệt là khu vực tỉnh Kiên Giang, Phú Quốc</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">- An Gia Khang tự tin là công ty có các thế mạnh:</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">+ Triển khai thiết kế, thi công công trình, dự án</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">+ Trang trí nội ngoại thất công trình</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">+ Sản phẩm xây dựng chất lượng với chi phí hợp lý và mang tính mỹ thuật cao</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Những giá trị tạo dựng đạt được đang đi đúng với tôn chỉ mà An Gia Khang đề ra ngay từ khi bắt đầu thành lập nhằm cung cấp dịch vụ và sản phẩm chất lượng, an toàn.</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Bốn năm cho chặng hành trình hình thành và phát triển – Thương hiệu An Gia Khang khẳng định mình qua sự lựa chọn của khách hàng cho những dự án lớn mang tầm chiến lược của khu vực.</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Và vị thế ấy được tạo dựng bởi những con người và văn hóa mang tên An Gia Khang Đó là từ 15 nhân sự trong buổi đầu thành lập, đến nay, 25 cái tên đứng trong hàng ngũ nhân viên là đội ngũ kiến trúc sư, kỹ sư, quản lý dự án có năng lực và chuyên môn cao. Họ, dù ở vị trí nào, phòng ban nào - là nhân sự hành chính, phòng kế toán, phòng QLTC, hay phòng thiết kế đều là những con người luôn ý thức phát huy tinh thần trách nhiệm cao độ, sẵn sàng đón nhận thách thức, không ngừng tìm kiếm cơ hội</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Thúc đẩy tinh thần làm việc cống hiến, trách nhiệm từ văn phòng cho tới công trường ấy của nhân viên An Gia Khang không gì khác bởi đây là nơi mà mỗi cá nhân đều được trân trọng tài năng, được làm việc trong môi trường khoa học với hệ thống trao đổi thông tin đảm bảo mọi nhân viên đều dễ dàng tiếp cận và sử dụng thông tin cần thiết cho các hoạt động trong công việc.</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Cùng đó, mức thu nhập tương xứng với đóng góp,  những phần thưởng theo doanh thu thực hiện công trình đã hoàn thành đúng tiến độ... cũng chính là động lực để nhân viên phát huy năng lực, ra sức cống hiến.</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Sự lớn mạnh của một doanh nghiệp phản ánh chân thực năng lực của đội ngũ nhân viên ở đó. Và với chính sách luôn vì sự phát triển của người lao động; An Gia Khang đã xây dựng được đội ngũ quản lý, cán bộ kỹ thuật; các tổ công nhân chuyên ngành năng lực và kinh nghiệm đáp ứng yêu cầu cao của đối tác.</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Từ đây, với nền móng vững chắc về con người và giá trị đầu tư cùng với niềm tin được khẳng định qua những dự án lớn - An Gia Khang tự tin vươn xa hơn trong lĩnh vực thi công các công trình cao tầng, biệt thự, nhà phố thuộc các dự án trên toàn quốc.</span></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n', NULL, 1, NULL, 1574589387, 1592224987, NULL),
