(93, 'category', 7, 6, '7. Năng lực nhân sự', '7-nang-luc-nhan-su', '<p style=\"text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><img src=\"/uploads/contents/1574604728_0655ad6af6fda10b3c46.png\" style=\"width: 660px;\" class=\"fr-fic fr-dib\"></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Năng lực nhân sự</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1574604799_1169d8bc42c6e3983371.png\" style=\"width: 392px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Năng lực nhân sự</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1574604900_40fb4e0293faa0b9d77b.png\" style=\"width: 370px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Năng lực nhân sự</span></p>\n', NULL, 1, NULL, 1574604748, 1592467551, NULL),
(94, 'category', 7, 7, '8. Danh sách thiết bị máy móc', '8-danh-sach-thiet-bi-may-moc', '<p style=\"text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><img src=\"/uploads/contents/1574605007_08455209df03ee53c5df.png\" style=\"width: 491px;\" class=\"fr-fic fr-dib\"></span></p>\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Danh sách thiết bị máy móc</span></p>\n', NULL, 1, NULL, 1574605020, 1592467551, NULL),
(95, 'category', 7, 8, '9. Một số công trình tiêu biểu đã và đang thi công', '9-mot-so-cong-trinh-tieu-bieu-da-va-dang-thi-cong', '<p><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Năm 2016, tại Việt Nam cũng ghi dấu sự kiện quan trọng: Đảo ngọc Phú Quốc chuyển mình đón du khách và các nhà đẩu tư với tham vọng trở thành Thiên đường du lịch. </span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1574605204_c8b53538dea619e116a4.jpg\" style=\"width: 557px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các dự án đã và đang thi công</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với tẩm nhìn chiến lược, đón đẩu xu hướng và sự đẩu tư kỹ càng về nguồn lực, về con người - An Gia Khang tự tin đặt tên trên những công trình xây dựng thiên đường Phú Quốc.</span></span></p>\n<p><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Những người thợ An Gia Khang trong niềm hân hoan xây dựng những công trình. Từ đây, với thế mạnh xây dựng, 48 căn biệt thự sang trọng và đẳng cấp trong khu tổ hợp du lịch Sonasea Villas and Resorts đã chính thức được An Gia Khang thi công.</span></p>\n', NULL, 1, NULL, 1574605144, 1592467551, NULL),
(96, 'category', 7, 9, '9.1 Biệt thự nghĩ dưỡng 3 Key tại Dương Tơ Phú Quốc', '91-biet-thu-nghi-duong-3-key-tai-duong-to-phu-quoc', '<p style=\"text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><img src=\"/uploads/contents/1574605346_170903251c9303cff7ce.png\" style=\"width: 468px;\" class=\"fr-fic fr-dib\"></span></p>\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Biệt thự nghĩ dưỡng 3 key tại Phú Quốc</span></p>\n', NULL, 1, NULL, 1574605365, 1592467551, NULL),
(97, 'category', 7, 10, '9.2 Biệt thự nghĩ dưỡng 4 key tại Phú Quốc', '92-biet-thu-nghi-duong-4-key-tai-phu-quoc', '<p style=\"text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><img src=\"/uploads/contents/1574605461_260fbbd04ee3030ab19e.png\" style=\"width: 441px;\" class=\"fr-fic fr-dib\"></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Biệt thự nghĩ dưỡng 4 key</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><img src=\"/uploads/contents/1574605529_6dbdd3517ff8801f93a2.png\" style=\"width: 447px;\" class=\"fr-fic fr-dib\"></span></p>\n', NULL, 1, NULL, 1574605476, 1592467551, NULL),
(98, 'category', 7, 11, '9.3 Công trình Shophouse tại Phú Quốc', '93-cong-trinh-shophouse-tai-phu-quoc', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px; line-height: 115%; font-family: Arial, Helvetica, sans-serif; color: rgb(65, 65, 65); background: white;\">Dẩn định danh thương hiệu, An Gia Khang bắt đẩu chuyển mình mạnh mẽ với hình ảnh của một chiến binh trong lĩnh vực thiết kế và thi công xây dựng. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%; color: rgb(65, 65, 65); background: white;\"><img src=\"/uploads/contents/1574605672_d9e148da194fbc027646.png\" style=\"width: 466px;\" class=\"fr-fic fr-dib\"></span></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công trình shop house tại Phú Quốc</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1574605813_f9f2694e8dea84f39987.png\" style=\"width: 449px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%; color: rgb(65, 65, 65); background: white;\">Chủ đẩu tưCEO Group đã chọn An Gia Khang là một trong những nhà thẩu đáp ứng đủ điều kiện để giao thi công các căn nhà phố thương mại, Villas Shop trong khu 80ha thuộc dự án Sonasea Villas &amp; Resorts 01</span></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%; color: rgb(65, 65, 65); background: white;\"><img src=\"/uploads/contents/1574605888_1f4ec45169d8746fd696.png\" style=\"width: 406px;\" class=\"fr-fic fr-dib\"></span></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng tại phú quốc</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1574605985_fcc1180c4e897085115c.png\" style=\"width: 436px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Công ty xây dựng tại phú quốc</span></p>\n<p><br></p>\n', NULL, 1, NULL, 1574605692, 1592467551, NULL),
