(154, 'category', 49, 2, '3. Bảng giá hoàn thiện trong nhà tại Phú Quốc', '3-bang-gia-hoan-thien-trong-nha-tai-phu-quoc', '<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin-top: 0px; margin-bottom: 1rem; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; text-align: center;\"><img src=\"/uploads/contents/1587013588_078263ba57da870182e4.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin-top: 0px; margin-bottom: 1rem; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; text-align: center;\"><img src=\"/uploads/contents/1590047905_65b0c3c597a84c65b15e.jpg\" style=\"width: 660px;\" class=\"fr-fic fr-dib\"></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin-top: 0px; margin-bottom: 1rem; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; text-align: center;\"><img src=\"/uploads/contents/1590048091_091e6365c4353da5012c.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin-top: 0px; margin-bottom: 1rem; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; text-align: center;\"><img src=\"/uploads/contents/1590048164_de53a462a10761fb3737.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin-top: 0px; margin-bottom: 1rem; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; text-align: center;\"><img src=\"/uploads/contents/1590048226_042667bd08d6b0810512.jpg\" style=\"width: 657px;\" class=\"fr-fic fr-dib\"></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n', NULL, 1, NULL, 1575446564, 1592465492, NULL),
(157, 'category', 18, 1, '2. Quy trình xây nhà trọn gói', '2-quy-trinh-xay-nha-tron-goi', '<p style=\'margin:0in;margin-bottom:12.0pt;line-height:115%;font-size:15px;font-family:\"Arial\",\"sans-serif\";margin-top:12.0pt;margin-right:0in;margin-left:0in;text-align:justify;\'><span style=\"font-size: 14px; line-height: 115%; font-family: Arial, Helvetica, sans-serif;\">Quy trình xây dựng nhà trọn gói thường có những bước cơ bản sau đây. Hãy chọn cho mình một đơn vị thi công xây dựng uy tín và chất lượng.</span></p>\n<p style=\"margin: 12pt 0in; line-height: 115%; font-size: 15px; font-family: Arial, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%;\"><strong><img src=\"/uploads/contents/1581615326_20ec73915fd0307601c1.jpg\" style=\"width: 613px;\" class=\"fr-fic fr-dib\"></strong></span></span></span></p>\n<p style=\"margin: 12pt 0in; line-height: 115%; font-size: 15px; font-family: Arial, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà thi công xây dựng trọn gói</span></span></p>\n<p style=\'margin:0in;margin-bottom:12.0pt;line-height:115%;font-size:15px;font-family:\"Arial\",\"sans-serif\";margin-top:12.0pt;margin-right:0in;margin-left:0in;text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%;\"><strong>Bước 1:</strong></span></span></span></p>\n<p style=\'margin:0in;margin-bottom:12.0pt;line-height:115%;font-size:15px;font-family:\"Arial\",\"sans-serif\";margin-top:12.0pt;margin-right:0in;margin-left:0in;text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%;\">Lên lịch hẹn và tiếp nhận thông tin từ phía khách hàng. Sau khi khách hàng liên hệ công ty xây dựng sẽ đưa ra lịch hẹn để trao đổi và tiếp nhận thông tin khách hàng.</span></span></span></p>\n<p style=\'margin:0in;margin-bottom:12.0pt;line-height:115%;font-size:15px;font-family:\"Arial\",\"sans-serif\";margin-top:12.0pt;margin-right:0in;margin-left:0in;text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%;\"><strong>Bước 2:</strong></span></span></span></p>\n<p style=\'margin:0in;margin-bottom:12.0pt;line-height:115%;font-size:15px;font-family:\"Arial\",\"sans-serif\";margin-top:12.0pt;margin-right:0in;margin-left:0in;text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%;\">Khảo sát công trình xây nhà trọn gói: Bộ phận kinh doanh của công ty   và phòng thi công gặp gỡ để tiếp nhận yêu cần từ phía khách hàng. Tiếp đến thu thập những tư liệu liên quan tới việc khảo sát công trình. Khách hàng sẽ được trao đổi và tư vấn kỹ càng hơn về dịch vụ thi công xây dựng.</span></span></span></p>\n<p style=\'margin:0in;margin-bottom:12.0pt;line-height:115%;font-size:15px;font-family:\"Arial\",\"sans-serif\";margin-top:12.0pt;margin-right:0in;margin-left:0in;text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%;\"><strong>Bước 3:</strong></span></span></span></p>\n<p style=\'margin:0in;margin-bottom:12.0pt;line-height:115%;font-size:15px;font-family:\"Arial\",\"sans-serif\";margin-top:12.0pt;margin-right:0in;margin-left:0in;text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%;\">Bóc tách khối lượng công trình và lập dự toán báo giá thi công xây dựng công trình cho khách hàng. Sau đó gửi trực tiếp báo giá đến khách hàng.</span></span></span></p>\n<p style=\'margin:0in;margin-bottom:12.0pt;line-height:115%;font-size:15px;font-family:\"Arial\",\"sans-serif\";margin-top:12.0pt;margin-right:0in;margin-left:0in;text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%;\"><strong>Bước 4:</strong></span></span></span></p>\n<p style=\'margin:0in;margin-bottom:12.0pt;line-height:115%;font-size:15px;font-family:\"Arial\",\"sans-serif\";margin-top:12.0pt;margin-right:0in;margin-left:0in;text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%;\">Nếu khách hàng đồng ý với báo giá đã được duyệt thì công ty sẽ soạn hợp đồng và thương lượng những điều kiện cụ thể với khách hàng ở trong hợp đồng. Lãnh đạo của phòng thi công sẽ trực tiếp ký kết hợp đồng với khách hàng.</span></span></span></p>\n<p style=\'margin:0in;margin-bottom:12.0pt;line-height:115%;font-size:15px;font-family:\"Arial\",\"sans-serif\";margin-top:12.0pt;margin-right:0in;margin-left:0in;text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%;\"><strong>Bước 5:</strong></span></span></span></p>\n<p style=\'margin:0in;margin-bottom:12.0pt;line-height:115%;font-size:15px;font-family:\"Arial\",\"sans-serif\";margin-top:12.0pt;margin-right:0in;margin-left:0in;text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%;\">Sau khi có thiết  kế và ký hợp đồng công ty thực hiện xây dựng giấy phép trước khi thi công.</span></span></span></p>\n<p style=\'margin:0in;margin-bottom:12.0pt;line-height:115%;font-size:15px;font-family:\"Arial\",\"sans-serif\";margin-top:12.0pt;margin-right:0in;margin-left:0in;text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%;\"><strong>Bước 6:</strong></span></span></span></p>\n<p style=\'margin:0in;margin-bottom:12.0pt;line-height:115%;font-size:15px;font-family:\"Arial\",\"sans-serif\";margin-top:12.0pt;margin-right:0in;margin-left:0in;text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%;\">Thực hiện hợp đồng với các công đoạn cụ thể như: vật tư, chuẩn bị nhân lực và máy móc thi công phục vụ cho công trình, thực hiện thi công công trình theo như các nội dung đã kí trong hợp đồng. Bố trí kỹ sư giám sát thi công xây dựng công trình và nghiệm thu các hạng mục công trình theo đúng quy định.</span></span></span></p>\n<p style=\'margin:0in;margin-bottom:12.0pt;line-height:115%;font-size:15px;font-family:\"Arial\",\"sans-serif\";margin-top:12.0pt;margin-right:0in;margin-left:0in;text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%;\"><strong> Bước 7:</strong></span></span></span></p>\n<p style=\'margin:0in;margin-bottom:12.0pt;line-height:115%;font-size:15px;font-family:\"Arial\",\"sans-serif\";margin-top:12.0pt;margin-right:0in;margin-left:0in;text-align:justify;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 115%;\">Làm hồ sơ hoàn công công trình và biên bản nghiệm thu để bàn giao công trình</span></span></span></p>\n<p style=\'margin:0in;margin-bottom:12.0pt;line-height:115%;font-size:15px;font-family:\"Arial\",\"sans-serif\";margin-top:12.0pt;margin-right:0in;margin-left:0in;text-align:justify;\'><span style=\"font-size: 14px; line-height: 115%; font-family: Arial, Helvetica, sans-serif;\"><strong>Bước 8:</strong> Thanh lý hợp đồng và bảo hành công trình theo như nội dung đã ký kết. Thực hiện bảo hành công trình theo đúng quy định.</span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n', NULL, 1, NULL, 1575447898, 1590049742, NULL),
(167, 'category', 25, 1, '2.Thủ tục xin giấy phép xây dựng khách sạn', '2thu-tuc-xin-giay-phep-xay-dung-khach-san', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Để có thể tiến hành xây dựng khách sạn chủ đầu tư cần thực hiện đầy đủ các loại giấy tờ. Để có thể tiến hành xây dựng khách sạn thì quý khách hàng, các chủ đầu tư cần chú ý, cần phải bổ sung các loại giấy tờ sau.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575595784_ebef574783d2139e2b7f.png\" style=\"width: 635px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thủ tục xin phép xây dựng khách san</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trong nội dung hôm nay, An Gia Khang xin được chia sẽ những thông tin về các quy định và thủ tục xin giấy phép xây dựng khách sạn theo đúng quy định pháp luật hiện nay.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây là một trong những nội dung sẽ giúp những ai quan tâm đến việc đầu tư kinh doanh khách sạn . Những thông tin trong bài viết này sẽ cung cấp cho bạn một số thông tin, xin cấp phép xây dựng khách sạn. Tuy nhiên không phải ai cũng nắm rõ được các quy định và nội dung này.</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>a) Thủ tục xin giấy phép xây dựng khách sạn</strong></span></span></h3>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đăng ký kinh doanh khách sạn tại sở kế hoạch đầu tư. Quý khách hàng có thể đăng ký dưới dạng hình thức công ty hoặc hộ kinh doanh cá thể.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế bộ hồ sơ bản vẽ kỹ thuật, xin phép xây dựng khách sạn đạt tiêu chuẩn yêu cầu . Xin thỏa thuận yêu cầu phòng cháy chữa cháy theo đúng quy định.( Nên chọn đơn vị thiết kế xây dựng khách sạn có kinh nghiệm để thiết kế bản vẽ khách sạn)</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chuẩn bị đầy đủ hồ sơ xin phép xây dựng, kèm theo bản vẽ và giấy phép đăng ký kinh doanh</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xin kinh doanh khách sạn từ quận hoặc từ trên sở xây dựng gửi xuống quận( tiêu chuẩn này đối với các dự án xây dựng khách sạn trên 7 tầng hoặc kinh phí xây dựng trên 15 tỷ trở lên).</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nộp hồ sơ và chờ quận kiểm duyệt hồ sơ</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trong vòng khoản 15 ngày nếu cơ quan quận có chỉ tiêu và hồ sợ đạt chuẩn sẽ cấp phép xây dựng khách sạn và ngược lại. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1584982450_464358f246b4ff99a2d5.jpg\" style=\"width: 627px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tư vấn thiết kế và thi công khách sạn</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>b) Hồ sơ chuẩn bị xin giấy phép xây dựng khách sạn</strong></span></span></h3>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đơn xin cấp giấy phép xây dựng ( theo mẫu) do chủ đầu tư đứng tên.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bản sao một trong những giấy tờ về quyền sử dụng đất và quyền sở hữu nhà (nếu có), kèm theo trích lục bản đồ hoặc trích đồ trên thực địa hoặc sơ đồ ranh giới lô đất.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giấy đăng ký kinh doanh (nếu là công trình xây dựng của doanh nghiệp).</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">03 bộ hồ sơ thiết kế gồm:</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mặt bằng công trình trên lô đất (tỷ lệ 1/100-1/200) kèm theo sơ đồ vị trí công trình;</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bản vẽ mặt bằng móng (tỷ lệ 1/100-1/200) và chi tiết mặt cắt móng (tỷ lệ 1/50);</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sơ đồ hệ thống thoát nước mưa, nước thải, cấp nước, cấp điện (tỷ lệ 1/100-1/200); ảnh chụp khổ 9cm x 12cm mặt cắt công trình có không gian liền kề trước khi cải tạo, sửa chữa và mở rộng.</span></span></li>\n</ul>\n<div style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575595819_b86e0ec3cffeb818d5a5.jpg\" style=\"width: 631px;\" class=\"fr-fic fr-dib\"></span></span></div>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu bạn không rõ vấn đề về thủ tục xin phép đăng ký kinh doanh có thể liên hệ công ty Tân Thành Thịnh</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">(Nguồn: thủ tục đăng ký giấy phép xây đựng khách sạn từ công ty Tân Thành Thịnh)</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>c) Thủ tục xin giấy phép xây dựng khách sạn mini</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">An Gia Khang xin gửi đến quý khách hàng thông tin về việc chuẩn bị thủ tục xin cấp phép xây dựng khách sạn mini như sau:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1584982338_b0a59b943a0f31908bd8.jpg\" style=\"width: 620px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây khách sạn mini</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mặt bằng định vị khách sạn trên lô đất, tỷ lệ 1/200- 1/500, kèm theo sơ đồ vị trí công trình.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mặt bằng các tầng, các mặt đứng và các mặt cắt thiết kế khách sạn tỷ lệ 1/100- 1/200.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bản vẽ mặt bằng móng tỷ lệ 1/100- 1/200 và  chi tiết mặt cắt móng tỷ lệ 1/50, theo sơ đồ hệ thống và điểm đấu nối kỹ thuật thoát nước mưa, xử lý nước thải, cấp nước, cấp điện tỷ lệ 1/100-1/200.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giải pháp gia cố, chống đỡ khi tháo dỡ công trình cũ và xây dựng công trình mới nhằm bảo đảm an toàn cho công trình liền kề và cam kết chịu trách nhiệm.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đơn xin cấp giấy phép xây dựng khách sạn mini (theo mẫu mua tại Cơ quan cấp phép xây dựng) do chủ đầu tư đứng tên.</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575595929_6b8d2777400fb1541b92.jpg\" style=\"width: 627px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tiêu chí chọn công ty xây dựng khách sạn</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>d) Thủ tục xin giấy phép sửa chữa khách sạn</strong></span></span></h3>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ðơn xin gia hạn giấy phép xây dựng, sửa chữa nhà theo mẫu (1 bản chính).</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giấy phép xây dựng, sửa chữa (1 bản chính) kèm theo bản vẽ thiết kế (1 bản chính).</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Lưu ý: Thủ tục xin phép sửa chữa khách sản, được giải quyết trong trường hợp giấy phép xây dựng, sửa chữa còn hiệu lực( trong vòng 1 năm kể từ ngày ký).</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trường hợp nếu quá 1 năm không gia han hoặc chủ đã gia hạn 1 lần thì chủ đầu tư phải lập lại thủ tục xin phép xây dựng khách sạn theo quy định mới.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Quý khách hàng có thể tham khảo thêm: <a href=\"http://www.angiakhang.com/khach-san-duoc-xay-dung-som-nhat-o-sai-gon\"><span style=\"color: rgb(184, 49, 47);\">Khách sạn được xây dựng sớm nhất ở Sài Gòn.</span></a>[[contact_short_code]]</span></p>\n', NULL, 1, NULL, 1575595611, 1590572217, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(173, 'category', 23, 1, '2. Chi phí xây nhà cấp 4 mái thái', '2-chi-phi-xay-nha-cap-4-mai-thai', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Đây bài viết hướng dẫn quý khách hàng tính chi phí xây nhà cấp 4 mái thái 100m2 cụ thể và chi tiết, giúp các gia chủ có được ý tưởng xây nhà cấp 4 mái thái với diện tích khoảng 100m2 hết bao nhiêu tiền nhé.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img data-fr-image-pasted=\"true\" src=\"https://www.angiakhang.com/uploads/contents/1575605218_f9733b07a8e01014dcde.png\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; border-style: none; -webkit-font-smoothing: antialiased; cursor: pointer; padding: 0px 1px; max-width: 100%; color: rgb(65, 65, 65); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: center; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; width: 618px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà cấp 4 mái thái đẹp</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với diện tích xây dựng nhà cấp 4 khoản 100m2 tùy theo điều kiện kính tế quý khách hàng có thể lựa chọn xây nhà cấp 4 mái thái hoặc nhà 2 tầng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chi phí xây dựng nhà 2 tầng thì chi phí xây dựng sẽ gấp đôi với với xây nhà cấp 4. Hãy tham khảo bài viết này , chi phí xây nhà cấp 4 100m2 năm 2020.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Để chuẩn bị cho việc xây dựng nhà cấp 4 cần các bước sau:</strong></span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Lập kế hoạch xây dựng nhà</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chọn công ty tư vấn thiết kế xây dựng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chọn vật liệu xây dựng và nhà thầu thi công</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thi công phần thô</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây dựng phần hoàn thiện nội thất.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiểm tra nghiệm thu công trình.</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có nhiều cách tính chi phí xây dựng nhà cấp 4 100m2 những bài viết này sẽ chia sẽ cho các bạn 2 các đơn giản như sau:</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tính tiền nhân công xây dựng 1m2 + tính tiền vật tư riêng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tính tiền trọn gói xây dựng nhà, tức là chọn nhà thầu xây dựng nhà cấp 4 trọn gói( bào gồm chi phí nhân công xây dựng và tiền vật tự xây dựng) hay còn gọi là khoán gói xây nhà trọn gói.</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Theo quan điểm của chúng tôi, thì gia chủ nên chọn gói xây nhà cấp 4 trọn gói trên sẽ đảm bảo an toàn và phù hợp với chi phí hơn.</span></span></p>\n<ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Cách tính chi phí nhân công xây dựng nhà cấp 4 diện tích 100m2</strong></span></span></li></ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">-Tiên nhân công xây dựng năm 2020( tiền nhân công xây dựng cả thợ, lẫn thầu có thể giao động từ: 850.000 nghìn – 1.200.000 nghìn / 1m2</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây mới là tiền công thợ xây dựng chưa tính đến tiền vật liệu xây dựng nhà. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với thiết kế nhà cấp 4 mái thái có diện tích khoản 100m2, do đó tiền công thợ để xây dựng 1 căn nhà cấp 4 mái thái khoản 85.000.000/ đông – 120.000.000 triều đồng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Lưu ý:</strong> Các bạn cần nghiên cứu kĩ, về bản thiết kế nhà cấp 4 mái thái và hợp đồng cụ thể để tránh phát sinh chi phí khác.</span></span></p>\n<ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Cách tính tiền vật liệu xây dựng nhà.</strong></span></span></li></ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chi phí vật liệu xây dựng bạn có thể tự mua ở các nhà cung cấp uy tín như : xi măng, cốt thép, gạch, tôn, cốt pha…</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu bạn chọn </span></span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>nhà thầu xây nhà trọn gói</strong></span></span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> thì sẽ đỡ tính cho bạn hơn, vì nhà thầu xây nhà sẽ dự tính hết cho bạn về khoản vật tư này. Sẽ có thêm một khoản chi phí phát sinh như tiền vận chuyển, tiền công mua hàng…chi phí có thể tăng thêm 5- 10tr.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trọng kinh nghiệm xây nhà chúng tôi khuyên bạn, nếu bạn có kinh nghiệm xây nhà và có nguồn vật liệu xây dựng tốt thì hãy chọn tự mua vật liệu và thuê nhân công xây dựng. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Còn không hãy chọn gói thầu xây nhà cấp 4 trọn gói( nhà thầu xây dựng sẽ có dự tính về vật tư cho bạn).</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chi phí xây nhà cấp 4 bao gồm công thợ, vật liệu xây dựng dao động từ 3.000.000 – 6.000.000 /1m2.   Nếu bạn lựa chọn nguồn vật tự cao cấp có thể giá sẽ tăng lên 1-2tr. Với vật liệu thường xây dựng nhà vẫn đảm bảo an toàn cho ngôi nhà của bạn nhé.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Vậy chi phí tổng xây nhà cấp 4 mái thái 100m2 sẽ dao động khoản 300 triệu đến 600 triệu, chưa tính phần nội thất bên trong nhà.</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trên là cách tính chi phí xây dựng nhà cấp 4 100m2 để giúp bạn thảm khảo và lựa chọn giải pháp phù hợp với kinh tế của mình nhé.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Việc biết cách tính toán chi phí xây dựng nhà cập 4 ở nông thôn, sẽ giúp cho bạn có những tính toán riêng, phù hợp với quyết định xây nhà cho bạn. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một ngôi nhà cấp 4 mái thái hoàn hảo cần được kết hợp cùng với khoảng sân vườn được thiết kế gần gũi, màu sắc và tràn đầy sức sống. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chỉ cần vậy thôi khuôn viên nhà bạn đã trở thành một biệt thự mini được nhiều người ao ước rồi đó.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu bạn là một người thích sự ấm áp, mộc mạc thì đừng bỏ qua thiết kế tường trắng đan xen với những chất liệu gỗ nhé. Và liệu đây có phải thiết kế trong mơ mà bạn luôn mong muốn được thực hiện?</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575605239_b5309ccd5ffef536cfc4.png\" style=\"width: 616px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thi công xây dựng nhà tại Phú Quốc</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chúng tôi là người đưa ra những phân tích cụ thể nhất để giúp bạn có thể chọn ra mẫu nhà cấp 4 mái thái đẹp và ưng ý nhất. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hiện nay công ty cổ phần thiết kế và xây dựng An Gia Khang đang là đang công ty được nhiều người tin tưởng và đánh giá cao bởi chất lượng công trình cũng như thái độ phục vụ của công nhân viên. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chúng tôi luôn tự hào là một trong những công ty xây dựng nhà cấp 4 uy tín, chuyên nghiệp nhất, luôn mang đến sự hài lòng cho tất cả các quý khách hàng dù là khách hàng khó tính nhất.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575605313_69881079cf3486421575.png\" style=\"width: 613px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công ty xây dựng nhà uy tín</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n', NULL, 1, NULL, 1575603118, 1590049531, NULL),
(175, 'category', 52, 0, '1. Mẫu biệt thự 1 tầng đẹp xây theo kiểu pháp hiện đại', '1-mau-biet-thu-1-tang-dep-xay-theo-kieu-phap-hien-dai', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Những mẫu biệt thự đẹp 1 tầng được thiết kế và xây dựng theo nhiều phong khách khác nhau. </span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Điểm nổi bật của mẫu thiết kế này là sự cầu kỳ, công phu khi thiết kế kiến trúc từ đó cũng phần nào thể hiện được gia chủ là một người yêu thích sự phóng khoáng và không thích bất kỳ khuôn mẫu hay lối mòn nào.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575689745_d73663ff49b12eaaa518.jpg\" style=\"width: 611px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự 1 tầng với mái trệt bằng hiện đại </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cũng với mong muốn có thể mang lại một mẫu thiết kế hiện đại và phóng khoáng nhất thì chủ nhà và nhà thiết kế phải mất rất nhiều thời gian để có thể đưa ra mẫu nhà hoàn hảo nhất được gia chủ hài lòng nhất.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575689809_11775201cf9145528765.jpg\" style=\"width: 608px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế biệt thự đẹp 1 tầng mái ngói tiện nghi</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế với đặc điểm nổi bật là phần chân đế cao kết hợp với bậc tam cấp và hệ thống lan can được các kiến trúc sư đánh giá rằng chúng tôn trọng sự bề thế, sang trọng mà gia chủ rất mong muốn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575689767_b59dae0ad4b42248466e.jpg\" style=\"width: 609px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế biệt thự 1 tầng với thiết kế đơn giản hiện đại</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế nổi bật với hệ thống được lợp bằng mái ngói vừa giúp giữ lại nét truyền thống dân tộc vừa giúp cảm giác ngôi nhà trở nên gần gũi và thân thương hơn giúp những thành viên trong gia đình luôn muốn về ngay với gia đình sau khi hết giờ làm.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575689827_5db84ced745512338449.jpg\" style=\"width: 607px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế biệt thự đẹp 1 tầng công viên rộng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thêm một điểm cộng cho mẫu thiết kế mái này là giúp chống nóng kết hợp cùng tính bền vững cao thích hợp cho mọi kiểu thời tiết.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575689910_37b7ce5595ecf4e986fe.jpg\" style=\"width: 601px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế biệt thự 1 tầng mái thái hiện đại</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây có lẽ là một trong các mẫu biệt thự đẹp 1 tầng được nhiều khách hàng yêu thích nhất nhờ sự phối hợp hài hòa, nhịp nhàng, khéo léo từ các chi tiết hiện đại kết hợp với sự gần gũi. Tuy nhiên để có một ngôi nhà trang nhã, hiện đại và thanh thoát thì nhà thiết kế cần biết phối màu một cách hài hòa nhất.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1575689940_cf323a7e42a4f029bc7e.jpg\" style=\"width: 610px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế biệt thự 1 tầng cổ kính</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu so sánh về mức giá xây dựng thô thì chi phí hoàn thiện của các mẫu thiết kế thường tương đương nhau, nhưng nếu tính cả chi phí hoàn thiện thì các mẫu nhà biệt thự tân cổ điển sẽ có mức giá cao hơn do chi phí nhân công, thời gian hoàn thiện cũng mất nhiều thời gian hơn. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tuy nhiên nếu mẫu thiết kế này được gia chủ yêu thích thì mọi chuyện đều không phải là trở ngại lớn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575689975_ae9eaea5fd5601242f47.jpg\" style=\"width: 633px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự 1 tầng kiểu Pháp tinh tế</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu bạn là người yêu phong cách nhà kiểu Pháp thì chắc chắn đây là lựa chọn ưu tiên dành cho bạn. Với vẻ đẹp nhẹ nhàng và tinh tế là nguồn cảm hứng bất tận giúp kiến trúc sư có thể tạo ra những công trình tuyệt đẹp.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1575690002_93d9fcc34f1e193fe521.jpg\" style=\"width: 619px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự 1 tầng kiểu Pháp xưa</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế một tầng kiểu Pháp này thường được xây dựng trên một diện tích rộng, xung quanh là một khoảng vườn xanh giúp khuôn viên biệt thự luôn là nơi thư giãn tuyệt vời dành riêng cho gia chủ.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575690035_c199da0cbdfd9388cae9.jpg\" style=\"width: 622px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế biệt thự 1 tầng mang phong cách Châu Âu</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây là mẫu thiết kế cũng được khá nhiều khách hàng ưa chuộng bởi sự kết hợp giữa màu sắc đơn giản, tinh tế nhưng vẫn vô cùng hiện đại và toát lên được vẻ đẹp sang trọng của ngôi biệt thự.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một không gian hiện đại sẽ giúp cuộc sống của bạn trở nên  dễ dàng và thú vị hơn với những tiện ích có trong ngôi nhà đó.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575690083_2c7869a13b6ccf8bbb09.jpg\" style=\"width: 625px;\" class=\"fr-fic fr-dib\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế biệt thự 1 tầng mang phong cách Châu Âu</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với gam màu chủ đạo của mẫu thiết kế này là gam màu cà phê được kết hợp cùng những sắc độ đậm nhạt khác nhau đã làm nổi bật lên màu sắc đặc trưng và cuốn hút của phong cách này.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575690104_a9129126c5ee3294225c.jpg\" style=\"width: 620px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà biệt thự 1 tầng hình chữ L          </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà được thiết kế với mái ngói cao cấp với tone màu đỏ tươi có vai trò đặc biệt giúp ngôi nhà tránh khỏi những ảnh hưởng xấu từ thời tiết cũng như mang lại sự năng động và tươi trẻ hơn cho khuôn viên nhà bạn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575690126_396fdf48b6f93056b8ac.jpg\" style=\"width: 623px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự cổ theo phong cách pháp</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Những mẫu thiết kế có phần mái được lợp ngói luôn là những mẫu thiết kế gần gũi và thân thương được nhiều người lựa chọn. Đây cũng là lý do tại sao những mẫu thiết kế biệt thự tại Việt Nam lại có những nét đặc trưng riêng không thể nhầm lẫn với bất kỳ quốc gia nào trên thế giới. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Để có một không gian hoàn hảo hơn thì gia chủ có thể kết hợp xây dựng hệ thống tường rào kiên cố kết hợp với các chi tiết thiết kế tinh xảo giúp mang lại sự an toàn cũng như tăng mức độ bề thế của mẫu biệt thự.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sau khi tham khảo những mẫu biệt thự đẹp 1 tầng trên thì chắc mỗi người cũng đã chọn được những mẫu thiết kế mà mình yêu thích nhất. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quý khách có thể xem thêm: <a href=\"https://www.angiakhang.com/xay-biet-thu\"><span style=\"color: rgb(226, 80, 65);\">Xây dựng biệt thự đẹp</span></a><br></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">[[contact_short_code]]</span></span></p>\n<p style=\'margin:0in;margin-bottom:10.0pt;font-size:13px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;line-height:150%;\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><strong>Rất hân  hạnh được hợp tác cùng quý khách hàng.</strong></span></p>\n', NULL, 1, NULL, 1575690155, 1590053344, NULL),
(176, 'category', 53, 0, '1. Mẫu biệt thự đẹp 2 tầng', '1-mau-biet-thu-dep-2-tang', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Với điều kiện kinh tế như hiện nay các mẫu biệt thự đẹp 2 tầng được ra đời thích hợp với thu nhập của nhiều hộ gia đình và chúng thường được xây dựng ở các vùng nông thôn phát triển hơn là những thành phố bởi vì tính chất cũng như diện tích đất ở các thành phố còn bị hạn chế.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575690850_dfea03634b8bd6ffa853.jpg\" style=\"width: 619px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Biệt thự đẹp 2 tầng kiểu pháp</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Dưới sự nhìn nhận của các chuyên gia kiến trúc thì mẫu biệt thự đẹp 2 tầng là loại hình nhà ở có chiều cao là 2 tầng và diện tích mỗi sàn của ngôi nhà giao động từ 100 – 200 m2. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kết cấu của các mẫu biệt thự 2 tầng thường là những khung bê tông có kết cấu chắc chắn hoặc sử dụng các loại gạch có tính chịu lực chắc chắn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thông thường mỗi ngôi biệt thự 2 tầng sẽ có từ 2- 4 mặt thoáng khác nhau được thiết kế kèm với sân vườn, cây xanh và hệ thống hàng rào bao quanh chúng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575690875_5a7b906f8e2f43cf2011.jpg\" style=\"width: 629px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Biệt thự kiểu pháp có gara xe oto</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các ngôi biệt thự 2 tầng được xây dựng với mục đích đáp ứng và khai thác hết triệt để mọi công năng của ngôi nhà như là 1 phòng khách, phòng ăn, 3 – 6 phòng ngủ và các loại phòng khác phục vụ nhu cầu và yêu cầu khác nhau của từng gia chủ như phòng sinh hoạt chung, phòng ở cho khách hoặc người giúp việc, phòng làm việc, gara để xe,…. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575690957_2fb77af1001e35aa2caf.jpg\" style=\"width: 620px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một mẫu biệt thự đẹp 2 tầng với sân vườn rộng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.1 Mẫu biệt thự 2 tầng đẹp hiện nay gồm những loại điển hình nào?</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khi nhắc đến kiến trúc mẫu biệt thự đẹp 2 tầng người ta sẽ liên tưởng ngay đến 3 loại hình mẫu nổi tiếng và sử dụng phổ biến hiện nay đó là mẫu biệt thự 2 tầng theo phong cách cổ điển, theo phong cách tân cổ điển và phong cách hiện đại. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hãy cùng tìm hiểu kỹ hơn về những điểm khác biệt của các loại phong cách này nhé!</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575691003_2fb2fad20c7efa63820c.jpg\" style=\"width: 640px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phong cách mẫu biệt thự 2 tầng đẹp cổ điển</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chắc hẳn vừa nghe cái tên người ta sẽ liên tưởng đến các phong cách kiến trúc kiểu Pháp. Các mẫu biệt thự đẹp 2 tầng được xây dựng theo phong cách cổ điển là việc các kiến trúc sư đã sử dụng các dạng thiết kế theo kiểu đối xứng với nhau, xây dựng với nhiều đường nét trang trí cầu kỹ và bao gồm nhiều lớp.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575691041_d18c3c27a346420e6535.jpg\" style=\"width: 624px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự 2 tầng đẹp mang phong cách châu âu</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các mẫu biệt thự dạng này thường được xây dựng khá đồ sộ và bề thế với nhiều cột lớn thể hiện rõ nét sự trang nghiêm quý phái và mang lại sự tinh tế và cao cấp cho người thưởng thức hơn so với các mẫu biệt thự khác.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575691083_cb11383809133df1fe38.jpg\" style=\"width: 623px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phong cách mẫu biệt thự đẹp 2 tầng tân cổ điển</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây là phong cách kiến trúc được thiết kế pha trộn giữa phong cách kiến trúc cổ điển và phong cách hiện đại mang đến những sự nhẹ nhàng, tinh tế nhưng cũng không kém phần hiện đại cho ngôi nhà.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các mẫu biệt thự đẹp 2 tầng theo phong cách tân cổ điển thường được thiết kế với những tường và cột có kết cấu lớn và tự do, hệ thống cửa có thể được mở lệch sang một bên hoặc mở lớn ra hơn so với hệ thống cửa của phong cách thiết kế cổ điển.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các thiết kế biệt thự 2 tầng mang phong cách này thường có sự kết hợp của các tấm kính lớn cùng với hệ thống mái dốc được đan xen nhau với một tỉ lệ cân đối nhất định đem đến sự hiện đại và tinh tế cho ngôi biệt thự.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575691124_bd2b578a301a329082d5.jpg\" style=\"width: 641px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Mẫu biệt thự đẹp 2 tầng mang phong cách tân cổ điển</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với sự du nhập của các phong cách thiết kế phương tây thì các nhà thiết kế đã sáng tạo ra các mẫu biệt thự đẹp 2 tầng mang phong cách hiện đại được nhiều gia đình lựa chọn và yêu thích hiện nay.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các mẫu biệt thự 2 tầng hiện đại này thưởng nổi trội với việc sử dụng các loại hình khối tự do để bố trí cho bố cục của mình. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không gian ngôi biệt thự được thiết kế với độ thông thoáng cao với nhiều nhiều cửa, hệ thống ban công lớn và tầm nhìn rộng đồng thời còn kết hợp với các mảng thường lớn được thiết kế đan xen nhau và kết cấu nghịch mang đến cho người sử dụng và người nhìn cảm giác mới lạ nhưng cũng không kém phần tinh tế và hiện đại cho ngôi nhà.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575691166_d643b31b78f934180339.jpg\" style=\"width: 617px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự đẹp 2 tầng mang phong cách thiết kế hiện đại</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chi phí cho việc thiết kế và xây dựng các mẫu biệt thự đẹp 2 tầng như thế nào?</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575691194_abef9d63c453be9e6ae1.jpg\" style=\"width: 609px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Mẫu biệt thự đẹp 2 tầng với thiết kế hình khối hiện đại và sân vườn rộng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tùy vào điều kiện thực tế của bản vẽ thiết kế và đơn vị thiết kế và thi công mà giá của mỗi mẫu biệt thự đẹp 2 tầng sẽ giao động khác nhau. Dưới đây là các mẫu đơn giá mà chúng tôi đã khảo sát được bạn đọc có thể tham khảo.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.2 Chi phí cho việc thiết kế thi công các mẫu biệt thự đẹp 2 tầng sẽ giao động từ 120.000 đồng đến 150.000 đồng/m2. </strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thông thường diện tích cần thiết kế càng rẻ thì giá thành cho việc thiết kế sẽ càng thấp xuống. Chính vì vậy chi phí cho việc thiết kế các biệt thự 2 tầng sẽ giao động từ 24 – 40 triệu đồng/ căn biệt thự.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575691221_8318fd1cc1213f877675.jpg\" style=\"width: 622px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><a href=\"https://www.angiakhang.com/xay-biet-thu\">Mẫu biệt thự 2 tầng đẹp phong cách châu âu sang trọng</a></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Về giá thi công xây dựng công trình cho đến khi hoàn thiện cũng nằm trong khoảng từ 5 – 7 triệu đồng/ m2. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chính vì thế chi phí xây dựng cho toàn bộ căn nhà sẽ giao động từ 1,2 – 3,5 tỷ đồng tùy theo diện tích xây dựng cũng như phong cách thiết kế là đơn giản hay phức tạp.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trên đây là toàn bộ những thông tin cần thiết về các mẫu biệt thự đẹp 2 tầng mà bạn đọc có thể tham khảo. Chúc bạn có được không gian sống hiện đại và như ý!</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quý khách hàng xem thêm: <a href=\"https://www.angiakhang.com/xay-biet-thu\"><span style=\"color: rgb(226, 80, 65);\">Mẫu biệt thự mini đẹp</span></a><br></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">[[contact_short_code]]</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif;\">Rất hân hạnh được phục vụ quý khách hàng.</span></span></p>\n', NULL, 1, NULL, 1575691251, 1590053285, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(177, 'category', 54, 0, '1. Mẫu biệt thự 3 tầng đẹp nhất', '1-mau-biet-thu-3-tang-dep-nhat', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Có thể nói mẫu biệt thự 3 tầng là mẫu thiết kế theo phong cách hiện đại thường xây dựng bố cục không gian hình khối, mặt bằng được xây dựng tự do phi đối xứng và loại bỏ các phong cách trang trí như các kiểu biệt thự cổ điển. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span class=\"fr-img-caption fr-fic fr-dib\" style=\"width: 615px;\"><span class=\"fr-img-wrap\"><img src=\"/uploads/contents/1575692026_de6d53a8b8f41c8403c2.jpg\"><span class=\"fr-inner\"></span></span></span></span></span></p>\n<div data-empty=\"true\" style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự kiểu pháp 3 tầng</span></span></div>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thay vào đó là việc áp dụng các vật liệu hiện đại như kính, thép, bê tông giúp cho biệt thự trở nên đơn giản hơn nhưng cũng không kém phần sang trọng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575693043_2da0f305fb4ec43a7c87.jpg\" style=\"width: 630px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự đẹp sang trọng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự 3 tầng luôn được thiết kế dựa trên sở thích và tính cách của từng gia chủ. Chủ yếu là các kiểu bổ trí tự do, đón được nhiều ánh nắng, thông thoáng và mở rộng được không gian bên trong tạo cho những người sống trong ngôi biệt thự cảm giác thoải mái nhất.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1575692130_ea738359cc22a658ccb6.jpg\" style=\"width: 618px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự 3 tầng với thiết kế mái Thái hiện đại</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Biệt thự 3 tầng thường có những đặc trưng cơ bản nào</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575693327_0b761645d147120c76eb.jpg\" style=\"width: 620px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiểu biệt thự cao theo phong thủy</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Như đã nói ở trên thì đặc trưng đầu tiên của các biệt thự 3 tầng hiện đại hiện nay đó là sự đơn giản trong bố cục thiết kế và tổ chức không gian phi đối xứng nhưng chúng lại luôn mang đến sự thống nhất trong việc sử dụng các gam màu sắc tạo nên sự thu hút và làm điểm nhấn mới lạ cho căn biệt thự 3 tầng của bạn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575693097_83f82a3f49e2bad0d55a.jpg\" style=\"width: 605px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự có sân thượng đẹp</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các mẫu biệt thự 3 tầng sử dụng các chất liệu vật liệu mới và hiện đại như kính, thép và bê tông với tỷ lệ cân đối và chính xác nhất thích nghi với điều kiện vị trí và địa hình của từng khu vực để đảm bảo chất lượng của công trình qua thời gian.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span class=\"fr-img-caption fr-fic fr-dib\" style=\"width: 633px;\"><span class=\"fr-img-wrap\"><img src=\"/uploads/contents/1575692144_473c38307fc45a6ac537.jpg\"><span class=\"fr-inner\"></span></span></span></span></span></p>\n<div data-empty=\"true\" style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự 3 tầng đẹp với khu viên rộng</span></span></div>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các mẫu biệt thự 3 tầng hiện đại luôn mang những đường nét khỏe khoắn và trẻ trung thích hợp với thị yếu và nhu cầu của thị trường. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575693143_ba3cd982c59e8710fcb9.jpg\" style=\"width: 620px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự kiểu chữ L</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Để tránh được sự nhàm chán cũng như tăng sự bắt mắt cho các biệt thự các kiến trúc sư thiết kế các kiểu dáng cách tân, đường nét trang trí mạch lạc và khỏe khoắn. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự 3 tầng hiện đại với thiết kế hình khối và hài hòa với thiên nhiên</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575692189_3fa24480bd55b524065e.jpg\" style=\"width: 605px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự 3 tầng với thiết kế kiểu pháp hiện đại</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây là mẫu biệt thự được thiết kế với sự kết hợp vô cùng hài hòa về mặt kiến trúc cũng như màu sắc. Kết hợp với đó là việc sử dụng hệ thống mái Thái mang đến sự hiện đại và sang trọng có ngôi biệt thự.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575693182_948d4bba5fe2ea4ebbb8.jpg\" style=\"width: 605px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Biệt thự kiểu pháp có sân vườn theo tầng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Bên cạnh đó mái Thái còn có ích nghĩa tích cực về mặt phong thủy giúp cho gia chủ làm ăn phát đạt và tài lộc hơn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575693283_297459369a59bae3e751.jpg\" style=\"width: 616px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiểu biệt thự cho những cặp đại gia</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ngoài ra những mẫu biệt thự 3 tầng mái thái luôn đi kèm với các thiết kế sân vườn giúp tạo nên sự hài hòa và gần gũi với thiên nhiên hơn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự phố 3 tầng sang trọng và hiện đại</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575692213_8c1923103cbe51fc14fc.jpg\" style=\"width: 632px;\" class=\"fr-fic fr-dib\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một mẫu biệt thự phố 3 tầng đẹp hiện nay</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các mẫu biệt thự này thường được thiết kế dành cho những vị trí nằm cạnh đường phố lớn. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575693237_da5164d975c27636314a.jpg\" style=\"width: 612px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một kiểu biệt thự 2019 nhiều đại gia mơ ước</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Kiến trúc ở dạng biệt thự 3 tầng này thường được sử dụng theo dạng kiến trúc hình khối với nhiều điểm nhấn cách tân và khoảng không gian để có thể bố trí cây xanh hoặc sân vườn vừa đem lại sự hiện đại vừa làm ngôi biệt thự trở nên sang trọng và thu hút hơn.</span></p>\n', NULL, 1, NULL, 1575692082, 1590053161, NULL),
(178, 'category', 54, 1, '2. Những điều lưu ý khi xây dựng biệt thự 3 tầng', '2-nhung-dieu-luu-y-khi-xay-dung-biet-thu-3-tang', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Việc xây dựng một mẫu biệt thự 3 tầng<strong> </strong>là một việc làm không nhỏ và tốn nhiều chi phí chính vì vậy bạn cần phải cẩn thận và tính toán một cách chính xác nhất cho từng công đoạn.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiểu dáng và phong cách của ngôi biệt thự phải phù hợp với vị trí, tính chất của mảnh đất mà bạn định xây lên. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1580562906_ed697622590d893c9126.jpg\" style=\"width: 613px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây biệt thự trọn gói uy tín</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bởi vì hiện nay có rất nhiều mẫu biệt thự 3 tầng với nhiều kiểu dáng khác nhau từ hình vuông, hình chữ nhật, đến mái bằng mái thái, mái vòm, biệt thự hiện đại, tân cổ,…. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nên khi lựa chọn thiết kế hình dáng cho ngôi biệt thự bạn cần chú ý đến việc đáp ứng đầy đủ các yêu tố như tính thẩm mỹ, phong thủy và phù hợp với kích thước lô đất của mình nhất.</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế phần nội thất cũng như các phòng trong ngôi biệt thự cũng là vấn đề quan trọng đáng quan tâm vì nó thể hiện công năng sử dụng của ngôi nhà. </span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các phòng cần được sắp xếp một cách thuận lợi và hợp lý nhất để đảm bảo quá trình sử dụng cũng như trành sự lãng phí.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Điểm lưu ý tiếp theo đó là thiết kế phải có sự kết hợp hài hòa của các yếu tố cảnh quan và  phong thủy để mang đến sự phú quý nhất cho gia chủ.</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đặc biệt khi chọn đơn vị thiết kế và thi công công trình bạn cần phải chọn những đơn vị lâu năm, uy tín và chất lượng nhất với những thỏa thuận rõ ràng, lành mạch về tiến độ thi công cũng như các điều khoản hợp đồng, cam kết bàn giao cho bạn sản phẩm đúng tiến độ cũng như mẫu mã tốt nhất. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1585234582_ee5d215d4f247f6044a2.jpg\" style=\"width: 606px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Biệt thự 3 tầng đẹp</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trên đây là những thông tin về các thiết kế mẫu biệt thự 3 tầng cũng như những lưu ý khi bạn có mong muốn xây dựng một ngôi biệt thự 3 tầng cho gia đình của mình mà bạn có thể tham khảo.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quý khách có thể xem thêm: <a href=\"https://www.angiakhang.com/mau-biet-thu-2-tang\"><span style=\"color: rgb(226, 80, 65);\">Mẫu biệt thự 2 tầng đẹp</span></a><br></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">[[contact_short_code]]</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Rất hân hạnh được phục vụ quý khách hàng.</strong></span></span></p>\n', NULL, 1, NULL, 1575692253, 1590053161, NULL),
(179, 'category', 55, 0, '1. Những mẫu biệt thự kiểu pháp đẹp theo phong khách hiện đại', '1-nhung-mau-biet-thu-kieu-phap-dep-theo-phong-khach-hien-dai', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Vào thời kì Pháp thuộc các phong cách kiến trúc Pháp dần dần du nhập vào nước ta trong đó có các mẫu biệt thự kiểu Pháp đẹp. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thông thường các mẫu biệt thự kiểu Pháp đều có tông màu chính là màu trắng điều này làm nổi bật lên các đường nét trang trí cũng như các chi tiết của ngôi biệt thự.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575695218_425744ed996d39e5a84d.jpg\" style=\"width: 579px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Biệt thự kiểu pháp</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đặc trưng của mẫu biệt thự xây dựng theo kiến trúc Pháp là gì? </span></span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phần thiết kế kiểu dáng của các ngôi biệt thự Pháp đều mang đậm phong cách cổ điển của Pháp từ các đường nét, hoa văn trang trí, cách sắp xếp các bộ cục và bày trí nội thất không gian biệt thự.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575695258_2e6792629ece5eec2451.jpg\" style=\"width: 600px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự kiểu pháp 1 trệt 3 tầng </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các mẫu biệt thự Pháp đẹp luôn được xây dựng dựa trên nguyên tắc đối xứng với một sảnh lớn bên ngoài làm trung tâm và hai bên được trang trí đồng nhất tương tự nhau. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575695332_acd9fdf654714dd770bf.jpg\" style=\"width: 595px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một mẫu biệt thự 2 tầng kiểu Pháp được thiết kế ấn tượng và sang trọng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.1 Ưu điểm xây dựng biệt thự kiểu pháp</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các mẫu biệt thự kiểu Pháp đẹp thường đảm bảo độ thông thoáng cao nhờ vào hệ thống lưu thông không khí và hệ thống đón gió tự nhiên từ một hệ thống các ô cửa sổ và ô thông gió của biệt thự. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575695348_371aa75057b4555233d8.jpg\" style=\"width: 593px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự kiểu pháp hiện đại</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Điều này giúp cho biệt thự mang lại cảm giá thoải mái, hạn chế tối thiểu trình trạng bị ẩm mốc đặc biệt thích hợp với điều kiện khí hậu của nước ta. Mang cảm giác mát mẻ vào mùa hè và ấm cúng vào mùa đông.</span></span></p>\n<ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một trong những ưu điểm nổi trội của các dạng biệt thự kiểu Pháp đó là phần mái của biệt thự thường được sử dụng bằng chất liệu bê tông với một không trần cao giúp điều hòa không khicho ngôi biệt thự một cách tốt nhất. </span></span></li></ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575695383_88beb13e63b3938d31a0.jpg\" style=\"width: 576px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Biệt thự kiểu pháp cổ kính</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không những thế phần móng của biệt thự thường được xây dựng lên cao nên giảm thiểu tối đa sự tác động của hiện tượng nhiệt đất bốc hơi ảnh hưởng đến sức khỏe của những người sống trong đó.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575695463_a2bd005cc36bb18da8d9.png\" style=\"width: 579px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Mẫu biệt thự 3 tầng kiểu Pháp đẹp tại Đà Nẵng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các mẫu biệt thự kiểu Pháp đẹp thường được thiết kế kèm theo với sân vườn và hệ thống cảnh quan xung quanh vừa giúp ngôi biệt thự trở nên sang trọng, đẳng cấp vừa thân thiện với môi trường hơn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.2 Nhược điểm xây dựng biệt thự</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bởi vì sự tỉ mỉ trong từng chi tiết ở ngôi biệt thự nên những mẫu biệt thự kiểu Pháp đẹp luôn luôn tốn nhiều chi phí thiết kế và xây dựng hơn so với các mẫu biệt thự hiện đại khác.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575695486_b645eac1b55c127bd68c.jpeg\" style=\"width: 595px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các mẫu biệt thự kiểu Pháp luôn đòi hỏi những nhà kiến trúc sư phải có kinh nghiệm và trình độ trong mảng này.</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự kiểu Pháp luôn có những hoa văn và đường nét đa dạng nên cần người thợ thi công phải tỉ mỉ từng chi tiết. Chính vì vậy nên việc thi công thường tốn nhiều công sức và thời gian hơn so với việc xây dựng các mẫu biệt thự kiểu khác.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không gian bên trong của các biệt thự kiểu Pháp nếu là người không chuyên thưởng thức thì sẽ cảm thấy hơi khó gần và có chút cô lập vì độ bề thế, xa hoa của nó không phải như các phong cách thiết kế đơn giản thường thấy ở các mẫu biệt thự hiện đại ngày nay. </span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575695528_aef70fd97ac73f2c5c3d.png\" style=\"width: 594px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự Pháp với những đường nét trang trí cầu kỳ và tinh tế</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575695582_924405821a799ae5db08.png\" style=\"width: 591px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một mẫu biệt thự Pháp thiết kế theo phong cách cổ điển</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">ở nước ta sẽ không khó khi bắt gặp các mẫu biệt thự mang đạm nét phong cách kiến trúc Pháp. Các mẫu biệt thự kiểu pháp đẹp thường được thiết kế và phân loại thành các dạng bao gồm:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phân loại mẫu biệt thự kiểu Pháp theo phong cách thiết kế thì gồm có biệt thự kiểu Pháp cổ điển, biệt thự vườn kiểu Pháp và loại biệt thự mini kiểu Pháp.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phân theo số tầng xây dựng thì các mẫu biệt thự kiểu Pháp thường phân thành mẫu biệt thự kiểu Pháp đẹp 1 tầng, mẫu 2 tầng, 3 tầng và các mẫu 4 tầng trở lên.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575695595_fbd6294e26bf766d5a1b.jpg\" style=\"width: 588px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Biệt thự kiểu pháp có gara xe</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Ở phương diện phân loại theo hình khối xây dựng và kiểu dạng mái thì có mẫu biệt thự Pháp dạng mái Thái, dạng mái dốc và dạng chữ L,…</span></p>\n', NULL, 1, NULL, 1575695136, 1590053127, NULL),
(180, 'category', 55, 1, '2. Yếu tố để xây dựng mẫu biệt thự kiểu Pháp đẹp', '2-yeu-to-de-xay-dung-mau-biet-thu-kieu-phap-dep', '<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif;\">Để có thể xây dựng được một mẫu biệt thự kiểu Pháp đẹp và ưng ý nhất cả về mặt kiểu dáng lẫn chất lượng thì bạn cần lưu ý đến 3 yêu tố sau:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575695681_f4a8cb913bcbfe433d75.jpg\" style=\"width: 600px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Biệt thự kiểu pháp sang trọng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(226, 80, 65);\"><strong>Chú ý</strong></span><strong>:</strong> Đến việc thiết kế tổng quan cho ngôi biệt thự Pháp. Các ngôi biệt thự kiểu Pháp luôn đòi hỏi một không gian thiết kế bề thế và có phần hơi cổ kính. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chính vì vậy mà việc thiết kế tổng quan phải đảm bảo về sự thống nhất của các chi tiết, sự tỉ mỉ và am hiểu sâu về lối kiến trúc Pháp để có thể tạo ra một ngôi biệt thự đẳng cấp thể hiện được vị thế của gia chủ.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các ngôi biệt thự kiểu Pháp phải luôn đảm bảo về sự hài hòa, trang trọng về các đường nét trang trí cũng như các hình khối sử dụng cho ngôi biệt thự.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575695702_cb482df4d467bcd944e8.jpg\" style=\"width: 603px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thể hiện bề thế của gia chủ<br></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cuối cùng đó là xây dựng một ngôi biệt thự kiểu pháp đẹp phải dựa trên quan điểm đơn giản nhưng không hề đơn điệu thông qua thiết kế ăn ý của phần thiết kế bên ngoài và phần nôi thất bên trong làm tăng lên vẻ đẹp cũng như sự sang trọng cho ngôi biệt thự này.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575695714_12fabd511fa5b66ca8db.jpg\" style=\"width: 610px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự mini<br></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trên đây là những thông tin chi tiết nhất về mẫu biệt thự kiểu Pháp đẹp, hy vọng sẽ giúp bạn có những cái nhìn khách quan nhất về dạng kiến trúc vô cùng độc đáo này.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quý khách có thể xem thêm: <a href=\"https://www.angiakhang.com/mau-biet-thu-3-tang\"><span style=\"color: rgb(226, 80, 65);\">Mẫu biệt thự 3 tầng đẹp</span></a><br></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">[[contact_short_code]]</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif;\">Rất hân hạnh được phục vụ quý khách.</span></span></p>\n', NULL, 1, NULL, 1575695638, 1590053127, NULL),
(181, 'category', 56, 0, '1. Mẫu biệt thự hiện đại', '1-mau-biet-thu-hien-dai', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu biệt thự kiểu hiện đại là gì? Có thể nói qua thời gian các kiến trúc xây dựng mẫu biệt thự kiểu hiện đại du nhập vào nước ta và được xem là không gian sống hoàn hảo phù hợp với nhịp sống và nhu cầu hiện nay.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1580565834_82c6ae96e0b8eac35e94.png\" style=\"width: 607px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự hiện đại có gara</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự kiểu hiện đại là các công trình thiết kế có kiến trúc tương đối đơn giản về cả bố cục hình khối không gian lẫn hình thức trang trí. Cách tổ chức mặt bằng của dạng biệt thự này theo kiểu tự do và phi đối xứng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một biệt thự xây dựng theo kiểu hiện đại đòi hỏi phải đáp ứng đầy đủ các tiêu chí như mật độ xây dựng thấp, kiến trúc xây dựng, nội thất bên ngoài và bên trong của biệt thự phải đảm bảo được sự tinh tế cũng như sang trọng hiện đại nhất, biệt thự phải được xây dựng trong không gian yên tĩnh, sạch sẽ, đảm bảo được độ an ninh tốt cũng như các trang thiết bị sử dụng phải tiện nghi và cao cấp nhất.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1580565877_0d570be9205a4659e011.jpg\" style=\"width: 613px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự hiện đại với thiết kế phá cách độc đáo</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.1 Những đặc trưng cơ bản của mẫu biệt thự kiểu hiện đại</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các mẫu biệt thự kiểu hiện đại thường đặc trưng bởi các đường nét khỏe khoắn, cứng rắt và vô cùng dứt khoát hoặc những đường thẳng song song với các thiết kế mảng hình khối. Các mẫu biệt thự hiện đại lược bỏ những đường nét trang trí cầu kỳ nên tạo nên sự đơn giản nhất.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1580566151_b7ddb0699f8e221462b2.jpg\" style=\"width: 620px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không gian sống thoải mái</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Biệt thự hiện đại được thiết kế theo kiểu phi đối xứng mang phong cách cách tân lớn giúp cho không gian trở nên tự do thoáng mát với những tông màu sắc trẻ trung, thiết kế các tường kình cùng với một phần không gian mở giúp không gian trở nên phóng khoáng và thân thiện với môi trường hơn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khác biệt với các mẫu biệt thự thiết kế theo kiểu truyền thống thì mẫu biệt thự kiểu hiện đại thường sử dụng các tông màu sáng như trắng, xám,… tạo nên sự bắt mắt nổi bật tính năng hiện đại của ngôi biệt thự.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1580565901_ea1bf42651c783c856ff.jpg\" style=\"width: 597px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự hiện đại mái bằng với các dạng hình khối vô cùng bắt mắt</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế của các mẫu biệt thự kiểu hiện đại luôn coi trọng phần không gian mở thay cho các thiết kế các không gian kiến đáo như trước đảm bảo không gian thông thoáng và gắn kết những người trong gia đình lại với nhau.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1580565942_325b17c1646ccb79ddec.jpg\" style=\"width: 598px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự hiện đại có thiết kế hồ bơi</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một đặc trưng khác của mẫu biệt thự kiểu mới là ưu tiên phần không gian sống với thiết kế các kiểu mái bằng và các ban công lớn kết hợp với các kiểu vật liệu chủ yếu bằng kính tạo nên nét đặc sắc cho kiểu biệt thự hiện đại này.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1580565954_42963d7b53dd62bc60e8.jpg\" style=\"width: 605px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự kiểu hiện đại 2 tầng tuyệt đẹp tại Thanh Hóa</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.2 Các mẫu biệt thự kiểu hiện đại phổ biến nhất hiện nay là gì?</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Căn cứ vào các tiêu chí khác nhau mà mà các nhà thiết kế phân các biệt thự kiểu hiện đại với các loại khác nhau bao gồm:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Dựa trên số tầng xây dựng mà người ta phân thành kiểu biệt thự hiện đại 1 tầng, 2 tầng, 3 tầng, hoặc 4 tầng trở lên.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1580565979_f0c5bfe8c9c2212952dd.jpg\" style=\"width: 595px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự hiện đại phổ biến</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Dựa trên đặc điểm công trình và công năng có các mẫu biệt thự kiểu đại là mẫu biệt thự cấp 4 hiện đại, mẫu biệt thự hình khối hiện đại, mẫu biệt thự dạng chữ L, mẫu có mái dốc, mẫu nhà vườn, mẫu mái ngói hiện đại và mẫu mini hiện đại. Ngoài ra còn có các mẫu có bể bơi, biệt thự phố hiện đại,…</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Dựa trên số lượng mặt tiền có các mẫu biệt thự 1 mặt tiền, 2 mặt tiền, 3 mặt tiền và mẫu mặt tiền 10m hiện đại.</span></span></p>\n', NULL, 1, NULL, 1575696128, 1590053079, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(182, 'category', 57, 0, '1. Mẫu biệt thự kiểu Ấn Độ', '1-mau-biet-thu-kieu-an-do', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nếu như lúc trước những căn biệt thự theo phong cách Châu Âu, Pháp được lựa chọn nhiều thì đến bây giờ, có vẻ nhiều người lại thích những mẫu biệt thự kiểu Ấn Độ cổ điển và chọn nó khi thiết kế. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Những mẫu biệt thự được cách tân theo theo kiểu cổ điển vẫn là đỉnh cao của nghệ thuật và thiết kế. Nhiều người tìm cảm hứng từ những tòa lâu đài cổ xưa ở Ấn Độ và tạo nó thành một công trình cực kỳ nghệ thuật với các trang trí cũng như điêu khắc. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1580568994_0332202599356a56d4dd.jpg\" style=\"width: 596px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Biệt thự kiểu Ấn Độ</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thường những gia chủ đại gia luôn mong muốn và tìm kiếm các ngôi biệt thự cực đẹp đem đến sự phong cách và cổ điển khiến cho những mẫu biệt thự kiểu Ấn Độ như thế này càng được ưa chuộng hơn. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Những mẫu biệt thự dạng lâu đài cổ điển mang trong mình một sự cuốn hút của phong cách cung diện, hơn nữa mang lại một sự đẳng cấp và tinh tế cho những người sống ở trong đó. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một sự cuốn hút từ phong cách cổ xưa, một nét nghệ thuật cổ điển càng khiến nhiều người mê mẩn và thu hút. Thêm các nội thất được bày trí sẽ góp phần tạo nên một phong cách ấm cúng mê hoặc của kiến trúc.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1580569037_94e0756ead29f31e99d0.jpg\" style=\"width: 593px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Biệt thự lâu đài vừa hiện đại vừa cổ điển</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Và để phù hợp khi sinh hoạt trong ngôi nhà thì nhiều kiểu cổ điển được cách tân và tối giản một số thành phần. Bởi chính vì vậy mà nét cổ điển không mất đi nhưng nét hiện đại vẫn được tìm thấy trong những căn biệt thự lâu đài như thế này. Có lẽ chính vì vậy mà đây là mẫu biệt thự kiểu Ấn Độ được mọi người luôn muốn được sở hữu.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1580569056_d5c91fbdc498e7c57691.jpg\" style=\"width: 600px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự kiểu Ấn Độ dạng nhà vườn</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hòa mình với thiên nhiên, tìm một không gian thoáng đãng, tươi xanh để nghỉ ngơi cũng là lựa chọn của nhiều người. Chính vì thế mà một trong những biệt thự kiểu Ấn Độ dạng nhà vườn cũng được nhiều người lựa chọn. Không quá phá cách hay trông lộng lẫy, nhưng một sự ấm êm và thoải mái. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tuy đơn giản nhưng hài hòa thể hiện tình yêu của gia chủ đối với thiên nhiên. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu bạn là một người thích hòa mình với thiên nhiên, thích màu xanh cây cỏ hay muốn trưng bày cây kiểng, có một vườn nhỏ ở xung quanh nhà thì mẫu biệt thự kiểu Ấn Độ dạng nhà vườn có lẽ là một trong những kiểu nhà phù hợp với bạn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1580569071_546af41bf34bb80edefb.png\" style=\"width: 603px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự nhà vườn ấn tượng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế nội thất giản đơn đem lại cảm giác tự nhiên, nhẹ nhàng, thoải mái. Thư giãn với cây cỏ xanh tươi trong không gian xanh mát. Hơn nữa, đây là một kiến trúc không quá khó khăn để sở hữu và bạn hoàn toàn có thể tự mình xây dựng và trang trí chúng mang phong cách của riêng mình.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Không gian tự tạo, hòa mình với thiên nhiên sẽ là một nơi nghỉ ngơi khá lý tưởng. Một lối sống yên bình, tự do và thoải mát rất phù hợp cho những ngày tháng nghỉ dưỡng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1580569087_a5b1118c303b5804dbc8.jpg\" style=\"width: 582px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự mang phong cách hiện đại</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không chỉ nổi tiếng về những tòa lâu đài cổ xưa mà phong cách hiện đại của Ấn Độ cũng được nhiều kiến trúc sư áp dụng vào trong quá trình thiết kế những ngôi biệt thự. Bạn là một người có phong cách sống hiện đại, thích cách tân đổi mới thì những kiểu biệt thự hiện đại là lựa chọn thích hợp. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự kiểu Ấn Độ<strong> </strong>phong cách hiện đại có thể là nơi tụ tập những bữa tiệc tùng hoành tráng nhưng riêng tư. Là nơi tụ tập lý tưởng hay là nơi nghỉ ngơi cho mọi người trong những chuyến du lịch. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trải nghiệm những cơ sở vật chất một cách tinh tế nhất như hồ bơi riêng, tận hưởng những phút giây thư giãn nơi căn phòng hiện đại có lẽ cũng không quá tệ nhỉ.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đầu tiên, bạn xác định rằng mình mong muốn một mẫu biệt thự kiểu Ấn Độ như thế nào? Bạn muốn hiện đại, cổ điển hay chỉ là một nơi thật thoải mái và yên bình? Khi bạn hiểu được mình muốn gì, muốn không gian như thế nào thì việc thiết kế bạn có thể hợp tác với các kiến trúc sư.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">An Gia Khang là công ty thi công xây dựng dự án nhà phố, biệt thự, cao ốc văn phòng, nhà xưởng, các công trình xây dựng,.. Với đội ngũ quản lý cùng nhân viên có kinh nghiệm, chúng tôi sẽ tạo nên cho các bạn những công trình vừa ý nhất.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quý khách có thể xem thêm: </span></span><a href=\"https://www.angiakhang.com/xay-biet-thu\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(235, 107, 86);\">Nhà thầu xây dựng biệt thự trọn gói</span></span></a></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trên đây là những mẫu biệt thự kiểu Ấn Độ được ưa chuộng hiện nay, bạn đã biết mình muốn loại nào chưa. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Rất hân hạnh được phục vụ quý khách.</span></p>\n', NULL, 1, NULL, 1575696201, 1590053051, NULL),
(183, 'category', 58, 0, '1. Nhà phố 4 tầng đẹp', '1-nha-pho-4-tang-dep', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><img src=\"/uploads/contents/1575711504_6a6317ab7800579d3c67.png\" style=\"width: 587px;\" class=\"fr-fic fr-dib\"></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một mẫu nhà phố đẹp 4 tầng đang được ưa chuộng hiện nay</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.1 Tại sao nên chọn xây nhà phố 4 tầng tại các khu đô thị?</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trong thời kỳ kinh tế phát triển hiện nay, tỷ lệ dân cư lựa chọn sống tại các khu đô thị ngày càng nhiều, vì thế quỹ đất để xây nhà tại các thành phố lớn là rất đắt và diện tích vô cùng ít. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575711723_dbc108798c6309d9cab8.jpg\" style=\"width: 595px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cho nên việc xây nhà dựa trên quỹ đất hẹp nhưng tăng không gian bằng chiều cao với nhiều tầng đang là ưu tiên được nhiều hộ gia đình lựa chọn, trong đó mẫu nhà phố đẹp 4 tầng cũng là một gợi ý hết sức hấp dẫn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1575711555_a0bd0910b2286f24ca0f.png\" style=\"width: 587px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà phố đẹp 4 tầng dạng mái thái hiện đại</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố đẹp 4 tầng có nhiều loại khác nhau, có thể được xây dựng thông qua yêu cầu thiết kế của chủ nhà hoặc mua sẵn tại các công trình của công ty nhà đất. Tuy nhiên, hầu hết chúng đều có một điểm chung đó là thiết kế vô cùng hiện đại và sang trọng phù hợp với thị hiếu người tiêu dùng. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575711738_22b7e21e8ba96de3f649.jpg\" style=\"width: 568px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hơn hết, nhà 4 tầng trên mẫu đất có diện tích hẹp còn đáp ứng được nhu cầu nhà ở rộng rãi cho hộ gia đình có nhiều người, cùng với cách bài trí không gian nội thất hiện đại sẽ mang đến cho bạn và gia đình một không gian sống đầy đủ và tiện nghi.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không chỉ đem lại một không gian sống tiện nghi, mẫu nhà phố đẹp 4 tầng còn thuận lợi cho việc phát triển kinh doanh đặt biệt là các khu vực ở mặt tiền đường. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575711753_148b52c2ecc407a085ad.jpg\" style=\"width: 606px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hầu hết khi xây dựng một căn nhà 4 tầng, các gia chủ chỉ sử dụng từ 2 đến 3 tầng cho việc sinh hoạt, ăn ở, còn tầng trệt hoặc tầng 1 thường để sử dụng vào các hoạt động kinh doanh như: shop, cho thuê, cửa hàng ăn uống,… điều này mang đến một nguồn thu nhập nữa cho gia đình.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1575711574_fb602aad5347f9345efb.png\" style=\"width: 599px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế mẫu nhà phố với sự kết hợp của hệ thống cây xanh</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.2 Những đặt điểm thường có ở các mẫu nhà phố đẹp 4 tầng hiện nay</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các căn nhà phố thường được xây dựng trên khu đất có dạng hình chữ nhật, mặt tiền rộng từ 3 đến 5 m, có chiều dài trung bình từ 15 – 20 m và được xây cao lên 4 tầng hoặc 3 tầng và 1 tầng tum với mục đích đáp ứng không gian sinh hoạt cho gia đình và các hoạt động kinh doanh buôn bán.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575711765_7e73535dcb87ffee63dc.jpg\" style=\"width: 578px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Những căn nhà này thường được xây dựng theo dạng ống và vì quỹ đất hạn hẹp nên các ngôi nhà này thường sát liền nhau, có thiết kế không cầu kì nhưng cực kỳ hiện đại và sang trọng.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bên cạnh đó, tùy theo đặc điểm kinh doanh của hộ gia đình mà thiết kế của các mẫu nhà phố đẹp 4 tầng sẽ khác nhau. Ví dụ như:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575711776_5ab43764b087b343de76.jpg\" style=\"width: 558px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các căn nhà hướng tới việc cho thuê phòng trọ thường sẽ hướng theo phong cách kiến trúc bán cổ điển, có gác mái, tầng trệt và lầu 1 thường là không gian sinh hoạt cho gia chủ, có một cầu thang riêng từ tầng trệt lên tầng 1, một cầu thang chung từ lầu 1 đến các tầng khác của ngôi nhà. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575711788_5c586561478562c78529.jpg\" style=\"width: 538px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tổng thể thiết kế tối giản, giảm bớt các chi tiết để phù hợp với mục đích kinh doanh.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575711802_b3d8b60fd11b794903d4.jpg\" style=\"width: 584px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế nhà phố 4 tầng kết hợp với kinh doanh shop đồ hoặc cửa hàng ăn uống. Với các căn nhà này, tầng trệt hoặc tầng 1 thường được sử dụng cho các hoạt động kinh doanh, phía trước mặt tiền lầu 1 thường được trang bị kính cường lực trong suốt để trưng bày đồ hoặc mang đến không gian mở cho khách hàng. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575711661_73711d73a29cced430c8.png\" style=\"width: 575px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 4 tầng đẹp</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Tổng thể ngôi nhà được thiết kế theo yêu cầu của gia chủ, có thể là hiện đại sang trọng, cũng trẻ trung khỏe khoắn tùy mục đích kinh doanh.</span></p>\n', NULL, 1, NULL, 1575711394, 1590064559, NULL),
(184, 'category', 58, 1, '2. Các lưu ý khi thiết kế và xây nhà phố 4 tầng', '2-cac-luu-y-khi-thiet-ke-va-xay-nha-pho-4-tang', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Các ngôi nhà phố 4 tầng thường được xây dựng trên khu đất nhỏ, để đảm bảo không gian rộng rãi thoải mái cho gia đình yêu cầu phải tăng không gian về chiều cao.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575711829_319927da5dbd667163f4.jpg\" style=\"width: 605px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Vì thế, yêu cầu trong thiết kế mẫu nhà phố đẹp 4 tầng phải kết hợp với các chi tiết, sắp xếp, phân phối các hình khối đường nét sao cho khi nhìn vào căn nhà có cảm giác không quá cao.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các chi tiết bố trí trong các khu vực của ngôi nhà yêu cầu không chiếm quá nhiều diện tích của căn nhà, vì nhà ống thường có diện tích không quá rộng. Cùng với đó, các phòng và các chi tiết nội thất đòi hỏi vừa phải được bố trí hài hòa với phong thủy trong ngôi nhà vừa phải phát huy đầy đủ các tính năng của mình.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575711861_21070d46def3e9003b52.jpg\" style=\"width: 601px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đại đa số các công trình nhà phố 4 tầng đều chỉ có một mặt tiền hướng ra đường, 3 mặt còn lại tiếp giáp với các căn hộ liền kề 2 bên và cả ở phía sau. Vì thế mà để lấy sáng và không khí trong lành, các căn nhà 4 tầng yêu cầu phải có một giếng trời hoặc sân phía trước hay phía sau nhà để tránh sự bí bách và thiếu sáng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575711881_688c46f8715b240aa2d7.jpg\" style=\"width: 604px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vì quỹ đất tại các khu đô thị hạn hẹp nên nếu công trình nhà phố 4 tầng của bạn có một khoảng không gian xung quanh dù rất nhỏ thì cũng nên tận dụng triệt để. Đây có lẽ sẽ là một không gian vàng để bạn bố trí khoảng ngoại thất nho nhỏ nhưng sẽ có giá trị thẩm mĩ vô cùng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1575711595_f91c59877a60773f774d.png\" style=\"width: 605px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một mẫu nhà phố 4 tầng đẹp</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trên đây là một số đặc điểm và lưu ý khi xây các mẫu nhà phố đẹp 4 tầng tại các khu đô thị, nếu bạn đang có dự định tìm cho mình một ngôi nhà phố 4 tầng với không gian đầy đủ tiện nghi, thẩm mĩ và phong thủy thì có thể tham khảo qua bài viết này để có được một ngôi nhà cực kỳ ưng ý nhé.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575711894_2690c4cfadbc5472baa9.jpg\" style=\"width: 585px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><a href=\"http://www.angiakhang.com/xay-dung-nha-pho\">Xây dựng nhà phố uy tín</a></span></span></p>\n', NULL, 1, NULL, 1575711910, 1590064559, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(185, 'category', 14, 0, '1. Xây dựng nhà phố', '1-xay-dung-nha-pho', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif;\"> An Gia Khang tự tin là đơn vị thầu xây dựng chuyên thiết kế, thi công xây dựng các công trình dân dụng và công nghiệp uy tín nhất.</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chúng tôi sở hữu đội ngũ kỹ sư, kiến trúc sư giàu kinh nghiệm và chuyên nghiệp đảm bảo mang đến một chất lượng công trình tốt nhất cho quý khách hàng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hợp đồng rõ ràng, có hỗ trợ và tư vấn nếu quý khách hàng có thắc mắc khi tham khảo qua hợp đồng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Luôn thanh lý hợp đồng đúng theo những gì nội dung đã cam kết. Thực hiện chính sách bảo hành công trình theo đúng quy định.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giá thành đưa ra đảm bảo hợp lý, có cơ sở, phù hợp với mọi đối tượng khách hàng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Luôn luôn hỗ trợ và tư vấn miễn phí cho quý khách hàng thiết kế công trình sao cho tốt nhất và đẹp nhất.</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586756024_5b6cd46e77ca00957a6b.jpg\" style=\"width: 587px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà xây dựng phố 2 tầng</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>a)  Các bước thực hiện xây dựng nhà phố</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bạn đang có dự định xây nhà phố thì không nên bỏ qua các bước tư vấn xây nhà quan trọng. Gia chủ cần chủ động tìm kiếm các công ty xây dựng để được tư vấn về dịch vụ xây nhà. Việc tìm đến sự hỗ trợ từ phía những người đã có nhiều năm kinh nghiệm trong lĩnh này sẽ giúp bạn lên ý tưởng tốt hơn cho tổ ấm của mình.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586756103_9701a05fa98f64b83190.jpg\" style=\"width: 586px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 3 tầng</span></span></p>\n<ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hiểu rõ quy định về xây dựng nhà phố</span></span></li></ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hiện nay, vấn đề xây dựng nhà không chỉ làm theo sở thích của gia chủ mà còn cần tuân thủ đúng theo quy định của pháp luật nhà nước. Để tránh khỏi những rủi ro pháp lý mà bạn có thể gặp phải trong quá trình xây dựng nhà, thì gia chủ cần nắm rõ quy định về cấp giấy phép trong xây dựng nhà phố, quy định trong thi công, ... </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu bạn không hình dung được về những thủ tục pháp lý mình cần phải làm thì gần như chắc chắn quá trình xây dựng sẽ gặp phải gián đoạn. Ảnh hưởng trực tiếp đến thời gian hoàn thiện, chi phí thuê nhân công xây dựng và nhiều vấn đề khác.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586756455_20711f4c6947d331bfb1.jpg\" style=\"width: 601px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 4 tầng đẹp</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trước khi xây dựng nhà phố: gia chủ phải xem xét các yếu tố liên quan đến pháp lý như: quyền sở hữu nhà đất, các yếu tố về quy hoạch lộ giới, chiều cao, số tầng, ... trước khi thực hiện xin cấp giấy phép và thiết kế căn nhà.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khi thực hiện xây dựng: Lựa chọn nhà thầu xây dựng uy tín, đảm bảo chất lượng và tiến độ công trình. Phối hợp cùng công ty xây dựng giải quyết những vấn đề như: đăng ký giấy tạm trú cho công nhân ở lại công trình, vấn đề pháp lý liên quan đến an toàn lao động, bảo hiểm tai nạn lao động, ...</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hoàn thành công trình: sau khi công trình đã được hoàn thiện thì thành xin cấp phép hoàn công là điều quan trọng nhất. Gia chủ chuẩn bị các giấy tờ liên quan để nộp cho UBND quận (huyện).</span></span></p>\n<ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Lên ý tường thiết kế nhà ở</span></span></li></ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Để có được ý tưởng thiết kế nhà đẹp tuyệt với nhất gia chủ có thể tham khảo những mẫu thiết kế nhà đẹp từ các kiến trúc sư nổi tiếng. Hiện nay, xu hướng xây dựng nhà phố đang thay đổi từng ngày, được du nhập vào Việt Nam những xu hướng thiết kế nhà ở mái thái, hay phong cách tân cổ điển, cổ điển, phong cách pháp, ...</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công năng sử dụng là điều quan trọng nhất mà gia chủ cần để tâm đến. Ngôi nhà bạn xây dựng dành để kinh doanh, cho thuê, nghỉ dưỡng hay nhà ở thì cần có những thiết kế riêng để phù hợp với mỗi công năng. Xác định rõ nhu cầu của gia đình để định lượng số phòng, vị trí, không gian riêng, không gian xanh, ... mà bạn muốn.</span></span></p>\n<ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Chọn lựa đơn vị thiết kế nhà phố uy tín, chuyên nghiệp.</span></span></li></ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Gia chủ nên lựa chọn đơn vị thiết kế nhà phố có nhiều kinh nghiệm, uy tín. Họ sẽ giúp bạn ước tính chuẩn xác nhất chi phí dầu tư xây dựng nhà, thiết kế công năng, tính thẩm mỹ, ... Thông qua bản thiết kế 3D gia chủ sẽ có cái nhìn tổng quát nhất và căn nhà của mình.</span></span></p>\n<ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Lên kế hoạch dự trù và ước tính chi phí xây dựng</span></span></li></ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bạn sẽ phải dự trù trước kinh phí xây dựng cơ bản cho toàn bộ căn nhà, bao gồm: thi công phần thô, gạch ốp lát, nội thất, nhân công, ... phương pháp tính phổ biến hiện nay là theo m2 xây dựng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chi phí mua đồ đồ trang trí căn nhà: đồ nội thất, tủ bếp, sofa, bàn ghế phòng ăn, điều hòa, máy giặt, tủ lạnh, tivi, ... không liên quan đến chi phí xây dựng. Bạn cần có kế hoạch cụ thể chi tiết để đủ hoàn thiện toàn bộ căn nhà, tránh tình trạng sau khi xây nhà xong bị thiếu kinh phí trang trí nhà.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hoạch định chi tiết chi phí từng mục để hạn chế phát sinh trong quá trình xây dựng làm đội mức chi phí lên cao so với dự kiến ban đầu.</span></span></p>\n<ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chọn nhà thầu xây dựng và ký hợp đồng.</span></span></li></ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Làm nhà là việc quan trọng nên bạn cần thận trọng trong tất cả các khâu, bởi nếu xảy ra sai sót thì việc sửa chữa sẽ rất khó khăn. Khi bạn đã cầm trong tay hồ sơ thiết kế, thi công chi tiết hoàn chỉnh, bộ dự toán chi phi thi công và giấy cấp phép xây dựng nhà thì việc quan trọng là lựa chọn nhà thầu đủ năng lực, trình độ chuyên môn để biến bản thiết kế thành hiện thực. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mọi thông tin tư vấn thiết kế và thi công xây dựng nhà phố vui lòng liên hệ Công ty thiết kế và xây dựng An Gia Khang, hotline: 0937 181 181</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586756131_fa55acdf6713ef4e9dd4.jpg\" style=\"width: 596px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 4 tầng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Gia chủ nên xem xét, tham khảo kỹ lựa để đánh giá, lựa chọn và làm rõ hợp đồng trước khi quyết định. Nhiều trường hợp gia chủ lựa chọn nhà thầu theo giới thiệu của người quen, bạn bè nên cả nể đã lựa chọn, nhưng họ không đáp ứng được khả năng để xây dựng nên dẫn đến công trình không hoàn thành đúng như dự kiến.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu bạn là sắp xếp hợp lý các giai đoạn thi công thì sẽ giúp ích rất nhiều trong việc lập kế hoạch phân bổ tài chính và các dự định khác. Gia chủ cần ước định thời gian bắt đầu xây dựng, thời gian hoàn thiện những công việc sau:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thực hiện chuẩn bị trước khi thi công: phá dỡ công trình cũ (nếu có), tập kết nguyên vật liệu, chuẩn bị kho bãi để đồ, chuẩn bị điện nước để phục vụ quá trình xây dựng, ....</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thực hiện xây dựng phần thô: Thời gian đào móng, đổ trần, đầm nền, lắp khung bao cửa, hệ thống đường điện, ống nước, ...</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phần hoàn thiện căn nhà: sơn, lắp ráp cửa, lắp lan can cầu thang, lan can mặt tiền, lát nền, ốp tường, ốp đá cầu thang, hoàn thiện thiết bị điện, chiếu sáng, máy bơm nước, trang trí nội thất, ...</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trong quá trình xây dựng sẽ có các đợt nghiệm thu công trình vấn đề này là do gia chủ quyết định cùng nhà thầu để xác định thời gian cụ thể. Với mỗi đợt nghiệm thu công trình sẽ tương ứng với một lần thanh toán. Thông thường với một công trình xây dựng nhà sẽ có từ 8-10 lần.</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>b) Cách tính m2 xây dựng nhà phố để dự toán chi phí</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ngày nay đa số các khách hàng chỉ quan tâm và tìm hiểu đơn giá xây dựng nhà /m2 và các công ty xây dựng nhà ở đều áp dụng cách tính chi phí xây nhà dựa theo diện tích m2. Đây là cách đơn giản để gia chủ có cái nhìn tổng quát về mặt chi phí căn nhà của mình một cách nhanh nhất.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586756494_0ca83e92f6e44f0bcc5e.jpg\" style=\"width: 599px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 3 tầng đẹp</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Cách 1:</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Móng băng tính bằng 30-50% diện tích tầng trệt x đơn giá xây thô</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Diện tích xây dựng các tầng bao gồm cả cầu thang, chuồng cu tính 100% diện tích x đơn giá chọn gói.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sân thượng sẽ được tính 30 – 50% diện tích x đơn giá trọn gói.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Cách 2:</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Móng băng sẽ bằng 10-25% diện tích tầng trệt x đơn giá xây thô.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Diện tích xây dựng các tầng kể cả cầu thang và chuồng cu tính 100% diện tích x đơn giá trọn gói.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sân thượng tính 30 – 50% diện tích x đơn giá trọn gói.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mái BTCT được tính 30 – 50% diện tích x đơn giá trọn gói.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">So sánh giữa 2 cách tính này thì có sự khác nhau ở chỗ:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cách 1: Diện tích móng được tính bằng ½ diện tích sàn trệt, nhưng lại không tính diện tích mái bê tông cốt thép.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cách 2: Tính diện tích móng bằng 15% diện tích sàn trệt, nhưng với diện tích mái 50%.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tùy theo mỗi nhà thầu mà có các đơn giá khác nhau, tuy nhiên sự chênh lệch về chi phí là không nhiều và có thể chấp nhận được.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với cách tính 1 m2 xây dựng nhà phố như trên sẽ giúp gia chủ có cái nhìn tổng quát về chi phí xây dựng căn nhà một cách nhanh chóng và dễ hiểu nhất. Lời khuyên dành cho gia chủ khi chọn nhà thầu cần cân nhắc chi tiết đơn giá xây dựng/m2 với những vật liệu phù hợp để tránh đội chi phí.</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>c) Thủ tục xin giấy phép xây dựng nhà phố</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hiện nay, theo quy định pháp luật của nhà nước thì xây dựng nhà ở phải xin giấy phép xây dựng từ chính quyền địa phương. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586756210_3f7a51e75f10197e2aac.png\" style=\"width: 589px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tư vấn thủ tục xin giấy phép xây dựng nhà trọn gói</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhiều gia chủ vẫn đang băn khoăn không biết thủ tục là gì, cần nộp những giấy tờ gì, nộp cho ai. Hiểu được nỗi lòng của gia chủ chúng tôi tổng hợp những thủ tục xin giấy phép xây dựng nhà mà các bạn phải làm. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Căn cứ vào khoản 1 điều 95 Luật xây dựng 2014 quy định về hồ xơ xin cấp giấy phép xây dựng nhà ở gồm:</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đơn xin đề nghị cấp giấy phép xây dựng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bản sao giấy tờ chứng minh quyền sử dụng đất theo quy định của pháp luật.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bản vẽ thiết kế xây dựng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đối với công trình xây dựng có công trình liền kề phải có bản cam kết an toàn đối với công trình liền kề.</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bộ hồ sơ xin giấy phép xây dựng nhà ở bao gồm:</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đơn đề nghị cấp giấy phép xây dựng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bản sao những giấy tờ chứng minh quyền sử dụng đất.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bộ bản vẽ thiết kế. Gồm: Bản vẽ mặt bằng công trình trên lô đất tỷ lệ 1/50 – 1/500, kèm theo sơ đồ vị trí công trình; </span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bản vẽ mặt bằng các tầng, các mặt đứng và mặt cắt chủ yếu của công trình, tỷ lệ 1/50 – 1/200; Bản vẽ mặt bằng móng tỷ lệ 1/50 – 1/200 và mặt cắt móng tỷ lệ 1/50, kèm theo sơ đồ đấu nối hệ thống thoát nước mưa, xử lý nước thải, cấp nước, cấp điện, thông tin tỷ lệ 1/50 – 1/200.</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đối với công trình xây dựng có công trình liền kề phải có bản cam kết của chủ đầu tư bảo đảm an toàn đối với công trình liền kề hoặc đối với công trình xây chen có tầng hầm, ngoài các tài liệu nêu trên thì hồ sơ còn phải bổ sung bản sao hoặc tệp tin chứa bản chụp chính văn bản chấp thuận biện pháp thi công móng của chủ đầu tư đảm bảo an toàn cho công trình và công trình lân cận (điều 11 Thông tư 15/2016/TT-BXD).</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>d) Trình tự của thủ tục nộp hồ sơ xin cấp giấy phép xây dựng nhà phố</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Bước 1:</strong> Nộp 01 bộ hồ sơ tại UBND cấp quận (huyện) nơi chuẩn bị xây nhà.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Bước 2:</strong> Bộ phận tiếp nhận hồ sơ kiểm tra hồ sơ, nếu thiếu thì yêu cầu người xin giấy phép xây dựng bổ sung thêm giấy tờ, nếu đã đủ thì viết giấy biên nhận và trao cho người xin giấy phép xây dựng. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu trong trường hợp cần phải xem xét thêm thì cơ quan cấp giấy phép xây dựng sẽ thông báo lý do bằng văn bản và báo cáo cấp có thẩm quyền quản lý trực tiếp xem xét chỉ đạo.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Bước 3: </strong>Người xin giấy phép xây dựng tới nơi tiếp nhận hồ sơ theo thời gian hẹn trong giấy biên nhận để nhận kết quả và nộp lệ phí. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Người xin giấy phép tiếp nhận giấy phép xây dựng kèm theo hồ sơ thiết kế đã được đóng dấu của cơ quan cấp giấy phép xây dựng hoặc văn bản trả lời (nếu công trình không đủ điều kiện để cấp giấy phép xây dựng)</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Thời gian giải quyết hồ sơ xin cấp giấy phép xây dựng nhà ở:</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kể từ ngày nhận đủ hồ sơ hợp lệ, cơ quan có thẩm quyền sẽ xem xét và giải quyết trong thời gian 10 ngày làm việc đối với nhà ở nông thôn và 15 ngày đối với nhà ở riêng lẻ tại đô thị.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trong trường hợp đến thời gian cấp giấy phép nhưng cần phải xem xét thêm thì cơ quan cấp giấy xây dựng phải thông báo trả lời bằng văn bản cho chủ đầu tư biết lý do và thông báo với cấp có thẩm quyền quản lý trực tiếp xem xét chỉ đạo thực hiện, nhưng không quá 10 ngày từ ngày hết hạn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu vượt quá thời hạn ghi trong giấy biên nhận mà cơ quan cấp giấy phép không trả lời thì chủ đầu tư được pháp xây dựng theo hồ sơ thiết kế đã được thẩm định.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">[[contact_short_code]]</span></p>\n', NULL, 1, NULL, 1575785874, 1590571348, NULL),
(186, 'category', 14, 1, '2. Báo giá xây dựng nhà phố', '2-bao-gia-xay-dung-nha-pho', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Bảng báo giá xây dựng nhà phố phần thô và nhân công hoàn thiện 2020. Xin gửi bảng báo giá xây dựng nhà phố để quý khách tiện tham khảo, hoặc liên hệ: 0937 181 181 để được tư vấn và báo giá chính xác nhất.</span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà phố hiện đại: 3.200.000 - 3.300.000 đồng/m2              </span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà phố tân cổ điển: 3.300.000 - 3.500.000 đồng/m2        </span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Biệt thự hiện đại: 3.300.000 - 3.500.000 đồng/m2</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Biệt thự tân cổ điển: 3.500.000 - 4.000.000 đồng/m2   </span></span></li>\n</ul>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586757633_4a70952156e7a206a674.jpg\" style=\"width: 643px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586757646_6bfc86b1a53f351133cb.jpg\" style=\"width: 645px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bảng giá xây nhà trọn gói và vật tư xây dựng</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">[[contact_short_code]]</span></p>\n', NULL, 1, NULL, 1575785874, 1590571348, NULL),
(189, 'category', 14, 2, '3. 15 mẫu nhà phố đẹp nhất 2020', '3-15-mau-nha-pho-dep-nhat-2020', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Quý khách đang có nhu cầu tìm hiểu xây dựng nhà phố,  hãy liên hệ ngay với chúng tôi hotline: 0937 181 181 để được tư vấn và báo giá xây dựng nhà phố chi tiết. Xin được chia sẽ với quý khách những mẫu nhà phố đẹp 2020.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586759308_4d093b7cc5ccbb409576.jpg\" style=\"width: 592px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 3 tầng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586759330_825892598491d5826c6d.jpg\" style=\"width: 580px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 1 tầng có gara</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586759350_1c607d16b9bca2587c37.jpg\" style=\"width: 587px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 2 tầng có sân thượng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586759375_71408a98ab0ce71ad007.jpg\" style=\"width: 582px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 1 tầng có sân thượng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586759398_0757f0afacd9e87db057.jpg\" style=\"width: 574px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố kểu Châu Âu</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586759423_3040ad945bee3d826057.jpg\" style=\"width: 546px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 3 tầng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586759451_b0c89279651e3acf1854.jpg\" style=\"width: 545px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 1 tầng có sân thượng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586759480_b732cf62c28f383cdba4.jpg\" style=\"width: 533px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 5 tầng có sân thượng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586759522_c2bff3ac5f6d153a5b60.jpg\" style=\"width: 529px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 2 tầng có sân thượng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586759553_a30f8dbd65bf70f505b4.jpg\" style=\"width: 526px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 1 trệt 2 lầu</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586759589_2562234f33be096e19ab.jpg\" style=\"width: 540px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 3 tầng có hầm để xe</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586759625_754992e2d4009ab0f85e.jpg\" style=\"width: 530px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố kiểu hiện đại </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586759656_c17ccbad2ccf4a68ecf8.jpg\" style=\"width: 525px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 1 trệt 2 lầu có sân thượng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586759694_bda4eff766f8a0da2065.jpg\" style=\"width: 502px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 2 cổng đẹp</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1586759726_f0f7dab9ba3708c20934.jpg\" style=\"width: 518px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu nhà phố ghép đôi có gara</span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n', NULL, 1, NULL, 1575786914, 1590571348, NULL),
(192, 'category', 19, 1, '2. Thủ tục xây nhà trọn gói', '2-thu-tuc-xay-nha-tron-goi', '<p>thủ tục xây nhà trọn gói</p>', NULL, 1, NULL, 1575790961, 1575796341, NULL),
(193, 'category', 19, 2, '3. Công ty xây nhà trọn gói uy tín', '3-cong-ty-xay-nha-tron-goi-uy-tin', '<p>3. Công ty xây nhà trọn gói uy tín tại tphcm</p>', NULL, 1, NULL, 1575790961, 1575796341, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(197, 'category', 59, 0, '1. Xây nhà trọn gói tại tphcm', '1-xay-nha-tron-goi-tai-tphcm', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Về nội dung cần biết về việc lựa chọn loại hình xây nhà trọn gói, quý khách đang tìm hiểu một công ty xây dựng nhà trọn gói, chi tiết hơn về báo giá xây nhà, trong đó có các hạng mục thi công trong quá trình xây dựng nhà gồm những gì, chi phí xây nhà trọn gói nhà thầu xây dựng tính như thế nào, các thủ tục và hỗ trợ danh cho khách hàng khi ký hợp đồng xây nhà trọn gói được thực hiện đúng pháp luật hay không?...</span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Công ty thiết kế và xây dựng An Gia Khang cung cấp các thông tin chi tiết về nhu cầu mà khách hàng đang quan tâm tìm kiếm.  Xin gửi quý khách hàng tham khảo các nội dung chi tiết về bảng báo giá xây dựng nhà trọn gói mà công ty chúng tôi thiết kế.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1583767247_da0397834125084bbc53.jpg\" style=\"width: 630px;\" class=\"fr-fic fr-dib\"><br></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với nội dung thông tin bên dưới, sẽ giúp quý khách hàng hiểu rõ hơn các thắc mắc về các hạng mục trong xây nhà trọn gói và đặc biệt hơn dịch vụ xây nhà ở tphcm, xây dựng nhà phố, xây biệt thự… hay hợp đồng xây nhà như thế nào là hợp pháp. Các phương pháp tính chi phí xây dựng nhà và cách lựa chọn nhà thầu xây nhà uy tín.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây nhà là một trong những công việc rất quan trọng của một đời người, ngôi nhà được xây không hẳn là nơi che mưa, che nắng mà ngôi nhà là nơi cho chúng ta có thể sống quây quần, vui vẻ hạnh phúc bên nhau suốt đời. Vì vậy xây dựng một ngôi nhà nó rất quan trọng tư khi bắt đầu cho đến hoàn công xây dựng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1583767305_0ba1cb05b33c9cc3ee3d.jpg\" style=\"width: 634px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế nội thất bên trong</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khi chuẩn bị xây dựng nhà các quý khách hàng lo lắng rất nhiều, bởi khi bắt đầu phải chuẩn bị rất nhiều vấn đề, nào là kinh phí xây nhà, xây như thế nào, lựa chọn xây phần thô hay khoán xây nhà trọn gói, mẫu nhà thiết kế như thế nào? Giấy phép và thủ tục pháp lý chuẩn bị như thế nào….và quan trọng nhất là chọn nhà thầu xây dựng uy tín.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chọn nhà thầu xây dựng, chắc quý khách rất lo lắng, làm thế nào để chọn được đơn vị thi công xây dựng nhà uy tín với giá xây nhà hợp lý nhất. Một nhà thầu xây dựng uy tín, quý khách có thể tin tưởng gửi gắm căn nhà của mình cho nhà thầu, nhưng không hẳn nhà thầu nào cũng làm gia chủ hài lòng. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hiện này trên thị trường xây dựng có rất nhiều đơn vị thầu xây dựng nhưng không phải đơn vị nào cũng thật sự tin tưởng, không ít những trường hợp xây dựng vật tư kém chất lượng, thi công không theo đúng mẫu đã chọn, không làm théo đúng hợp đồng cam kết về tiến độ….</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1583767370_a1fec00586bd9ba8b504.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng An Gia Khang</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(184, 49, 47);\"><strong>Lưu ý thêm một số vấn đề sau:</strong></span></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công tác giám sát thi công xây dựng, giám sát nhà thầu thi công, nhiều lúc có những trường hợp quá cả tin vào đơn vị thi công mà chủ đầu tư quên đi công tác giám sát thi công ngôi nhà của mình.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Và đây cũng là lý do, mà nhiều nhà thầu lợi dụng lòng tin của quý khách hàng , đã giảm vật liệu xây dựng, chất lượng vật liệu không đảm bảo, thi công không đúng với bản vẻ thiết kế, hay tiến độ không hoàn thành dẫn đến chất lượng công trình về sau không đảm bảo tốt.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1583767468_f057486a7aa3e1bcadc3.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đôi ngũ công nhân An Gia Khang</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công ty thiết kế và xây dựng An Gia Khang đã thi công xây dựng, sửa chữa và cải tạo rất nhiều công trình, chúng tôi đã chứng kiến rất nhiều công trình xây dựng công nghiệp, dân dụng vẫn chưa đạt tiêu chuẩn về chất lượng . </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có rất nhiều tình trạng nhà mới xây được 1-2 năm thì đã xảy ra tường nứt, nứt cột, và một điều thường thấy đó là khi   mưa xuống, hiện tượng thấm nước, hệ thống thoát nước có vấn đề…</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">An Gia Khang đã khảo sát, đã tư vấn sửa chữa rất nhiều dự án và công trình . Vì vậy chúng tôi biết được nhiều sai sót mà các đơn vị thầu xây dựng khác đã làm và có lẽ các đơn vị đó họ cũng biết nhưng họ chưa làm hết và nhiệt tình với trách nhiệm của mình.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khi đã chọn được nhà thầu xây nhà, quý khách đã cho là tốt nhất, uy tín, giá xây nhà hợp lý và ký hợp đồng xây dựng nhà với đơn vị thầu xây dựng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important;\">Xây dựng nhà là cả một quá trình và tốn rất nhiều thời gian và công sức. Chi phí xây dựng nhà là yếu tố quan trọng nhất, ảnh hưởng đến tiến độ, hình thức của căn nhà. Đơn giá xây nhà tác động trực tiếp đến chi phí xây dựng nhà.</span></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.1 Giá xây nhà trọn gói</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đơn giá xây nhà có sự khác biệt giữa các đơn vị nhà thầu cũng như cách tính. Để gia chủ hiểu thêm về giá xây dựng nhà cũng như mẫu nhà đẹp, bài viết dưới đây của chúng tôi sẽ giới thiệu toàn bộ những thông tin về đơn giá xây dựng nhà. Mời các bạn tham khảo.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1583769046_4c2bfa4423b07ac4bb00.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1583769077_adf2fd328bb9b63f0b1b.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1583769088_0a307404321a31679d0e.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1583769099_4a632d9706b0640d97f4.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1583769116_c0fb6495e8b286470b9f.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1583769129_a1e4d3c61ee44d3fad6a.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1583769143_b2139f047c522ab49b0b.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bảng giá xây nhà trọn gói</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>BẢNG GIÁ NHÂN CÔNG XÂY DỰNG TẠI TPHCM 2020</strong></span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Gói phổ thông: Già từ 4.800.000 – 5.000.000 đ/m2</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Gói Khá : Giá từ 5.200.000 – 5.500.000 đ/2</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Gói Khá +: Giá từ 6.000.000- 6.600.000 đ/m2</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>BẢNG GIÁ VẬT LIỆU XÂY THÔ VÀ NHÂN CÔNG HOÀN THIỆN</strong></span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Nhà phố hiện đại: 3.200.000 - 3.300.000 đồng/m2          </span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Nhà phố tân cổ điển: 3.300.000 - 3.500.000 đồng/m2        </span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Biệt thự hiện đại: 3.300.000 - 3.500.000 đồng/m2           </span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Biệt thự tân cổ điển: 3.500.000 - 4.000.000 đồng/m2    </span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu Thuê nhân công xây dựng giúp chủ đầu tư không phải lo nghĩ nhiều, liệu nhà thầu này có uy tín, trách nhiệm hay không.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1590049241_6b919acac63bbc1ce363.png\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; border-style: none; -webkit-font-smoothing: antialiased; cursor: pointer; padding: 0px 1px; max-width: 100%; color: rgb(65, 65, 65); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: center; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial; background-color: rgb(255, 255, 255); width: 634px;\" class=\"fr-fic fr-dib\">Thi công xây dựng nhà trọn gói</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Gia chủ hay chủ đầu tư chỉ cần thuê đơn vị thiết kế nhà sau đó dựa theo bản vẽ chi tiết mà có thể thuê nhân công xây dựng, đặc biệt là những gia đình có người thân bên lĩnh vực xây dựng lại càng yên tâm. Đặc biệt, đối với những gia đình có nhiều thời gian nhàn rỗi sẽ có thời gian giám sát công trình. <strong>An Gia Khang</strong> là một trong những công ty xây nhà trọn gói uy tín và chất lượng tại tphcm.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>NHÀ THẦU XÂY DỰNG AN GIA KHANG TẠI TPHCM</strong></span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><iframe width=\"100%\" height=\"360\" src=\"https://www.youtube.com/embed/ccDoa-vR3ak\" frameborder=\"0\" allowfullscreen=\"\"></iframe></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Do vậy, hiện nay có nhiều công ty xây dựng nhà ở đã cung cấp dịch vụ nhận thầu nhân công đưa ra mức giá nhân công khác nhau.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576428898_69b51502c2b9df7e7984.jpg\" style=\"width: 629px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Nhân công thi công lắp máy lạnh</span></span></p>\n<p style=\"box-sizing: border-box; margin: 0in 0in 10pt; font-size: 15px; color: rgb(56, 56, 56); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">[[contact_short_code]]</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đơn giá xây nhà trọn gói trên là giá được Công ty An Gia Khang đưa ra và tính ra qua nhiều công trình xây dựng, tuy nhiện đơn giá trên còn tùy thuộc vào nhiều yếu tố, như diện tích, vị trí khu đất, tổng diện tích xây dựng, hồ sơ và thiết bị vật tư hoàn thiện…..</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.2 Các hạng mục thi công xây dựng nhà trọn gói:</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Để có được đơn giá xây nhà trọn gói được chính xác và rõ hơn, quý khách nên liên hệ với Công ty An Gia Khang để được tư vấn và xem qua hồ sơ thi công, mẫu nhà , công năng sử dụng dự án, tính thẩm mỹ công trình và chi tiết thực hiện các hạn mục</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.2.1 Phần xây dựng cơ bản( nhà thầu cung cấp vật tư và nhân công xây dựng)</strong></span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây dựng công trường, làm trại cho công nhân( dự mặt bằng dự án thi công cho phép)</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thi công mặt bằng, định vị tim móng, cột.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đào đất, hố móng, móc cọc, dầm móng, đà kiềng, hầm phân, hố ga…</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thi công copha, cốt thép, đổ bê tông móng, đà kiềng, dàm sàn các lầu, cột, ..theo bản vẽ thiết kế mẫu nhà.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây tường gạch 100mm- 200mm 8x8x18cm, theo bản vẽ thiết kế công trình. Tô trát tường đúng theo tiêu chuẩn, chất lượng xi măng tốt.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cán nền, lầu, sân thượng, mái che và nhà vệ sinh</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giai đoạn quan trọng thi công chống thấm, sàn mái, sàn vệ sinh, sân thượng…..</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Lăp đặt hệ thống đường ống cấp và thoát nước</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Lắp đặt hệ thống đường dây điện , chiếu sáng, đế âm, hộp nối..</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Lắp đặt hệ thống đường dây truyền hình cáp, internet…</span></span></li>\n</ul>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.2.2. Phần hoàn thiện( nhà thầu cung cấp vật liệu và nhân công xây dựng)</strong></span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thực hiện ốp lát gạch toàn bộ sàn nhà, phòng bếp, tường vệ sinh theo bản vẽ thiết kế</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cung cấp gạch ốp lát, đá, gạch trang trí đẹp và chất lượng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thực hiện lắp đặt hệ thống điện, chiếu sáng, công tắc, ổ cắm, bóng đèn.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thực hiện lắp đặt thiết bị vệ sinh, bồ cầu, lavabo, vòi nước….</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thực hiện và thi công bao cửa gỗ, tủ bếp trên dưới, cửa sắt, bông bảo vệ, cửa nhôm..</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thực hiện thi công trần thạch cao phẳng, trần thả, trần trang trí, vách tường thạch cao.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thực hiện thi công trét mát tít và sơn nước toàn bộ bên trong và ngoài nhà.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thực hiện thi công vệ sinh công trình trước khi bàn giao và đưa vào sử dụng</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.3 Các yếu tố ảnh hưởng đến giá nhân công xây dựng tphcm.</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thành phố Hồ Chí Minh là thành phố phát triển nhất cả nước, do đó mọi thứ ở đây đều đắt đỏ. So sánh với các vùng tỉnh, huyện lân cận thì giá nhân công xây dựng tphcm thường cao hơn. Yếu tố vị trí địa lý là một trong những yếu tố ảnh hưởng đến giá nhân công xây dựng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576428924_0815e1be0a0f1c2de3c9.png\" style=\"width: 642px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công nhân đang thực hiện sơn tường</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phong cách thiết kế của căn nhà cũng ảnh hưởng rất nhiều đến giá nhân công xây dựng. Thông thường thì thi công nhà phố, biệt thự theo phong cách hiện đại, đơn giản sẽ có giá nhân công rẻ hơn so với những công trình quy mô lớn như phong cách cổ điển, tân cổ điển. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với những công trình xây dựng theo phong cách cổ điển hay tân cổ điển đòi hỏi nhiều chi tiết đường nét trang trí tỉ mỉ và có kinh nghiệm thông công nhà đẹp mới có thể làm được. Như vậy, chi phí mức giá nhân công thường cao hơn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576428958_2d78e9c1b33df228dc4a.jpg\" style=\"width: 652px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">San lấp mặt bằng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tại mỗi thời điểm thì giá nhân công xây dựng tphcm sẽ có sự khác nhau. Theo như thống kê thì giá nhân công sẽ tăng lên mỗi năm.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tùy theo quy mô, tính chất của mỗi công trình như diện tích xây dựng, sàn, tầng… mà các nhà thầu sẽ cung cấp những bảng báo giá nhân công xây dựng tphcm khác nhau.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phần thô của công trình xây dựng là phần móng cùng bể ngầm, các hệ thống kết cấu chịu lực (khung, cột, dầm, sàn bê tông), mái bê tông, cầu thang, hệ thống tường bao che và ngăn chia. Phần thô là tiền đề quan trọng cho công trình cũng như tất cả các quy trình thi công sau này. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576428526_61f00489e690300de9b6.png\" style=\"width: 641px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây dựng phần thô công trình</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phần thô càng tốt, càng chính xác thì những phần sau của công trình càng thuận tiện, càng tiết kiệm thời gian và chi phí. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576428616_1f9b5be7be9a6e593615.jpg\" style=\"width: 637px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây móng công trình</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><br></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576428772_7ed0b218548d57ba468e.jpg\" style=\"width: 639px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công nhân thi công lát gạch</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">[[contact_short_code]]</span></p>\n', NULL, 1, NULL, 1575797258, 1590049312, NULL),
(198, 'category', 60, 0, '1.Công ty xây dựng tại Phú Quốc', '1cong-ty-xay-dung-tai-phu-quoc', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nói đến Phú Quốc ai cũng biết nơi đây còn có biệt danh là Đảo Ngọc, là hòn đảo lớn nhất Việt Nam.  Đảo Ngọc Phú Quốc thuộc tỉnh Kiên Giang. Toàn bộ huyện đảo có tổng diện tích 589,23km2 (Theo thống kế 2005). Phú Quốc nằm cạnh thành phố Rạch Giá 120km và cách thành phố Hà Tiên 45km.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576423568_3948b769db04f68ebf71.jpg\" style=\"width: 619px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đảo Ngọc Phú Quốc nhìn từ trên cao( Nguồn Youtube)</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với sự phát triển mạnh mẽ của nền kinh tế, cùng với sự thịnh hành của ngành du lịch, Đào Ngọc Phú Quốc trở thành điểm du lịch hấp dẫn tại tỉnh Kiên Giang. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Và đây cũng là lý do, ngành xây dựng đang phát triển mạnh, nhiều công ty xây dựng ra đời, thúc đầy quá trình phát triển ngành xây dựng công nghiệp.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công ty xây dựng Phú Quốc là những đơn vị hoặc tổ chức có đầy đủ năng  lực xây dựng để có thể ký kết các hợp đồng xây dựng với chủ đầu tư và nhận thầu toàn bộ một loại công việc hay toàn bộ công việc của dự án đầu tư xây dựng công trình.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576423611_1c23960369e46afea7b2.jpg\" style=\"width: 628px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây dựng các biệt thự cao cấp</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công ty xây dựng ở Phú Quốc có vai trò quan trọng trong xây dựng hạ tầng tại Phú Quốc tỉnh Kiên Giang.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công ty xây dựng hay còn được gọi là nhà thầu xây dựng, nhà thầu gồm các loại  được chia ra như sau:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Nhà thầu xây dựng nói chung và nhà thầu xây dựng Phú Quốc thường có hai nhóm là :</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các nhà thầu chính là đơn vị chuyên thiết kế, thi công xây dựng ký hợp đồng với chủ đầu tư</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các nhà thầu phụ</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các nhà thầu xây dựng Phú Quốc cần phải có các chức năng và pháp lý liên quan đến ngành được nhà nước cấp phép như: có chứng chỉ hành nghề liên quan đến đội ngũ kiến trúc sư, kỹ sư, giám sát, chỉ huy công trình và cả các công nhân lành nghề.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576423640_3790f49ebd9417ce48d0.jpg\" style=\"width: 617px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây dựng nhà phố</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Nhà thầu xây dựng cần phải có trách nhiệm với các thiết bị, phương tiện cũng như biện pháp thi công sử dụng trong khi thi công công trình. Đồng thời công ty xây dựng cũng có trách nhiệm cung cấp vật tư, nhân công theo như thỏa thuận ban đầu với chủ đầu tư. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Để có thể làm được hết các công việc của một công trình nhà thầu cần phải ký hợp đồng giao khoán với nhà thầu phụ nhằm thực hiện một số những công việc chuyên ngành.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><strong>NHÀ THẦU XÂY DỰNG AN GIA KHANG TẠI PHÚ QUỐC</strong></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><br><iframe width=\"100%\" height=\"360\" src=\"https://www.youtube.com/embed/3fQu0tIbMvQ\" frameborder=\"0\" allowfullscreen=\"\"></iframe></p>\n', NULL, 1, NULL, 1575797303, 1590049156, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(200, 'category', 8, 1, '2. Những điều cơ bản trong thiết kế kiến trúc mà bạn cần biết.', '2-nhung-dieu-co-ban-trong-thiet-ke-kien-truc-ma-ban-can-biet', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Để tạo ra được công trình đẹp, mang lại giá trị thì khi thiết kế kiến trúc cần chú ý đến những điều cơ bản sau:</span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">2.1 Sáng tạo. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575817978_5ab017828df7ceb93fba.png\" style=\"width: 623px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế kiến trúc phải sáng tạo để tạo ra công trình đẹp</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với mồi người kiến trúc sư thì khi có cơ hội được thiết kế, phải cố gắng hết mình, không chỉ làm tốt những điều kiện đã đưa mà phải tìm tòi để những thiết kế được tiến xa hơn, có ích hơn, tiện lợi hơn và đẹp hơn. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575818729_c3dc33429166ee66e003.png\" style=\"width: 607px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế nhà phố 2019</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Để đạt được điều này thì phải có niềm đam mê, sự tìm hiểu, suy nghĩ, quan sát và tìm tòi cái mới, cái đẹp. Tuy nhiên cũng trong khả năng của bản thân để có thể tạo ra những thiết kế mang chất lượng cao.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">2.2 Tạo không gian</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế kiến trúc là làm việc trong một môi trường liên đới và tổng thể. Điều này có nghĩa là khi thiết kế một công trình thì nó phải điều tiết một cách hài hòa vào trong từng tầng lớp của môi trường đó.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575818760_1c4bc967b33facc8db13.png\" style=\"width: 625px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế biệt thự đẹp 2019</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bên cạnh đó, cùng lúc ấy phải tạo ra tính chất đặc thù cho không gian chung và một sự cảm nhận riêng biệt đối với từng người sử dụng công trình. Những công trình thiết kế kiến trúc phải mang những thứ được lên kế hoạch trước nhưng vẫn tồn tại những thứ không có trong kế hoạch hoặc những yếu tố chưa có thể biết hoặc hiểu được.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">2.3 Các yếu tố tương tác từ bên ngoài</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mỗi công trình kiến trúc đã mang những tính chát đặc biệt bởi các yếu tố thời gian, không gian, chủ đầu tư, ngân sách đầu tư, các tính năng và chức năng của công trình.  Quy hoạch và thiết kế công trình nên cố gắng một cách tỉ mỉ trong sự kết nối những sự liên đới này ở mức độ hợp lý và có ý nghĩa. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575818786_7dafd53aebb0ff38c5a6.png\" style=\"width: 618px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế nhà phố mái thái</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không nên tạo ra những context mới mà chưa qua bước nghiên cứu cũng như thử nghiệm. Khi thiết kế công trình nên làm cho công trình và môi trường xung quanh kết hợp theo hướng cải thiện và làm cho môi trường đó tốt hơn.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">2.4 Phát triển bền vững.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các công trình xây dựng cần được thiết kế một cách cẩn thận để có thể giảm diện tích xây dựng. Mặc khác cần phải luôn tiếp tục tham khảo, học tập, tìm tòi, nghiên cứu những cách phát triển và áp dụng những yếu tố của kỹ thuật phát triển bền vững vào trong các công trình thiết kế kiến trúc.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575819099_f47c7908e9ba7e268485.png\" style=\"width: 601px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế kiến trúc 2019</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">2.5 Tính toán kỹ lưỡng.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các công trình kiến trúc phải tạo ra sự rõ ràng ở các không gian cũng như các khối của công trình. Hình dáng, tỷ lệ phải có sự hài hòa và sâu sắc trong sự tương uan và tương phản nhằm tôn trọng tính thiết yếu của từng không gian. Thiết kế kiến trúc phải tạo nên công trình có hình thức hài hòa và tinh tế trên tinh thần đơn giản. Trong khi sự phức tạp của mỗi công trình sẽ làm tăng tính phong phú của công trình, thì công trình càng phải được hoàn thành với tầm quan trong của rõ ràng trong hình khối cũng như tổ chức không gian tốt.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">2.6 Công năng tiện lợi.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mỗi công trình nên là sự phản chiếu của tính công năng của công trình. MỘt thiết kế mang tính công năng sẽ thể hiện được cái mà gia chủ muốn và cần, đây là cái tối thiểu mà người thiết kế có thể làm được. Nó mang lại được cảm giác thực sự, mang đến những vẻ đẹp thiết thực và gần gũi. Nhưng quan trọng nhất vẫn là, tính công năng luôn có thể đáp ứng cho hiện tại, và hội nhập với những yêu cầu và thay đổi của tương lai.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">2.7 Sự lưu chuyển giữa các không gian trong công trình kiến trúc. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575818843_80a3c5c3134eafd2dd6c.png\" style=\"width: 614px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế kiến trúc tạo ra không gian sống tối ưu, lý tưởng</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sự lưu thông của khu vực tổng thể và công trình là yếu tố được đòi hỏi rất rõ ràng, giúp công năng của công trình được nâng cao. Những kiến trúc sư phải nghiên cứu tính chất liên kết của sự di chuyển trong mỗi công trình. Từ đó có thể tạo ra những sự tinh tế và bất ngờ của không gian đối với người tham quan và sử dụng.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">2.8 Vật liệu và chi tiết.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Những vật liệu được lựa chọn xây dựng công trình có thể trở thành điểm đặc biệt riêng không thể quên được của công trình. Khi thiết kế kiến trúc cần đặc biệt chú ý đến vấn đề này, mỗi vật liệu khi áp dụng phải mang tính hợp lý và ý nghĩa. Ngoài ra những kết cấu chi tiết để có thể lắp ráp những vật liệu vào công trình, sẽ luôn đóng góp vào toàn thể vẻ đẹp của công trình.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575819062_4215138f9e50b688c991.png\" style=\"width: 594px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế biệt thự mini</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Điều này càng khẳng định rằng, người thiết kế luôn phải quan sát tỉ mỉ từ mọi chi tiết mà mình tạo ra cho công trình. Đặc biệt hơn nữa là sự phối hợp giữa các vật liệu vào với nhau để đem lại hiệu quả về vẻ đẹp cũng như sự bền vững cho công trình.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trên đây là những điều mà các bạn cần lưu ý khi thiết kiến trúc công trình để tạo ra được kiến trúc đẹp, tiện lợi, độc đáo.</span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 7.5pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial; font-size: 15px; font-family: Calibri, sans-serif; line-height: 17.25px; text-align: justify; background: white;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bạn đang cần đơn vị tư vấn và thiết kế kiến truc cho căn hộ hay nhà phố hay liên hệ ngay công ty thiết kế kiến trúc An Gia Khang để được tư vấn và khảo sát.</span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 7.5pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial; font-size: 15px; font-family: Calibri, sans-serif; line-height: 17.25px; text-align: justify; background: white;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased; color: rgb(51, 51, 51);\">Từ đây, với nền móng vững chắc về con người và giá trị đầu tư cùng với niềm tin được khẳng định qua những dự án lớn - An Gia Khang tự tin vươn xa hơn trong lĩnh vực thi công các công trình cao tầng, biệt thự, nhà phố thuộc các dự án trên toàn quốc.</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 7.5pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial; font-size: 15px; font-family: Calibri, sans-serif; line-height: 17.25px; text-align: justify; background: white;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased; color: rgb(51, 51, 51);\"><strong>&gt;&gt; Xem thêm:</strong> </span></span></span><a href=\"https://www.angiakhang.com/thiet-ke-kien-truc\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(235, 107, 86);\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Công ty thiết kế kiến trúc</span></span></span></a></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 7.5pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial; font-size: 15px; font-family: Calibri, sans-serif; line-height: 17.25px; text-align: justify; background: white;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased; font-size: 14px; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51);\"><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; font-weight: 700; -webkit-font-smoothing: antialiased; color: rgb(56, 56, 56); font-family: geomanist; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Chúng tôi hân hạnh được làm việc với quý khách!</span></strong></span></p>\n', NULL, 1, NULL, 1575817508, 1590065155, NULL),
(206, 'category', 16, 1, '2. Tiêu chuẩn xây dựng nhà hàng 3 sao và 5 sao', '2-tieu-chuan-xay-dung-nha-hang-3-sao-va-5-sao', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Khi thiết kế và xây dựng nhà hàng ăn uống hay nhà hàng tiệc cưới cần có các tiêu chuẩn quy định hay còn goi( quy chuẩn thiết kế nhà hàng) để được xếp hạng và đánh giá theo tiêu chuẩn sao( 3 sao hay 5 sao). </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578990737_f45c8ea1ca9ab3bc552a.png\" style=\"width: 637px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế và xây dựng nhà hàng theo đúng chuẩn</span></span></p>\n<h3><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.1 Tiêu chuẩn thiết kế xây dựng nhà hàng ăn uống</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tiểu chuẩn thiết kế nhà hàng giúp cho KTS và gia chủ cần nắm được quy định, để có thể thiết kế nhà hàng với không gian phân bố hợp lý và hài hòa nhất.</span></span></p>\n<h4><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>a) Không gian khu vực ăn uống</strong></span></span></h4>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây cũng là khu vực quan trọng nhất khi xây dựng nhà hàng. Bạn cần phải đầu tư làm sao để không gian này không những đẹp, độc đáo mang dấu ấn đặc biệt mà đồng thời cần phải tiện nghi, thoải mái. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một không gian đáp ứng hết các tiêu chí này có thể ghi điểm trong mắt khách hàng và khiến họ quay lại mỗi khi có dịp. Do đó bạn đừng xem nhẹ điều này.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575820558_1d7b1167f3431b6d6674.jpg\" style=\"width: 634px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không gian riêng dành cho khách hàng</span></span></p>\n<h4><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>b) Không gian bếp, khu vực chế biến</strong></span></span></h4>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khu vực này có thể tạo cảm hứng cho đầu bếp để tạo nên các món ăn ngon, đẹp mắt. Do đó khi xây dựng nhà hàng cần phải đảm bảo rộng rãi hoặc không quá rộng rãi nhưng thoáng mát để đầu bếp sáng tạo, thăng hoa trong việc chế biến món ăn. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bạn cũng phải tính tới cả những thời gian cao điểm để có thể phục vụ tốt nhất.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575820573_1037d3e6cf70605204dc.jpg\" style=\"width: 643px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Quan tâm đến cảm nhận của khách hàng khi xây dựng nhà hàng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bếp chính là nơi quyết định sự thành công cho nhà hàng do đó cần phải tối ưu một cách triệt để không gian này. Khi thiết kế và xây dựng nhà hàng cần phải chú ý tới các khu vực như kho, nhận hàng, chế biến, chuẩn bị thực phẩm, rửa bát, nấu nướng và không thể thiếu một văn phòng nhỏ quản lý khu vực này.</span></span></p>\n<h4><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>c) Không gian quầy bar tính tiền</strong></span></span></h4>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây là một không gian đòi hỏi vừa phục vụ khách hàng tốt nhất vừa thuận lợi cho nhân viên làm việc. Quầy bar tính tiền cũng là nơi mà khách hàng rút hầu bao phục vụ khách hàng tốt nhất. Đây là địa điểm cuối cùng trước khi khách hàng rời nhà hàng. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Do đó bạn cần phải thiết kế một không gian tạo được sự dễ chịu, thoải mái để lại dấu ấn với khách hàng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578970838_f2925856cda2fd4de021.png\" style=\"width: 615px;\" class=\"fr-fic fr-dib\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà hàng thiết kế đẹp</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vì thế khi thiết kế và xây dựng quầy tính tiền cũng cần phải có tiêu chuẩn nhất định như: khoảng cách giữa 2 tầng ít nhất là 25cm vì còn liên quan tới không gian sắp xếp đồ đạc. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chiều dài thì còn căn cứ vào không gian, quy mô phục vụ của nhà hàng nhưng chiều rộng của mặt quầy sẽ dao động trong khoảng từ 40 đến 60cm.</span></span></p>\n<h4><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>e) Các không gian khác</strong></span></span></h4>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ngoài những không gian cơ bản trên đây khi thiết kế và xây dựng nhà hàng cần phải tính toán các không gian khác như khu trang trí, khu vệ sinh. Tùy vào phong cách, quy mô hay vị trí của nhà hàng mà lựa chọn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575820610_542af2c7f9dbb4c7e206.jpg\" style=\"width: 632px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trên đây chúng tôi vừa giới thiệu đến các bạn những thông tin chi tiết về những lưu ý khi xây dựng nhà hàng. Mong rằng đây là những thông tin hữu ích để bạn có thể xây dựng nhà hàng hiệu quả và chất lượng.</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.2 Tiêu chuẩn thiết kế xây dựng nhà hàng tiệc cưới</strong>   </span></span></h3>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Thiết kế xây dựng nhà hàng tiệc cưới</strong> chủ đầu tư cần có sự nghiên cứu đầu tư cẩn trọng, tỉ mỉ và tinh tế trong từng chi tiết nhỏ, để tạo ra không gia nhà hàng tiệc cưới sang trọng. Từ đó đêm đến một không gian tiệc cưới cho khách hàng thực sự thoải mái, ấm cúm và sang trọng.</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578987169_54c94afca5479e8e02b5.png\" style=\"width: 633px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế không gian nhà hàng tiệc cưới</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trong quá trình thiết kế KTS thiết kế phải có sự kết hợp hài hòa giữa rất nhiều yếu tố như: bày trí nội thất, sự đồng bộ về màu sắc, cảnh quan đẹp mắt, tạo ra không gian ấm cúm sang trọng…</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây cũng là lý do vì sao cần có các tiêu chuẩn thiết kế nhà hàng tiệc cưới hiện nay để phân loại ra các tiêu chuẩn 3 sao hay 5 sao cho các nhà hàng tiệc cưới.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xin chia sẽ với các bạn 5 tiêu chuẩn đánh giá qua  tọng khi thiết kế nhà hàng tiệc cưới cần quan tâm nhé.</span></span></p>\n<h4><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>a) Yêu cầu về sảnh tiệc cưới nhà hàng</strong></span></span></h4>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tiêu chí quan trọng khi thiết kế nhà hàng tiệc cưới là sảnh nhà hàng, phải đáp ứng được các yêu cầu về số lượng khách hàng, vì vây là khu vực trung tâm của nhà hàng, nơi sẽ diễn ra tổ chức các buổi lễ lớn do đó, sảnh nhà hàng phải đủ chỗ ngồi, diện tích đủ lớn, đủ rộng,…</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578987230_eb1ddaa39a075ef8cdd2.png\" style=\"width: 580px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Màu tím kết hợp màu ánh sáng tạo không gian huyền bí</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trong quá trình thiết kế, KTS cần phải tính đến khoảng cách giữa các bàn phải phù hợp và đủ rộng để tiện lợi cho việc đi lại của khách hàng và nhân viên phục vụ.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trang bị đầy đủ thiết bị như máy lạnh, quạt thông gió, máy hút không khí , khi tình trạng đông khách…</span></span></p>\n<h4><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>b) Đảm bảo sự riêng tư và kín đáo</strong></span></span></h4>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây cũng là tiêu chuẩn quan trọng, nên chú trọng vào sự riêng tư với việc thiết kế hệ thống cách âm hiện đại, đảm bảo vị trí tiệc cưới của khách hàng không bị ảnh hưởng bởi các tiếng ồn do âm thanh, ánh sáng,.. từ tiệc cưới ra bên ngoài.</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578987286_3203d8d07e8ebc8b697c.png\" style=\"width: 601px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hệ thống cách âm tốt</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu số lượng tầng thiết kế lớn: Nên có sự bố trí nhân viên hướng dẫn và thiết kế bảng hiệu hướng dẫn cho khách hàng biết đến từ khu vực tổ chức.</span></span></p>\n<h4><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>c) Về kiến trúc nội thất.</strong></span></span></h4>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà hàng tiệc cưới tổ chức tiệc cưới trọng đại của một đời người, vì vậy khâu thiết kế và trang trí cần tạo được sự sang trọng và tinh tế. Thiết kế mẫu nhà hàng tiệc cưới thể hiện được sự lãng mạn chuyên nghiệp và chu đáo để tạo được sự tin tưởng, lòng thiện cảm đối với khách hàng.</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578987388_f4fef59c9c6285fd7c87.png\" style=\"width: 612px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nội thất sang trọng</span></span></p>\n<h4><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>d) Âm thanh ánh sáng</strong></span></span></h4>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế dàn dựng âm thanh, ánh sáng chuyên nghiệp, tạo ra không gian sống động cho buổi tổ chức đám cưới. Phải có sự đồng bộ giữa âm thanh và màu sắc ánh sáng .Tạo ra bầu không khí vui tươi và đầm thắm.</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578987444_10afae2fbbcd3ebbc0d6.png\" style=\"width: 611px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Màu sắc hài hòa</span></span></p>\n<h4><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>e) Yêu tố về phong thủy</strong></span></span></h4>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ngoài việc xây dựng, thiết kế nhà hàng tiệc cưới sang trọng và tinh tế. Thì yêu tố về phong thủy cũng rất quan trọng cho chủ đầu tư dự án. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Về yếu tố này, chủ đầu tư nên có sự tham khảo các chuyên gia về phong thủy, xem về hướng nhà hàng, màu sắc, ngày khởi công…. Để đảm bảo cho nhà hàng có được sự máy mắn luôn luôn phát triển bền vững.</span></span></p>\n<h4><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>f) 10 mẫu nội thất nhà hàng tiệc cưới đẹp</strong></span></span></h4>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578987534_e9a56fc5a9473e0ace41.png\" style=\"width: 594px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mầu nhà hàng sử dụng màu tím </span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578987565_3e3f8bb37501d8cfdd03.png\" style=\"width: 599px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Màu chủ đạo màu trắng</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578987586_ef2018b42f6eb33ed8f6.png\" style=\"width: 614px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Màu gỗ sang trọng</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578987604_19b854c5b53cc80185ff.png\" style=\"width: 600px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Màu đỏ chủ đảo</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578987618_b720c353e1bd2d8eda5c.png\" style=\"width: 606px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Màu vàng chủ đạo</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578987632_03ed15d70964e4652876.png\" style=\"width: 611px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mầu nhà hàng diamond Place</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578987666_ab3f1e7dfa370079dbf2.png\" style=\"width: 582px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không gian đẹp</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578987728_07abfb1a6fcc763493f0.png\" style=\"width: 579px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Màu vàng sang trọng</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578987770_0071ecced175dfcf6415.png\" style=\"width: 584px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578987794_97157a029907727648d6.png\" style=\"width: 593px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Thiết kế không gian nhà hàng sang trọng</span></p>\n', NULL, 1, NULL, 1575820329, 1590733726, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(208, 'category', 13, 1, '2.Quy trình thi công xây dựng', '2quy-trinh-thi-cong-xay-dung', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><img src=\"/uploads/contents/1576024056_7c95e74e8906c7f5a17f.jpg\" style=\"width: 589px;\" class=\"fr-fic fr-dii\"></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chuẩn bị san lắp mặt bằng</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.1. Quy trình triển khai thi công xây dựng:</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giai đoạn chuẩn bị.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thông báo khởi công thi công xây dựng công trình đến chính quyền địa phương bằng văn bản, thông báo cho các hộ dân kế cận, chụp hình hiện trạng công trình kế cần. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giai đoạn chuẩn bị thi công xây dựng</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chuẩn bị mặt bằng, mượn vỉa hè, chuẩn bị nguồn điện, nguồn nước cho thi công.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Lắp đặt cổng, tường rào công trình theo đúng tiêu chuẩn.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chuẩn bị bộ hồ sơ thiết kế xây dựng làm căn cứ kỹ thuật để thi công.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Định vị công trình, xác định cao độ chuẩn.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chuẩn bị nhân công, chuẩn bị quy trình cung ứng vật tư phần thô.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giai đoạn thi công móng và công trình ngầm.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đào đất hố móng, đổ bê tông lót móng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Lắp dựng ván khuôn, cốt thép, đổ bê tong móng, đà kiêng, đà giằng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đào đất, xây hầm phân, hố ga, hầm chứa bồn nước âm bằng gạch thẻ.</span></span></li>\n</ul>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giai đoạn thi công phần thô và mái. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thi công phần thô của công trình xây dựng</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Lắp dựng cột thép cột, lắp dựng ván khuôn cột, đổ bê tông cột.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây tường bao che, che chăn công trình theo kỹ thuật.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Lắp đặt hệ thống đường ống kỹ thuật đi âm sàn.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đổ bê tông dầm, sàn.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây dựng ngăn, thi công cầu thang.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trát trần, trát tường trong, tường ngoài nhà.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xử lý chống thấm các ô sàn âm và sàn nằm lộ thiên.</span></span></li>\n</ul>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giai đoạn hoàn thiện công trình.</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sơn bả trần, tường trong và ngoài nhà, sơn cửa.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ốp lát gạch, đá trang trí…</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Lắp đặt cửa, lan can…</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Lắp đặt đèn chiếu sáng, điện, nước.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vệ sinh công trình, sửa lỗi hoàn thiện.</span></span></li>\n</ul>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong> 2.2. Quy trình bảo hành, bảo trì.</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1576024104_680e2965b19e6a22f35b.jpg\" style=\"width: 611px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công trình xây dựng khi đang hoàn thiện</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thời gian bảo hành kỹ thuật sẽ được tính từ lúc hoàn thành công trình, tùy theo cam kết trong hợp đồng ban đầu. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Trong thời gian bảo hành nếu có vấn đề hỏng hóc lỗi kỹ thuật thi công, nhà thầu sẽ cử người xuống khảo sát tại công trình, xác định nguyên nhân và đưa ra phương án sửa chữa</span></p>\n', NULL, 1, NULL, 1576024209, 1590064816, NULL),
(209, 'category', 13, 2, '3.Các giải pháp kỹ thuật chính cho biện pháp thi công xây dựng.', '3cac-giai-phap-ky-thuat-chinh-cho-bien-phap-thi-cong-xay-dung', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"color: rgb(65, 65, 65); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important;\">Trong quá trình thi công xây dựng, nhà thầu sẽ thường xuyên bố trí cán bộ kỹ thuật kiểm tra hiện trường, xác định vụ trí trên bản vẽ thiết kế với hiện trường các hạng mục công trình cũng như tổng thể công trình trong quá trình thi công xây dựng.</span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576024184_0b52fedaae67f1c0589f.jpg\" style=\"width: 585px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giải pháp thi công hiệu quả</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.1. Định vị công trình</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cán bộ này sẽ ghi chép đầy đủ vào sổ nhật ký thi công hàng ngày, làm cơ sở cho công tác thi công, kiểm tra và nghiệm thu được đảm bảo chính xác nhất.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576024485_21f38211538263dbd547.png\" style=\"width: 579px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Định vị công trình xây dựng</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.2. Biện pháp kỹ thuật thi công xây dựng. </strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Để đảm bảo chất lượng và tiến độ cho quá trình thi công xây dựng một công trình, thì nhà thầu cần thực hiện được những điều sau:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">• Toàn bộ nhân viên kỹ thuật, công nhân tham gia thi công xây dựng đều được đào tạo bài bản, tốt nghiệp tại các trường nghiệp vụ chuyên ngành và có chứng chỉ chuyên môn phù hợp với quy chuẩn hiện hành của nhà nước.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">• Trước khi công nhân ra thi công xây dựng tại hiện trường, nhà thầu phải tiến hành tổ chức đào tạo, sát hạch, phân loại lao động đối với từng cá nhân và nhóm các nhân cụ thể. Trang bị đầy đủ những kiến thức và thiết bị an toàn lao động. Đối với công nhân lao động phổ thông sẽ được nhà thầu chú trọng hơn trong quản lý và hướng dẫn thi công.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576024564_d0aad1496605f73cee0b.jpg\" style=\"width: 577px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Dự án đang dần hoàn thiện</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">• Nhà thầu phải đáp ứng đầy đủ các nghĩa vụ như: thuế, bảo hiểm xã hội, vệ sinh môi trường.... Những loại chi phí phát sinh khác trong quá trình thi công như đền bù, khắc phục hậu quả do thi công sẽ được thực hiện đúng theo quy định.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">• Nhà thầu chủ động sẽ chủ động tiến hành đề xuất kịp thời với chủ đầu tư về các thông tin cần thiết trong việc chuẩn bị và triển khai thi công.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">• Các tổ đội thi công và bên liên quan phải thường xuyên trao đổi để thống nhất phương án kỹ thuật, tiến độ thi công, tháo gỡ vướng mắc... Điều này giúp hạn chế tới mức thấp nhất việc thi công chồng chéo, đục phá làm ảnh hưởng đến chất lượng và tiến độ công trình.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576024758_30d0baa28b295d76fc18.png\" style=\"width: 592px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đội ngũ thi công xây dựng An Gia Khang</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">• Sau khi chính thức được thông báo trúng thầu, nhà thầu sẽ tiến hành triển khai thiết kế thi công và chuyển hồ sơ thi công cho phía chủ đầu tư và tư vấn thiết kế, ký duyệt để làm cơ sở định hướng thi công xây dựng. Những sai lệch hay có sự thay đổi so với hồ sơ thi công ban đầu đều phải được chủ đầu tư, tư vấn thiết kế trực tiếp quyết định kèm theo xác nhận chi tiết trong nhật ký thi công.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">• Kết thúc công việc thi công toàn bộ công trình, căn cứ vào biên bản nghiệm thu công trình, hồ sơ hoàn công, nhật ký thi công, biên bản làm việc phát sinh... phía nhà thầu sẽ thực hiện những công đoạn tiếp theo: theo dõi, hiệu chỉnh, chạy thử, quyết toán bàn giao, bảo hành, bảo trì theo đúng những điều khoản ghi trong hợp đồng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576024794_f7e079337765ecf1e843.png\" style=\"width: 601px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công trình xây dựng An Gia Khang</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.3. Giải pháp cấp điện, trang bị cứu hỏa phục vụ thi công xây dựng.</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Để đảm bảo cho thi công được thuận lợi, cần đảm bảo cấp điện liên tục và trang bị cứu hóa sản sàng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576024842_ecf406def866016468db.png\" style=\"width: 587px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công ty xây dựng An Gia Khang</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nguồn cấp điện thi công được lấy từ 2 nguồn:</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nguồn cấp điện của hội trường, hệ thống điện 3 pha từ tủ tổng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nguồn cấp điện của máy phát điện dự phòng, nguồn điện do máy tạo ra - điện 3 pha.</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tất cả các tủ điện thi công phải có độ chịu ẩm (IP 54) bảo đảm hoạt động an toàn trong cả những điều kiện ẩm ướt của công trình (nếu có), vỏ tủ bắt buộc nối đất bằng dây đồng trên 4,0 mm - các ổ cắm 3P &amp; 2P cấp điện cho máy công cụ đều được dấu sau aptomat chống dòng chạm đất. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Từ vị trí tủ trung tâm các cáp điện loại + dây tiếp địa được lồng trong ống PVC đi nổi sát tường nhà tới vị trí phụ tải.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576024881_93cc3ee2834f4fe6df3e.png\" style=\"width: 611px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">từng bước hoàn thành mục tiêu</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trong trường hợp cáp bắt buộc đi ngầm sàn qua trục giao thông việc lồng cáp được thực hiện bằng ống thép bảo đảm chịu được lực cơ học tác động. Việc cấp điện thi công được thực hiện từ cáp trục chính sẽ được đi từ tổng tới vị trí đấu nối.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Để đảm bảo an toàn và tránh những sự cố điện gây ra làm thiệt hại cho người và thiết bị, thì việc thi công điện tại công trình cần lưu ý:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576024958_49c6e830caee05e0840b.jpg\" style=\"width: 605px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chú ý trong quá trình xây dựng dự án</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các công nhân và cán bộ kỹ thuật phải được trang bị đầy đủ dụng cụ chuyên dụng, trang bị bảo hộ theo đúng đặc điểm công việc đã được giao.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tất cả công nhân điện nhất thiết phải qua một lớp huấn luyện về an toàn điện theo đúng tiêu chuẩn và có biên bản xác nhận.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các cán bộ kỹ thuật là người trực tiếp kiểm tra và chịu trách nhiệm về kỹ thuật và an toàn.</span></span></li>\n</ul>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.4. Biện pháp thi công các hạng mục.</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Để đảm bảo tiến độ công trình và chất lượng công trình thì nhà thầu sẽ tiến hành hoàn chỉnh về các mặt kỹ thuật và phương diện pháp lý để kịp thời đưa cho các bên nghiên cứu tỉ mỉ trước khi chính thức triển khai thi công.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576024993_bbbac96ee3e5f0a74adc.png\" style=\"width: 607px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các hạng mục đang dần hoàn thiện</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu sẽ chủ động chuẩn bị tập kết tại chân công trình với một số đặc điểm phù hợp với yêu cầu trong hồ sơ mời thầu đưa ra cũng như những thay đổi được đề xuất từ phía chủ đầu tư cùng các thay đổi của nhà thầu đã đề xuất và được chủ đầu tư chấp thuận với một số những lưu ý như:</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các chủng loại vật tư được nhập về công trình theo đúng như cầu của từng giai đoạn thi công do đội trưởng thi công chỉ định. KHông tiến hành nhập vật tư ồ ạt gây khó khăn trong việc thi công và bảo quản các nguyên vật tư, trang thiết bị máy móc.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Toàn bộ các chủng loại vật tư được sử dụng thi công là các loại vật tư đặc chủng nên sẽ được nhà thầu bảo quản cẩn thận, tránh nhưng tác động xấu của môi trường như mưa, ẩm, nhiệt, hóa chất, côn trùng, vi khuẩn và va đập cơ học...</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576025018_dcc978f10d712f1fb944.jpg\" style=\"width: 579px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Những ngôi nhà hoàn thiện khang trang</span></span></p>\n<ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Những vật tư khi được đưa về nhập kho tại công trình đều được kiểm tra về mẫu mã, nhãn mác, ký hiệu, xuất sư, niên hạn xuất xưởng... theo đúng nội dung đã ghi trong hồ sơ thầu.</span></span></li></ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ngoài việc kiểm tra về số lượng thì thủ kho, cán bộ kỹ thuật còn phải bóc mỗi kiện hàng mẫu chuyển lên ban quản lý dự án để làm mẫu đối chiếu trong suốt thời gain thi công.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576025062_de441ae41ffd3cac21e7.png\" style=\"width: 581px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công ty xây dựng An Gia Khang</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu vật tư không đảm bảo chất lượng hoặc có sai khác về quy cách, chủng loại... sẽ phải lập biên bản để thông báo với các bên liên quan để có biện pháp thay thế kịp thời, tránh ảnh hưởng đến tiến độ thi công, chất lượng công trình xây dựng.</span></span></p>\n<p><strong><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">&gt;&gt; Xem thêm: </span></strong><a href=\"https://www.angiakhang.com/nang-luc-nha-thau\"><span style=\"color: rgb(235, 107, 86); font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Năng lược nhà thầu An Gia Khang</span></a></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><strong style=\"box-sizing: border-box; font-weight: 700; color: rgb(56, 56, 56); font-family: geomanist; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Chúng tôi hân hạnh được làm việc với quý khách!</span></strong></p>\n', NULL, 1, NULL, 1576024209, 1590064816, NULL),
(212, 'news', 9, 0, '1. Top 5 nhà thầu xây dựng lớn nhất thế giới', '1-top-5-nha-thau-xay-dung-lon-nhat-the-gioi', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Dưới đây chúng tôi giới thiệu đến các bạn danh sách 10 nhà thầu có doanh thu cao nhất thế giới, đặc biệt được xếp hạng ở top 250 ENR</span></p>', NULL, 1, NULL, 1576121487, 1590679626, NULL),
(213, 'news', 9, 1, '1.1 China State Construction Engineering Corporation (Trung Quốc)', '11-china-state-construction-engineering-corporation-trung-quoc', '<ul>\n<li><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nhà thầu có tổng doanh thu 115,1 tỷ USD</span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Doanh thu quốc tế đạt tới 8,7 USD.</span></span></li>\n</ul>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576124748_0212255c2ee00c9c374d.jpg\" style=\"width: 616px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng China State Construction Engineering Corporation</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ở thị trường nội địa, những dự án nổi bật vào năm 2015 giúp cho doanh thu của China State Construction Engineering Corporation tăng lên con số đáng kể. Điển hình là các công trình như: </span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Dự án Nhà máy Điều chế Etylen ở Phủ Thuận, thuộc tỉnh Liêu Ninh (626 triệu USD), Tổ hợp giải trí Zhengzhou Jinrong International Plaza (1,5 tỷ USD), </span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Dự án Nhà máy Lọc hóa dầu tại Dinh Khẩu, tỉnh Liêu Ninh (220 triệu USD),  hay dự án Xây dựng Raffles City tại Trùng Khánh (460 triệu USD) Tổ hợp trung tâm thương mại và cao ốc Wanda ONE Nine Elms tại London (637 triệu USD),</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Dự án Xây dựng tòa cao ốc làm trụ sở cho Ngân hàng thương mại của Ethiopia (266,5 triệu USD).</span></span></li>\n</ul>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nói chung doanh thu của nhà thầu này chủ yếu đến từ các dự án xây dựng tổ hợp trung tâm thương mại hay cao ốc ( khoảng 79 dự án).  </span></p>\n', NULL, 1, NULL, 1576121487, 1590679626, NULL),
(214, 'news', 9, 2, '1.2 China Railway Group (Trung Quốc)', '12-china-railway-group-trung-quoc', '<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Tổng doanh thu: 112,7 tỷ USD</span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Doanh thu quốc tế: 6 tỷ USD</span></span></li>\n</ul>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không giống như China State Construction Engineering Corporation những dự án mà China Railway Group đảm nhận phần lời là dự án xây dựng hệ thống giao thông vận tải gồm có 61 dự án. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576125359_42ad38f3ae66736a0891.png\" style=\"width: 647px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng China Railway Group </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trong số này có một dự án lớn không những mang về kinh tế mà nó còn có ý nghĩa chính trị đó chính là dự án xây dựng tuyến đường sắt cao tốc nối giữa thủ đô Moscow với Trung Quốc. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trong năm 2015 China Railway Group cũng trúng thầu một dự án khác ở Hungary với giá trị lên tới 1,57 tỷ USD.  </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vào giữa năm 2016 China Railway Group gặp phải một rắc rối khi Mỹ hủy hợp đồng của dự án xây dựng đường sắt cao tốc Las Vegas- Los Angeles với tổng giá trị lên tới 5 tỷ USD. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chính vì điều này mà cổ phiếu của China Railway Group bị giảm mạnh. Thế nhưng vị Phó Chủ tịch của China Railway Group vẫn tự tin khẳng định việc những nhà thầu Trung Quốc xây dựng các dự án đường sắt tại nước ngoài vẫn là một xu hướng. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Bằng chứng cụ thể chính là chỉ trong 6 tháng đầu năm 2016 lợi nhuận thu được của công ty đã tăng lên 14%.</span></p>\n', NULL, 1, NULL, 1576121487, 1590679626, NULL),
(217, 'news', 9, 3, ' 1.3 Nhà thầu xây dựng Vinci (Pháp)', '13-nha-thau-xay-dung-vinci-phap', '<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Tổng doanh thu: 43,5 tỷ USD</span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Doanh thu quốc tế: 18 tỷ USD</span></span></li>\n</ul>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây cũng là nhà thầu lớn nhất ở châu Âu. Tập đoàn Vinci chủ yếu hoạt động ở nhiều lĩnh vực từ xây dựng cao ốc, giao thông vận tải và cả các nhà máy năng lượng. Đặc biệt không thể bỏ qua 8 dự án hạ tầng viễn thông, cũng là lĩnh vực mà không nhà thầu Trung Quốc nào có cơ hội đạt được.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576125472_1b1fe3406c8d409d0675.jpg\" style=\"width: 558px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng Vinci</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"> Đối tác chính của Vinci là Mỹ cùng một số nước châu Âu. Riêng ở Việt Nam tập đoàn này đã ký kết với Tổng công ty Đầu tư và Phát triển đường cao tốc Việt Nam (VEC) về hợp đồng nhượng quyền khai thác hai dự án là cao tốc Long Thành- Dầu Giây và cao tốc Cầu Giẽ- Ninh Bình, đánh dấu bước phát triển chiến lược về lĩnh vực kinh tế giữa Việt Nam với Pháp.</span></p>\n', NULL, 1, NULL, 1576121487, 1590679626, NULL),
(219, 'news', 9, 4, '1.4 ACS, Actividades De Construction Y Servicios SA (Tây Ban Nha)', '14-acs-actividades-de-construction-y-servicios-sa-tay-ban-nha', '<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Tổng doanh thu: 38,6 tỷ USD</span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Doanh thu quốc tế: 32,1 tỷ USD</span></span></li>\n</ul>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhờ có 20 năm trong ngành ÁC cũng là nhà thầu có doanh thu quốc tế lớn nhất ở top này. Mảng doanh thu quốc tế của đơn vị này chiếm đến 83% tổng doanh thu của công ty. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576125601_ac658c58df2a28e664e5.jpg\" style=\"width: 588px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng ACS</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Đây cũng là nhà thầu từng tham gia vào nhiều lĩnh vực nhất trong top mà chúng tôi giới thiệu ở đây. Các hạng mục mà nhà thầu sở hữu gồm có nhà máy sản xuất, xây cao ốc, thủy điện, năng lượng cho tới cả viễn thông, giao thông vận tải.</span></p>\n', NULL, 1, NULL, 1576121487, 1590679626, NULL),
(221, 'news', 9, 5, '1.5 Nhà thầu xây dựng Bouygues Sa (Pháp)', '15-nha-thau-xay-dung-bouygues-sa-phap', '<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Tổng doanh thu: 28,2 tỷ USD</span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Doanh thu quốc tế: 13,4 tỷ USD </span></span></li>\n</ul>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Doanh thu của nhà thầu này phần lớn đến từ xây dựng hệ thống giao thông vận tải với 48 dự án và xây dựng cao ốc với 32 dự án. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576125714_94d859901d092f01ae18.jpg\" style=\"width: 529px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng Bouygues Sa (Pháp)</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Trong thời gian qua Bouygues Sa cũng không ngừng đẩy mạnh, tập trung vào nhiều dự án ở khu vực châu Á- Thái Bình Dương như Singapore, Hồng Kong, Myanmar, Thái Lan, Hàn Quốc, Australia và Việt Nam.</span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Bạn đọc có thể xem thêm: </span><a href=\"https://www.angiakhang.com/tieu-chuan-xay-dung-khach-san-5-sao\">Tiêu chuẩn xây dựng khách sạn 5 sao</a></p>\n', NULL, 1, NULL, 1576121487, 1590679626, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(223, 'news', 9, 6, '2. Top 10 nhà thầu xây dựng uy tín tại Việt Nam', '2-top-10-nha-thau-xay-dung-uy-tin-tai-viet-nam', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Vietnam Report từng công bố 15 nhà thầu xây dựng uy tín của ngành xây dựng năm 2019. Đánh giá này căn cứ vào nhiều kết quả nghiên cứu khách quan và khoa học. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576126846_bfd82d6b86d2fbc0c869.png\" style=\"width: 638px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng An Gia Khang</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu có vai trò không nhỏ quyết định tới chất lượng cũng như tiến độ của công trình. Các căn hộ, tòa cao ốc, khu nghỉ dưỡng hay khu biệt thự đều phải được xây dựng để đáp ứng nhu cầu từng bước tăng lên của xã hội. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Chính vì thế nhà thầu cũng cần cải thiện và nâng cao khả năng hoạt động và năng lực xây dựng để mang tới những công trình tốt nhất. Cụ thể là top 15 nhà thầu xây dựng uy tín ở Việt Nam như:</span></p>\n', NULL, 1, NULL, 1576121487, 1590679626, NULL),
(224, 'news', 9, 7, '2.1 Nhà thầu xây dựng Coteccons', '21-nha-thau-xay-dung-coteccons', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Được thành lập vào năm 2004 có trụ sở ở TP Hồ Chí Minh. Đây là một trong những đơn vị top đầu những nhà thầu xây dựng uy tín năm 2019.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576126100_d9f1c308899e5c854f84.jpg\" style=\"width: 572px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng cotecons</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Coteccons từng thi công nhiều dự án thành công như: Vinhomes Times City Park Hill, Vinhomes Thăng Long, Condotel Đà Nẵng, Aeon Mall Long Biên,Tràng An Complex, StarCity Nha Trang, Masteri Thảo Điền, .....Coteccons đã thể hiện được sự uy tín và kinh nghiệm dày dặn cùng trách nhiệm to lớn trong mỗi hoạt động xây dựng.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Các hoạt động của Coteccons đều được minh bạch và công khai với nguồn nhân lực ở cả trong và ngoài nước với kinh nghiệm nhiều năm năng lực sáng tạo cao và rất nhiều hoạt động có trách nhiệm xã hội mang tới cho đơn vị này vô số giải thưởng cùng danh hiệu thi đua xuất sắc.</span></p>\n', NULL, 1, NULL, 1576121487, 1590679626, NULL),
(225, 'news', 9, 8, '2.2 Nhà thầu xây dựng Hòa Bình Corporation', '22-nha-thau-xay-dung-hoa-binh-corporation', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nhờ có hơn 30 năm kinh nghiệm trong lĩnh vực kinh doanh bất động sản và xây dựng Hòa Bình Corporation liên tiếp nằm trong danh sách top 15 nhà thầu xây dựng vào năm 2018.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1576126166_78ab35500cf87fc2cfbe.jpg\" style=\"width: 556px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng Hòa Bình Corporation</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Những năm qua đơn vị này cũng từng hợp tác hoạt động với nhiều chủ đầu tư uy tín, trở thành nhà thầu chính ở nhiều dự án lớn như: Chung Cư Phú Mỹ, Khách Sạn La Sapinette &amp; TTTM Langbiang, CoCoBay Đà Nẵng, Imperia Garden, JW Marriott Hanoi ,...</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Các thiết bị xây dựng và máy móc của Hòa Bình Corporation được chú trọng đầu tư. Cùng với đội ngũ các kỹ sư nhiều kinh nghiệm nên tiến độ của các dự án luôn được đảm bảo và nâng cao uy tín cũng như thương hiệu của đơn vị này trong kinh doanh bất động sản và xây dựng.</span></p>\n', NULL, 1, NULL, 1576121487, 1590679626, NULL),
(226, 'news', 9, 9, '2.3 Nhà thầu xây dựng Unicons', '23-nha-thau-xay-dung-unicons', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Đơn vị này được thành lập vào năm 2006 cũng là công ty thành viên nòng cốt trực thuộc Coteccons Group. Với hơn 12 năm hình thành và phát triển Unicons cũng có được những bước tiến vững chắc với tốc độ tăng trưởng doanh thu đáng nể. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576126233_c65c69d320b95327c249.jpg\" style=\"width: 538px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">2.3 Nhà thầu xây dựng Unicons</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Năm 2017 doanh thu của Unicons vượt mức 7000 tỷ đồng và là một trong 15 tổng thầu xây dựng uy tín nhất trên thị trường.</span></p>\n', NULL, 1, NULL, 1576121487, 1590679626, NULL),
(227, 'news', 9, 10, ' 2.4 Nhà thầu xây dựng Vinaconex', '24-nha-thau-xay-dung-vinaconex', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Năm 2018 Vinaconex ký kết nhiều hợp đồng xây lắp mới với giá trị lên đến hơn 3.900 tỷ đồng đó là: gói thầu 14 Cao tốc Đà Nẵng – Quảng Ngãi, Sunrise Bay Đà Nẵng, các công trình Mapletree Bắc Ninh, Bình Dương, dự án 21 Lê Văn Lương, dự án Mikazuki Đà nẵng, tòa nhà Viettel Quảng Ninh. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576126299_699acca6ee3b2e88fc84.jpg\" style=\"width: 497px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng Vinaconex</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Hiện nay tổng công ty cũng đang tiếp tục đàm phán các hợp đồng có giá trị lớn.</span></p>\n', NULL, 1, NULL, 1576121487, 1590679626, NULL),
(228, 'news', 9, 11, '2.5  Nhà thầu xây dựng Phục Hưng Holdings', '25-nha-thau-xay-dung-phuc-hung-holdings', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Được thành lập từ năm 2001 đơn vị này đã vươn lên và đứng vị trí thứ 3 trong danh sách những nhà thầu uy tín của Việt Nam sau 17 năm hoạt động ở lĩnh vực này. Có thể kể tới một vài công trình mà đơn vị này góp mặt như: Nhà máy Xi măng Tam Điệp, Golden Land, Nhà máy Xi măng Sông Thao, Florence...</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576126352_4dcebfcc48a41d54e659.jpg\" style=\"width: 569px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng Phục Hưng Holdings</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nhờ việc liên tiếp trúng các gói thầu và giành được các hợp đồng có giá trị khiến cho Phục Hưng Holdings giữ vững uy tín của mình và khẳng định thương hiệu với các khách hàng, đồng thời đạt được sự tín nhiệm của nhiều chủ đầu tư lớn.</span></p>\n', NULL, 1, NULL, 1576121487, 1590679626, NULL),
(229, 'news', 9, 12, '2.6  Nhà thầu xây dựng Ricons', '26-nha-thau-xay-dung-ricons', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Được thành lập từ năm 2004 và hoạt động chủ yếu trong lĩnh vực đầu tư, kinh doanh bất động sản với hai dự án nổi bật là Saigon Pavillon và Botanic Towers. Cho tới năm 2008 Ricons mở rộng cả lĩnh vực sang hoạt động xây lắp.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576126418_74232ab0a3f5230c5638.jpg\" style=\"width: 581px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng Ricons</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cũng trực thuộc Coteccons Group, Ricons được thừa hưởng giá trị tinh túy từ tập đoàn xây dựng hàng đầu Việt Nam này. Trải qua hơn 12 năm phát triển đơn vị này từng bước khẳng định uy tín và thương hiệu của mình. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nhiều dự án thiết kế xây dựng và quản lý, thi công công trình khách sạn, nhà cao tầng, khu nghỉ dưỡng, khách sạn hay nhà xưởng với quy mô lớn. Giá trị các hợp đồng lên tới vài nghìn tỷ đồng.</span></p>\n', NULL, 1, NULL, 1576121487, 1590679626, NULL),
(230, 'news', 9, 13, ' 2.7 Nhà thầu xây dựng Newtecons', '27-nha-thau-xay-dung-newtecons', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Newtecons ra đời vào năm 2003 tiền thân của nó chính là FDC. Trong bối cảnh mà nhiều công trình lớn đòi hỏi kỹ thuật cao đều do các đơn vị nước ngoài đảm nhận. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576126478_de448b06404e7dc646fa.jpg\" style=\"width: 571px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng Newtecons</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các thành viên trong ban lãnh đạo đã trăn trở làm sao để có được năng lực đảm nhận công trình lớn do người Việt làm chủ. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">FDC ra đời từ đó và hoạt động đến nay. Đây cũng là đơn vị thi công uy tín hoạt động ở một số lĩnh vực thi công xây dựng với các loại hình công trình đa dạng. Từ các khu căn hộ cao cấp đến trung tâm thương mại, dự án cao ốc văn phòng, dự án hạ tầng hay nhà xưởng sản xuất hiện đại.</span></p>\n', NULL, 1, NULL, 1576121487, 1590679626, NULL),
(231, 'news', 9, 14, '2.8 Nhà thầu xây dựng Fecon', '28-nha-thau-xay-dung-fecon', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Thành lập từ năm 2004 đây là đơn vị hàng đầu trong mảng thi công nền móng công trình ở Việt Nam. Các dự án Fecon tham gia đều có chất lượng đảm bảo và an toàn như: khu phức hợp nghỉ dưỡng , casino, sân golf HOIANA, đường nối cầu Tân Vũ – Lạch Huyện, nhà máy LG Hải Phòng,….</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576126535_db5b7d770b05d25249db.jpg\" style=\"width: 542px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nhà thầu xây dựng Fecon</span></p>\n', NULL, 1, NULL, 1576121487, 1590679626, NULL),
(232, 'news', 9, 15, '2.9 Nhà thầu xây dựng Delta', '29-nha-thau-xay-dung-delta', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Ra đời từ năm 1993 với nhân sự chưa tới 100 người nhưng tới nay đơn vị này được đánh giá là một trong các công ty xây dựng hàng đầu của Việt Nam với đội ngũ các chuyên gia trong ngành nhiều kinh nghiệm, đội ngũ nhân lực có trình độ cao và trách nhiệm với nghề nghiệp.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576126583_935f390ba34e60bb2352.jpg\" style=\"width: 580px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nhà thầu xây dựng Delta</span></p>\n', NULL, 1, NULL, 1576121487, 1590679626, NULL),
(233, 'news', 9, 16, ' 2.10 Nhà thầu xây dựng UDIC', '210-nha-thau-xay-dung-udic', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"> Thành lập từ năm 1971 tính đến nay đây là đơn vị tên tuổi và uy tín trong số top 15 nhà thầu xây dựng uy tín ở Việt Nam.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576126649_1810e550849126b83bd6.jpg\" style=\"width: 557px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng UDIC</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"> Có thể kể đến một số dự án có sự góp mặt của UDIC đó là: Trung tâm đào tạo vận động viên cấp cao Hà Nội, Chung cư cao tầng CT1 – tòa nhà D, E – Ciputra, Trụ sở bộ Ngoại giao, Khu đô thị mới Yên Hòa, Trung tâm thương mại và Khu căn hộ cao cấp Golden Palace,</span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><strong>Bạn đọc có thể xem thêm:</strong> </span><a href=\"https://www.angiakhang.com/xay-nha-cao-tang\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><strong><span style=\"color: rgb(41, 105, 176);\">Kinh nghiệm xây dựng nhà cao tầng</span></strong></span></a></p>\n', NULL, 1, NULL, 1576121487, 1590679626, NULL),
(239, 'news', 9, 17, '3. Nhà thầu xây dựng An Gia Khang', '3-nha-thau-xay-dung-an-gia-khang', '<p style=\'margin:0in;margin-bottom:10.0pt;font-size:13px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;line-height:150%;\'><span style=\"font-size: 14px; line-height: 150%; font-family: Arial, Helvetica, sans-serif; color: black;\">Chúng tôi luôn tự hào là một trong những <strong>nhà thầu xây dựng </strong>uy tín, chuyên nghiệp nhất tại tphcm, luôn mang đến sự hài lòng cho tất cả các quý khách hàng dù là khách hàng khó tính nhất.</span></p>\n<p style=\"margin: 0in 0in 10pt; font-size: 13px; font-family: Calibri, sans-serif; text-align: center; line-height: 150%;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\"><img src=\"/uploads/contents/1576127283_52ee526ab39bbc4eb5f3.jpg\" style=\"width: 607px;\" class=\"fr-fic fr-dib\"></span> <br></span></span></p>\n<p style=\"margin: 0in 0in 10pt; font-size: 13px; font-family: Calibri, sans-serif; text-align: center; line-height: 150%;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công ty xây dựng An Gia Khang</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">Công ty An Gia Khang tự tin là đơn vị thầu xây dựng chuyên thiết kế, thi công xây dựng các công trình dân dụng và công nghiệp uy tín nhất Phú Quốc.</span></span></span></p>\n<p style=\'margin:0in;margin-bottom:10.0pt;font-size:13px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;line-height:150%;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">Chúng tôi sở hữu đội ngũ kỹ sư, kiến trúc sư giàu kinh nghiệm và chuyên nghiệp đảm b</span><span style=\"line-height: 150%;\">ảo<span style=\"color:black;\"> mang đến một chất lượng công trình tốt nhất cho quý khách hàng.</span></span></span></span></p>\n<p style=\'margin:0in;margin-bottom:10.0pt;font-size:13px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;line-height:150%;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">Hợp đồng rõ ràng, có hỗ trợ và tư vấn nếu quý khách hàng có thắc mắc khi tham khảo qua hợp đồng.</span></span></span></p>\n<p style=\'margin:0in;margin-bottom:10.0pt;font-size:13px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;line-height:150%;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">Luôn thanh lý hợp đồng đúng theo những gì nội dung đã cam kết. Thực hiện chính sách bảo hành công trình theo đúng quy định.</span></span></span></p>\n<p style=\'margin:0in;margin-bottom:10.0pt;font-size:13px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;line-height:150%;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">Giá thành đưa ra đảm bảo hợp lý, có cơ sở, phù hợp với mọi đối tượng khách hàng.</span></span></span></p>\n<p style=\'margin:0in;margin-bottom:10.0pt;font-size:13px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;line-height:150%;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">Luôn luôn hỗ trợ và tư vấn miễn phí cho quý khách hàng thiết kế công trình sao cho tốt nhất và đẹp nhất.</span></span></span></p>\n<p style=\'margin:0in;margin-bottom:10.0pt;font-size:13px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;line-height:150%;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">Nếu quý khách hàng vẫn đang tìm kiếm một nhà thầu xây dựng Phú Quốc uy tín, chuyên nghiệp, giá thành phù hợp hãy liên hệ ngay với chúng tôi </span><span style=\"line-height: 150%; color: black; background: white;\">để được tư vấn và hỗ trợ tốt nhất.</span></span></span></p>\n<p style=\'margin:0in;margin-bottom:10.0pt;font-size:13px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;line-height:150%;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black; background: white;\">Quý khách có thể xem thêm: </span></span></span><a href=\"https://www.angiakhang.com/nha-thau\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(184, 49, 47);\"><span style=\"line-height: 150%; background: white;\">Nhà thầu xây dựng uy tín tại tphcm</span></span></span></a></p>\n<p style=\"margin: 0in 0in 10pt; font-size: 13px; font-family: Calibri, sans-serif; text-align: center; line-height: 150%;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0);\"><span style=\"line-height: 150%; background: white;\">Nhà thầu xây dựng An Gia Khang</span></span></span></p>\n<p><iframe width=\"100%\" height=\"360\" src=\"https://www.youtube.com/embed/3fQu0tIbMvQ\" frameborder=\"0\" allowfullscreen=\"\"></iframe></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n<p style=\'margin:0in;margin-bottom:10.0pt;font-size:13px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;line-height:150%;\'><strong><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Chúng tôi hân hạnh được làm việc với quý khách!</span></strong></p>\n', NULL, 1, NULL, 1576121619, 1590679626, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(243, 'category', 4, 1, '2.  Các tiêu chí chọn nhà thầu xây dựng uy tín', '2-cac-tieu-chi-chon-nha-thau-xay-dung-uy-tin', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Hiện nay ngày càng có nhiều các nhà thầu  xây dựng được thành lập và hoạt động. Tuy nhiên không phải nhà thầu nào cũng đảm bảo được uy tín và chất lượng. Điều này gây  khó khăn cho không ít người khi muốn lựa chọn được một nhà thầu uy tín để thi công công trình.</span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1582432129_a389888f6254f5518250.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tiêu chí chọn nhà thầu xây dựng</span></span></p><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Như chúng ta đã biết để hoàn thiện một công trình xây dựng đòi hỏi rất nhiều yếu tố. Trong đó nhà thầu xây dựng đóng vai trò quan trọng. Lựa chọn không đúng nhà thầu uy tín khiến cho công trình không đảm bảo chất lượng, có thể gây nên những mối nguy hiểm khôn lường. </span></span></p><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vậy nhà thầu xây dựng uy tín là như thế nào? Làm sao để nhận biết được nhà thầu xây dựng bạn chọn có uy tín hay không? Dưới đây là một vài gợi ý giúp bạn giải đáp được thắc mắc này.</span></span></p><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Để được đánh giá là một nhà thầu xây dựng uy tín thì đơn vị đó phải đảm bảo đáp ứng được các yêu cầu cần thiết như sau:</span></span></p><h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>a)  Đảm bảo được giá trị pháp lý</strong></span></span></h3><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây là yếu tố cần có đầu tiên của một nhà thầu xây dựng uy tín. Giá trị pháp lý là đảm bảo được những yếu tố cơ bản như:</span></span></p><ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Được cấp giấy phép kinh doanh: Nếu là một nhà thầu xây dựng uy tín thì thời gian thành lập và hoạt động ít nhất phải từ 5 năm trở lên.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phải có văn phòng hoạt động, tuyệt đối tránh xa những nhà thầu xây dựng không có văn phòng, văn phòng ảo, không có địa chỉ rõ ràng.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phải hoạt động và luôn hoạt động dưới sự quản lý của nhà nước. Có như vậy khi bạn hợp tác với đơn vị đó mới có thể được bảo hộ về mặt pháp lý.</span></span></li></ul><h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>b) Đảm bảo về kinh nghiệm và trình độ</strong></span></span></h3><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kinh nghiệm và trình độ thực hiện cũng sẽ là những yếu tố để đánh giá nhà thầu xây dựng có uy tín hay không. Bởi lẽ những nhà thầu được thành lập lâu năm, có thâm niên từng thực hiện nhiều công trình xây dựng sẽ có kinh nghiệm nhiều hơn. Để thực hiện được điều này thì nhà thầu xây dựng uy tín cần có:</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1582432198_2d9f4e5a7902d042944d.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kinh nghiệm xây dựng rất quan trọng</span></span></p><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kinh nghiệm thực tế công trường nhiều năm.</span></span></p><ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đội chỉ huy công trường giỏi.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đội thợ có tay nghề cao.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đội ngũ kiến trúc sư dày dạn kinh nghiệm.</span></span></li></ul><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có như vậy mới  tư vấn và đưa ra những phương án thích hợp nhất, tối ưu nhất cho bạn.</span></span></p><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thông thường những nhà thầu uy tín có kinh nghiệm dù chưa ký kết hợp đồng nhưng họ vẫn sẽ tư vấn cho bạn thấy được những giải pháp tối ưu khi thực hiện công trình xây dựng đó. </span></span></p><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đưa ra được những ý tưởng về vật liệu xây dựng nên chọn, các yếu tố liên quan đến địa chất nền, giải pháp kết cấu nhà… để chủ đầu tư có thể tham khảo.</span></span></p><h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>c) Đảm bảo luôn thực hiện đúng cam kế</strong>t</span></span></h3><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thực hiện đúng cam kết sẽ là yếu tố quan trọng giúp đánh giá nhà thầu xây dựng đó có phải là nhà thầu xây dựng uy tín hay không. Bởi trên thực tế có rất nhiều nhà thầu dù cam kết nhưng lại không thực hiện được dẫn đến mất uy tín, mất danh tiếng. </span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1582432235_3869d581e42e0d4d2711.jpg\" style=\"width: 646px;\" class=\"fr-fic fr-dib\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đảm bảo chất lượng công trình</span></span></p><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khiến cho công trình không được thực hiện đúng tiến độ hay chất lượng công trình không đảm bảo.</span></span></p><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một nhà thầu xây dựng uy tín sẽ sẵn sàng thực hiện các cam kết hoặc ký cam kết đảm bảo được các yếu tố như:</span></span></p><ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không bán thầu lại mà nhà thầu đó phải trực tiếp thực hiện theo đúng hợp đồng đã ký kết.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Luôn minh bạch trong các khâu như lựa chọn và báo giá vật tư, chi phí nhân công….</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cam kết thực hiện công trình đúng tiến độ, đảm bảo chính xác về các yếu tố như thời gian tháo cốp pha, thời gian hoàn thành, bàn giao công trình…</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thực hiện đúng cam kết về gói dịch vụ đã ký kết như gói hoàn thiện nhà xây thô, hoàn thiện nội thất…</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cam kết và thực hiện đúng chế độ bảo hành: Một số yếu tố  như bảo dưỡng tường, sàn bê tông sau khi hoàn thiện công trình….</span></span></li></ul><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các  nhà thầu xây dựng uy tín khi đã cam kết sẽ thực hiện đúng như hợp đồng đã ký. Để tạo được sự tin tưởng và đánh giá cao của các chủ đầu tư. Đồng thời cũng là tạo thương hiệu, tạo uy tín cho đơn vị của mình.</span></span></p><h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>d) Đảm bảo về chi phí thực hiện</strong></span></span></h3><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chi phí thực hiện cũng là một trong những yếu tố được sử dụng để đánh giá một nhà thầu xây dựng có uy tín hay không. Tất nhiên đó không phải là giá càng rẻ thì sẽ không uy tín hay giá càng cao sẽ càng chất lượng. Mà một nhà thầu xây dựng uy tín hay không phải căn cứ vào mức chi phí thực hiện được đưa ra đó có hợp lý hay không.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1582432273_38e42de6678af7372277.jpg\" style=\"width: 641px;\" class=\"fr-fic fr-dib\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đảm bảo đúng thời hạn cảm kết</span></span></p><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chi phí trong xây dựng thường được tính theo diện tích xây dựng. Tuy nhiên cách tính này đôi khi có sự khác biệt hoàn toàn so với thực tế. </span></span></p><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vì vậy khi so sánh về chi phí thực hiện giữa các nhà thầu xây dựng bạn không nên chỉ nhìn vào chi phí thực hiện thông qua số liệu được tính trên m2 diện tích. Để có cái nhìn khách quan và đánh giá chính xác nhất bạn nên nhìn chi phí thực hiện theo gói thầu.</span></span></p><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Hiện nay các nhà thầu xây dựng uy tín thường cung cấp các gói thầu xây dựng như:</strong></span></span></p><p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>a) Gói thầu trọn gói: </strong>Gói thầu này sẽ bao gồm tất cả các công đoạn như vật liệu xây dựng, nhân công, giám sát thi công…. Để đánh giá chi phí bỏ ra có tương xứng hay không bạn nên xem xét kỹ các hạng mục nhỏ bên trong gói thầu đó. </span></span></p><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chẳng hạn như giá cả và chất lượng vật tư xây dựng, nhà cung cấp vật tư, xuất xứ, nhãn hiệu của từng loại vật tư…. Sau đó đối chiếu với cách tính chi phí mà nhà thầu xây dựng đưa ra có hợp lý không? Bạn cũng có thể so sánh với một số nhà thầu khác để đánh giá được chính xác nhất.</span></span></p><p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>b) Thầu phần thi công:</strong> Lúc này bạn sẽ là người cung cấp vật tư và các thiết bị cần thiết cho nhà thầu. Do đó để đánh giá chi phí thi công bạn có thể xem xét, so sánh các hạng mục được nhà thầu đưa ra trong bảng báo giá có hợp lý hay không để quyết định lựa chọn được nhà thầu xây dựng uy tín.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1582432312_03a6c7f336709c4395f6.jpg\" style=\"width: 652px;\" class=\"fr-fic fr-dib\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đảm bảo đúng tiêu chuẩn đổ móng công trình</span></span></p><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây là một vài yếu tố chính để đánh giá nhà thầu xây dựng uy tín là như thế nào. Ngoài ra bạn có thể áp dụng một vào mẹo sau để chọn được một nhà thầu xây dựng uy tín khi xây dựng công trình:</span></span></p><ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tham khảo ý kiến của những người đã từng xây dựng công trình về cách lựa chọn nhà thầu xây dựng của họ. Hoặc những nhà thầu mà họ đã từng lựa chọn.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chọn so sánh giữa một vài nhà thầu để đánh giá được khách quan nhất.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chọn những nhà thầu có địa chỉ trụ sở, văn phòng rõ ràng.</span></span></li><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xem những công trình mà nhà thầu xây dựng đó từng thi công.</span></span></li></ul><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Lựa chọn được nhà thầu xây dựng uy tín sẽ giúp việc thi công công trình được an toàn, đảm bảo chất lượng hơn. Đồng thời chi phí bỏ ra sẽ tương xứng với chất lượng đạt được.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hầu như tất cả những ai đang có ý định xây nhà đều quan tâm tới nhà thầy xây dựng. Bởi nhà thầu xây dựng chính là nhân tố tạo nên chất lượng của công trình. </span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phần lớn mọi người không hiểu biết nhiều về thi công xây dựng do đó phải chọn lựa một nhà thầu xây dựng có kinh nghiệm và chịu trách nhiệm thi công giám sát.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576122898_b5f3f893e832f9310479.jpg\" style=\"width: 654px;\" class=\"fr-fic fr-dib\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Năng lượng thi công nhà thầu </span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ở lĩnh vực xây dựng hiện nay có khá nhiều nhà thầu xây dựng nhận thi công các hạng mục nhà ở, hoàn thiện nội thất hay cải tạo và sửa chữa nhà. Thế nhưng làm thế nào để biết nhà thầu có uy tín không, chất lượng cũng như giá cả của nhà thầu có phải là tốt nhất không. Những thông tin dưới đây sẽ giúp bạn giải đáp thắc mắc này.</span></span></p><h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>e)  Các tiêu chí khác</strong></span></span></h3><ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Giá trị pháp lý</strong></span></span></li></ul><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tiêu chí đầu tiên cần phải chú ý tới khi chọn nhà thầu xây dựng bạn cần phải xem các thông tin cơ bản về đơn vị đó như: ngành nghề kinh doanh, thời gian hoạt động, địa chỉ liên hệ, tư cách pháp nhân, văn phòng công ty,.. </span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576122985_39520ad46198004839b3.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chủ đầu tư nên chú ý trên thông tin nhà thầu</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tuy nhiên quan trọng nhất phải để ý tới giá trị pháp lý của công ty đó bởi khi một công ty xây dựng có đăng ký kinh doanh với địa chỉ rõ ràng, chịu sự quản lý của nhà nước thì khi bạn hợp tác với các công ty đó bạn được bảo hộ về mặt pháp lý.</span></span></p><ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Trình độ và kinh nghiệm</strong></span></span></li></ul><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà là để ăn ở, sinh hoạt và là nơi nghỉ ngơi, chốn đi về. Một căn nhà đẹp là mơ ước mà nhiều người muốn có được. Nhưng một căn nhà chắc chắn lại càng cần thiết hơn với mỗi người. Một nhà thầu xây dựng tốt sẽ mang đến cho bạn một ngôi nhà vừa đẹp lại chắc chắn.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576123039_e7431f873c94da2d0cd7.png\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Năng lưc thi công của nhà thầu </span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Để có thể làm được điều này họ phải có trình độ chuyên môn về lĩnh vực xây dựng cùng kinh nghiệm thực tế trên công trường nhiều năm với đội ngũ chỉ huy công trình giỏi, đội ngũ kiến trúc sư nhiều kinh nghiệm, đội thợ với tay nghề cao.</span></span></p><ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Dịch vụ của nhà thầu</strong></span></span></li></ul><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các công ty xây dựng hiện nay thường cung cấp một số gói thầu xây dựng như hoàn thiện nhà xây thô, hoàn thiện nội thất, trọn gói, cải tạo sửa chữa nhà,.. Căn cứ vào mục đích tìm nhà thầu xây dựng của bạn mà xem xét có phù hợp hay không.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576123074_94df76b4559e795dd1c2.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chủ thầu công trình An Gia Khang</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng uy tín thường được đánh giá dựa vào chất lượng dịch vụ mà nhà thầu cung cấp. Thế nhưng bạn chưa từng xây nhà trước đó bạn làm sao có thể biết được chất lượng dịch vụ nhà thầu cung cấp. Do đó để đánh giá được chất lượng sản phẩm dịch vụ mà nhà thầu bạn cần xem xét các vấn đề như:</span></span></p><ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Cam kết chất lượng dịch vụ</strong></span></span></li></ul><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Cam kết về chất lượng dịch vụ không thể nói mồm là xong mà nó thường được quy định cụ thể trong hợp đồng xây dựng. Một số cam kết mà nhà thầu xây dựng uy tín cần phải đưa ra được:</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576123124_d0457c45a27d8e15dbf1.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dii\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đang chuẩn bị hoàn tất công trình</span></span></p><ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Không bán thầu</strong></span></span></li></ul><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">hiện tượng bán thầu có thể nói là diễn ra khá phổ biến trong lĩnh vực xây dựng hiện nay. Hậu quả của nó là không kiểm soát và nghiệm thu chất lượng công trình kỹ càng được. Nếu có phát sinh các vấn đề khi thi công thì các đơn vị có thể đùn đẩy trách nhiệm. </span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hiện nay có nhiều công ty bán thầu một phần hay cả gói thầu cho đơn vị khác. Để tránh hiện tượng này xảy đến bạn cần tìm hiểu nhà thầu cam kết không bán thầu để chọn lựa nhé.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1582432386_328d8fc02b092a24df9a.jpg\" style=\"width: 620px;\" class=\"fr-fic fr-dii\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây dựng công trình</span></span></p><p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Minh bạch khi sử dụng vật tư, báo giá vật tư: các thông tin về bảng báo giá vật tư trong xây dựng cần phải được công khai từ thương hiệu, giá thành, số lượng cũng như mục đích sử dụng.</span></span></p><p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chịu trách nhiệm về các việc liên quan tới cộng đồng như môi trường, an toàn lao động, tiếng ồn,..</span></span></p><p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có kinh nghiệm xây dựng, trình độ chuyên môn, đội ngũ kỹ sư chuyên nghiệp và cả đội thợ lành nghề</span></span></p><p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bảo hành công trình: đơn vị nhà thầu cần phải có cam kết bảo hành sau khi xây dựng phần nội thất cung cấp, phần thô. Bảo hành và bảo dưỡng có vai trò quan trong trong hình thành uy tín của nhà thầu vì một sản phẩm hay dịch vụ tốt thì nhà cung cấp luôn sẵn sàng bảo hành dài hạn cho khách </span></span></p><ul><li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Hợp đồng xây dựng</strong></span></span></li></ul><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hợp đồng xây  dựng được hiểu là loại hợp đồng giữa bên giao thầu và bên nhận thầu. Trong đó cần xác định thỏa thuận của hai bên về quyền lợi cũng như nghĩa vụ khi tham gia hợp đồng. bên nhận thầu phải có nghĩa vụ thực hiện giám sát công trình hay một phần công trình. </span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576123466_6ef66867a97b7ea8d7b7.png\" style=\"width: 624px;\" class=\"fr-fic fr-dii\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hợp đồng mẫu nhà thầu xây dựng</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đồng thời cần phải bàn giao đúng thời hạn mà bên giao thầu đã đưa ra. Bên giao thầu phải có trách nhiệm đưa ra những số liệu, yêu cầu, bản thiết kế, vật tư xây dựng.,.. Số vốn đầu tư đúng với tiến độ của công trình và thanh toán, nghiệm thu đầy đủ các khoản tiền khi công trình hoàn thành.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1576123551_d5e1fa5dcdf1c16182eb.jpg\" style=\"width: 628px;\" class=\"fr-fic fr-dii\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cam kết bàn giao nhà đúng thời hạn</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hợp đồng xây dựng cũng cần phải được thể hiện bằng văn bản do hai bên ký và xác nhận. Điều này bạn cần quan tâm trước khi ký một hợp đồng chính là tính pháp lý của nó. Đơn vị thi công cần phải có giá trị pháp nhân nghĩa là công ty chịu sự quản lý của nhà nước và có địa chỉ rõ ràng.  </span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng thường sẽ lập hợp đồng xây dựng quy định trách nhiệm, quyền hạn các hạng mục thi công cụ thể.</span></span></p><ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Giá cả hợp lý</strong></span></span></li></ul><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây cũng là vấn đề quan trọng mà nhiều người quan tâm tới. Một nhà thầu xây dựng là đơn vị đưa ra được giá thầu hợp lý. Giá thấp hơn thị trường chưa hẳn tốt bởi rất khó đảm bảo được chất lượng thi công mà giá quá cao lại không phù hợp với khả năng tài chính khách hàng. </span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1582432467_f9ca6be745179e3e8b17.jpg\" style=\"width: 615px;\" class=\"fr-fic fr-dii\"></span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đội ngũ nhân sự An Gia Khang</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chi phí xây dựng thường được tính căn cứ vào diện tích xây dựng tức là giá trên m2. Thế nhưng diện tích xây dựng lại khác hoàn toàn với diện tích đất.</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Diện tích đất = chiều dài x chiều rộng</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Diện tích xây dựng = tổng các diện tích các sàn (mỗi tầng) + diện tích khác (móng, sân, mái, ban công)</span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cách tính diện tích xây dựng của mỗi nhà thầu xây dựng có sự khác nhau. Do đó nếu so sánh giá của các nhà thầu bạn phải xem xét giá cả theo các dịch vụ như cách tính diện tích, giá đã bao gồm phần gì chứ đừng nhìn vào con số/m2 của nhà thầu đó. </span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ở dịch vụ của mỗi gói thầu cụ thể sẽ thể hiện các hạng mục thi công cụ thể với giá cả tương ứng.</span></span></p><ul><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Thời gian, tiến độ thi công</strong></span></span></li></ul><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu cần phải đưa ra một kế hoạch rõ ràng gồm có thời gian hoàn thiện cũng như tiến độ thi công cụ thể cho mỗi giai đoạn. </span></span></p><p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nhà thầu uy tín sẽ đảm bảo được đúng những gì đã đưa ra và chịu trách nhiệm bồi thường nếu họ không giao nhà đúng hẹn cho bạn.</span></p>', NULL, 4, NULL, 1576121856, 1618286090, NULL),
(247, 'category', 60, 1, '1.1 Trách nhiệm của công ty xây dựng tại Phú Quốc', '11-trach-nhiem-cua-cong-ty-xay-dung-tai-phu-quoc', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Khi ký hợp đồng với nhà thầu xây dựng Phú Quốc chủ đầu tư có thể hoàn toàn an tâm bởi công ty xây dựng cần có trách nhiệm trước chủ đầu tư và pháp luật về chất lượng công trình nhằm đảm bảo chất lượng các hạng mục theo đúng chỉ tiêu của chủ đầu tư. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576423682_126895288ad8e06fc9ab.jpg\" style=\"width: 616px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hơn nữa còn phải chịu trách nhiệm về những sự cố liên quan tới nhà thầu phụ.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nhà thầu xây dựng có trách nhiệm về phương tiện, thiết bị, biện pháp thi công sử dụng trong quá trình xây dựng.</span></p>\n', NULL, 1, NULL, 1576421859, 1590049156, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(248, 'category', 60, 2, '1.2 Xây nhà trọn gói tại Phú Quốc', '12-xay-nha-tron-goi-tai-phu-quoc', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Phần lớn những gia đình hay các chủ đầu tư đang có kế hoạch xây dựng đều sẽ quan tâm đến xây nhà trọn gói tại Phú Quốc. Một số yếu tố liên quan như cách tính diện tích xây nhà,   giá thi công xây dựng nhà thô, giá vật liệu, chi phí hoàn thiện, giá nhân công, chi phí tính xây nhà theo m2, giá khai toán và cả dự toán chi tiết.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1576423705_95da98c612bbb0ab04c8.jpg\" style=\"width: 577px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây nhà phố trọn gói tại Phú Quốc</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các công trình xây nhà trọn gói được nhiều người lựa chọn</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chính vì thế bạn có thể tham khảo một số những thông tin quan trọng về chi phí xây nhà trọn gói tại Phú Quốc đó là:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.3.1 Bảng tiên lượng dự toán</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây là bảng khối lượng chính xác của tất cả các hạng mục, công việc cần thực hiện từ lúc khởi công đến hoàn tất công trình. Ví dụ trong công trình đó cần phải xây bao nhiêu m2 tường bằng gạch ống dày 100cm hoặc phải đổ bao nhiêu khối bê tông, cột, sàn,….</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.3.2 Bảng tổng hợp kinh phí vật tư</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trong bảng này nhà thầu cần phải liệt kê được đơn giá thị trường và các chủng loại vật tư được sử dụng. Ví dụ bao nhiêu sắt thép, xi măng, bao nhiêu gạch và giá tiền bao nhiêu.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.3.3 Bảng tổng hợp kinh phí dự toán</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bảng này sẽ có các thông tin rõ ràng về chi phí vật liệu, nhân công cũng như các chi phí khác khi xây dựng công trình. Đây cũng là kết quả dự toán cuối cùng có độ chính xác cao, sai số nhỏ hơn 5% cho việc định giá một công trình cần xây dựng trọn gói.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576423778_52c7abc5cc8daab2a5e8.jpg\" style=\"width: 604px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giá thuê nhân công hiện nay đang ổn định</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bên nhà thầu xây dựng cũng thường đưa ra một bảng tổng hợp chi phí để bạn có thể dễ dàng lên kế hoạch. Ví dụ bảng báo giá thi công xây dựng phần thô với các khoản chi như:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">2.900.000/m2. Với công trình có tổng diện tích hơn 350m2 sàn</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">3.000.000/m2. Với công trình có tổng diện tích hơn 300m2 đến dưới 350m2 sàn</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">3.100.000/m2. Với công trình có tổng diện tích hơn 250m2 đến dưới 300m2 sàn</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">3.300.000/m2. Với công trình có tổng diện tích nhỏ hơn 200m2</span></span></p>\n<p>[[contact_short_code]]</p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cần phải lưu ý này bảng giá này chưa có chi phí khoan địa chất, khoan cọc nhồi, ép cọc. Hơn nữa ở từng địa phương vật liệu có thể thay đổi sao cho có cùng giá trị chất lượng như nhau nếu khó khăn trong phần vật tư cung ứng cho công trình.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong> 1.3.4 Báo giá phần trọn gói</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Năm 2020 mức báo giá xây nhà trọn gói tại Phú Quốc có thể dao động trong khoảng từ 6.200.000 VNĐ/m2 tới 6.500.000 VNĐ/m2 là mức giá hợp lý mà đảm bảo chất lượng và giúp gia chủ an tâm hơn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.3.5  Bảng tiên lượng dự toán mẫu</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với các dự toán trên chủ đầu tư hay gia chủ cần phải chủ động khi lựa chọn, thay đổi những vật liệu xây dựng phần thô và hoàn thiện một ngôi nhà đẹp sao cho phù hợp với sở thích, nhu cầu cũng như điều kiện kinh tế của gia đình.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Đối với dự toán trên bạn có thể chủ động trong việc lựa chọn, thay đổi các vật liệu xây dựng phần thô cũng như phần hoàn thiện ngôi nhà phố đẹp để phù hợp với nhu cầu, sở thích và kinh tế của gia đình.</span></p>\n', NULL, 1, NULL, 1576421859, 1590049156, NULL),
(249, 'category', 60, 3, '1.4 Giá nhân công xây dựng tại Phú Quốc', '14-gia-nhan-cong-xay-dung-tai-phu-quoc', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Theo như đơn giá nhân công xây dựng Kiên Giang vào năm 2018 của Ủy ban nhân dân tỉnh có công bố kèm theo bộ đơn giá xây dựng công trình tỉnh Kiên Giang, quyết định số 451/QĐ-UBND . Đây là cơ sở để xác định và quản lý chi phí xây dựng công trình trên địa bàn.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576423816_fe6c7a07e3cb997787fc.jpg\" style=\"width: 624px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giá nhân công xây dựng tại Phú Quốc</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Theo <strong>đó giá nhân công xây dựng Phú Quốc- Kiên Giang</strong> năm 2018 có 3 vùng đó là:</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mức lương cơ sở đầu vào của vùng II là Lnc = 2.150.000 đồng/ tháng. Mức giá này áp dụng ở địa bàn thành phố Rạch Giá, thị xã Hà Tiên và cả huyện Phú Quốc.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mức lương cơ sở đầu vào của vùng III là Lnc = 2.000.000 đồng/ tháng. Áp dụng tại các huyện Kiên Hải, Kiên Lương, Châu Thành. Nếu như áp dụng bộ đơn giá này sẽ được tính chuyển đổi theo một số hệ số như:</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Hệ số nhân công là Knc = 0,93</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Hệ số máy thi công là Kmtc = 0,99</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Mức lương cơ sở đầu vào vùng IV là Lnc = 1.900.000 đồng/ tháng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hệ số này áp dụng tại các huyện Hòn Đất, Giang Thành,  Giồng Riềng, Tam Hiệp, An Biên, Gò Quao, Vĩnh Thuận, An Minh,  U Minh Thượng. Nếu áp dụng bộ giá này sẽ tính chuyển đổi theo các hệ số:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Hệ số nhân công là Knc = 0,884</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Hệ số máy thi công là Kmtc = 0,983</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0);\"><strong>Quý khách hàng có thể xem ngay chi tiết: </strong></span></span><a href=\"https://www.angiakhang.com/bao-gia-xay-nha-tai-phu-quoc\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(184, 49, 47);\"><strong>Báo </strong></span></span><span style=\"color: rgb(184, 49, 47);\"><strong><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">giá xây nhà trọn gói tại Phú Quốc</span></strong></span></a></p>\n', NULL, 1, NULL, 1576421859, 1590049156, NULL),
(250, 'category', 60, 4, '1.4 Top 5 công ty xây dựng tại Kiên Giang', '14-top-5-cong-ty-xay-dung-tai-kien-giang', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Ngành xây dựng tại Phú Quốc tỉnh Kiên Giang đang phát triển mạnh mẽ, thúc đẩy quá trình phát triển của tỉnh Kiên Giang. Dưới đây là danh sách các công ty xây dựng tại Phú Quốc, tỉnh Kiên Giang. Quý khách hàng có thể tham khảo.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576424482_c35c4afcda4c9fce1fa0.jpg\" style=\"width: 584px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đội ngũ công nhân xây dựng Công ty xây dựng An Gia Khang</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.4.1 Công Ty CP Cơ Khí Kiên Giang</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Lĩnh vực hoạt động chủ yếu là xây dựng công trình. Cụ thể là thi công xây dựng các công trình đèn chiếu sáng, xây dựng nhà phố thương mại, thi công lắp đặt trạm biến áp,..</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576424655_b7af01404182dd09db5d.jpg\" style=\"width: 586px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công nhân đang thi công nội thất bên trong</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.4.2 Công Ty Cổ Phần Xây Dựng Kiến Trúc Đẹp</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thành lập từ năm  2008 công ty cổ phần xây dựng kiến trúc Đẹp chuyên thiết kế và xây dựng các công trình kiến trúc, nội thất công nghiệp, dân dụng,… Mục tiêu hoạt động của công ty là hướng đến các công trình chất lượng, đúng tiến độ và có mức giá cạnh tranh để tạo nên sự tin tưởng tuyệt đối cho khách hàng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576424680_c70e7a5fcb7c0deae290.jpg\" style=\"width: 564px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đang lát nền </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong> 1.4.3 Công Ty Cổ Phần Đầu Tư Phát Triển Hạ Tầng Phú Quốc</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cũng được thành lập vào năm 2008 , công ty có nhiệm vụ xây dựng phát triển cơ sở hạ tầng kỹ thuật đảo Phú Quốc theo như định hướng của Chính Phủ. UBND tỉnh Kiên Giang, BQL đầu tư phát triển đảo Phú Quốc</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.4.4 Công Ty TNHH Thương Mại Và Dịch Vụ Xây Dựng Phú Quốc</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây là doanh nghiệp hoạt động chuyên về lĩnh vực xây dựng nhà ở các loại như nhà biệt thự, nhà chung cư, nhà phố. Ngoài ra còn có các loại sơn chống thấm trên thị trường.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576424864_15ea622ce29c427a5eb5.jpg\" style=\"width: 581px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thi công lắt đèn trần nhà</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1.4.5 Công ty Cổ Phần Thiết Kế và Xây Dựng An Gia Khang</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:7.5pt;margin-left:0in;line-height:normal;font-size:15px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Công ty xây dựng An Gia Khang là một trong những công ty xây dựng uy tín và chất lượng tại Phú Quốc . Với sự phát triển mạnh mẻ với đội ngũ nhân sự là những kiến trúc sư chuyên nghiệp nhiều năm kinh nghiệm, cùng với đội ngũ anh em công nhân đầy nhiệt quyết, gắn bó lâu dài trên những công trình dự án.</span></span></span></p>\n<p style=\"margin: 0in 0in 7.5pt; line-height: normal; font-size: 15px; font-family: Calibri, sans-serif; text-align: center; background: white;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\"><img src=\"/uploads/contents/1576424713_d0e878804efe06aa87bb.jpg\" style=\"width: 609px;\" class=\"fr-fic fr-dib\"></span></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thi công lát nền nhà</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ngành nghề kinh doanh chủ yếu của công ty là xây dựng, sửa chữa, bảo trì các công trình, nhận thầu xây dựng dân dụng, sửa chữa và bảo trì công trình.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif;\">Quý khách xem thêm: </span></span><a href=\"https://www.angiakhang.com/nha-thau\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(226, 80, 65);\">Nhà thầu xây dựng uy tín tphcm</span></span></a></p>\n<p>[[contact_short_code]]</p>\n<p style=\'margin:0in;margin-bottom:10.0pt;font-size:13px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;line-height:150%;\'><strong><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Chúng tôi hân hạnh được làm việc với quý khách!</span></strong></p>\n', NULL, 1, NULL, 1576421859, 1590049156, NULL),
(255, 'category', 59, 1, '2. Những lưu ý khi xây dựng nhà trọn gói', '2-nhung-luu-y-khi-xay-dung-nha-tron-goi', '<p style=\"text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Vậy khi xây dựng nhà trọn gói chúng ta cần lưu ý những gì? Hãy cùng nghe các chuyên gia của chúng tôi chia sẻ 3 lưu ý trong xây dựng nhà trọn gói ngay sau đây.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576428702_d6e0e19b5113c8796f88.jpg\" style=\"width: 649px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công ty xây dựng nhà trọn gói An Gia Khang</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.1 Xem xét hồ sơ thiết kế không gian nhà chất lượng</strong></span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây là lưu ý đầu tiên bạn cần quan tâm, thông thường khi sử dụng dịch vụ xây dựng nhà trọn gói bạn sẽ được tặng các bản thiết kế nhà miễn phí. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Để có được không gian sống hoàn mỹ bạn cần lưu ý chọn thiết kế chất lượng đảm bảo yêu cầu về tính thẩm mỹ và kỹ thuật thi công sao cho phù hợp với yêu cầu không gian sống của bạn và gia đình.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.2 Lưu ý về yếu tố phong thủy</strong></span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Dù lựa chọn xây nhà theo hình thức nào thì bạn cũng cần quan tâm đến yếu tố phong thủy. Theo quan điểm của người phương Đông thì yếu tố phong thủy tạo nên vận mệnh cho những người trong gia đình, đặc biệt là đối với gia chủ (liên quan đến con đường làm ăn – công danh sự nghiệp, sức khỏe và đường tình duyên). </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576429019_b93fe6684500a5b66ae4.jpg\" style=\"width: 652px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà xây dựng trọn gói</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây dựng nhà ở hợp phong thủy sẽ mang đến cho bạn tài lộc, sức khỏe và con đường hạnh phúc ngập tràn.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.3 Lưu ý về việc chọn nhà cung cấp dịch vụ xây nhà trọn gói uy tín</strong></span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây chính là lưu ý quan trọng nhất, quyết định đến hơn 90% chất lượng và tính thẩm mỹ cho ngôi nhà bạn đang mong ước. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bạn cần chọn đúng nhà cung cấp dịch vụ xây dựng nhà trọn gói uy tín để sử dụng được dịch vụ tốt nhất, đảm bảo từ khâu thiết kế xây dựng, kỹ thuật thi công xây dựng cho đến tiến độ xây dựng và bàn giao nhà ở.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.4 Công ty thiết kế và xây dựng An Gia Khang cung cấp dịch vụ xây nhà trọn gói uy tín và chất lượng</strong><strong> </strong></span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Dịch vụ xây nhà trọn gói đang phát triển, tuy nhiên việc lựa chọn đơn vị cung cấp dịch vụ này uy tín không hề đơn giản. Hiện nay có đến hơn 50% nhà cung cấp dịch vụ xây dựng nhà trọn gói kém chất lượng.</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1583770778_dcd05be5e66e821bfcac.jpg\" style=\"width: 622px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><a href=\"https://www.angiakhang.com/xay-nha-tron-goi\">Thi công xây dựng nhà trọn gói</a></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">An Gia Khang chính là đơn vị cung cấp dịch vụ xây dựng nhà trọn gói uy tín, chất lượng hàng đầu Việt Nam mà chúng tôi muốn nhắc đến trong bài viết này. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với nhiều năm hoạt động trong lĩnh vực xây dựng, chúng tôi tự tin khi nói rằng chúng tôi là sự lựa chọn tuyệt vời nhất dành cho bạn. Chúng tôi luôn hoạt động với phương châm:</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">“Đặt lợi ích của khách hàng song hành cùng lợi ích của doanh nghiệp, biến khách hàng thành những người marketing hiệu quả nhất cho doanh nghiệp”.</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1583770849_2f904af04c015d2320a9.jpg\" style=\"width: 647px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công nhân thực hiện đổ cọc</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Chính vì thế theo thống kê hằng năm có 100% khách hàng hài lòng với dịch vụ xây dựng nhà trọn gói do chúng tôi cung cấp, có đến hơn 70% khách hàng mới đến với chúng tôi thông qua lời giới thiệu của bạn bè và người thân (những người đã từng là khách hàng sử dụng dịch vụ xây nhà trọn gói do An Gia Khang cung cấp).</span></p>\n', NULL, 1, NULL, 1576427332, 1590049312, NULL),
(256, 'category', 59, 2, '3. 5 Mẫu thiết kế nhà trọn gói dưới 1 tỷ.', '3-5-mau-thiet-ke-nha-tron-goi-duoi-1-ty', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Hiện nay, có rất nhiều mẫu nhà đẹp mà chi tiết xây nhà trọn gói dưới 1 tỷ đồng, thiết kế bắt mắt, sang trọng. Dưới đây là 05 mẫu thiết kế nhà trọn gói dưới 1 tỷ, phù hợp với những gia đình có thu nhập trung bình.</span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.1. Mẫu thiết kế nhà trọn gói dưới 1 tỷ - Mẫu 01</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1576429730_c767495a2c0fe5129ab5.jpg\" style=\"width: 635px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế nhà trọn gói dưới 1 tỷ - Mẫu 01</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Căn biệt thự 1 tầng mini với chi phí xây dựng trọn gói dưới 1 tỷ đồng giúp tiết kiệm chi phí, phù hợp với những cặp vợ chồng trẻ. Nhà được thiết kế theo kiểu mang đến không gian sinh hoạt hòa nhập với thiên nhiên. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu nhu cầu công năng sử dụng không cao nhưng đầy đủ tiện nghi thì mẫu này rất phù hợp với các gia đình ít thành viên.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.2. Mẫu thiết kế nhà trọn gói dưới 1 tỷ - Mẫu 02</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1576429776_24ddc95432ac3bb9f40b.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế nhà trọn gói dưới 1 tỷ - Mẫu 02</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Căn nhà 2 tầng mái thái </strong>với 4 phòng ngủ, chi phí xây dựng dưới 1 tỷ đồng, đem đến cho gia đình bầu không gian ấm cúng, thoải mái sau một ngày làm việc dài. Thiết kế mái thái sáng tạo, kết hợp với gam màu tươi sáng làm cho căn nhà trở nên sang trọng.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.3. Mẫu thiết kế nhà trọn gói dưới 1 tỷ - Mẫu 03</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1576429790_203b8cfc498d6e30d387.jpg\" style=\"width: 624px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế nhà trọn gói dưới 1 tỷ - Mẫu 03</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Căn nhà ống với bề ngang tương đối hẹp sẽ khó trong việc thiết kế nội thất, đặc biệt là làm sao cho hợp lý và rộng rãi. Chi phí xây dựng căn nhà này chỉ chưa đến 1 tỷ đồng, rất phù hợp với những gia đình có thu nhập trung bình mà vẫn tạo cho tư gia không gian ấm cúng.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.4. Mẫu thiết kế nhà trọn gói dưới 1 tỷ - Mẫu 04</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1576429805_179e6516872ee2e98ece.jpg\" style=\"width: 650px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế nhà trọn gói dưới 1 tỷ - Mẫu 04</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Căn nhà 1 tầng chữ U cũng là sự lựa chọn hợp lý cho các gia đình có mức thu nhập trung bình. Căn nhà với 3 mặt thoáng giúp gia chủ dễ dàng phân chia các phòng, tạo ra không gian ấm cúng, các thành viên cùng quây quần bên nhau.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.5. Mẫu thiết kế nhà trọn gói dưới 1 tỷ - Mẫu 05</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1576429818_6a247d3952d977a10a96.png\" style=\"width: 654px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà trọn gói dưới 1 tỷ</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà 1 tầng có gara ô tô tiện nghi, hiện đại, nổi bật với hệ mái thái đơn giản mà lại phù hợp với túi tiền của nhiều gia đình. Với thiết kế này, căn nhà thường là mái tôn giả ngói với kiến trúc đơn giản làm giảm tối đa chi phí xây dựng.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Như vậy, hy vọng những thông tin mà chúng tôi vừa cung cấp bạn sẽ tự chọn được cho gia đình mình một mẫu nhà thật hợp lý cả về chi phí lẫn chất lượng.</span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Từ đây, với nền móng vững chắc về con người và giá trị đầu tư cùng với niềm tin được khẳng định qua những dự án lớn - An Gia Khang tự tin vươn xa hơn trong lĩnh vực thi công các công trình cao tầng, biệt thự, nhà phố thuộc các dự án trên toàn quốc.</span></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(51, 51, 51);\">Quý khách có thể xem thêm: </span></span></span><a href=\"https://www.angiakhang.com/nha-thau\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(184, 49, 47);\">nhà thầu xây dựng uy tín</span></span></a></p>\n<p style=\"margin: 0in 0in 7.5pt; font-size: 15px; font-family: Calibri, sans-serif; line-height: 115%; text-align: center; background: white;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(41, 105, 176);\"><strong>NHÀ THẦU XÂY DỰNG AN GIA KHANG</strong></span></span></p>\n<p><iframe width=\"100%\" height=\"360\" src=\"https://www.youtube.com/embed/3fQu0tIbMvQ\" frameborder=\"0\" allowfullscreen=\"\"></iframe></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">[[contact_short_code]]</span></span></p>\n<p style=\'margin:0in;margin-bottom:10.0pt;font-size:13px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;line-height:150%;\'><strong><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Chúng tôi hân hạnh được làm việc với quý khách!</span></strong></p>\n', NULL, 1, NULL, 1576427332, 1590049312, NULL),
(257, 'category', 27, 1, '2.Kinh nghiệm xây dựng nhà cao tầng', '2kinh-nghiem-xay-dung-nha-cao-tang', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Lưu ý rằng bê tông cần đổ liên tục không ngừng và trong mỗi kết cấu mạch ngừng cần bố trí ở các vị trí có lực cắt với mô men uốn nhỏ. Khi trời mưa cần phải che chắn và không để nước mưa rơi vào bê tông. Nên đổ bê tông cột có chiều cao dưới 5m và tường có chiều cao dưới 3m thì cần đổ liên tục.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576508049_e8a04a40e83b2beddcce.jpg\" style=\"width: 619px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đội ngũ công nhân An Gia Khang</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với những cột cao hơn 5m và tường cao hơn 3m cần chia thành nhiều đợt nhưng vẫn phải đảm bảo vị trí, cấu tạo mạch ngừng thi công hợp lý. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Do đó bê tông dầm và bản sàn phải được tiến hành đồng thời, khi dầm có kích thước hơn 80cm thì có thể đổ riêng các phần nhưng cần bố trí mạch ngừng thi công hợp lý.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trên đây chúng tôi vừa giới thiệu đến các những thông tin chi tiết về lưu ý trong quá trình xây dựng nhà cao tầng. Nếu chưa có những thông tin cụ thể về vấn đề này những thông tin trong bài viết chắc chắn sẽ hữu ích cho bạn. Quý khách hàng còn thắc mắc về vấn đề này có thể liên hệ ngay với chúng tôi qua hotline để được tư vấn trực tiếp và cụ thể nhé.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576508082_3c7e0f9e7e1bc732542f.jpg\" style=\"width: 585px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đổ sàn bê tông</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></p>\n<p style=\'margin-right:0in;margin-left:0in;font-size:15px;font-family:\"Calibri\",\"sans-serif\";margin-top:0in;margin-bottom:7.5pt;line-height:115%;text-align:justify;background:white;\'><span style=\"font-size: 14px; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51);\">Công ty xây dựng An Gia Khang đơn khẳng định niềm tin qua những dự án lớn - An Gia Khang tự tin vươn xa hơn trong lĩnh vực thi công các công trình cao tầng, biệt thự, nhà phố thuộc các dự án trên toàn quốc.</span></p>\n', NULL, 1, NULL, 1576507363, 1579259014, NULL),
(259, 'news', 10, 0, '1. Thế nào là thiết kế nội thất?', '1-the-nao-la-thiet-ke-noi-that', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><strong><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Thiết kế nội thất </span></strong><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">chính là nghệ thuật xử lý không gian bên trong của mỗi công trình kiến trúc. Thiết kế nội thất gồm có các công việc cụ thể như quy hoạch, bố trí sắp xếp không gian bên trong của công trình để thỏa mãn những yêu cầu cơ bản và tạo điều kiện tốt nhất cho đời sống sinh hoạt của con người.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576730776_2ade825a0f39aea1cc6c.png\" style=\"width: 641px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế nội thất đòi hỏi sự hài hòa</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khi thiết kế nội thất việc chọn lựa các đồ vật được sắp xếp vào trong không gian ba chiều theo công năng sử dụng. Mối quan hệ của các yếu tố thẩm mỹ sau cùng sẽ được xác định bởi cách đánh giá chất lượng xem có phù hợp với không gian bên trong không và sự ảnh hưởng của nó tới nhận thức của con người trong quá trình sử dụng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576730808_da4586bed095ca2616ec.png\" style=\"width: 628px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế nội thất nhà phố</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ngoài ra các thiết kế nội thất cũng gắn liền với nghệ thuật tạo dáng sản phẩm có quy mô phát triển ngày một lớn hơn. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Yếu tố tạo nên không gian nội thất hoàn hảo là sự kết hợp hài hòa của đường nét, tỷ lệ, hình khối,màu sắc, ánh sáng, chất liệu cũng như tỷ lệ của một số thành phần như tường, trần, sàn và đồ đạc nội thất. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576730844_9634740d2bb1ef8b49ee.png\" style=\"width: 629px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế nội thất gạch giả gỗ</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Các yếu tố này gắn kết chặt chẽ với nhau và tạo nên một không gian đồng bộ, hoàn thiện thể hiện được hiệu quả của công trình.</span></p>\n', NULL, 1, NULL, 1576731226, 1590678696, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(260, 'news', 10, 1, '2. Những lưu ý khi thiết kế nội thất', '2-nhung-luu-y-khi-thiet-ke-noi-that', '<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Trong quá trình thiết kế nội thất việc chọn lựa các đồ vật được sắp xếp vào không gian ba chiều theo khả năng sử dụng mang tính thẩm mỹ.</span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576730921_85b7cbcc2443700d125e.jpg\" style=\"width: 647px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nội thất căn hộ</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mối quan hệ giữa các yếu tố thẩm mỹ đó thường được xác định bằng cách đánh giá xem có phù hợp với không gian bên trong không và sự ảnh hưởng của nó tới nhận thức con người trong việc sử dụng. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thế nhưng giới hạn của thiết kế nội thất rất khó để có thể được xác định chính xác vì nó còn căn cứ vào tính liên quan với kiến trúc, thiết kế sản xuất. Tức là nó bao gồm cả công năng sử dụng, vật liệu và công nghệ sản xuất ra nó cùng với vẻ ngoài của mỗi sản phẩm.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576730955_3ecb6eac368130f39a5d.png\" style=\"width: 621px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không gian cần phải đảm bảo tính thẩm mỹ và sự cân đối</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Tính đến nay thiết kế nội thất đang gắn liền với nghệ thuật tạo dáng sản phẩm và quy mô phát triển ngày càng lớn hơn.  Do đó một công trình nội thất cần phải đáp ứng được các yêu cầu sau đây bất kể tính chất của công trình đó là như thế nào:</span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.1 Công năng sử dụng</strong></span></span></h3>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế nội thất phải đảm bảo được công năng sử dụng, chức năng của các bộ phận thiết kế phải thỏa mãn và mục đích của nó cũng phải đầy đủ</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576730977_09871220f96aba73522f.png\" style=\"width: 596px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nội thất chung cư tphcm</span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.2 Tính ứng dụng với kinh tế</strong></span></span></h3>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế nội thất đạt tiêu chuẩn phải có tính ứng dụng cao, dùng các nguyên vật liệu thích hợp với đặc tính của công trình.</span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.3 Mang tính thẩm mỹ cao</strong></span></span></h3>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ngoài các tiêu chí trên thiết kế nội thất cần phải đảm bảo có tính thẩm mỹ khi nhìn vào và được chủ đầu tư ưng thuận.</span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.4 Mang ý nghĩa nhất định</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1576731018_802dea04d40f46ab03e1.png\" style=\"width: 611px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mỗi phong cách thiết kế nội thất đều sẽ có ý nghĩa nhất định</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không đơn giản là việc sắp xếp các chi tiết với nhau một thiết kế nội thất đạt yêu cầu cần phải gợi lên những hình ảnh, ý nghĩa giúp con người có thể liên tưởng tới ý nghĩa bên trong của nó đồng thời tác động tới cả tình cảm và tâm tư của con người.</span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.5  Phân loại thiết kế nội thất</strong></span></span></h3>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tùy vào cách phân loại mà có nhiều kiểu thiết kế nội thất khác nhau. Tuy nhiên thiết kế nội thất được phân loại theo một số dạng chính đó là:</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Thiết kế nội thất nhà ở</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Thiết kế nội thất văn phòng</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Thiết kế nội thất cửa hàng bán lẻ</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Thiết kế nội thất chăm sóc sức khỏe</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Thiết kế nội thất khách sạn</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1576731039_75fe0e2e18c625e2fc1e.jpg\" style=\"width: 645px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đảm bảo rằng bản thiết kế được thống nhất</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Cũng có khi người ta xét phân loại một cách tổng quát hơn thì thiết kế nội thất gồm có: thiết kế và bố trí không gian nội thất trong nhà, bố trí và sắp xếp đồ đạc sao cho hợp lý và khoa học nhất.</span></p>\n', NULL, 1, NULL, 1576731226, 1590678696, NULL),
(261, 'news', 10, 2, '3. Quy trình thiết kế nội thất hiện nay', '3-quy-trinh-thiet-ke-noi-that-hien-nay', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Với các công ty thiết kế nội thất chuyên nghiệp thường có một quy trình chuẩn gồm 5 bước cụ thể đó là:</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576731073_dae02e50230c591a12bb.png\" style=\"width: 606px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế nội thất nhà phố Bình Dương</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">•             Thu thập thông tin và tiếp nhận yêu cầu thiết kế của khách hàng. Sau khi gặp gỡ khách hàng đơn vị thiết kế sẽ trao đổi kỹ với khách hàng để nắm được các yêu cầu và mong muốn của khách hàng. Ví dụ:</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">•             Khách hàng muốn thiết kế nội thất cho riêng không gian nào hay cả ngôi nhà</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">•             Phong cách thiết kế chủ đạo mà khách hàng muốn là gì</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">•             Màu sắc chủ đạo mà họ muốn sử dụng cho không gian nội thất là gì?</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">•             Vật liệu nội thất chủ đạo khách hàng muốn sử dụng là gì</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cũng trong bước này đơn vị thiết kế nội thất sẽ đưa ra các gói thiết kế nội thất để khách hàng có thể chọn lựa căn cứ vào nhu cầu cũng như mức đầu tư của mình.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576731106_2c61855f099b192fe4d3.png\" style=\"width: 617px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nội thất phòng ngũ</span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.1 Khảo sát hiện trạng công trình</strong></span></span></h3>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các kiến trúc sư cùng với kỹ thuật viên của đơn vị thiết kế sẽ tiến hành khảo sát hiện trạng ngôi nhà của bạn sau đó đo đạc để có thể nắm được các thông số kỹ thuật cần thiết như hệ thống dầm, các không gian, trần nhà,.. để đảo bảo bản thiết kế nội thất và thực tế phải ăn khớp với nhau.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.2 Thiết kế sơ bộ</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Căn cứ vào các thông tin thu thập được từ thực tế công trình và các thông số kỹ thuật khi khảo sát hiện trạng các kiến trúc sư sẽ đưa ra bản thiết kế sơ bộ.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576731134_f30c668da970ee5fa80c.jpg\" style=\"width: 622px;\" class=\"fr-fic fr-dib\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trước khi ký hợp đồng phải có sự thống nhất giữa hai bên</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bản thiết kế nội thất sơ bộ sẽ có bản vẽ mặt bằng bố trí để tham khảo kèm theo các hình ảnh tư liệu mang tính chất gợi ý. Qua các thông tin này khách hàng sẽ đưa ra ý kiến của mình để người thiết kế chỉnh sửa được. Công việc này có thể thống nhất khi có tiếp xúc giữa hai bên.</span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.3 Thiết kế chi tiết 3D</strong></span></span></h3>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khi  đã thống nhất và ký hợp đồng với phương án được đưa ra các kiến trúc sư sẽ vẽ 3D cho các không gian thiết kế như yêu cầu của khách hàng để bạn có thể hình dung rõ nhất ngôi nhà của mình sau khi được hoàn thiện sẽ như thế nào.</span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.4 Hoàn thiện hồ sơ và giao cho khách hàng</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong><img src=\"/uploads/contents/1576731153_5fef96fba5c8562adcfc.png\" style=\"width: 629px;\" class=\"fr-fic fr-dib\"></strong></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nội thất căn hộ quận 7</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khi đã hoàn thiện bản vẽ 3D với các không gian phối cảnh hợp lý đơn vị thực hiện thiết kế nội thất sẽ hoàn thiện hồ sơ rồi giao cho khách hàng. Cảm ơn quý khách hàng đã xem tin trong mục </span></span><a href=\"https://www.angiakhang.com/kinh-nghiem-hay\"><strong><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kinh nghiệm hay</span></span></strong></a><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> về mục xây dựng.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n<p><strong><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Chúng tôi hân hạnh được làm việc với quý khách!</span></strong></p>\n', NULL, 1, NULL, 1576731226, 1590678696, NULL),
(262, 'category', 25, 2, '3. Kinh nghiệm xây dựng khách sạn', '3-kinh-nghiem-xay-dung-khach-san', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Để đảm bảo khách sạn xây dựng và đi vào hoạt động hiệu quả cần phải quan tâm đến một số vấn đề như sau:</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><img src=\"/uploads/contents/1584982561_1b6ab7760202f7ca23c5.jpg\" style=\"width: 613px;\" class=\"fr-fic fr-dib\"></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"font-size: 14px;\">Chia sẽ kinh nghiệm xây dựng khách sạn</span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"font-size: 14px;\"><strong>a) Xác định phân khúc khách hàng</strong></span></span></h3>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"font-size: 14px;\">Xác định rõ ràng việc xây dựng khách sạn phục vụ cho phân khúc khách hàng như thế nào, bình dân hay trung bình, cao cấp. Vì việc bạn xây dựng khách sạn cao cấp 5 sao khác biệt rất nhiều với khách sạn 1 sao hay 2 sao. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"font-size: 14px;\">Nếu xây dựng với phân khúc cao cấp sẽ đòi hỏi yêu cầu cao hơn và bạn không thể xây dựng khách sạn 1 sao mà thu tiền cao của khách hàng. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"font-size: 14px;\">Cũng chẳng thể nào đi vào phân khúc bình dân mà trang hoàng và thiết kế khách sạn theo mô hình cao cấp được. Sự khập khiễng này có thể khó khăn cho hoạt động của khách sạn.</span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"font-size: 14px;\"><strong>b) Chú ý đến bản vẽ và thiết kế xây dựng khách sạn</strong></span></span></h3>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"font-size: 14px;\">Điều này có thể quyết định rất nhiều đến việc thành bại của kinh doanh khách sạn. Không ít người thụ động chỉ chờ khách tới thuê, những việc họ có thể làm chỉ là trang bị nội thất tốt nhất. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><img src=\"/uploads/contents/1584982632_2ca36897bb63ee8d051b.jpg\" style=\"width: 633px;\" class=\"fr-fic fr-dib\"></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"font-size: 14px;\">Lưu ý đến bàn thiết kế</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"font-size: 14px;\">Thế nhưng họ quên mất một điều thiết kế bên ngoài mới để lại ấn tượng cho khách hàng trước tiên. Nếu như khiến khách hàng chú ý họ sẽ đến thuê phòng ở khách sạn của bạn.</span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"font-size: 14px;\"><strong>c) Cân đối giữa kiến trúc và nội thất</strong></span></span></h3>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"font-size: 14px;\">Việc cân đối giữa kiến trúc tổng quan và nội thất trong quá trình xây dựng khách sạn vì thế đóng một vai trò quan trọng. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"font-size: 14px;\">Ví dụ bạn định hướng khách sạn hiện đại và tiện nghi thì có thể tham khảo những thiết kế xây dựng lắp đặt hệ thống cửa khung nhôm cao cấp, kính cường lực,…. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><img src=\"/uploads/contents/1584982704_8d7eac876961c0a66fe1.jpg\" style=\"width: 627px;\" class=\"fr-fic fr-dib\"></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"font-size: 14px;\">Sự hòa hợp giữa kiến trúc và nội thất</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"font-size: 14px;\">Nếu muốn chọn theo kiểu hoàng gia hay hoài cổ thì có thể trang trí bằng các họa tiết cổ điển. Nếu chọn khách sạn xanh gần gũi với thiên nhiên thì bạn nên xây dựng ban công với nhiều vị trí đặt cây xanh.</span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"font-size: 14px;\"><strong>d) Chọn đơn vị xây dựng khách sạn uy tín</strong></span></span></h3>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"font-size: 14px;\">Không hiếm những trường hợp chủ đầu tư có rất nhiều ý tưởng hay để thiết kế khách sạn nhưng không tìm được đơn vị thi công thích hợp. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"font-size: 14px;\">Do đó ý tưởng có hay đến mấy thì khả năng bị “xếp xó” là rất cao. Muốn thực sự nhận diện đẳng cấp trong phong cách thiết kế thì bạn cần tìm cho mình những đơn vị xây dựng khách sạn với uy tín và kinh nghiệm trong thi công. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><img src=\"/uploads/contents/1584982757_69f42ce0e16ed81a344a.jpg\" style=\"width: 630px;\" class=\"fr-fic fr-dib\"></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"font-size: 14px;\">Nên chọn đơn vị xây dựng khách sạn uy tín</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Muốn thế bạn phải dựa vào thực tế các dự án, công trình mà họ đã thực hiện bởi nó phản ánh chính xác kinh nghiệm của đơn vị thi công. Chọn được đơn vị uy tín cũng xem như việc xây dựng khách sạn của bạn đã thành công một nửa rồi.</span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n', NULL, 1, NULL, 1576855823, 1590572217, NULL),
(263, 'category', 25, 3, '4. Quy trình thi công xây dựng khách sạn', '4-quy-trinh-thi-cong-xay-dung-khach-san', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Trước khi bắt đầu thi công xây dựng khách sạn, quý khách hàng hoặc chủ đầu tư cần lưu ý những thông tin như sau:</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576855696_c0921abf265d18053773.jpg\" style=\"width: 604px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Lưu ý trước khi xây khách sạn</span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>a)  Chuẩn bị thủ tục khởi công</strong></span></span></h3>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nên gửi một văn bản tới chính quyền địa phương để thông báo về việc bắt đầu xây dựng khách sạn. Đồng thời thông báo cho các hộ dân liền kề về việc xây dựng. Treo biển báo cho công trình gồm 4 bảng: biến báo công trình, cảnh báo công trình, nội quy công trình và an toàn lao động.</span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>b) Chuẩn bị nguồn lực thi công</strong></span></span></h3>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cụ thể gồm có: hồ sơ xây dựng, hồ sơ thiết kế. Lượng lượng nhân công gồm có kỹ sư, giám sát, công nhân. Nguồn cung ứng vật tư xây dựng, tập kết vật liệu, lán trại cho công nhân, hàng rào che công trình, điện nước phục vụ cho quá trình xây dựng.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chuẩn bị mặt bằng thi công xây dựng gồm có phát quang, làm sạch nền đất. Định vị công trình và xác định cao độ chuẩn. Cần phải lưu ý rằng công tác chuẩn bị mặt bằng thường không thể hiện trong hồ sơ thiết kế do đó công tác này cần được thống nhất do bên nào làm.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576855750_a4086b4acbb22eda32c9.jpg\" style=\"width: 619px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">khách sạn có nhiều tầng</span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>c) Thi công phần móng</strong></span></span></h3>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giai đoạn này rất quan trọng và nó ảnh hưởng tới tuổi thọ công trình. Các khâu đào móng gồm có: gia cố nền bằng cọc bê tông cốt thép, đào đất hố móng, thi công xây dựng khung thép móng và đổ bê tông.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mỗi phương pháp đào móng đều sẽ có ưu, nhược điểm khác nhau và nhà thầu tùy vào từng công trình mà đưa ra phương pháp tối ưu nhất. Sau khi đào móng và đổ đà kiềng, đà giằng, hầm chứa nước, hố ga, xây bể phốt,…</span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>d) Thi công phần thô thân mái</strong></span></span></h3>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phần khung của ngôi nhà chính là nơi chịu lực của công trình. Do đó khi thi công phần thô thân mái cần phải tuân theo một số nguyên tắc như: ghép cốp pha theo yêu cầu bản vẽ, đan thép, bê tông đủ tuổi mới được phép rút cốp pha.</span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>e) Thi công hoàn thiện công trình</strong></span></span></h3>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công việc chính của giai đoạn này là ốp lát, trát tường, láng sàn, lắp ráp trần, sơn bả tường, hệ thống điện thoại,hệ thống điện nước, truyền hình cáp và chống sét,…</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khi thi công hoàn thiện công trình phải đáp ứng được các quy chuẩn. Khi ốp lát phải làm đúng quy định của nhà sản xuất. Viên gạch xây cần thẳng hàng, mạch gạch đều, không bị xô lệch. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khi thi công lắp đặt hệ thống điện nước hay liên lạc phải làm theo bản vẽ kỹ thuật. Hệ thống cấp thoát nước rất quan trọng vì nếu rò rỉ có thể gây mất thẩm mỹ, mất an toàn và tốn kém nhiều cho việc sửa chữa.</span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>f) Thi công nội thất</strong></span></span></h3>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khi đã xây dựng khách sạn hoàn thiện thì tiến hành thi công nội thất gồm có lắp đặt nội thất, hoàn thiện trang trí. Các thiết bị nội thất hay cấu trúc trang trí cần được lựa chọn và lắp đặt như hồ sơ thiết kế, tiến hành tỉ mỉ, cẩn thận, chính xác để đạt hiệu quả thẩm mỹ cao.</span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>g)  Bảo hành kĩ thuật</strong></span></span></h3>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tùy vào các công trình hay đơn vị nhà thầu mà thời gian bảo hành kỹ thuật khác nhau. Có thể từ 3 đến 5 năm hay hơn. Thời gian bảo hành được tính khi công trình hoàn thành và bàn giao cho chủ nhà.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trên đây chúng tôi vừa giới thiệu đến các bạn những lưu ý cần thiết khi xây dựng khách sạn. Mong rằng sẽ là những thông tin hữu ích khi bạn tìm hiểu về vấn đề này.</span></span></p>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>h) Công ty xây dựng khách sạn</strong><br></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đội ngũ nhân viên của chúng tôi sẽ đem đến chất lượng công trình khách sạn tốt nhất. Chúng tôi luôn tự hào là một trong những công ty xây dựng khách sạn uy tín, chuyên nghiệp nhất,luôn mang đến sự hài lòng cho tất cả các quý khách hàng dù là khách hàng khó tính nhất.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575595963_c2dc3fe8a33f19ad83e9.jpg\" style=\"width: 639px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">công ty xây dựng An Gia Khang</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công ty An Gia Khang tự tin là một công ty xây dựng chuyên thiết kế, thi công xây dựng các công trình khách sạn chuyên nghiệp, uy tín nhất.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công ty xây dựng khách sạn An Gia Khang chúng tôi sở hữu đội ngũ kỹ sư, kiến trúc sư giàu kinh nghiệm và chuyên nghiệp đảm bảo mang đến một chất lượng công trình biệt thự tốt nhất cho quý khách hàng.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hợp đồng rõ ràng, có hỗ trợ và tư vấn nếu quý khách hàng có thắc mắc khi tham khảo qua hợp đồng.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Luôn thanh lý hợp đồng đúng theo những gì nội dung đã cam kết. Thực hiện chính sách bảo hành công trình theo đúng quy định.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575595983_43bd4d266dd5ed1d0356.jpg\" style=\"width: 632px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><a href=\"http://www.angiakhang.com/thi-cong-xay-dung\">Thi công xây dựng khách sạn</a></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giá thành đưa ra đảm bảo hợp lý, có cơ sở, phù hợp với mọi đối tượng khách hàng.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Luôn luôn hỗ trợ và tư vấn miễn phí cho quý khách hàng thiết kế công trình khách sạn sao cho tốt nhất và đẹp nhất.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chúng tôi còn cung cấp cho quý khách hàng hàng loạt các mẫu thiết kế khách sạn đẹp, sang trọng để quý khách hàng có thể lựa chọn một mẫu ưng ý cho mình.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n<p><strong><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Chúng tôi hân hạnh được làm việc với quý khách!</span></strong></p>\n', NULL, 1, NULL, 1576855823, 1590572217, NULL),
(266, 'news', 11, 0, '1. Thế nào là gác lửng ?', '1-the-nao-la-gac-lung-', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><strong><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nhà gác lửng</span></strong><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> còn được gọi là công trình nhà được thiết kế thêm tầng lửng, gác xép chính là một tầng trung gian giữa các tầng trong tòa nhà chính. Phần lớn các mẫu nhà cấp 4 có gác lửng đều được nhiều gia chủ ưa chuộng. Đây là kiểu kiến trúc được sử dụng khá phổ biến trong một không gian nhỏ giúp tăng thêm diện tích sinh hoạt.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577030236_085c755d0ed0219aa527.png\" style=\"width: 619px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà gác lửng giúp thoải mái hơn về không gian</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Với các ngôi nhà lớn bạn cũng có thể thiết kế tầng lửng ở phía trên với các mục đích sử dụng khác nhau như phòng ngủ, phòng khách, sinh hoạt chung. Nhà gác lửng thường được ứng dụng nhiều ở các thành phố lớn nhất là nhà gác lửng được xây dựng để cho thuê nhằm giải quyết nhu cầu về nhà ở giúp tiết kiệm cả chi phí và không gian.</span></p>\n', NULL, 1, NULL, 1577029592, 1590678781, NULL),
(267, 'news', 11, 1, '2. Những lợi ích khi thiết kế nhà có gác lửng', '2-nhung-loi-ich-khi-thiet-ke-nha-co-gac-lung', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Không phải ngẫu nhiên mà nhà có gác lửng được nhiều người ưa chuộng đến vậy, khi xây dựng nhà có gác lửng bạn có thể nhận thấy những ưu điểm như:</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577030300_0dbe27113b1e738772d3.jpg\" style=\"width: 605px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây nhà có gác lửng</span></span></h3>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.1  Tăng diện tích sử dụng cho ngôi nhà</strong></span></span></h3>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với những căn hộ có diện tích nhỏ hẹp thì việc thiết kế gác lửng được xem như là giải pháp tối ưu để tăng diện tích sử dụng. Đây gọi là biết cách tận dụng chiều cao ngôi nhà để tiết kiệm được chiều rộng.</span></span></h3>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.2 Đa chức năng</strong></span></span></h3>\n<h3 style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với những ngôi nhà có gác lửng bạn có thể dùng nó làm nơi chứa đồ đạc linh tinh hay phòng ngủ. Nếu làm nơi sinh hoạt cá nhân có thể tận dụng tầng dưới nấu nướng, kinh doanh hay làm phòng khách.</span></span></h3>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.3 Tạo điểm nhấn cho ngôi nhà</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Thiết kế gác lửng theo phong cách sang trọng và mới lạ có thể tạo nên điểm nhấn cũng như sự ấn tượng cho ngôi nhà bạn.</span></p>\n', NULL, 1, NULL, 1577029592, 1590678781, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(268, 'news', 11, 2, '3. Nhược điểm của thiết kế nhà có gác lửng', '3-nhuoc-diem-cua-thiet-ke-nha-co-gac-lung', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Ngoài ưu điểm các mẫu thiết kế nhà có gác lửng còn tồn tại các điểm hạn chế như:</span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.1 Khó trang trí đèn, trần</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế gác lửng có thể làm cho việc trang trí đèn thả, đèn chùm càng khó khăn hơn. Ngoài ra trần thạch cao cho ngôi nhà có gác lửng cũng khó mà thực hiện được. Điều này có thể gây nóng nực vào mùa hè nếu không có trần cách nhiệt.</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong> 3.2 Hạn chế hình thức trang trí</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Ngoài việc gây khó khăn cho trang trí đèn, trần, thiết kế nhà gác lửng cũng hạn chế với các hình thức trang trí như dùng vách ngăn nên không đảm bảo tính riêng tư, không bày quá nhiều đồ đạc khi dùng gác lửng làm phòng ngủ,…</span></p>\n', NULL, 1, NULL, 1577029592, 1590678781, NULL),
(269, 'news', 11, 3, '4. Kinh nghiệm xây nhà gác lửng tiết kiệm', '4-kinh-nghiem-xay-nha-gac-lung-tiet-kiem', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Như xu hướng xây dựng nhà hiện nay nhà gác lửng không những có ưu điểm mở rộng không gian mà còn làm tăng tính thẩm mỹ.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577030344_d9d90788a55d30fec06e.jpg\" style=\"width: 641px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vị trí để xây nhà gác lửng cũng rất quan trọng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Để có thể xây tầng lửng theo đúng như nhu cầu và mong muốn, tiết kiệm được cả về chi phí và không gian thì bạn nên tham khảo những kinh nghiệm cụ thể dưới đây:</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>4.1  Xác định vị trí và không gian xây nhà gác lửng</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Việc xác định xây nhà gác lửng từ giai đoạn thiết kế có thể giúp bạn giảm được chi phí xây dựng đồng thời hoàn thiện hơn về tính thẩm mỹ. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577030901_1e6382d6a24bebe2e933.jpg\" style=\"width: 631px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nội thất thiết kế đẹp</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xét về mặt không gian bạn nên lựa chọn khu vực mình muốn làm gác lửng như phòng ngủ, phòng khách. Ngoài ra nên xem xét không gian sinh hoạt xem có đủ ánh sáng, đủ rộng không, khoảng cách từ trần tới sàn có đủ chiều cao để xây thêm gác không.</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>4.2 Kích thước của nhà gác lửng</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây cũng là một bước khá quan trọng khi muốn làm gác lửng cho ngôi nhà. Thường thì diện tích gác lửng có thể chiếm tới 70% diện tích mặt sàn của khu vực chứa nó. Độ cao từ trệt tới tầng lửng thường ở trong khoảng từ 2,5 đến 2,8m. Tầng lửng sẽ chiếm khoảng 2/3 chiều sâu căn nhà.</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>4.3 Đặt cầu thang lên gác lửng</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cầu thang từ trệt lên lửng có thể đặt ở các vị trí nhỏ gọn bởi số bậc ít mà không chiếm nhiều diện tích. Cầu thang từ phần lửng lên các tầng trên có thể bố trí ở vị trí khác thuận lợi hơn và phân chia không gian một cách hợp lý.</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong> 4.4 Chọn vật liệu nhẹ cho sàn gác lửng</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hiện nay có khá nhiều loại vật liệu có thể đáp ứng theo nhu cầu làm sàn bê tông nhẹ cho gác lửng, gác xép cho nhà, gác lửng nhỏ đẹp như ván ép công nghiệp, tấm cemboard, gỗ, tấm xi măng sợi DURAflex,... Do đó bạn cần tham khảo tất cả các loại vật liệu có thể dùng làm gác và chọn ra loại tốt nhất cho nhà mình.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577030381_fb1fff2e451e9a19c96c.jpg\" style=\"width: 626px;\" class=\"fr-fic fr-dib\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nên chọn vật liệu chắc chắn và thân thiện môi trường</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Trong những trường hợp cải tạo nhà, bổ sung gác lửng hay xây nhà cấp 4 mái thái có gác lửng, cơi nới làm thêm gác xép thì gia chủ nên dùng tấm lót sàn xi măng để thuận tiện cho việc thi công mà giảm được chi phí xây dựng, thời gian hoàn thiện công trình nhanh và độ bền cao.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"> Nhờ khả năng siêu cứng, chịu ẩm, chống cháy, sản phẩm tấm lót sàn cũng được tạo khổ sẵn để thi công dễ dàng hơn khi dùng cho gác lửng hay sàn.</span></p>\n', NULL, 1, NULL, 1577029592, 1590678781, NULL),
(270, 'news', 11, 4, '5. Lưu ý khi thiết kế gác lửng', '5-luu-y-khi-thiet-ke-gac-lung', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Căn cứ vào kiến trúc, phong cách hay mục đích sử dụng gác lửng của gia chủ mà lựa chọn phong cách nhà gác lửng.</span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với các căn nhà vừa và nhỏ, có 1 tầng thì bạn có thể dùng gác lửng làm phòng khách hay phòng ngủ. Không gian phía dưới có thể dùng làm khu vực vệ sinh, bếp, nơi để xe, … Ngoài ra với các gia đình có kinh doanh tại nhà thì có thể dùng gác lửng cho phòng ngủ, phòng khách, phòng bếp, sân phơi, WC,…</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577030404_9e107a0e7840c2fbd248.jpg\" style=\"width: 628px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu bố trí hợp lý không gian sẽ càng ấn tượng hơn</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Bạn đừng lo lắng gác lửng làm mất đi tính thẩm mỹ của ngôi nhà, ngược lại có thể thấy nhà có gác lửng đẹp và tiện không kém việc chia tầng nếu như bạn biết cách bố trí hợp lý cho từng không gian.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các mẫu nhà cấp 4 có gác lửng cũng rất phổ biến ở Việt Nam. Thiết kế gác lửng cho nhà cấp 4 thường sử dụng cho các căn nhà có diện tích hạn chế. Không giống như nhà tầng, nhà cấp 4 dù có diện tích mặt bằng nhỏ hơn nhưng với sự thông minh trong thiết kế gác lửng có thể tạo nên không gian thoáng đãng, rộng rãi và hiện đại hơn. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577030873_4331196c2f2829940e1b.jpg\" style=\"width: 624px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà có gác lửng mái thái đẹp</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ngoài ra có thể vận dụng linh hoạt, tận dụng một cách triệt để khoảng trống trong nhà. Gác lửng không những dùng làm phòng bếp, phòng khách, phòng ngủ,… mà nó còn có thể là không gian nghỉ ngơi và vui chơi hoặc chứa đồ rất tiện lợi.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577030422_77883d1016eb25c86321.jpg\" style=\"width: 630px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà cấp 4 mái thái có gác lửng cũng là lựa chọn của nhiều gia đình</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Với nhà ống lại sâu thì có thể thiết kế gác lửng nằm ở phần trệt làm nơi sinh hoạt chung cho cả gia đình. Còn phòng ngủ bố trí ở tầng 2 và 3. Bạn cũng có thể dùng làm phòng khách vì khi ngồi ở đây vẫn quan sát được không gian bên dưới.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Thường thì độ cao của tầng lửng và tầng một thường là 2,5 đến 2,8m. Căn cứ vào chiều cao này bạn có thể chọn các món đồ nội thất thích hợp với không gian sinh hoạt theo mục đích của bạn để tạo nên một công trình với gác lửng đẹp.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Với các căn nhà có diện tích rộng hơn hay cả biệt thự sang trọng gác lửng vẫn có thể được ứng dụng làm khu trưng bày, đọc sách, trang trí sẽ càng tăng thêm sự trang trọng cho căn nhà của bạn.</span></p>\n', NULL, 1, NULL, 1577029592, 1590678781, NULL),
(271, 'news', 11, 5, '5.  Các mẫu thiết kế nhà có gác lửng', '5-cac-mau-thiet-ke-nha-co-gac-lung', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><br></p>\n<p style=\"text-align: justify;\"><span style=\"color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important;\">Không những có thể nhân đôi không gian sống nhà gác lửng cũng tạo nên sự ấn tượng nhất định cho kiến trúc căn hộ của gia đình bạn. </span> Xin được giới thiệu đến quý khách hãng những mẫu nhà gác lửng đẹp nhất 2020.</p>\n<p><img src=\"/uploads/contents/1577030444_d4af5727a4b071394e5f.jpg\" style=\"width: 619px;\" class=\"fr-fic fr-dib\"></p>\n<p><br></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Gác lửng có thể giúp nhà bạn thêm một phòng ngủ và làm việc</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577030474_7f9fe852d6f20ada2192.jpg\" style=\"width: 605px;\" class=\"fr-fic fr-dib\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Gác lửng làm phòng sách trong gia đình cũng rất tuyệt</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577030508_cb37636c341f0e0a0e1f.jpg\" style=\"width: 605px;\" class=\"fr-fic fr-dib\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chất liệu gỗ làm gác lửng vừa thân thiện môi trường vừa ấm cúng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577030517_1ae306ac21ad7fa65f12.png\" style=\"width: 624px;\" class=\"fr-fic fr-dib\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một phòng ngủ độc đáo và ấn tượng từ không gian của gác lửng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577030558_66bcd8ad3e4c47cd7a7c.jpg\" style=\"width: 610px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bạn cũng có thể sử dụng gác lửng vào nhiều mục đích khác nhau nữa</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577030568_c38d7fe6a71da06aeda7.jpg\" style=\"width: 602px;\" class=\"fr-fic fr-dib\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hoặc có thể làm phòng khách nếu thiết kế quan sát được bên dưới nhà</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><strong>&gt;&gt; Quý khách có thể xem thêm: </strong></span><a href=\"https://www.angiakhang.com/xay-nha-tron-goi-tai-tphcm\"><span style=\"color: rgb(235, 107, 86); font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Dịch vụ xây nhà trọn gói tại tphcm</span></a></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hi vọng với những mẫu thiết kế nhà gác lửng đẹp mà chúng tôi giới thiệu trên đây có thể giúp bạn có thêm những gợi mở về mái ấm của gia đình mình.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Mọi thông tin khách hàng vui lòng liên hệ:</strong></span></span></p>\n', NULL, 1, NULL, 1577029592, 1590678781, NULL),
(272, 'news', 12, 0, '1.  Nhà vườn nông thôn là gì?', '1-nha-vuon-nong-thon-la-gi', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><strong><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nhà vườn ở nông thôn</span></strong><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> là kiểu nhà được thiết kế và xây dựng theo phong cách truyền thống. Thay vì lên tầng gia chủ thường ưu tiên cho sân vườn. Khi xây nhà kiểu truyền thống thế này phần lớn hay sử dụng các vật liệu như cát, đất nung, vữa,…</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577100456_8655dddc5e41edf5aa31.jpg\" style=\"width: 622px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà vườn ở nông thôn với vẻ đẹp xanh mát</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"> Tuy nhiên hiện nay để rút ngắn thời gian và tiết kiệm chi phí đồng thời vẫn đảm bảo được tính thẩm mỹ cũng như công năng sử dụng người ta ưu tiên dùng thép tiền chế.</span></p>\n', NULL, 1, NULL, 1577100657, 1590043323, NULL),
(273, 'news', 12, 1, '2.  Nhà vườn ở nông thôn có lợi ích gì?', '2-nha-vuon-o-nong-thon-co-loi-ich-gi', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Kiến trúc nhà vườn ở nông thôn được nhiều người yêu thích vì nhà vườn luôn mang tới không gian thoáng mát, sống động, tự nhiên, trong lành mà vẫn có tính thẩm mỹ cao khi sử dụng. Với các mẫu nhà vườn chủ nhà có thể thỏa sức sáng tạo và thiết kế theo ý thích riêng của mình sao cho ấn tượng.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577100523_7b6ca56f48e842922f39.jpg\" style=\"width: 636px;\" class=\"fr-fic fr-dib\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiến trúc độc đáo và gần gũi với thiên nhiên</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"> Nhà vườn có thể mang đến cho gia đình bạn một không gian sang trọng, hiện đại, tiết kiệm tối đa chi phí lúc xây dựng. Một căn nhà vườn với phong thủy, hướng nhà đẹp, được trang trí đẹp mắt, gọn gàng, đơn giản nhưng vẫn đủ mang tới vẻ đẹp tinh tế và hiện đại.</span></p>\n', NULL, 1, NULL, 1577100657, 1590043323, NULL),
(274, 'news', 12, 2, '3. Kiến trúc nhà vườn nông thôn theo vùng miền', '3-kien-truc-nha-vuon-nong-thon-theo-vung-mien', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Hiện nay có một số lối kiến trúc nhà vườn có thể kể đến là:</span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.1 Kiến trúc nhà vườn ở đồng bằng Bắc Bộ</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Theo quan niệm của những người dân vùng châu thổ sông Hồng ngôi nhà không những là nơi che mưa, nắng mà cũng là nơi gìn giữ phong cách sinh hoạt văn hóa, tâm linh thuần Việt.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577100944_6577f7ec2703bc4b1563.jpg\" style=\"width: 626px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ngôi nhà vườn nông thôn đẹp 2019</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiến trúc nhà vườn ở Bắc Bộ thường là ngôi nhà 1 tầng đơn sơ với nền sát mặt đất tạo nên sự mát mẻ và thông thoáng cho không gian. Vật liệu chủ yếu được dùng xây nhà vườn là tận dụng các thứ sẵn có ở địa phương như nứa lá, tre, rơm rạ,….</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577100971_6d8df22720f4b0d485bf.jpg\" style=\"width: 635px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một vẻ đẹp hiện đại nhưng vẫn ấm cúng</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiến trúc ngôi nhà thường bao quanh bằng tre làng rồi tới cổng nhà, vườn cây, sân, nhà chính, phụ, bếp, khu vệ sinh, chuồng nuôi, ao, vườn, hàng rào bao quanh. Đây dường như là không gian sinh thái khép kín, tiện dụng mà lại tạo nên môi trường thoáng đãng và mang tính thẩm mỹ cho ngôi nhà.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Theo quan niệm của người xưa quá trình xây nhà cần phải lưu ý cả việc chọn hướng, chọn đất tới thiết kế, xây dựng đều phải tính toán hợp lý.  </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577100994_bacd73093f9c9635d176.jpg\" style=\"width: 646px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà vườn đẹp</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Người ta thường thiết kế mái nhà dốc để tránh dột và thoát nước mưa rõ ràng. Hơn nữa cũng có thể tận dụng độ dốc để làm gác lửng làm kho chứa ngô, thóc mà có thể tránh ánh nắng chiếu vào nhà. Chất liệu lợp mái của nhà vườn có thể là tranh hay ngói tùy vào điều kiện kinh tế của mỗi gia đình.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quanh nhà có thể trồng các giàn dây leo như mướp, bầu, bí vừa che nắng vừa tránh nóng tự nhiên được mà lại tận dụng rau quả làm thức ăn. Ngoài ra còn tận dụng triệt để việc trồng cây quanh nhà chọn các loại cây rau củ, hoa vừa để tạo bóng, trang trí cho ngôi nhà có thể tận dụng vào khai thác kinh tế.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.2 Kiến trúc nhà vườn Huế</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà vườn ở Huế có nhiều nét đặc sắc với các loại bể cá, cây, chuồng chim cảnh tạo nên không gian sinh động cho ngôi nhà, vừa tăng tính thẩm mỹ vừa giúp tăng giá trị kinh tế.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577101029_72eefb3b5119ff6197db.jpg\" style=\"width: 612px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà vườn ở Huế chú trọng nhiều tới cảnh quan</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xét một cách tổng quan nhà vườn ở Huế có 3 loại đó là nhà vườn ở nhà ở, trong triều đình và ở các công trình tín ngưỡng tôn giáo. Trong kiến trúc nhà vườn, khuôn viên của vườn là không thể thiếu được. Đây có thể là không gian thư giãn cũng có thể là nơi để gia đình tăng gia sản xuất. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà vườn trồng các loại cây như: cam, quýt, nhãn, xoài,.. tạo nên sự gần gũi với thiên nhiên, là bóng mát mà vẫn có thể đáp ứng được giá trị kinh tế. Điều này cũng thể hiện nét văn hóa khá đặc sắc của người Huế.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577101065_b580bdb433168032cf41.jpg\" style=\"width: 635px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà vườn nông thôn cổ kính</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"> Nhiều công trình tôn giáo ở Huế thường được trồng các loại cây như cây đại, cây đa, cây si mang đến không gian trang nghiêm, thanh tịnh mà thoáng đãng. Đây cũng là điểm dừng chân nghỉ ngơi để du khách tới thăm viếng và hành lễ.</span></p>\n', NULL, 1, NULL, 1577100657, 1590043323, NULL),
(275, 'news', 12, 3, '4. Các mẫu nhà vườn đẹp ở nông thôn', '4-cac-mau-nha-vuon-dep-o-nong-thon', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><strong><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu nhà vườn đẹp ở nông thôn</span></strong><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> không mới hơn nữa từ lâu các mẫu thiết kế nhà vườn đẹp, giá rẻ được nhiều khách hàng lựa chọn. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577101101_f7c0fc00d5403a6e7dcf.jpg\" style=\"width: 626px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà vườn nông thôn kiểu biệt thự mini</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các mẫu thiết kế này không lỗi thời và đặc biệt phù hợp với khí hậu của Việt Nam. Hơn nữa đây cũng là mẫu nhà vườn đẹp được nhiều người ưa thích.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>4.1 Mẫu nhà vườn đẹp 1 tầng</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đặc trưng của <strong>mẫu nhà vườn ở nông thôn</strong> này là có kiến trúc xếp tầng ở hệ mái. Ngói được xếp chồng lên nhau có độ dốc, ở cửa chính, phụ và cả mái che. Phong cách thiết kế này mang đến chiều sâu đồng thời giúp cho không khí trở nên ấm cúng và gần gũi.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577101134_3d4437b8977feea5898b.jpg\" style=\"width: 618px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiểu nhà vườn nông thôn sang trọng </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các <strong>mẫu nhà vườn 1 tầng mái thái</strong> cũng không mất quá nhiều thời gian cũng như chi phí xây dựng lại không cần diện tích rộng. gia chủ vẫn có thể sở hữu một không gian sống xanh, bình dị mà vẫn đáp ứng được các công năng cần thiết của ngôi nhà.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>4.2 Mẫu nhà vườn đẹp thiết kế mái đua</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà vườn này được yêu thích đặc biệt năm 2019. Do thời tiết Việt Nam có độ ẩm cao, mưa nhiều do đó tường nhà bị nấm mốc, bám bẩn. Chính bởi thế nhiều gia chủ lựa chọn mẫu thiết kế này để bảo vệ tường và che mưa. Nhờ vậy có thể giúp ngăn ngừa nấm mốc mà mang đến vẻ đẹp độc đáo cho công trình.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577101174_c15b12e32777fe4f50f6.jpg\" style=\"width: 627px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không gian sống xanh gần gũi với thiên nhiên</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà vườn có sự kết hợp độc đáo của phong cách Á Âu lại mang đến vẻ đẹp hoài cổ của kiến trúc Á Đông mà vẫn giữ được sự tự do và phóng khoáng của kiến trúc châu Âu. Do đó mẫu thiết kế này trở thành trào lưu thiết kế nhà vườn của năm 2019. Mẫu này đặc biệt phù hợp với các gia đình có khuôn viên rộng rãi với các mảng xanh quanh nhà.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577101211_dbcde9476f70c24b5d95.jpg\" style=\"width: 658px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tường nhà màu trắng và xám giúp ngôi nhà mang vẻ đẹp hiện đại</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577101229_36ca587159a73ac0bd76.jpg\" style=\"width: 652px;\" class=\"fr-fic fr-dib\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mái được thiết kế độc đáo giúp ngôi nhà thoáng mát hơn</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577101244_e0e4418ff7e15120588d.jpg\" style=\"width: 648px;\" class=\"fr-fic fr-dib\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà vườn thiết kế đẹp</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><strong>&gt;&gt; Quý khách có thể xem thêm:</strong> </span><a href=\"https://www.angiakhang.com/xay-dung-nha-pho\"><span style=\"color: rgb(235, 107, 86); font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Xây dựng nhà phố</span></a></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trên đây chúng tôi vừa giới thiệu đến các bạn một số mẫu nhà vườn nông thôn đẹp hi vọng có thể là những thông tin hữu ích cho bạn khi muốn xây dựng nhà vườn cho gia đình mình hoặc đơn giản tìm hiểu về kiểu kiến trúc này.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Mọi thông tin khách hàng vui lòng liên hệ:</strong></span></span></p>\n', NULL, 1, NULL, 1577100657, 1590043323, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(277, 'category', 45, 0, '1. Các điểm đặc trưng của nhà mái thái 2 tầng', '1-cac-diem-dac-trung-cua-nha-mai-thai-2-tang', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Để phân biệt kiểu nhà mái thái 2 tầng này với các loại hình kiến trúc khác bạn cần phải căn cứ vào hình dáng và kết cấu của nhà. Cụ thể là các chi tiết như:</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577371439_810c66f32fd87bd6dfe6.png\" style=\"width: 596px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một mẫu nhà mái thái mà nhiều người mơ ước</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.1 Nhà mái thái cấp 4</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiểu nhà này thường gặp ở khu vực nông thôn vì chi phí xây dựng thấp phù hợp với các gia đình có thu nhập vừa phải. Những ngôi nhà này thường có diện tích sàn khoảng 1000m2 đổ xuống và có dưới 3 tầng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577371509_3cb2e99e92ccbc19b3d0.png\" style=\"width: 586px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Dù nhỏ nhưng hẳn vẫn nhiều người thích mẫu nhà mái thái độc đáo này</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một ưu điểm nữa của nhà mái thái là thời gian xây dựng ngắn, có thể hoàn thành nhanh chóng mà không đòi hỏi kỹ thuật cao.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.2 Nhà mái thái 2 tầng   </strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà mái thái 2 tầng đặc biệt phù hợp với các gia đình ở thành thị. Các mẫu nhà mái thái có 2, 3 tầng rất phù hợp với những gia đình sống ở thành phố và có diện tích đất hẹp. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đảm bảo gia đình bạn có thể sở hữu một căn hộ sang trọng, nổi bật khi lựa chọn kiểu nhà mái thái này.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577371545_0f0bcb3529bc31730d53.png\" style=\"width: 601px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu có diện tích rộng nên làm vườn bao quanh để không khí trong lành</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thế nhưng với các căn nhà mái thái có từ 2 đến 3 tầng trở lên thì kỹ thuật thi công thường cầu kỳ hơn, thời gian thi công cũng dài hơn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khi lựa chọn kiểu kiến trúc này bạn cũng nên biết một số ưu điểm của nó đó là:</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.3 Về công năng sử dụng</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các mẫu nhà xây mái thái thường có khả năng tản nhiệt và chống nóng rất tốt. Bởi các mẫu nhà mái thái thường có độ dốc vừa phải. Khi mưa nước sẽ nhanh rơi xuống là không xảy ra hiện tượng bị ứ đọng trên mái. Điều này có thể bảo vệ cho mái nhà không bị ngấm nước hay thấm dột</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577371619_0fa75cde346baff45e3f.png\" style=\"width: 594px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các mẫu nhà mái thái giúp chống nóng hiệu quả</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong> 2.4 Thiết kế đa dạng</strong> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có nhiều mẫu thiết kế rất độc đáo để bạn thoải mái lựa chọn. Nhà mái thái 2 tầng có kiểu vừa cổ điển vừa hiện đại.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Những ngôi nhà thiết kế có mái thái thường có nhiều kiểu dáng hơn và có thể đáp ứng được các công trình kiến trúc khác nhau với chất liệu khác nhau.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577371645_1a576b9600a5c6e3056b.png\" style=\"width: 590px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Màu sơn trắng được dùng cho ngoại thất của nhiều công trình</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Đáp ứng được nhu cầu của gia chủ. Nói chung với kiểu nhà mái thái này sẽ có các kiểu thiết kế khác nhau để bạn có thể dễ dàng lựa chọn và xây dựng một mẫu nhà đẹp cho gia đình mình.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.5 Có tính thẩm mỹ cao</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Xu hướng xây nhà kiểu mái thái hiện nay được nhiều người lựa chọn. Có được điều này là do kiểu thiết kế đẹp mắt. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577371679_09a490358950518735e2.png\" style=\"width: 597px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sự kết hợp tinh tế khiến ngôi nhà càng nổi bật hơn</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không thể phủ nhận một điều là các kiến trúc mái thái có thể tôn lên vẻ đẹp thanh thoát và thoáng đãng giúp những ngôi nhà này thu hút mọi ánh nhìn của khách khứa nhờ sự nổi bật và độc đáo.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.6 Lưu ý khi lợp mái nhà mái thái</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quá trình thi công lợp ngói nhà mái thái bạn cần phải tính toán và đo đạc cho chính xác rồi mới thi công. Độ dốc tiêu chuẩn và cũng đẹp nhất cho kiểu nhà này là 30 độ. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577371725_0a943a2f3cc5349dcb3f.png\" style=\"width: 590px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khâu thi công mái thái đòi hỏi nhiều kỹ thuật phức tạp</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chiều xuôi của mái ngói tối đa là 10m. Với các căn hộ có độ dốc 45 độ thì có thể chọn mái ngói có độ xuôi từ 10 đến 15m. </span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nên lợp một hàng dưới cho thật chuẩn sau đó lợp dần các lớp khác lên trên.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Để có thể lợp ngói chuẩn nhất thì nên lợp từ phải sang trái, từ dưới lên trên. Cần phải chú ý được khoảng cách của ngói đều, đủ, không khít quá hay cách xa quá.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khi lợp ngói cũng cần cẩn thận để tránh vỡ ngói cũng như sự an toàn của người thợ thi công. Sau khi hợp xong nên vệ sinh cho mái, có thể lấy khăn lau chùi mái sau khi đã hoàn thành. </span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Để mái thái được đẹp hơn và bảo vệ tốt cho mái có thể dùng sơn quét cho mái.</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.7 Hạn chế của nhà mái thái</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ngoài các ưu điểm về hình thức của mái thái loại hình kiến trúc này vẫn có các hạn chế nhất định khi thi công:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577371795_326d238280036323f452.png\" style=\"width: 603px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chi phí thiết kế nhà mái thái cao</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cụ thể là một số điểm như:</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà mái thái được thi công chi tiết và tỉ mỉ. Sau một thời gian đưa vào sử dụng bạn cần phải thi công mái thái lại. Việc này thường mất nhiều thời gian và khó khăn vì nhà mái thái có rất nhiều chi tiết.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chi phí xây dựng nhà mái thái thường cao hơn mức bình thường. Lý do đưa ra là nhà mái thái đòi hỏi sự tỉ mỉ và cũng có yêu cầu khá cao.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chính bởi vậy một ngôi nhà mái thái 2 tầng có thể so với một ngôi nhà mẫu bình thường có 3 tầng.</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.8 Đặc trưng của nhà mái thái</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mỗi ngôi nhà mái thái thường có những điểm đặc trưng khác nhau tùy vào hình dáng và kết cấu của căn nhà mà chúng ta có thể phân biệt được.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577371850_7b2b7ca77a20d1d5db0c.png\" style=\"width: 592px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đặt trưng của nhà mái thái</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Do đó một ngôi nhà mái thái 2 tầng có thể ngang bằng với khi xây dựng ngôi nhà 3 tầng mẫu nhà bình thường khác.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mỗi kiểu nhà mái thái sẽ có những đặc điểm khác nhau. Tuỳ theo hình dáng cũng như kết cấu của căn nhà mà chúng ta có thể phân biệt được các kiểu nhà mái thái.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ví dụ: nhà cấp 4 mái thái thường là những ngôi nhà mà có tổng diện tích mặt sàn chưa đến 1000m2, số tầng không đến 3 tầng. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Những ngôi nhà này thường được xây dựng ở nông thôn bởi người dân có diện tích đất rộng, kiểu nhà này cũng đặc biệt phù hợp với mức thu nhập khiêm tốn của họ.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chi phí để xây dựng nhà cấp 4 mái thái thường khá thấp và có thể hoàn thành trong thời gian ngắn mà không đòi hỏi kỹ thuật cao.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ở thành phố không có nhiều các ngôi nhà cấp 4 mái thái như nông thôn mà thay vào đó là những ngôi nhà mái thái có 2 tầng trở lên.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thi công nhà mái thái 2 tầng là sự lựa chọn thích hợp với nhiều gia đình ở thành phố bởi diện tích đất ở đây không nhiều. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế mái thái mang đến cho căn nhà của bạn vẻ đẹp thu hút và hiện đại. Thế nhưng các căn nhà mái thái nhiều tầng đòi hỏi các kỹ thuật thi công phức tạp, thời gian thi công khá dài. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Và điều quan trọng để hoàn thiện một căn nhà theo lối kiến trúc này chi phí mà gia chủ phải bỏ ra không hề nhỏ.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Kiểu nhà mái thái 2 tầng hiện nay đang được nhiều người ưa chuộng. Do đó nếu cũng đang muốn tìm cho gia đình mình một mẫu nhà đẹp thì đừng bỏ qua nó nhé.</span></p>\n', NULL, 1, NULL, 1577371898, 1590064630, NULL),
(282, 'category', 45, 1, '2. Những mẫu nhà vuông 2 tầng mái thái đẹp nhất 2019', '2-nhung-mau-nha-vuong-2-tang-mai-thai-dep-nhat-2019', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Trong số nhiều mẫu nhà được ưa chuộng hiện nay mẫu nhà vuông 2 tầng mái thái được nhiều gia chủ tin tưởng lựa chọn. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577372700_9313191416c2fcde2c33.png\" style=\"width: 587px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một cấu trúc nhà vuông độc đáo khác khiến nhiều người ấn tượng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có được điều này là bởi mô hình kiến trúc này có thể đáp ứng các công năng như sinh hoạt dễ dàng, trang trí nội thất dễ dàng,… Do đó các mẫu thiết kế này dù đơn giản mà vẫn được nhiều người ưa chuộng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà vuông mái thái dù đơn giản nhưng rất đẹp mắt và ấn tượng. Đây cũng là mẫu thiết kế được nhiều người lựa chọn cho mái ấm của mình.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà vuông 2 tầng này có màu sơn ngoại thất trắng tinh ngôi và mái thái khá đơn giản nhưng vẫn rất ấn tượng và nổi bật. Các mẫu thiết kế này có thể dễ dàng bố trí nội thất một cách dễ dàng và hợp lý nhất.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577373049_f2ce6a3e3dca15649f96.png\" style=\"width: 593px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà 2 tầng đẹp</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà vuông 2 tầng này lại có hệ thống mái thái đa dạng và phức tạp. Phần mái chính được chia nhỏ thành các hệ mái nhỏ ghép với nhau giúp ngôi nhà có được vẻ góc cạnh và nổi bật hơn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế nhà vuông 2 tầng này đặc biệt thích hợp với các gia đình có diện tích đất rộng. Đây là mẫu thiết kế với giá thành không quá cao, chỉ khoảng từ 700-1 tỷ đồng. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà vuông 2 tầng này tuy đơn giản nhưng lại rất nổi bật với sơn ngoại thất màu trắng, mái lợp ngói với màu xanh ghi thanh thoát.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế nhà hình vuông này đẹp và sang trọng. Nếu muốn xây dựng ngôi nhà của mình đẹp như một căn biệt thự thì bạn không nên bỏ qua mẫu thiết kế này. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577373074_0a39ac9d76a6208ab33a.png\" style=\"width: 571px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 2 tầng giống biệt thự mini</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khi xây dựng ngôi nhà cho gia đình mình ai cũng mong muốn xây được một căn hộ đẹp và ấn tượng. Đây rất có thể chính là gợi ý tuyệt vời cho bạn khi muốn xây dựng mái ấm cho gia đình mình.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà vuông 2 tầng này rộng 80m2 và có 3 phòng ngủ. Mái ngói màu nâu mang đến vẻ đẹp sang trọng, khỏe khoắn và hiện đại. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà màu trắng này mang một vẻ đẹp nhẹ nhàng và tinh tế. Dù đơn giản nhưng lại rất ấn tượng. Các khung cửa mở rộng thân thiện với thiên nhiên mang đến không gian sống thoáng mát dễ chịu cho các thành viên trong gia đình bạn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà vuông này tuy đơn giản nhưng được đánh giá là rất đẹp. Ngôi nhà có 2 cửa vào nhà 1 cửa chính và 1 cửa phụ được thiết kế rất tinh tế.  </span></span></p>\n<p style=\"text-align: justify;\"><br></p>\n<p style=\"text-align: center;\"><img src=\"/uploads/contents/1577372804_7ec23637faf83f698244.png\" style=\"width: 595px;\" class=\"fr-fic fr-dib\"></p>\n<p><br></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu ngôi nhà vuông 2 tầng mái thái 110m2 đẹp</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu ngôi nhà vuông đẹp này có 2 tầng, 5 phòng ngủ, 1 phòng thờ với tổng diện tích xây dựng 110m2 sàn thiết kế ở chính giữa của khu đất. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Căn nhà vuông đẹp đảm bảo chuẩn kết cấu thoáng khí với tầng trệt 4m, tầng 2 cao3m8 mái đổ với kiểu mái vát lợp ngói không lo bị thấm dột.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577372824_6e396b6d1517e348d856.png\" style=\"width: 574px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một mẫu nhà vuông gần gũi với thiên nhiên được nhiều người ưa chuộng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà này có không gian bếp nằm riêng nên mặt bằng không tung ra. Mục đích chính của không gian chủ yếu là để tiếp khách và ngủ nghỉ bên trong. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu căn nhà vuông 2 tầng này đẹp và thiết kế theo cấu trúc villa mini hiện đại với ngoại thất sơn màu trắng mái lợp ngói xanh ghi độc đáo. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chân tường có ốp đá rối với hệ thống cửa kính khung gỗ mộc sang trọng giúp ngoại thất ngôi nhà xinh có độ thanh lịch và tương  phản vừa cổ kính vừa đơn sơ. </span></span></p>\n<p style=\"text-align: justify;\"><br></p>\n<p style=\"text-align: center;\"><img src=\"/uploads/contents/1577372842_8e6ac484b7a69c4086c5.png\" style=\"width: 600px;\" class=\"fr-fic fr-dib\"></p>\n<p><br></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sử dụng kính cường lực cho cửa sổ giúp không gian sáng và thoáng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế nhà 2 tầng ở nông thôn có tổng diện tích mặt sàn là 110m2 với thiết kế ngoài mặt là khoảng sân rộng. Trang hoàng có gần khoảng thông tầng 2 bên.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577372866_249b226c5e4d6eba7ef2.png\" style=\"width: 603px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sân vườn bao quanh giúp không gian xanh mát dễ chịu</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xung quanh ngôi nhà có thiết kế bồn hoa trồng cây cảnh để tránh bụi bẩn và cũng giúp nhìn từ bên ngoài vào không gian hòa nhã và khang trang hơn. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ngoài ra ở ngoài cột tòa nhà vuông đẹp này có thêm 2 tuýp đèn Neon buổi tối bật lên trông thật tráng lệ.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577372885_3faa4058d9ce4a6b1143.png\" style=\"width: 581px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một mẫu nhà vuông theo lối kiến trúc tân cổ điển khá độc đáo</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu ngôi nhà vuông 1 trệt 1 lầu với 5 phòng ngủ mái ngói có kiến trúc villa. Tòa nhà vườn mini kiểu châu Âu được phủ rộng viền mái đổ bê tông cốt thép được kẻ viền trang trí có dẫn giải pháp thu nước. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Từ phòng thờ đi ra có 4 cánh cửa rộng và thoáng, ban công dùng kính cường lực hai bên có hai chậu cây cảnh càng tạo điểm nhấn cho ngôi nhà đẹp theo xu hướng thân thiện với môi trường. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577372909_073f7fcaf3120805b41c.png\" style=\"width: 565px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mái thái màu xanh giúp ngôi nhà như bừng sáng lên</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ngoài ra mẫu nhà vuông 2 tầng mái thái ở nông thôn này còn được phối thêm tông màu vàng nhạt ở các đường diềm trang trí bao quanh tường. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mặt ngoài kẻ chỉ trắng với đường thẳng cô đọng, gọn và tinh tế khiến cho tổng thể kiến trúc ngôi nhà xinh càng sang trọng và đẹp hơn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577372934_af51372b9859ce609da2.png\" style=\"width: 581px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chỗ để ô tô thông thoáng bảo vệ sức khỏe gia đình bạn</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một phần không thể bỏ qua chính là chỗ để ô tô. Với diện tích và khuôn viên rộng thì việc bố trí chỗ để xe ngoài trời với tấm lợp kính tránh mưa chứ không phải để trong nhà thực sự là ý tưởng tốt, giúp bảo vệ sức khỏe cho gia đình. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577372959_c5a9d3d8a594c380d60e.png\" style=\"width: 579px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Màu sắc có vai trò quan trọng với mỗi ngôi nhà</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với những ngôi nhà 2 tầng mái thái có diện tích hơn 100m2 ở nông thôn thì việc kết hợp mái thái giúp gia đình có một không gian sống thoải mái. Có thể đáp ứng nhu cầu sử dụng của một gia đình có đông thành viên từ 2 đến 3 thế hệ với từ 7 đến 9 thành viên an dưỡng thoải mái.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577372990_6456e69fb4ee1118165f.png\" style=\"width: 559px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: center; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important;\">Các gam màu được phối kết hợp hài h</span>òa và độc đáo</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Với căn nhà vuông đẹp mắt với viền trắng trên nền mái ngói xanh lạ mắt mang đến sự khác lạ và độc đáo hơn hẳn so với các mẫu nhà vuông đẹp có mái thái chữ A và khung kèo thép lợp ngói bình thường đơn giản mà chúng ta hay thấy ở quê.</span></p>\n', NULL, 1, NULL, 1577373250, 1590064630, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(284, 'category', 45, 2, '3. Kinh nghiệm xây dựng nhà 2 tầng mái thái', '3-kinh-nghiem-xay-dung-nha-2-tang-mai-thai', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Hiện nay, theo quan đểm xây nhà hiện đại thì mái nhà không đơn thuần là bảo vệ ngôi nhà khỏi những tác động của tự nhiên mà còn là điểm nhấn tạo nên nét thẩm mỹ, bên cạnh đó cũng ảnh hưởng trực tiếp đến sự lưu thông khí cho ngôi nhà. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577374669_502ef0258d92c23ea8d5.png\" style=\"width: 579px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:19px;font-family:\"Times New Roman\",\"serif\";text-align:center;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><em>Mẫu nhà nhà 2 tầng mái thái đẹp, đơn giản, hiện đại</em></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Trên thị trường hiện nay xây nhà mái thái đang trở thành xu hướng, được nhiều gia chựa chọn. Vậy nhà mái thái có ưu điểm gì? Những mẫu nhà 2 tầng mái thái đơn giản, đẹp sẽ được chúng tôi tổng hợp qua bài viết dưới đấy. Mời các bạn tham khảo.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ngày nay, nhu cầu xây dựng nhà ống hay nhà cao tầng ngày càng tang, có thể nói đó là một lựa chọn tối ưu cho các gia đình sinh sống tại các thành phồ lớn, có diện tích đất hạn hẹp. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vậy xây dựng nhà ống 2 tầng mái thái làm sao cho vừa tiết kiệm, vừa ấn tượng, và đủ công năng sử dụng cho sinh hoạt của gia đình. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Dưới đây là kinh  nghiệm xây dựng nhà 1 trệt 2 tầng diện tích nhỏ cùng những điều cần biết khi xây dựng nhà hữu ích dành cho bạn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577374622_6ccbb1351e9e74c0e876.png\" style=\"width: 576px;\" class=\"fr-fic fr-dib\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà 2 tầng mái thái tân cổ điển đẹp</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.1 Tìm hiểu về thủ tục pháp lý.</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Gia chủ cần phải chú ý đến khu vực đất mà bạn quyết định xây nhà đã được cơ quan nhà nước có thẩm quyền cấp giấy phép xây dựng hay chưa. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trường hợp này rất cần thiết khi bạn xây dựng nhà ở các thành phố, còn đối với nông thôn có một số nơi vẫn chưa cần đến thủ tục này. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tuy nhiên, bạn cần dành thời gian tìm hiểu kỹ và xin giấy phép xây dựng cũng như những giấy tờ liên quan.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.2 Xác định ý tưởng kiến trúc cho căn nhà 2 tầng mái thái</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tùy theo nhu cầu sử dụng và sở thích của mỗi gia chủ mà lựa chọn ý tưởng thiết kế, kiến trúc phù hợp riêng. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Gia chủ nên tham khảo thêm ý kiến của các kiến trúc sư để đưa ra phương án hợp lý nhất. Khi đã xác định rõ phong cách thiết kế cho căn nhà 2 tầng má thái thì cần xem xét đến vấn đề chức năng sử dụng của ngôi nhà như diện tích, vị trí, số lượng phòng…</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.3 Lựa chọn đơn vị thiết kế uy tín.</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây là khâu quan trọng trong vấn đề xây dựng nhà, gia chủ cần trao đổi ý kiến thiết kế với kiến trúc sư. Bạn hãy nêu ra quan điểm của mình về ý tưởng thiết kế cho ngôi nhà, cũng như điều mà mình mong muốn. Kiến trúc sư sẽ giúp bạn thiết kế nên một ngôi nhà 2 tầng mái thái đơn giản nhưng đầy đủ tiện nghi và tính thẩm mỹ cao.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.4 Kinh nghiệm lựa chọn vật liệu xây dựng.</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vật liệu xây dựng là yếu tố quan trọng hàng đầu để tạo nên một ngôi nhà đẹp. Do đó, bạn không nên lựa chọn những vật liệu kém chất lượng, giá thành quá rẻ sẽ ảnh hưởng trực tiếp đến chất lượng công trình cũng như tính thẩm mỹ của ngôi nhà. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Gia chủ cần tham khảo các mức giá nguyên vật liệu của nhiều nhà cung cấp để lựa chọn loại vật liệu phù hợp nhất với ngôi nhà của mình. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bạn nên chia nguyên vật liệu thành hai loại là: vật liệu xây dựng phần thô và vật liệu xây dựng hoàn thiện.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>5.5 Lựa chọn nhà thầu xây dựng uy tín.</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Gia chủ cần lựa chọn nhà thầu xây dựng uy tín, đáp ứng đầy đủ các yếu tố về chuyên môn cũng như tuân thủ quy trình an toàn lao động và kinh nghiệm xây dựng nhà ở và biết đến những điều cần tránh khi xây nhà. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Gia chủ nên lựa chọn nhà thầu xây dựng đáp ứng đầy đủ 2 yếu tố sau để có thể biến ngôi nhà thiết kế trên giấy của bạn trở thành hiện thực. Cụ thể:</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đội ngũ xây dựng có trình độ về chuyên môn kỹ thuật.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đáp ứng được thời gian và tiến độ thi công xây dựng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giá thành thi công xây dựng rõ ràng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đảm bảo về an toàn lao động.</span></span></li>\n</ul>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3.6 Tính toán thời gian và chọn thời điểm khởi công xây dựng nhà</strong></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Chúng tôi khuyên bạn nên bắt đầu thi công vào mùa hè, khi thời tiết có khí hậu khô ráo như vậy sẽ thuận lợi cho việc thi công và ít bị gián đoạn. </span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Điều này sẽ đảm bảo được chi phí, giảm thất thoát vật tư, hoàn thành đúng theo dự kiến.</span></p>\n', NULL, 1, NULL, 1577374914, 1590064630, NULL),
(285, 'category', 45, 3, '4. 8 Mẫu nhà 2 tầng mái thái đơn giản.', '4-8-mau-nha-2-tang-mai-thai-don-gian', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Những năm gần đây, nhà 2 tầng mái thái đang trở nên phổ biến và được ưa chuộng. Xu hướng kiến trúc này được xu nhập từ xứ sở chùa vàng, mẫu nhà 2 tầng mái thái không chỉ mang vẻ đẹp hiện đại, trẻ trung, khả năng chống nóng, nhanh thoát nước cho phần mái. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiến trúc nhà mái thái khéo léo tôn vinh lên vẻ đẹp thanh thoát, cao ráo của ngôi nhà. Dưới đây là một số mẫu nhà 2 tầng mái thái đơn giản, đẹp.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>4.1. Mẫu nhà 2 tầng mái thái đơn giản – Mẫu 01</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577374731_2b56a57271802229a269.png\" style=\"width: 586px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà 2 tầng mái thái đơn giản – Mẫu 01</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà 2 tầng mái thái đơn giản được thiết kế lấy màu trắng làm chủ đạo, phần mái thái màu xanh làm nổi bật ngôi nhà. Căn nhà tuy đơn giản nhưng mang vẻ đẹp sang trọng, hiện đại.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>4.2. Mẫu nhà 2 tầng mái thái đơn giản – Mẫu 02</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577374745_30b9c650f0de9bbe182e.png\" style=\"width: 577px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà 2 tầng mái thái đơn giản – Mẫu 02</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà 2 tầng mái thái này đơn giản, không quá cầu kỳ cùng những chi tiết rườm rà bởi những đường nét ngoại thất đơn giản và nhẹ nhàng. Ngoại thất căn nhà mang vẻ trầm ấm và lịch lãm.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>4.3. Mẫu nhà 2 tầng mái thái đơn giản – Mẫu 03</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577374766_45ae9b76b5956452273a.png\" style=\"width: 583px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà 2 tầng mái thái đơn giản – Mẫu 03</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chiêm ngưỡng nhà 2 tầng mái thái với màu ngói nâu được thiết kế những khối vuông vắn của thân tường, tạo cảm giác vững chắc và bền vững.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>4.4. Mẫu nhà 2 tầng mái thái đơn giản – Mẫu 04</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577374783_52ae386ff4b66767b009.png\" style=\"width: 584px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà 2 tầng mái thái đơn giản – Mẫu 04</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà 2 tầng mái thái đơn giản mang kiến trúc hiện đại, người nhìn sẽ bị thu hút bởi vẻ đẹp tinh tế và bắt mắt.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>4.5. Mẫu nhà 2 tầng mái thái đơn giản – Mẫu 05</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577374804_e13a8ff00125147d6914.png\" style=\"width: 578px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà 2 tầng mái thái đơn giản – Mẫu 05</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ngôi nhà được thiết kế họa tiết trang trí sắc sảo, tạo cuốn hút trong mọi ánh nhìn. Với lan can ban công bằng sắt tạo sự an toàn cho gia chủ và không gian thoải mái.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>4.6. Mẫu nhà 2 tầng mái thái đơn giản – Mẫu 06</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577374826_10cab609c768d0df890b.png\" style=\"width: 573px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà 2 tầng mái thái đơn giản – Mẫu 06</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà ống 2 tầng mái thái thiết kế màu sắc hài hòa với gam màu trắng làm chủ đạo, kết hợp với ốp đá ghi xám tầng một khá bắt mắt, tạo nên tổng thể hoàn hảo và cuốn hút cho người nhìn.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>4.7. Mẫu nhà 2 tầng mái thái đơn giản – Mẫu 07</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577374849_3e531ec62b40fd45647b.png\" style=\"width: 554px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà 2 tầng mái thái đơn giản – Mẫu 07</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà mái thái 2 tầng đơn giản kiểu nhà vườn, sử dụng mái ngói hiện đại, cùng không gian rộng rãi, mang đến nét khác biệt cho ngôi nhà.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Trên đây là một số mẫu nhà 2 tầng mái thái đơn giản, đẹp, độc đáo các bạn có thể tham khảo và lựa chọn theo điều kiện của gia đình nhà mình.</span></p>\n', NULL, 1, NULL, 1577374914, 1590064630, NULL),
(286, 'category', 45, 4, '5. Những mẫu nhà 2 tầng mái thái nông thôn được ưa chuộng năm 2019', '5-nhung-mau-nha-2-tang-mai-thai-nong-thon-duoc-ua-chuong-nam-2019', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Không ít gia chủ hiện nay khi có nhu cầu xây dựng nhà thường lựa chọn nhà hai tầng mái thái. Vậy quá trình xây dựng cần lưu ý tới điều gì và những mẫu nhà 2 tầng mái thái đẹp hiện nay có gì đặc biệt.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bài viết dưới đây giúp các bạn hiểu thêm về các vấn đề này để tiện tham khảo.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577375864_ba80f3c35f356e865ce8.jpg\" style=\"width: 565px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà mái thái 2 tầng ở nông thôn</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Qua những mẫu nhà 2 tầng mái thái trên đây có thể nhận thấy một số ưu điểm điển hình của loại hình kiến trúc này đó là:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577375911_c9868f9e33cb5c7358d3.jpg\" style=\"width: 600px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà 2 tầng mái thái theo lối kiến trúc độc đáo</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà 2 tầng mái thái được kết hợp giữa phòng cách cổ điển và hiện đại mang đến một vẻ đẹp tinh tế.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577375990_ef77e2ef28318d48248c.jpg\" style=\"width: 593px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một mẫu kiến trúc hiện đại nhưng pha màu sắc châu Âu</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhiều gia chủ lựa chọn loại hình kiến trúc này bởi nó giúp ngôi nhà bạn trở nên thanh thoát và cao ráo hơn. Ngoài ra đây cũng là giải pháp chống thấm và chống nóng hiệu quả dưới khí hậu nóng ẩm gió mùa của nước ta.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577376009_4059cbc42629f7bc5e61.png\" style=\"width: 580px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thân thiện hơn với thiên nhiên</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">So với những mẫu thiết kế biệt thự cao tầng hay biệt thự cổ điển, các loại hình kiến trúc cầu kỳ khác thì nhà 2 tầng mái thái có chi phí thấp hơn nên phù hợp với khả năng tài chính của nhiều gia đình.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577376027_eb19b9d9c13438747bc2.jpg\" style=\"width: 545px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiến trúc ấn tượng nhờ mái thái</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cách sắp xếp và bố trí không gian của nhà 2 tầng mái thái cũng rất hợp lý và khoa học. Phòng khách có thể bố trí ở cạnh sảnh đón để nhận được nhiều vận khí tốt đẹp hợp với phong thủy.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577376043_087e3275ff45aaf2b729.png\" style=\"width: 547px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mái thái được cách tân mang vẻ đẹp độc đáo</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng ngủ ở tầng trệt có thể hạn chế việc di chuyển lên xuống cầu thang phù hợp với độ tuổi cũng như sức khỏe của người già.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <img src=\"/uploads/contents/1577376078_1a1db4da71790957455b.jpg\" style=\"width: 550px;\" class=\"fr-fic fr-dib\"><br></span></span><a href=\"https://www.angiakhang.com/xay-dung-nha-pho\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu Xây dựng nhà phố đẹp</span></span></a></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với mặt bằng cũng như công năng thiết kế khoa học bạn có thể áp dụng cho gia đình mình nếu yêu thích các mẫu thiết kế này.</span></span></p>\n<p style=\'margin:0in;margin-bottom:10.0pt;font-size:13px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;line-height:150%;\'><strong><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Chúng tôi hân hạnh được làm việc với quý khách!</span></strong></p>\n', NULL, 1, NULL, 1577376101, 1590064630, NULL),
(287, 'category', 30, 0, '1.Vậy nhà phố là gì?', '1vay-nha-pho-la-gi', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><strong>Nhà phố</strong> là nhà ở phổ biến ở thành thị, phố thị nơi mà dân cư tập trung đông, phố xá phát triển, giao thông thuận tiện cho việc đi lại và mua bán  kinh doanh.</p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\">Tên gọi chung là nhà của các thành thị, nhà có mặt tiền, có giao thông thuận tiện….</p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><strong>Vậy nhà phố có mấy loại?</strong></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\">Nhà phố có nhiều loại như xây nhà phố có sân vườn, nhà phố xanh, nhà phố thương mại…</p>\n', NULL, 1, NULL, 1577670128, 1577670920, NULL),
(288, 'category', 30, 1, '2. Nên xây nhà phố mấy tầng', '2-nen-xay-nha-pho-may-tang', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Việc xây nhà phố mấy tầng phù thuộc vào nhiều yếu tố, các bạn tham khảo các thông tin sau nhé</span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây nhà phố số tầng phù thuộc vào quy định của chủ đầu tư và quy định của Nhà nước tại các dự án nhà phố đó.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà phố xây dựng dựa trên công năng sử dụng của con người, có thể kết hợp để sinh sống hoặc xây để kinh doanh buôn bán.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây nhà phố thường đồng bộ, không đơn điệu, việc xây dựng nhiều nhà phố thể hiện được sự phát triển của khu vực, thành phố đó ngày càng lớn mạnh.</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Dân số ngày càng phát triển, diện tích xây nhà phố thường nhỏ, chính vì nhu cầu sử dụng ngày càng cao. Nhà phố là giải pháp tiết kiệm diện tích nhưng vẫn đảm báo yêu cầu sinh hoạt và phát triển của con người..</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\">Mọi thông tin cần tư vấn vui lòng liên hệ: <strong>0937181181</strong></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><strong style=\"box-sizing: border-box; font-weight: 700; color: rgb(56, 56, 56); font-family: geomanist; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Chúng tôi hân hạnh được làm việc với quý khách!</span></strong></p>\n', NULL, 1, NULL, 1577670920, 1577670920, NULL),
(290, 'category', 61, 0, '1. Lưu ý khi thiết kế nhà phố 5 tầng', '1-luu-y-khi-thiet-ke-nha-pho-5-tang', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Các mẫu nhà phố đẹp 5 tầng được các kiến trúc sư thiết kế thông thường đều có phong cách hiện đại, tận dụng tối ưu mọi công năng cũng như đảm bảo về mặt phong thủy cho gia chủ,…</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bài viết dưới đây sẽ nói rõ hơn về các mẫu nhà phố đẹp 5 tầng và những lưu ý khi xây dựng chung mà nếu bạn đang có nhu cầu tìm hiểu thì không nên bỏ qua.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577677215_a6c9bcf77b650eb01000.png\" style=\"width: 652px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 5 tầng Riverside</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các mẫu nhà phố 5 tầng được xây dựng với mục đích đầu tiên là phục vụ nhu cầu sinh hoạt của gia đình, đảm bảo các không gian sống được tiện nghi và thoải mái nhất, khai thác tối ưu mọi công năng của ngôi nhà.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577678250_442ed94c9c7c601312dd.png\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà phố 5 tầng có sân vườn</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Theo như thống kê hiện nay để đảm bảo cho sinh hoạt và các nhu cầu sống cần thiết căn cứ vào tập quán, khí hậu cũng như điều kiện con người Việt Nam thì mỗi người phải cần bình quân khoảng 7m2 không gian sống trong một gia đình.</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>a) Không gian Phòng khách</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng khách là phòng có diện tích lớn và đẹp nhất trong mọi căn nhà và đây cũng là nơi mà thể hiện một cách rõ nét nhất các đặc điểm về tính cách cũng như sở thích của gia chủ. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577678329_e8edadea48629483855d.png\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không gian phòng khách thiết kế ấn tượng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà 5 tầng có diện tích phòng khách giao động từ 14 – 30 m2. Các phòng khách thường được thông trực tiếp với phần hiên sảnh và có cửa chính thường ở dạng cửa lớn 4 cánh hoặc cửa mở 2 cánh.</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>b) Không gian phòng ăn</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng ăn là phòng không thể thiếu của mọi căn nhà các phòng ăn thường được thiết kế bao gồm với không gian bếp có diện tích tiêu chuẩn đạt từ 12 – 15 m2.</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>c) Không gian phòng ngủ</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng ngủ thông thường ở các mẫu nhà 5 tầng được thiết kế với đầy đủ các loại phòng ngủ từ phòng ngủ cho vợ chồng có diện tích từ 12 – 18 m2.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577678375_67fb3ac2f939a7f46c98.png\" style=\"width: 642px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố hiện đại</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các phòng ngủ cho cá nhân chủ yếu là con cái hoặc những người thân trong gia đình có diện tích khoảng 6m2, phòng ngủ cho tập thể có diện tích khoảng 10 -12 m2,… tùy vời số lượng người trong gia đình và nhu cầu của gia chủ mà số lượng các phòng ngủ được thiết kế và bố trí một cách thích hợp trong ngôi nhà.</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế nhà phố 5 tầng phải đảm bảo được việc tính toán một cách hợp lý các chi phí xây dựng và chi phí phải đảm bảo một cách hợp lý.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kết cấu công trình phải được tính toán một cách cẩn thận để đảm bảo sự an toàn và bền đẹp theo thời gian.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các mẫu nhà phố này phải đặt biệt lưu ý đến vấn đề giao thông đứng và nếu có điều kiện kinh tế bạn nên lắp đặt hệ thống thang máy để đảm bảo tối ưu nhất nhu cầu di chuyển trong căn nhà.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đặc biệt ở các mẫu nhà phố đẹp 5 tầng các gia chủ đều có mong muốn thiết kế để sử dụng những tầng dưới dùng vào mục đích kinh doanh (thông thường là tầng 1 và 2). </span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chính bởi vì đây đều là các ngôi nhà ở mặt tiền có điều kiện vô cùng thuận lợi cho việc kinh doanh buôn bán các loại mặt hàng khác nhau như kinh doanh café, vàng bạc, đá quý,kinh doanh mảng thời trang,…</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>d) Những ưu và nhược điểm khi xây dựng nhà phố đẹp 5 tầng </strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1577678432_ec9548572cfdc71a9ae7.png\" style=\"width: 621px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây dựng nhà phố giúp tiết kiệm diện tích với số tầng cao</span></span></p>\n<h4><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Ưu điểm:</strong></span></span></h4>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố đẹp 5 tầng đảm bảo được sự kết hợp của 2 mục tiêu đó là việc kinh doanh và nhu cầu sống của gia đình một cách thuận lợi và thoải mái nhất.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giúp cho gia chủ dễ dàng và tiện lợi trong việc quản lý các tài sản kinh doanh cũng như không tồn các khoản phí phải thuê mặt bằng nếu kinh doanh ở một mặt bằng riêng.</span></span></p>\n<h4 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Nhược điểm:</strong></span></span></h4>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đội ngũ thiết kế và thi công xây dựng phải có kinh nghiệm, chi phí đầu tư cao.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cũng chính sự tương tác giữa 2 mục đích trong ngôi nhà là kinh doanh và sinh sống nên nhiều khi còn xảy ra một số hạn chế như hạn chế không gian yên tĩnh và riêng tư cho các thành viên trong gia đình.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Thông thường mặt tiền của các mẫu nhà phố đẹp 5 tầng chưa đủ rộng chính vì thế vấn đề giao thông giữa 2 luồng sinh hoạt và kinh doanh vẫn còn một số hạn chế.</span></li>\n</ul>\n', NULL, 1, NULL, 1577677896, 1592205035, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(292, 'category', 61, 1, '2. 10 mẫu nhà phố 5 tầng đẹp 2020', '2-10-mau-nha-pho-5-tang-dep-2020', '<p style=\"text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nhà phố 5 tầng ngày này được nhiều khách ngày ưa chuộng bởi chúng đáp ứng được nhiều yêu tố. Xin giới thiệu một số mẫu nhà phố 5 tầng đẹp nhất hiện nay 2020. An Gia Khang nhà thầu xây dựng nhà phố uy tín và chất lượng</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/nha-pho-5-tang-dep-1_1592129975.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"><br></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 5 tầng diện tích 5x20</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/nha-pho-5-tang-dep-2_1592129990.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"><br></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 5 tầng có sân thượng đẹp</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/nha-pho-5-tang-dep-3_1592130007.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"><br></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 5 tầng theo phong cách cổ kính</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/nha-pho-5-tang-4_1592130021.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"><br></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 5 tầng có sân trồng rau</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/nha-pho-5-tang-5_1592130040.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"><br></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 5x20 thiết kế điểm nhấn màu đỏ ấn tượng</span></span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></p>\n<p style=\"text-align: center;\"><br></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/nha-pho-5-tang-7_1592130100.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"><br></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà phố 5x20 kiểu dáng hiện đại</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/nha-pho-5-tang-8_1592130109.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"><br></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 5x20 sân thượng rộng</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/nha-pho-5-tang-9_1592130117.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"><br></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố 5x20 theo phong cách 2 cửa</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/nha-pho-5-tang-10_1592130126.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"><br></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố thiết kế hiện đại</span></span></p>\n<blockquote><p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(44, 130, 201);\">Nhà thầu xây dựng An Gia Khang tư vấn thiết kế</span><span style=\"font-family: Arial,Helvetica,sans-serif;\">, <a href=\"https://www.angiakhang.com/xay-dung-nha-pho\"><span style=\"color: rgb(226, 80, 65);\">thi công xây dựng nhà phố</span></a> </span><span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(44, 130, 201);\">uy tín và chất lượng hiện nay. Tất cả các dự an chúng tôi triển khai đều cam kết về chất lượng, tiến độ và bảo hành sau khi hoàn công. Khách hàng, hãy yên tâm khi sử dụng dịch vụ của chúng tôi. </span></span></p></blockquote>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">[[contact_short_code]]</span></span></p>\n<p style=\"text-align: left;\"><strong style=\"box-sizing: border-box; font-weight: 700; color: rgb(56, 56, 56); font-family: Calibri, sans-serif; font-size: 13px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"box-sizing: border-box; font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Chúng tôi hân hạnh được làm việc với quý khách!</span></strong></p>\n', NULL, 1, NULL, 1577681021, 1592205035, NULL),
(295, 'category', 15, 1, '2. Chi phí xây dựng biệt thự', '2-chi-phi-xay-dung-biet-thu', '<p><span style=\"color: rgb(65, 65, 65); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; display: inline !important; float: none;\">Chi phí thi công xây dựng biệt thự còn phụ thuộc vào những yếu tố cụ thể như diện tích, khu vực xây dựng, phong cách thiết kế hay nguồn nguyên vật liệu.</span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Để có thể hoàn thiện việc xây dựng biệt thự cần phải có các khoản chi phí đầu tư như:</span></span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Chi phí thiết kế kiến trúc, c</span></span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">hi phí thi công kiến trúc, c</span></span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">hi phí hoàn thiện nội thất..</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1584035811_a130a47d4ce1e19ce54f.jpg\" style=\"width: 603px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây dựng biệt thự hiện đại</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Căn cứ vào mức giá cụ thể của các công ty kiến trúc mà chi phí có sự dao động nhất định. Thế nhưng mức chênh lệch thường không nhiều.  </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chi phí hoàn thiện nội thất buộc phải kể tới các chi phí mua sắm nội thất, hay trang trí tủ, bàn ghế, giường, thiết bị điện, nội thất nhà bếp,…</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1584035919_134cdfa516ab06e93b34.jpg\" style=\"width: 596px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xác định đơn giá xây dựng biệt thự để chủ động hơn về tài chính</span></span></p>\n<h3><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>a) Giá xây biệt thự trọn gói bao nhiêu tiền?</strong></span></span></h3>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ở những thành phố lớn như Hà Nội hay TP Hồ Chí Minh, Quảng Ninh, Hải Phòng các biệt thự được thiết kế theo phong cách tân cổ điển hay cổ điển.</span></span></p>\n<ul>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chi phí xây dựng trung bình từ 6 đến 7 triệu/m2 sàn. Với khu vực xây dựng tại các tỉnh, thành phố chi phí dao động từ 4,5 tới 6 triệu/m2 sàn</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chi phí xây dựng biệt thự cổ điển thường từ khoảng 7 đến 8 triệu/m2. Nguyên nhân là bởi kiểu kiến trúc này đòi hỏi sự phức tạp trong thiết kế cũng như thi công nên chi phí thường cao hơn.</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với các kiểu dinh thự, lâu đài, siêu biệt thự mức đầu tư có thể hơn 10 triệu/m2</span></span></li>\n</ul>\n<h3><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>b)  Cách tính chi phí xây dựng biệt thự trên m2</strong></span></span></h3>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhiều gia chủ và chủ đầu tư muốn biết cách tính chi phí xây dựng biệt thự tính theo m2. Để giải đáp thắc mắc này chúng tôi cũng sẽ giới thiệu chi tiết dưới đây. Cách tính này được nhiều chuyên gia đánh giá là đơn giản. Bạn có thể khái quát tổng thể về chi phí hoàn thiện công trình của mình một cách nhanh nhất. </span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1584035963_84716323ba355ec3faa6.jpg\" style=\"width: 607px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có nhiều cách tính chi phí xây dựng biệt thự có thể áp dụng</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Để giúp các bạn có thể hình dung rõ hơn về cách tính chi phí xây dựng biệt thự theo m2, chúng tôi giới thiệu một ví dụ cụ thể như sau:</span></span></p>\n<ul>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giá xây dựng phần thô: từ 2.600.000đ đến 3.500.0000đ/m2 xây dựng. Mức giá dao động tùy vào yêu cầu, quy mô công trình.</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giá xây dựng và hoàn thiện trọn gói: từ 4.000.000đ đến 7.000.000đ/m2. Mức giá dao động căn cứ vào quy mô và chủng loại vật tư theo yêu cầu.</span></span></li>\n</ul>\n<h3><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>c) Xây nhà biệt thự mini khoảng bao nhiều tiền? </strong></span></span></h3>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đ</span></span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">ây là câu hỏi của rất nhiều gia chủ muốn tìm kiếm đơn vị thầu xây dựng uy tín và chất lượng. Nhà thầu xây dựng biệt thự An Gia Khang xin gửi đến quý khách hàng các cách tính sau:</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Cách tính chi phí xây dựng biệt thự như sau: </strong></span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>+ Cách tính 1:</strong> Móng băng từ 30% đến 50% đơn giá xây thô.</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>+ Cách tính 2:</strong> Diện tích xây dựng các tầng tính 100% đơn giá.</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>+ Cách tính 3: </strong>Ví dụ: Công trình biệt thự sân vườn nhà chị Minh, Đồng Nai có diện tích là: rộng 11m x dài 16m = 176m2, gồm 2 tầng. Đơn giá trọn gói là: 5.500.000đ/m2. Đơn giá xây thô 2.800.000đ/m2.</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cách tính giá là: 506m2 sử dụng + chi phí móng băng 30%/đơn giá thô.</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Móng tính 50% đơn giá xây thô/m2 = 1.400.000 x 253m2  = 147.840.000 đồng.</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Diện tích sử dụng 176m2 x 2 tầng = 352m2 x 5.500.000 = 1.936.000.000 đồng.</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tổng đơn giá: 147.840.000 + 1.936.000.000 = 2.083.840.000 đồng. (Hai tỷ không trăm tám mươi ba triệu tám trăm bốn mươi nghìn đồng chẵn).</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Việc thiết kế và thi công xây dựng biệt thự thể hiện sự mạnh mẻ của gia chủ về vấn đề tài chính như muốn khẳng định vị thế của gia chủ, vì vậy khi nhà thầu sở hữu bản thiết kế biệt thự phải có sự thiết kế phù hợp cho chính gia chủ của bạn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">Chúng tôi luôn tự hào là một trong những <strong>công ty xây dựng biệt thự </strong>uy tín, chuyên nghiệp nhất,luôn mang đến sự hài lòng cho tất cả các quý khách hàng dù là khách hàng khó tính nhất.</span></span></span></p>\n<p style=\"margin: 0in 0in 10pt; font-size: 13px; font-family: Calibri, sans-serif; text-align: center; line-height: 150%;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1584036014_a3a7a6e21da5e7eb1247.jpg\" style=\"width: 616px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; font-size: 13px; font-family: Calibri, sans-serif; text-align: center; line-height: 150%;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><em><span style=\"line-height: 150%;\">An Gia Khang tự tin luôn mang đến chất lượng công trình tốt nhất </span></em></span></span></p>\n<p style=\'margin:0in;margin-bottom:10.0pt;font-size:13px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;line-height:150%;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">Công ty An Gia Khang tự tin là một công ty xây dựng chuyên thiết kế, thi công xây dựng các công trình biệt thự chuyên nghiệp, uy tín nhất.</span></span></span></p>\n<p style=\'margin:0in;margin-bottom:10.0pt;font-size:13px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;line-height:150%;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\"><strong>Công ty xây dựng biệt thự</strong></span><span style=\"line-height: 150%; color: black;\"><strong> An Gia Khang</strong> chúng tôi sở hữu đội ngũ kỹ sư, kiến trúc sư giàu kinh nghiệm và chuyên nghiệp đảm b</span><span style=\"line-height: 150%;\">ả<span style=\"color:black;\">o mang đến một chất lượng công trình biệt thự tốt nhất cho quý khách hàng.</span></span></span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; font-size: 13px; font-family: Calibri, sans-serif; text-align: justify; line-height: 150%;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">Hợp đồng rõ ràng, có hỗ trợ và tư vấn nếu quý khách hàng có thắc mắc khi tham khảo qua hợp đồng.</span></span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; font-size: 13px; font-family: Calibri, sans-serif; text-align: justify; line-height: 150%;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">Luôn thanh lý hợp đồng đúng theo những gì nội dung đã cam kết. Thực hiện chính sách bảo hành công trình theo đúng quy định.</span></span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; font-size: 13px; font-family: Calibri, sans-serif; text-align: justify; line-height: 150%;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">Giá thành đưa ra đảm bảo hợp lý, có cơ sở, phù hợp với mọi đối tượng khách hàng.</span></span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; font-size: 13px; font-family: Calibri, sans-serif; text-align: justify; line-height: 150%;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">Luôn luôn hỗ trợ và tư vấn miễn phí cho quý khách hàng thiết kế công trình biệt thự sao cho tốt nhất và đẹp nhất.</span></span></span></li>\n</ul>\n<p style=\'margin:0in;margin-bottom:10.0pt;font-size:13px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;line-height:150%;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\">Chúng tôi còn cung cấp cho quý khách hàng hàng loạt các mẫu thiết kế biệt thự đẹp, sang trọng để quý khách hàng có thể lựa chọn một mẫu ưng ý cho mình. Để nhận được <strong>đơn giá xây dựng biệt thự</strong> chính xác nhất vui lòng liên hệ: 0937 181 181 để được nhân viên tư vấn và báo giá cho quý khách hàng.</span></span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">[[contact_short_code]]</span></p>\n', NULL, 1, NULL, 1577952947, 1590571782, NULL),
(297, 'category', 26, 0, '1. Xây dựng nhà xưởng công nghiệp', '1-xay-dung-nha-xuong-cong-nghiep', '<p><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Xây dựng nhà xưởng công nghiệp là một trong những loại hình xây dựng nhà khung thép được nhiều chủ doanh nghiệp lựa chọn. Vì vậy bản thiết kế nhà xưởng cần đặc biệt chú trọng, thiết kế nhà xưởng sẽ gồm rất nhiều loại hình khác nhau, tùy vào quy mô, hoạt đọng sản xuất của doanh nghiệp, lối kiến trúc,…</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575555485_3225a01d34582f30b161.jpg\" style=\"width: 596px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Lắp mặt bằng chuẩn bị xây nhà xưởng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tuy nhiên, đa số các kỹ sư đều thực hiện dựa trên các nội dung của bản vẽ thiết kế nhà xưởng. Nội dung thiết kế của bản vẽ gồm  những gì?, truy trình thực hiện ra sao, tuổi thọ của nhà xưởng công nghiệp bao lâu? ..mời quý bạn đọc xem tiếp phần diễn giải bên dưới nhé.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một doanh nghiệp khi chọn thi công xây dựng nhà xưởng công nghiệp, nên tìm hiểu các đơn vị nhà thầu, đơn vị tư vấn, và có năng lực thi công loại hình này. Trước khi bắt tay vào xây dựng nhà xưởng, chủ doanh nghiệp nên yêu cầu bên đơn vị thi công tư vấn và thiết kế bản vẽ nhà xưởng công nghiệp trước khi tiến hành xây dựng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Theo kinh nghiệm của chúng tôi nội dung bản thiết kế nhà xưởng gồm 2 phần như sau:</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong><img src=\"/uploads/contents/1587359438_47f5e461d89ac0ed52be.png\" style=\"width: 619px;\" class=\"fr-fic fr-dib\"></strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà xưởng công nghiệp</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>a) Phần thuyết trình bản vẽ thiết kế nhà xưởng</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đối với đơn vị thi công xây dựng nhà xưởng, làm bản vẽ thiết kế nhà xưởng là đều bắt buộc. Trước khi lên kế hoạch, trước khi lên bản thiết kế nhà xưởng công nghiệp, đơn vị thầu sẽ có sự trao đổi với doanh nghiệp về phương án thi công xây dựng như: vị trí đặt xưởng, địa điểm xây dựng, quy mô diện tích, mặt bằng,…</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>b) Phần nôi dung thiết kế bản vẽ nhà xưởng</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với bản vẽ thiết kế nhà xưởng công nghiệp gồm : 1 bản vẽ tổng thể trên bề mặt xây dựng, sơ đồ dây chuyền công nghệ, bản vẽ khung thép, vị trí lắp đặt các thiết bị trong nhà xưởng, liệt kê phương án kiến trúc, kết cấu xây dựng, kết cấu hạ tầng..</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>c)  Một số lưu ý khi thiết kế và thi công xây dựng nhà xưởng công nghiệp</strong></span></span></h3>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sự nhiễm bẩn được giảm thiểu xuống mức thấp nhất.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế mặt bằng nhà xưởng phải bố trí thuận tiện cho phép dễ dàng duy tu bảo dưỡng, làm sạch và tẩy trùng để hạn chế ở mức tối thiểu ô nhiễm do không khí.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các bề mặt và vật liệu phải không độc hại đối với mục đích sử dụng và nơi cần phải có độ bền phù hợp.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà xưởng phải có sẵn các phương tiện cần thiết để kiểm soát nhiệt độ, độ ẩm không khí và nhiều kiểm soát khác.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có biện pháp bảo vệ hiệu quả chống dịch hại xâm phạm và khu trú.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết bị máy móc phải bố trí để cho phép duy tu bảo dưỡng và làm sạch dễ dàng. </span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mọi thông tin tư vấn thiết kế xây dựng nhà xưởng công nghiệp, quý khách vui lòng liên hệ: 0937 181 181</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">[[contact_short_code]]</span></p>\n', NULL, 1, NULL, 1578360904, 1590570749, NULL),
(298, 'category', 26, 1, '2. Nguyên tắc thiết kế bản vẽ nhà xưởng công nghiệp', '2-nguyen-tac-thiet-ke-ban-ve-nha-xuong-cong-nghiep', '<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif;\">Trong quá trình xây dựng nhà xưởng, chủ đầu tư cũng phải tính toán đến những yếu tố phục vụ cho nhu cầu hoạt động sau này của nhà xưởng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1587359495_0e87b2eb34d5aad26eae.png\" style=\"width: 632px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà xưởng khu công nghiệp</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bản vẽ thiết kế nhà xưởng khung thép sẽ ảnh hưởng tới chất lượng công trình sau này. Vì vậy, những người kỹ sư trong quá trình thiết kế nhà xưởng cần hết sức chú trọng và phải nắm rõ các nguyên tắc.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tiến hành phân tích dự án nhà xưởng, dự toán các phương án sơ bộ để tiến hành bản vẽ chi tiết.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quy tắc thiết kế nhà xưởng phải theo đúng nhu cầu khách hàng. Phải có sự trao đổi và giải thích rõ ràng cho chủ doanh nghiệp, giúp khách hàng nắm được các thông tin sơ bộ về nhà xưởng sẽ được hoàn thiện trong tương lại như thế nào.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cần có các biện pháp dự phòng, điều chỉnh bản vẽ thiết kế cho phù hợp, nếu trong quá trình xây dựng có sự thay đổi cần thiết.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiểm tra và đánh giá bản vẽ thiết kế nhà xưởng, quy tắc kiểm tra các sai sót trong quá trình làm bản vẽ. Hoàn tất thiết kế, giao cho chủ doanh nghiệp xem xét và sửa đối nếu cần thiết.</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1587359579_c76a30e07dab5fa88c15.png\" style=\"width: 626px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà xưởng công nghiệp sản xuất vải</span></span></p>\n', NULL, 1, NULL, 1578361317, 1590570749, NULL),
(303, 'news', 13, 0, '1. 3 Khách sạn được xây dựng sớm nhất ở Sài Gòn', '1-3-khach-san-duoc-xay-dung-som-nhat-o-sai-gon', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Chia sẽ thông tin thú vị các bạn nên biết. Khách sạn được xây dựng sớm nhất ở sài gòn thời pháp thuộc hiện nay mang tên gì? Dưới đây là 3 khách sạn được xây dựng sớm nhất ở Sài Gòn xưa.<br></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Thứ 1:</strong> <strong>Khách sạn Grand </strong>xây dựng ở số 8 Đồng Khởi năm 1930, ban đầu chỉ một cửa hàng giải khát, sau này Henry Edouard tổng biên tập một tờ báo Pháp, xin được giấy phép chính thức mở Grand Hotel Saigon( Khách sạn Grand Sài Gòn). </span></span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><br></span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sau đó đến năm 1937 Grand được đổi tên thành <strong>Sài Gòn Palace</strong>, này đến năm 1958 , có tên tiếng việt là Sài Gòn Lữ Quán</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Thứ 2:</strong> Vào năm 1978 , tại số 16 đường Ngô Đức Kế xuất hiện một <strong>khách sạn Sai Gòn Palace</strong>. Năm 1989 hai khách sạn số 8 Đồng Khởi và 16 Ngô Đức Kế hợp nhất thành khách sạn Đồng Khởi. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578500914_41aa1d7e28fbb5e26e66.jpg\" style=\"width: 601px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn Grand Palace(Nguồn Vnexpress)</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Năm 1989 Grand được tân trang và đổi mới cho đến năm 1998 mở cửa trở lại thành một trong những khách sản 5 sao có tiếng đầu tiên tại Sài Gòn.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Thứ 3:</strong> K<strong>hách sạn Majestic</strong> đực xây dựng 1925 tại góc đường Catinat và Quai de Belgique, là đường Đồng Khởi và Tôn Đức Thắng do kiến trúc sư Pháp thiết kế sới sự đầu tư của một chủ thương gia Góc Việt giàu có bậc nhật Sài Gòn hiện lúc này. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578500948_d3225e1f336979c15432.jpg\" style=\"width: 593px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn Majestic(Nguồn Vnexpress)</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đến 1975  khách sạn bị trúng pháo, sau đó được sửa chữa theo lối kiến trúc châu âu thời phục hưng vào năm 1994. Đến năm 1997 được cục du lịch Việt Nam công nhân là khách sạn đạt chuẩn 4 sao., đây là khách sạn 4 sao đầu tiên do người Việt tự đầu tư và quản lý.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quý khách hàng có thể xem thêm: </span></span><a href=\"https://www.angiakhang.com/xay-dung-khach-san\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong><span style=\"color: rgb(184, 49, 47);\">T</span></strong><span style=\"color: rgb(184, 49, 47);\"><strong>hủ tục xin cấp giấy phép xây dựng khách sạn</strong></span></span></span></a></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Khách sạn được xây dựng sớm nhất ở Sài Gòn thời Pháp</strong></span></span></p>\n<p><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><iframe width=\"100%\" height=\"360\" src=\"https://www.youtube.com/embed/g6pLKQ4mQss\" frameborder=\"0\" allowfullscreen=\"\"></iframe></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n', NULL, 1, NULL, 1578500992, 1590043506, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(304, 'category', 18, 2, '4. Đơn giá xây nhà trọn gói', '4-don-gia-xay-nha-tron-goi', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif;\">Vì vậy để chuẩn bị cho việc xây dựng nhà, quý khách hàng sẽ có rất nhiều lo lắng, về kinh phí, xây như thế nào, nên chọn nhà theo mẫu thiết kế nào, thủ tục xin giấy phép là sao… và cuối cùng là chọn công ty xây dựng uy tín.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1581615358_5dbc56999cac88095cb5.jpg\" style=\"width: 594px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây biệt thự trọn gói kiểu Nông thôn</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trong quá trình chọn lựa đơn vị thi công là nhà thầu xây dựng, gia chủ sẽ rất lo lắng, không biết nhà thâu xây dựng có đúng theo thiết kế, đúng số lượng vật tư, thị công có đúng theo tiến độ hay không?, rồi có phát sinh thêm chi phí hay không?, …</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>4.1. Báo giá xây nhà trọn gói tại tphcm gồm có các gói như sau:</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Gói phổ thông: Già từ 4.800.000 – 5.000.000 đ/m2</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Gói Khá : Giá từ 5.200.000 – 5.500.000 đ/2</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Gói Khá +: Giá từ 6.000.000- 6.600.000 đ/m2</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Bảng giá vật liệu xây thô và nhân công hoàn thiện đơn giá 2020</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà phố hiện đại:       3.200.000 - 3.300.000 đồng/m2          </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà phố tân cổ điển: 3.300.000 - 3.500.000 đồng/m2        </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Biệt thự hiện đại:       3.300.000 - 3.500.000 đồng/m2           </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Biệt thự tân cổ điển: 3.500.000 - 4.000.000 đồng/m2    </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>4.2. Báo giá xây nhà trọn gói tại Phú Quốc</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bảng báo giá xây nhà trọn gói tại Phú Quốc gồm có các gói như sau:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Gói phổ thông: Già từ 6.200.000 – 6.500.000 đ/m2</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Gói Khá : Giá từ 6.800.000 – 7.200.000 đ/2</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Gói Khá +: Giá từ 7.500.000- 8.000.000 đ/m2</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bảng gái vật liệu xây thô và nhân công hoàn thiện đơn giá 2020</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà phố hiện đại:5.200.000 - 5.300.000 đồng/m2    </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà phố tân cổ điển: 5.300.000 - 5.500.000 đồng/m2    </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Biệt thự hiện đại: 5.300.000 - 5.500.000 đồng/m2    </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Biệt thự tân cổ điển: 5.500.000 - 6.000.000 đồng/m2 </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]<span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">   </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công ty Xây Dựng An Gia Khang với <strong>dịch vụ xây nhà trọn gói</strong>, cam kết đem lại niềm vui và hạnh phúc cho quý khách hàng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đội ngũ nhân sự sẽ đến sự nhiệt tình, nhiệt quyết, lắng nghe những ý kiến đóng góp từ quý khách hàng hay những yêu cầu của gia chủ, để từ đó chúng tôi hoàn thành tốt các dự án thầu ngày càng hoàn thiện</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đối với chi phí xây dựng nhà trọn gói năm 2020, giá có thể giao động từ 5.200.000 đến 6.500.000 / m2 đối với những mẫu nhà phố thông thường, còn đối với những mẫu nhà phố cao cấp thì giá có thể thay đổi tùy theo nhu cầu gia chủ.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1581615416_4923bbde7d2b5a0038ee.jpg\" style=\"width: 592px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự kiểu mini</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với những mầu thiết kế đặc biệt và điều kiện thi công phức tạp có thể liện hệ trực tiếp hotline để được nhân viên tiếp nhận và tư vấn báo giá chi tiết công trình cho quý khách hàng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công ty cổ phần xây dựng An Gia Khang là nhà thầu xây dựng uy tín và chất lượng, đã và đang triển khai rất nhiều dự án cho nhiều đối tác khách hàng . Chúng tôi hiểu rất rõ những tâm tư, lo lắng của quý vị khi bắt đầu xây dựng nhà.</span></span></p>\n<ul>\n<li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chúng tôi luôn cam kết đảm bảo chất lượng công trình tuyệt đối</span></span></li>\n<li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đảm bảo đúng tiến độ</span></span></li>\n<li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đảm bảo đúng kết cấu xây dựng</span></span></li>\n<li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đảm bảo không phát sinh chi phí nào khác.</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với những mẫu nhà phố thiết kế hiện đại, đẹp mặt và hợp với phong thủy gia chủ. Đơn giá xây dựng nhà phố phần thô và nhân công hoàn thiện sẽ giao động như sau:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công ty cổ phần xây dựng An Gia Khang là nhà thầu xây dựng uy tín và chất lượng, đã và đang triển khai rất nhiều dự án cho nhiều đối tác khách hàng. Chúng tôi hiểu rất rõ những tâm tư, lo lắng của quý vị khi bắt đầu xây dựng nhà.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>4.3 Công ty xây nhà trọn gói tại tphcm</strong><br></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Sẽ mang đến chất lượng và sự hài lòng cho mọi khách hàng. Cùng với sự phát triển không ngừng của nền kinh tế như hiện nay thì nhu cầu nhà ở tiện nghi và hiện đại của người dân ngày một gia tăng. </span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\"><img data-fr-image-pasted=\"true\" src=\"https://www.angiakhang.com/uploads/contents/1575451705_0fcc2c80d5e74934cf24.png\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; border-style: none; -webkit-font-smoothing: antialiased; cursor: pointer; padding: 0px 1px; max-width: calc(100% - 10px); width: 606px;\" class=\"fr-fic fr-dii\"></span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Đội ngũ công nhân xây dựng An Gia Khang</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Kéo theo đó dịch vụ xây dựng nhà trọn gói đã và đang được nhiều gia đình chọn lựa và đánh giá cao. Hãy để công ty xây dựng nhà trọn gói An Gia Khang nói rõ hơn về vấn đề này.   </span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\"><img data-fr-image-pasted=\"true\" src=\"https://www.angiakhang.com/uploads/contents/1575451763_4730ed97bd041ca50900.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; border-style: none; -webkit-font-smoothing: antialiased; cursor: pointer; padding: 0px 1px; max-width: calc(100% - 10px); width: 571px;\" class=\"fr-fic fr-dii\"></span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Công ty xây dựng nhà trọn gói luôn giúp bạn có được một không gian hoàn hảo nhất</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Xây dựng nhà trọn góp luôn mang đến chất lượng và sự hài lòng nhất dành cho khách hàng. Hiện nay các công ty xây dựng nhà trọn gói đều có đội ngũ cad kỹ sư, kiến trúc sư có trình độ kiến thức và chuyên môn tốt. </span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\"><img data-fr-image-pasted=\"true\" src=\"https://www.angiakhang.com/uploads/contents/1576851515_a9aa990b1bc2303c28a1.png\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; border-style: none; -webkit-font-smoothing: antialiased; cursor: pointer; padding: 0px 1px; max-width: 100%; width: 560px;\" class=\"fr-fic fr-dii\"></span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin-top: 0px; margin-bottom: 1rem; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố xây dựng trọn gói đẹp</span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin-top: 0px; margin-bottom: 1rem; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; font-weight: 700; -webkit-font-smoothing: antialiased;\">Quý khách hàng có thể xem </strong><a href=\"https://www.angiakhang.com/bao-gia-xay-nha-tron-tai-tphcm\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; color: rgb(156, 39, 176); text-decoration: none; background-color: transparent; -webkit-font-smoothing: antialiased; user-select: auto;\"><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; font-weight: 700; -webkit-font-smoothing: antialiased;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased; color: rgb(41, 105, 176);\">báo giá xây nhà trọn gói tại tphcm</span> </strong></a><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; font-weight: 700; -webkit-font-smoothing: antialiased;\">tại đây.</strong></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin-top: 0px; margin-bottom: 1rem; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Chính vì thế sẽ giúp chủ gia có được những cái nhìn vô cùng khách quan về vị trí, công năng, cũng như không gian sử dụng một cách chi tiết nhất từ bản vẽ kỹ thuật cho tới thực tế thi công công trình.</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Đến với các công ty xây dựng nhà trọn gói bạn sẽ vông cùng an tâm về các khoản chi phí phát sinh cũng như những thay đổi trong quá trình xây dựng từ đó sẽ giúp công trình của bạn được thực hiện một cách nhanh chóng và tiết kiệm nhất.</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Trước khi bắt đầu thi công thì dịch vụ xây dựng nhà trọn gói sẽ đưa đầy đủ các thông tin từ bản vẽ xây dựng tổng thể căn nhà, kết cấu, phối cảnh,… một cách chi tiết nhất để chủ nhà có thể xem và điều chỉnh phù hợp với mong muốn cũng như tình cách của mình trước khi bước vào quá trình xây dựng chính thức.</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\"><img data-fr-image-pasted=\"true\" src=\"https://www.angiakhang.com/uploads/contents/1575452019_30e629b10fd708afbbc6.png\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; border-style: none; -webkit-font-smoothing: antialiased; cursor: pointer; padding: 0px 1px; max-width: calc(100% - 10px); width: 601px;\" class=\"fr-fic fr-dii\"></span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Các bản vẽ thi công được thiết kế</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\"><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; font-weight: 700; -webkit-font-smoothing: antialiased;\">4.3.1 Chi phí khi xây dựng nhà trọn gói như thế nào?</strong></span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Thông thường tùy thuộc vào yêu cầu và mức giá ngôi nhà mà gia chủ mong muốn là bao nhiêu mà công ty xây dựng nhà trọn gói sẽ có các mức giá thích hợp khác nhau.</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\"><img data-fr-image-pasted=\"true\" src=\"https://www.angiakhang.com/uploads/contents/1575452127_36acec50eae4bff882bc.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; border-style: none; -webkit-font-smoothing: antialiased; cursor: pointer; padding: 0px 1px; max-width: calc(100% - 10px); width: 610px;\" class=\"fr-fic fr-dii\"></span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Những mẫu nhà phố đẹp 2019</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\"> Sau đó tiến hành lập các bảng dự toán chi phí xây dựng ngôi nhà một cách chi tiết nhất để báo cho gia chủ. Khi hai bên đã đi đến những thỏa thuận thì mới tiến hành việc thi công ngôi nhà.</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Tùy vào các điều kiện khách quan khác nhau như quy mô công trình, điều kiện thi công cũng như các chi phí của các nguyên vật liệu tại thời điểm đó như thế nào mà các công ty xây dựng nhà trọn gói sẽ có các mức giá khác nhau cho chủ nhà lựa chọn.</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\"><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; font-weight: 700; -webkit-font-smoothing: antialiased;\">Công ty xây dựng nhà trọn gói An Gia Khang </strong>luôn đảm bảo mang đến cho khách hàng những tiện nghi cũng như một mức giá thích hợp nhất đảm bảo sự hài lòng cũng như chất lượng tốt nhất.</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">An Gia Khang luôn mang đến cho khách hàng chi phí hợp lý nhất khi sử dụng dịch vụ</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\"><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; font-weight: 700; -webkit-font-smoothing: antialiased;\">4.3.2 Tại sao bạn nên chọn công ty xây dựng nhà trọn gói An Gia Khang?</strong></span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Điều đầu tiên mà các gia chủ điều làm khi quyết định lựa chọn hình thức xây dựng nhà trọn gói đó là tìm kiếm thông tin cũng như so sánh giá cả và sự đánh giá của các khách hàng trước của các nhà thầu công ty xây dựng khác nhau để chọn một mô hình phù hợp cũng như chất lượng và giá cả hợp lý nhất.</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\"><img data-fr-image-pasted=\"true\" src=\"https://www.angiakhang.com/uploads/contents/1575452468_5e97c4acc0d73d68a2f6.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; border-style: none; -webkit-font-smoothing: antialiased; cursor: pointer; padding: 0px 1px; max-width: calc(100% - 10px); width: 621px;\" class=\"fr-fic fr-dii\"></span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Công ty xây dựng An Gia Khang</span></span></span></p>\n<ul><li style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Chất lượng, kinh nghiệm và sự uy tín</strong></span></span></li></ul>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Một công ty xây dựng nhà trọn gói uy tín sẽ giúp bạn có được một ngôi nhà giống như mong muốn đầy đủ tiện nghi và thoải mái nhất. Ngoài ra còn đảm bảo được các yếu tố quan trọng khác nữa như là sự an toàn của ngôi nhà, chi phí, tính thẩm mỹ,…</span></span></span></p>\n<ul><li style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\"><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; font-weight: 700; -webkit-font-smoothing: antialiased;\">Sự chuyên nghiệp và tận tâm</strong></span></span></span></li></ul>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Công ty cổ phần thiết kế và xây dựng An Gia Khang sẽ là một trong những địa chỉ đáng tin cậy dành cho bạn và ngôi nhà của bạn.</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Công ty có đội ngũ các kỹ sư xây dựng, kiến trúc sư lành nghề và vô cùng tâm huyết với công việc sẽ cho bạn những bản thiết kế đẹp mắt đúng với thực tế xây dựng nhất, đồng thời sẽ nhiệt tình giải đáp mọi thắc mắc cũng như sửa đổi để phù hợp nhất với mong muốn của chủ nhà.</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\"><img data-fr-image-pasted=\"true\" src=\"https://www.angiakhang.com/uploads/contents/1575452553_398151e1e8f7e4519e7a.png\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; border-style: none; -webkit-font-smoothing: antialiased; cursor: pointer; padding: 0px 1px; max-width: calc(100% - 10px); width: 612px;\" class=\"fr-fic fr-dii\"></span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Sự hoàn hảo cho ngôi nhà đúng tiêu chuẩn</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\"><strong>An Gia Khang</strong> hiện đang là khách hàng uy tín của các của nhà đang có nhu cầu xây dựng nhà trọn gói. </span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Tại đây luôn đưa ra những khoản chi phí xây dựng sát với thị trường và hợp lý nhất so với các cơ sở xây dựng khác. Nên bạn sẽ hoàn toàn yên tâm vấn đề chi phí cũng như các khoản phát sinh trong quá trình xây dựng.</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Công ty luôn cam kết với khách hàng là bàn giao nhà đúng thời hạn, đúng với những thiết kế trên bản vẽ. </span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Chất lượng và uy tín của An Gia Khang đã và đang được khẳng định bởi các thế hệ khách hàng sử dụng dịch vụ của chúng tôi.</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\"><img data-fr-image-pasted=\"true\" src=\"https://www.angiakhang.com/uploads/contents/1575452735_fdc37f24d147044323e0.png\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; border-style: none; -webkit-font-smoothing: antialiased; cursor: pointer; padding: 0px 1px; max-width: calc(100% - 10px); width: 608px;\" class=\"fr-fic fr-dii\"></span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Công nhân đang chuẩn bị đổ sàn</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Ngoài ra một trong những điều mà khiến Công ty xây dựng nhà trọn gói An Gia Khang được đánh giá và tin tưởng cao của khách hàng đó là chúng tôi luôn có chế độ bảo hành sau khi bàn giao nhà. </span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Trong suốt quá trình sử dụng nếu khách hàng có những thắc mắc hoặc vấn đề gì liên quan đến bảo hành có thể liên hệ trực tiếp tới công ty để được tư vấn và giải quyết một cách nhanh chóng nhất.</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\"><img data-fr-image-pasted=\"true\" src=\"https://www.angiakhang.com/uploads/contents/1575453709_53070ff60faf0873f4f6.png\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; border-style: none; -webkit-font-smoothing: antialiased; cursor: pointer; padding: 0px 1px; max-width: calc(100% - 10px); width: 597px;\" class=\"fr-fic fr-dii\"></span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Dự án sắp hoàn công</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Hãy đến với Công ty thiết kế nhà trọn gói An Gia Khang để cùng tận hưởng một dịch vụ hiện đại và chất lượng nhất</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Trên đây là toàn bộ những thông tin về hình thức xây dựng nhà trọn gói cũng như công ty xây dựng nhà trọn gói An Gia Khang. </span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin: 0in 0in 10pt; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; line-height: 17.25px; font-size: 15px; font-family: Calibri, sans-serif;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Nếu đang có nhu cầu cũng như mong muốn được ở trong một không gian tiện nghi thoải mái và hiện đại nhất. Hãy đến với chúng tôi Công ty cổ phần thiết kế và xây dựng An Gia Khang hân hạnh phục vụ mọi đối tượng khách hàng</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin-top: 0px; margin-bottom: 1rem; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; -webkit-font-smoothing: antialiased;\">Nếu quý khách hàng vẫn đang tìm kiếm một nhà thầu xây dựng thành phố Hồ Chí Minh uy tín, chuyên nghiệp, giá thành phù hợp hãy liên hệ ngay với chúng tôi.</span></span></span></p>\n<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; margin-top: 0px; margin-bottom: 1rem; -webkit-font-smoothing: antialiased; color: rgb(65, 65, 65); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: center; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial;\"><strong>THIẾT KẾ VÀ THI CÔNG HOÀN THIỆN NỘI THẤT - AN GIA KHANG</strong></p>\n<p><iframe width=\"100%\" height=\"360\" src=\"https://www.youtube.com/embed/MOWf_YMNPoU\" frameborder=\"0\" allowfullscreen=\"\"></iframe></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n', NULL, 1, NULL, 1578589515, 1590049742, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(306, 'category', 16, 2, '3. Những lưu ý khi thiết kế xây dựng nhà hàng', '3-nhung-luu-y-khi-thiet-ke-xay-dung-nha-hang', '<p style=\"text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Trong quá trình <strong>xây dựng nhà hàng</strong>, đặc sản ngon và dịch vụ chăm sóc khách  hàng tốt là tiêu chí quan trọng để giúp khách hàng có được một lượng khách ổn định. Bạn muốn khách hàng cảm thấy thoải mái và có được không giang ấm cúm trong một bữa ăn, thì phải có được sự thiết kế chi tiết trong những lưu ý sau:</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578969723_b8a81d075da7d0620fce.png\" style=\"width: 626px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà hàng sang trọng</span></span></p>\n<h3 style=\"text-align: left;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong><span style=\"white-space:pre;\">a) </span>Tiêu chi thiết kế lối vào nhà hàng</strong></span></span></h3>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Lối vào nhà hàng là ấn tượng đầu tiên về nhà hàng của bạn đối với khách lần đầu bước chân vào nhà hàng. Nên khi thiết kế nên tạo được sự ấn tượng và hình dung mô phỏng được sự sang trọng và âm cúm của nhà hàng. </span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578969791_2d518c0384ae5b0d6ae0.png\" style=\"width: 611px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không gian thông thoáng</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một không gian lý tưởng chào đáo khách hàng và đêm đến sự tò mo của khách đến thưởng thức các đặc sản của nhà hàng là một trong những tiêu chí quan trọng.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vi dụ: Nhà hàng Hoa Mai tại tphcm, sử dụng viên gạch hoa văn xếp thành những món ăn đặc sản và biểu thưởng thành những nơi nổi tiếng của vùng miền trước lối vào, giúp khách hàng cảm thấy hứng thú.</span></span></p>\n<h3 style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong><span style=\"white-space:pre;\">b) </span>Khu vực ăn uống của nhà hàng</strong></span></span></h3>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng nhà ăn hay khu vực ăn uống của khách hàng khi đến nhà hàng, đặc biệt lưu ý khách hàng sẽ chi tiêu khoản 90% thời gian khu vực này.</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578969843_63b1cbd98a284df4f596.png\" style=\"width: 617px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không gian ăn uống riêng</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vì vậy khi thiết kế và bố trí phòng ăn, mục đích chính của chúng ta là làm cho khách hàng cảm thấy ấm cúm và thoải mái nhất. Nên thiết kế chú trọng vào chỗ ngồi cho khách và quan cảnh chỗ ngồi hợp lý.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chỗ ngồi có thể thiết kế 1-4 ghế hoặc nhiều hơn nhưng vẫn phải lưu ý khoản cách phù hợp theo tiêu chuẩn là từ 3-4 fit để tránh bất sự chật chội và ảnh hưởng đến không gian riêng của mỗi khách hàng.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vị trí khi khách hàng đợi món là không gian khi thiết kế nhà hàng, nên tạo sự chú ý về một quản cảnh hay sự chú ý nào đó để khách quên đi sự chờ đợi thực đơn khi đặt món.</span></span></p>\n<h3 style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong><span style=\"white-space:pre;\">c) K</span>hu vực nhà bếp</strong></span></span></h3>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khu vực nhà bếp được thiết kế chủ yếu dựa vào yêu cầu của chủ đầu bếp tại nhà hàng, hoặc kiến trúc sư hay chủ nhà hàng.</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1578969898_17a3c8ca6a48b21988b8.jpg\" style=\"width: 595px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khu vực trưng bày đồ ăn</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Du theo quan điểm ai đi nữa vẫn phải đáp ứng các tiêu chuẩn sau:</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các khu vực làm việc trong nhà bếp phải được thiết kế: Khu vực chuẩn bị, khu vực sơ chế, khu vực trang trí, khu vực đóng gói và Khu vực vệ sinh.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bố tri phù hợp lôi đi để nhân viên nhà bếp di chuyển nhanh chóng, hiệu quả và không va chạm vào nhau.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đặc biệt bố trị các thiết bị an toàn theo quy định của Nhà hàng về phòng tránh cháy nổ.<br></span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng vệ sinh của nhân viên nên để phia sau, tránh gần khu vực bếp núc làm mất cảm quan nhà bếp.<br></span></span></p>\n<h3 style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong><span style=\"white-space:pre;\">d) </span>Phòng vệ sinh của nhà hàng</strong></span></span></h3>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà vệ sinh sạch sẽ là một trong những tiêu chí quan trọng giúp khách hàng quan tâm đến nhà hàng rất nhiều. Nên thiết kế và tạo không gian sạch sẽ để đêm lại ấn tưởng cho khách hàng.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Lưu ý khi thiết kế nhà vệ sinh:</span></span></p>\n<ul>\n<li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nên chia ra các khu vực rõ ràng, như khu vực cho nam, khu vực cho nữ và đặc biết nên có khu vực dành cho người khuyết tật.</span></span></li>\n<li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà vệ sinh trang bị đầy đủ tác tiện ích.</span></span></li>\n<li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phối màu và cảnh quan, ánh sáng phù hợp.</span></span></li>\n</ul>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Còn những tiêu chí và không gian khách như: Kho, hệ thống điện,  hệ thống nức, hệ thống rửa các bạn có thể tham khảo thêm các đơn vị cung cấp và thiết kế hệ thống điện điện và các nhà cung cấp thiết bị rửa dụng cụ mới nhất nhé.<br></span></span></p>\n', NULL, 1, NULL, 1578966549, 1590733726, NULL),
(307, 'news', 14, 0, '1. Tiêu chuẩn xây dựng khách sạn 4 sao', '1-tieu-chuan-xay-dung-khach-san-4-sao', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Theo tài liệu chuyên ngành về các quy định thiết kế khách sạn, thì khách sạn 4 sao là một loại hình công trình được xây dựng độc lập với những tiêu chi như:</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579172575_9a1bec5b569b3a4ec232.png\" style=\"width: 630px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn 4 sao gần bãi biển</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quy mô từ 80 phòng trở lên, đảm bảo nơi để xe và giao thông nội bộ, đảm bảo đầy đủ chất lượng và số lượng về cơ sở vật chất, trang thiết bị. Đảm bao cung cấp dịch vụ, phục vụ nhu cầu sinh hoạt, vui chơi giải trí, nghỉ dưỡng của du khách…</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Tổng quát về khách sạn 4 sao</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây là một trong những tiêu chi đánh giá rất quan trọng để thiết kế và thi công khách sạn 4 sao . Mặt khác, khách sạn 4 sao phải đực thiết kế với kiến trúc độc đáo, ấn tượng, mang giá trị thiết thực cho người dùng, góp phần quan trọng trong việc trong việc phát triển và điều quan trọng là phải tuần theo quy hoạch đô thị.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579173669_e7626501db2d7ea9d869.png\" style=\"width: 626px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn và khu nghỉ dưỡng tại Phú Quốc.</span></span></p>\n<p><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nguyện vật liệu được sử dụng trong xây dựng khách sạn cũng phải được chú ý và kiểm tra nghiêm ngặt với mục đích đảm bảo chất lượng công trình, mang lại không gian thiết kế ấn tượng, vẻ đẹp riêng và bản sắc riêng của khách sạn, giúp cho khách sạn thu hút được sự quan tâm của du khách.</span></p>\n', NULL, 1, NULL, 1579172394, 1590043722, NULL),
(308, 'news', 14, 1, '1.1 Một số tiêu chuẩn thiết kế khách sạn 4 sao', '11-mot-so-tieu-chuan-thiet-ke-khach-san-4-sao', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nguyện vật liệu được sử dụng trong <strong>xây dựng khách sạn </strong>cũng phải được chú ý và kiểm tra nghiêm ngặt với mục đích đảm bảo chất lượng công trình, mang lại không gian thiết kế ấn tượng, vẻ đẹp riêng và bản sắc riêng của khách sạn, giúp cho khách sạn thu hút được sự quan tâm của du khách.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579173717_9e82ac8ee9d8abedeb98.png\" style=\"width: 591px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bảng thiết kế khách sạn 4 sao tham khảo</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế khách sạn nói chung, công việc thiết kế khách sạn 4 sao cũng bao gồm nhiều công đoạn khác nhau và các tiêu chí nghiêm ngặt về kiến trúc, nội thất, công năng….</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây là những tiêu chuẩn được lập ra và ban hành nhằm hình thành quy trình những công trình khách sạn san trọng đạt tiêu chuẩn nhà nước và tiểu chuẩn khách sạn 4 sao.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đó những tiêu chuẩn gì?. Mời các bạn xem những tiêu chuẩn mà chúng tôi đã tóm tắt những thông tin cơ bản từ bộ tài liệu tiêu chuẩn thiết kế khách sạn ban hành năm 2015.</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>I.Về vị trí, kiến trúc</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Vị trí:</strong> </span></span></p>\n<ol>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vị trí rất thuận lợi</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Môi trường cảnh quan đẹp </span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Thiết kế kiến trúc:</strong> </span></span></p>\n<ol>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế kiến trúc phù hợp với yêu cầu kinh doanh, các khu vực dịch vụ được bố trí hợp lý, thuận tiện</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế kiến trúc đẹp</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nội, ngoại thất được thiết kế, bài  trí, trang  trí hợp lý, đẹp, sang trọng</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đường vào đảm bảo cho người khuyết tật tiếp cận sử dụng</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khu vực buồng ngủ cách âm tốt</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có mái che trước sảnh đón tiếp (áp dụng đối với khách sạn).</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vật liệu xây dựng chất lượng tốt</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cửa ra vào riêng cho khách và nhân  viên</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Buồng ngủ cho người khuyết tật đi bằng xe lăn, xe đẩy</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Quy mô buồng ngủ: 80</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Nơi để xe và giao thông nội bộ:</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    <strong>Nơi để xe:</strong> Nơi để xe cho khách trong hoặc cách khu vực khách sạn tối đa 200m; Nơi để xe cho khách trong khu vực khách sạn cho 10% buồng ngủ.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>    Giao thông nội bộ: </strong>Lối đi bộ và giao thông nội bộ thuận tiện, an toàn, thông gió tốt.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Khu vực sảnh đón tiếp:</strong></span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Có sảnh đón tiếp</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Diện tích 60m2 (không áp dụng đối với khách sạn nổi)</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Diện tích 20m2 (áp dụng đối với khách sạn nổi)</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Phòng vệ sinh sảnh hoặc gần khu vực sảnh cho nam và nữ riêng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Bar sảnh (áp dụng đối với khách sạn nghỉ dưỡng)</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Khu vực hút thuộc riêng </span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Không gian xanh:</strong></span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Cây xanh ở các khu vực công cộng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Sân vườn, cây xanh (áp dụng đối với khách sạn nghỉ dưỡng)</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Diện tích buồng ngủ (gồm phòng ngủ và phòng vệ sinh, không áp dụng với khách sạn nổi):</span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Buồng một giường đơn 21m2</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Buồng một giường đôi hoặc hai giường đơn 25m2</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Buồng đặc biệt 41m2</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà hàng, bar:</span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    01 nhà hàng, có phục vụ món Âu, Á</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    01 quầy bar</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Số ghế bằng 80% số giường</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Phòng vệ sinh cho nam và nữ riêng (áp dụng đối với nhà hàng tách rời sảnh đón tiếp)</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Khu vực hút thuốc riêng</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khu vực bếp:</span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Thuận tiện cho việc vận chuyển thức ăn đến nhà hàng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Diện tích đảm bảo quy trình sơ chế, chế biến</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Thông gió tốt</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Có biện pháp ngăn chặn động vật, côn trùng gây hại</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Tường phẳng, không thấm nước, dễ làm sạch</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Trần bếp thuận tiện cho vệ sinh và đảm bảo an toàn</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Sàn phẳng, lát vật liệu chống trơn, dễ cọ rửa</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Bếp Âu, Á</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Bếp bánh</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Khu vực soạn, chia thức ăn</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Khu vực sơ chế biến nhiệt, nguội, nóng, lạnh được tách riêng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Lối chuyển rác tách biệt, đảm bảo vệ sinh</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Phòng vệ sinh cho nhân viên bếp (bên ngoài khu vực bếp)</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Phòng đệm, đảm bảo cách âm, cách nhiệt, cách mùi giữa bếp và phòng ăn.</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Kho:</strong></span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Kho bảo quản nguyên vật liệu, thực phẩm</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Kho để đồ dùng, thiết bị dự phòng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Các kho  lạnh (theo loại thực phẩm)</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Phòng hội nghị, hội thảo, phòng họp:</strong></span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Sảnh chờ, đăng  ký khách và giải khát giữa giờ</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    01 phòng hội nghị</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    01 phòng hội thảo</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    01 phòng họp</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Cách âm tốt</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Phòng vệ sinh cho nam và nữ riêng  (áp  dụng  đối  với phòng họp,hội nghị,hội thảo tách  rời sảnh đón tiếp)</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Khu vực dành cho cán bộ nhân viên:</strong></span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Phòng làm việc của người quản  lý và các  bộ phận   chức năng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Phòng trực buồng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Phòng thay quần áo</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Phòng vệ sinh cho nam và nữ riêng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Phòng tắm</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Phòng ăn </span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Hành lang:</strong> Thuận tiện đi lại và đảm bảo an toàn thoát hiểm</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>II. Trang thiết bị và tiện nghi</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Chất lượng trang thiết bị, bài trí, trang trí:</strong></span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Chất lượng cao</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Bài trí hợp lý</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Màu sắc hài hòa</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Hoạt động tốt</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Đồng bộ</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Trang trí nội thất đẹp</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Trang thiết bị nội thất:</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>+ Sảnh đón tiếp:</strong></span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Quầy lễ tân</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Internet không dây (wifi)</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Điện thoại</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Máy vi tính</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Bàn ghế tiếp khách</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Bảng hoặc màn hình niêm yết giá  dịch vụ và phương thức thanh toán</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Bảng hoặc màn hình niêm yết tỷ giá ngoại tệ.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Thiết bị phục vụ thanh toán bằng  thẻ tín dụng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Cửa ra vào  được bố   trí thuận tiện</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Nơi giữ  đồ  và tài  sản giá trị của  khách</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Xe đẩy cho người khuyết tật</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Giá để báo, tạp chí hoặc hệ  thống đọc báo trực tuyến (Press Reader)</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Quầy thông tin,quan hệ khách hàng và hỗ trợ đón tiếp (trực cửa, chuyển hành lý, văn thư, xe đưa  đón khách) </span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>+ Phòng vệ sinh sảnh và các khu  vực công cộng:</strong></span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Bồn cầu</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Vòi nước di động cạnh  bồn cầu</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Bồn tiểu nam (phòng vệ sinh nam)</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Giấy vệ sinh</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Chậu rửa  mặt, gương soi, vòi nước, xà phòng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Móc treo hoặc chỗ để túi</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Thùng rác có nắp</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Thiết bị thông gió</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Giấy hoặc khăn lau tay</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Máy sấy tay</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">    Hệ thống ga và xi phông thoát sàn đảm bảo ngăn mùi hôi.</span></span></li>\n</ol>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>&gt;&gt; Quý khách hàng xem thêm:</strong> </span></span><a href=\"https://www.angiakhang.com/xay-dung-khach-san\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(235, 107, 86);\">Xây dựng khách sạn 5 sao</span></span></a></p>\n', NULL, 1, NULL, 1579172394, 1590043722, NULL),
(309, 'news', 14, 2, '1.2 Thiết kế và thi công xây dựng khách sạn năng lực như thế nào?', '12-thiet-ke-va-thi-cong-xay-dung-khach-san-nang-luc-nhu-the-nao', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Trên là những thông tin mà chúng tôi chia sẽ, việc thiết kế khách sạn tiêu chuẩn 4 sao, đây là một quá trình có sự kết hợp hài hòa của các tiêu chuẩn khắt khe và những quy định của nhà nước ban hành 2015.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579173783_64b8e916e6f5f6bb70d4.png\" style=\"width: 587px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bảng thiết kế khách sạn 4 sao tham khảo</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vì vậy, việc các chủ đầu tư nên nắm rõ tiêu chuẩn và quy định để trong quá trình thi công xây dựng khách sạn 4 sao mang đẳng cấp và giúp du khách trải nghiệm được những dịch vụ chất lượng mà khách sạn mang lại.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chúng tôi xin gửi đến quý khách hàng, chủ đầu tư nên tìm hiểu đến các đơn vị tư vấn thiết kế uy tín và giàu kinh nghiệm trong việc thiết kế và thi công xây dựng khách sạn 4 sao.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>AN GIA KHANG</strong> tự hào là đơn vị thiết kế khách sạn tốt nhất hiện nay tại Việt Nam với rất nhiều dự án công trình khách sạn tại TPHCM và Đảo Ngọc Phú Quốc.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Với nhiều năm kinh nghiệm, cùng với đội ngũ kiến trúc sư năng lực, giàu kinh nghiệm và tâm huyết. AN GIA KHANG rất mong được đồng hành cùng các chủ đầu tư, tư vấn và khách hàng trong việc tư vấn, thiết kế và thi công xây dựng các công trình khách sạn.</span></p>\n', NULL, 1, NULL, 1579172394, 1590043722, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(310, 'news', 14, 3, '2. Những mẫu khách sạn đẹp nhất 2020', '2-nhung-mau-khach-san-dep-nhat-2020', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Xin giới thiệu đến quý khách hàng qua những mẫu thiết kế khách sạn 4 sao, theo nhiều phong cách kiến trúc hiện đại hay cổ điển được xây dựng trong thời gian qua.</span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chúng tôi xin được tổng hợp và giới thiệu những mẫu khách sạn 4 sao đẹp nhất Việt Nam.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cảm ơn bạn, đa theo dỗi nội dung bài viết này và chia sẽ với bạn bè về những thông tin hữu ích này.</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.1. Những mẫu khách sạn 4 sao đẹp nhất tại TPHCM.</strong></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579173296_9efbb25bb35f6a755854.png\" style=\"width: 611px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn Parkroyal</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579173333_086ff9bda586c7e37505.png\" style=\"width: 617px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn Majestic</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579173363_c1d50ae7bf38a7683b39.png\" style=\"width: 626px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn Ibis Sài gòn</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579173386_ed7ea798b11ee35170ae.png\" style=\"width: 612px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn Hương Sen</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.2. Những mẫu khách sạn 4 sao sang trọng tại Phú Quốc</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiên Giang  là tỉnh thuộc vùng biển với hơn 137 hòn đảo lớn nhỏ, trong đó có Phú Quốc diện tích 567 km2 và cũng là đảo lớn nhất Việt Nam.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579173447_de42eefec51ebb7b9af6.png\" style=\"width: 602px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn Boulevard Phú Quốc</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579173483_08963272b5351a8f8484.png\" style=\"width: 606px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn Ocean pearl</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579173510_589d3a383b37436fbb77.png\" style=\"width: 607px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn Grand Word Phú Quốc</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579173553_2e1ed1a8ba071debf76a.png\" style=\"width: 600px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn Novotel Phú Quốc</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tỉnh Kiên Giang có địa hình rất đa dạng, vừa có đồng bằng, vừa có đồi núi và biển đảo rất đẹp. Đây là một trong những địa điểm du lịch nổi tiếng và thu hút rất nhiều khách du lịch nữa ngoài.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif;\"><strong>Rất mong được hợp tác cùng quý khách hàng.</strong></span></span></p>\n', NULL, 1, NULL, 1579172394, 1590043722, NULL),
(311, 'news', 15, 0, '1. Đôi nét về tiêu chuẩn xây dựng khách sạn 3 sao', '1-doi-net-ve-tieu-chuan-xay-dung-khach-san-3-sao', '<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Xét về cơ bản <strong>thiết kế khách sạn 3 sao đẹp </strong>là công việc thiết kế không gian nghỉ dưỡng và ẩm thực đạt tiêu chuẩn cụ thể. Ví dụ khách sạn có quy mô từ 50 phòng trở lên, đảm bảo cơ sở vật chất và trang thiết bị tiện nghi, hiện đại. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579194425_dd0a41cefc39ac9d21e4.jpg\" style=\"width: 634px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế khách sạn 3 sao</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đây đều là những vấn đề không đơn giản và làm đau đầu nhiều người.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thực tế cho thấy rằng nhiều chủ đầu tư đã chi một khoảng không nhỏ để thiết kế khách sạn 3 sao không những đẹp còn độc đáo và riêng biệt. Thế nhưng một khách sạn 3 sao đẹp còn căn cứ vào kiến trúc, vật liệu xây dựng, cảnh quan, thiết kế nội ngoại thất,… </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Và quan trọng hơn là còn phải căn cứ vào các văn bản, quy định của nhà nước về tiêu chuẩn khách sạn 3 sao để có sự chuẩn bị tốt nhất, cách triển khai khoa học nhất.</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khi thiết kế khách sạn 3 sao cần phải đáp ứng được các dịch vụ cần thiết với nhu cầu nghỉ dưỡng và sinh hoạt của du khách. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hơn nữa vẫn phải thỏa mãn các tiêu chuẩn thiết kế khách sạn 3 sao đẹp và sang trong. Một vấn đề nữa cần phải lưu tâm là khách sạn 3 sao thường được thiết kế với phong cách kiến trúc đẹp với nguyên liệu dùng xây dựng có chất lượng cao mang tới sự bền vững và sang trọng cho công trình. </span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nội- ngoại thất cũng phải được thiết kế ấn tượng và độc đáo với view đẹp nhằm thu hút sự quan tâm của du khách. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579193657_4f017915362de91e895a.jpg\" style=\"width: 589px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Khách sạn 3 sao cần những tiêu chuẩn nhất định để vận hành hoạt động</span></p>\n', NULL, 1, NULL, 1579193638, 1590043858, NULL),
(312, 'news', 15, 1, '2. Tiêu chuẩn xây dựng khách sạn 3 sao', '2-tieu-chuan-xay-dung-khach-san-3-sao', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"color: rgb(65, 65, 65); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important;\">Trong văn bản pháp quy đã có chi tiết các tiêu chuẩn của khách sạn 3 sao khi xây dựng và các bạn có thể tham khảo các tiêu chuẩn cụ thể đó là:</span><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> <br></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong> 2.1 Vị trí</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các khách sạn 3 sao muốn thu hút du khách và đảm bảo lợi nhuận cho mình cần phải đặt ở vị trí dễ tiếp cận, thuận lợi và có cảnh quan đẹp mắt.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong> 2.2 Thiết kế kiến trúc</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Ngoài ra về thiết kế kiến trúc các khách sạn 3 sao cần phải đảm bảo phù hợp với yêu cầu kinh doanh, khu vực dịch vụ cần được bố trí một cách thuận tiện và hợp lý.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Phần nội ngoại thất của khách sạn cần phải chú trọng đầu tư vào khâu thiết kế đảm bảo vừa hợp lý, vừa sang trọng .</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Đường vào khách sạn cần đảm bảo để tất cả các du khách bao gồm cả người khuyết tật đều có thể sử dụng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Buồng ngủ cần được cách âm tốt để đảm bảo tính riêng tư cho du khách</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Ở sảnh đón tiếp của khách sạn cần phải xây dựng phần mái che cẩn thận</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Nên xây dựng khách sạn 3 sao bằng những vật liệu xây dựng với chất lượng tốt</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Có phần cửa ra vào  dành cho nhân viên và khách đến nghỉ riêng biệt.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.2.1 Quy mô phòng khách sạn</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một tiêu chuẩn không thể bỏ qua khi xây dựng khách sạn 3 sao đó là phải đảm bảo có tối thiểu 50 phòng ngủ trở lên. Đối với các resort thì tiêu chuẩn tối thiểu thường phải là 40 phòng. Các phòng có diện tích theo thông số như sau:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Đối với giường đơn cần rộng khoảng 18m2</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Đối với các phòng có 1 giường đôi hay hai giường đơn thì phải đạt tới 22m2.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.2.2 Không gian xanh</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579193692_a9c2c3eafc45dc54607c.jpg\" style=\"width: 599px;\" class=\"fr-fic fr-dib\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không gian xanh giúp du khách thư giãn hơn</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn 3 sao cần phải có cây xanh trồng ở các khu vực công cộng cùng với khách sạn nghỉ dưỡng thì cần phải có sân vườn riêng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.2.3 Nơi để xe và giao thông nội bộ</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Nơi để xe cho khách có thể xây dựng ở trong khách sạn hoặc cách khách sạn một khoảng nhưng không quá 200m</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Nơi để xe cho du khách trong khu vực của khách sạn phải đủ cho 5% số phòng ngủ.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Đặc biệt lối đi bộ và nơi để xe cần có giao thông thuận tiện, an toàn và cần thông gió tốt.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.2.4 Khu vực sảnh đón tiếp</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đối với sảnh đón tiếp cần phải đạt những tiêu chuẩn cụ thể như:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Có diện tích ít nhất là 35m2</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Có phòng vệ sinh ở sảnh hoặc gần đó dành cho nam và nữ riêng biệt</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Có khu vực dành riêng để hút thuốc</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.2.5 Phòng hội nghị, hội thảo, phòng họp</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng họp cần đảm bảo có khoảng 30 ghế, có phòng vệ sinh dành riêng cho nam và nữ- áp dụng với các phòng họp tách rời sảnh đón tiếp.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong> 2.2.6 Nhà hàng, bar</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Về nhà hàng và ba cần đảm bảo có:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- 1 nhà hàng với quầy bar</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Số ghế của nhà hàng này phải bằng với 60% số giường</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Có phòng vệ sinh dành riêng cho nam và nữ nếu nhà hàng rách rời với sảnh đón tiếp</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Có một khu vực để hút thuốc riêng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.2.7 Khu vực bếp</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khu vực bếp của khách sạn 3 sao càn đảm bảo thuận tiện cho việc di chuyển thức ăn tới nhà hàng; có khu vực nóng lạnh riêng biệt. Diện tích của không gian này phải đủ rộng để có thể sơ chế và chế biến món ăn. Ngoài ra cần đảm bảo các tiêu chí như: </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579193728_c74a10bcf4e41c0da264.jpg\" style=\"width: 603px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khu vực bếp cần đảm bảo ngăn nắp, sạch sẽ</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Có hệ thống thông gió tốt, ngăn côn trùng gây hại</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Tường bếp phải phẳng không thấm nước và dễ dàng làm sạch</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Sàn bếp dễ cọ rửa và chống trơn</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Có lối chuyển rác riêng đảm bảo vệ sinh</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Có phòng vệ sinh cho nhân viên bếp</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.2.8 Kho</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cần phải có cả kho để thực phẩm và kho để đồ dùng hay thiết bị dự phòng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.2.9 Khu vực dành cho cán bộ, nhân viên</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Về khu vực này phải có phòng làm việc của quản lý và các bộ phận chức năng, phòng trực buồng, phòng thay quần áo và phòng vệ sinh nam nữ riêng biệt</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.2.10 Hành lang</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hành lang của khách sạn phải đảm bảo thuận tiện cho đi lại và thoát hiểm an toàn</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.3 Tiêu chuẩn về trang thiết bị, tiện nghi</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.3.1 Chất lượng trang thiết bị, bài trí, trang trí</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Về vấn đề trang thiết bị và trang trí cho khách sạn phải đảm bảo tính hợp lý, có màu sắc hài hòa, sử dụng nguyên vật liệu với chất lượng tốt và đồng bộ.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.3.2 Trang thiết bị nội thất</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">-  Sảnh đón tiếp: khu vực này cần phải có quầy lễ tân, có điện thoại, wifi, máy vi tính, bàn ghế tiếp khách, bảng giá dịch vụ và cách thức thanh toán, các thiết bị cho khách thanh toán bằng thẻ tín dụng, cửa ra vào cần phải bố trí thuận tiện, có khu vực để cất tài sản cho khách, xe đẩy dành cho người khuyết tật,..</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Khu vực công cộng và phòng vệ sinh sản cần đảm bảo các yêu cầu như: có bồn cầu, vòi nước di động gần bồn cầu, bồn tiểu nam, giấy vệ sinh, gương soi, chậu rửa mặt, xà phòng, vòi nước, móc treo hay chỗ để túi, thiết bị thông gió, thùng rác có nắp, khăn lau tay hay giấy, máy sấy tay, hệ thống ngăn mùi,…. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579193764_afe7c7ff0c3c3ac8a987.png\" style=\"width: 630px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Buồng ngủ cần đảm bảo đầy đủ tiện nghi cho du khách</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Buồng ngủ: buồng ngủ của khách sạn 3 sao cần đảm bảo có:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Giường đơn 1m x 2m</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Giường đôi 1,6m x 2m</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ngoài ra phải có đệm dày khoảng 20cm có ga bọc, có tủ, kệ đầu giường, đèn đầu giường, ổ cắm điện đầu giường, minibar để đặt đồ ăn nhẹ, đồ uống, đồ ăn vặt, tivi, điện thoại và cách sử dụng, tủ đựng quần áo có mắc treo, rèm cửa có thể chắn sáng, đèn đủ chiếu sáng, cốc uống nước, bàn ghế uống nước, ấm đun siêu tốc, giấy lau giày, ấm đun nước siêu tốc, wifi, bàn làm việc cho 50% số buồng ngủ, cặp đựng tài liệu hướng dẫn khách và thông tin khách sạn, sọt rác, gương soi, thiết bị báo cháy, dép đi trong phòng, tranh ảnh, két an toàn cho 30% số phòng ngủ, máy sấy tóc, thảm trải buồng ngủ, sơ đồ thoát hiểm, mắt nhìn trên cửa, chuông gọi cửa, chốt an toàn,…</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Phòng vệ sinh trong buồng ngủ: tường được làm từ vật liệu không thấm nước, sàn lát với vật liệu chống trơn, chậu rửa mặt, đèn trần, ổ cắm điện, vòi nước nóng, móc treo quần áo, giá để khăn, bồn cầu, thiết bị thông gió, các vật dụng cần thiết cho khách, khăn chùi chân, điện thoại nối với buồng ngủ, 80% số phòng ngủ có phòng tắm đứng hay bồn tắm nằm có rèm che, hệ thống ngăn mùi,..</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Phòng họp, hội thảo, hội nghị gồm có: micro, bàn ghế, đèn điện, máy chiếu, thiết bị phục vụ hội họp, sơ đồ chỉ dẫn, đèn thoát hiểm, hệ thống thông gió, điều hòa không khí, hệ thống camera phòng họp, thảm trải phòng họp,..</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579193806_08eef3bc22a8fd73b379.jpg\" style=\"width: 617px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Phòng họp hội thảo cũng cần đặc biệt chú trọng về tiện nghi</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Nhà hàng, bar, bếp gồm có: bàn ghế, tủ đựng dụng cụ ăn uống, mặt bàn soạn chia sơ chế và chế biến món ăn không thấm nước, bồn rửa dụng cụ, bồn rửa đồ ăn, hệ thống thoát nước chìm, dụng cụ tẩy rửa vệ sinh, thùng rác có nắp, ánh sáng, thông gió, tủ bảo quản thực phẩm, sơ đồ chỉ đèn thoát hiểm, thiết bị lọc rác, hệ thống hút mùi, điều hòa không khí,…</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Khu giặt là: có cầu là, bàn là, máy giặt, máy sấy,….</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.3.3 Hàng lang, cầu thang</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khu vực này cần đảm bảo có biển chỉ dẫn thoát hiểm, đèn báo thoát hiểm tại hành lang, các biển chỉ dẫn có hướng và chỉ dẫn cụ thể số tầng-phòng-phòng vệ sinh, cửa thoát hiểm chống cháy,…</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.3.4 Thông gió, điều hòa không khí ở các khu vực</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cần đảm bảo hệ thống thông gió hoạt động tốt, có điều hòa không khí, thông gió tự nhiên,….</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.3.5 Thang máy</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn 3 sao từ 3 tầng trở lên phải có thang máy, bao gồm cả tầng trệt.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2.4 Dịch vụ và mức độ phục vụ</strong></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Chất lượng phục vụ:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Đảm bảo có quy trình phục vụ khách của khách sạn</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Cần phục vụ đúng kỹ thuật nghiệp vụ</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Đảm bảo có tay nghề cao</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Nhân viên có thái độ thân thiện với du khách</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">- Phục vụ buồng cần đảm bảo các yếu tố như:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Có nhân viên trực buồng 24/24h</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Nên vệ sinh buồng ngủ 1 ngày 1 lần</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Cần thay ga, đệm, vỏ gối ngày một lần hay theo ý khách hàng, sau khi trả phòng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Thay khăn tắm và khăn mặt ngày một lần hoặc theo ý khách</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Hướng dẫn khách bảo vệ môi trường</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Có cà phê, trà, đường, sữa miễn phí</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">+ Phục vụ ăn sáng, trưa, tối, các món ăn và đồ uống dễ chế biến, phong phú, đảm bảo chất lượng, có bữa sáng tự chọn cho khách, phục vụ ăn uống và tiệc tại buồng ngủ,…</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bạn có thể xem thêm : </span></span><a href=\"https://www.angiakhang.com/tieu-chuan-xay-dung-khach-san-4-sao\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><strong><span style=\"color: rgb(184, 49, 47);\">T</span></strong><span style=\"color: rgb(184, 49, 47);\"><strong>iêu chuẩn xây dựng khách sạn 4 sao</strong></span></span></a></p>\n', NULL, 1, NULL, 1579193638, 1590043858, NULL),
(313, 'news', 15, 2, '4. Các mẫu khách sạn 3 sao đẹp nhất', '4-cac-mau-khach-san-3-sao-dep-nhat', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><img src=\"/uploads/contents/1579193842_45c8b70f07bfe90d5678.jpg\" style=\"width: 588px;\" class=\"fr-fic fr-dib\"> </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Một mẫu khách sạn đạt chuẩn 3 sao ở Nha Trang </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><img src=\"/uploads/contents/1579193853_f68b2fd6cf63a1289272.jpg\" style=\"width: 614px;\" class=\"fr-fic fr-dib\"></span><br></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vẻ nguy nga tráng lệ như một tòa lâu đài </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579193870_eac6e9e4f574735326bb.jpg\" style=\"width: 602px;\" class=\"fr-fic fr-dib\"></span></span><br></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiến trúc châu Âu độc đáo khiến khách sạn này càng nổi bật hơn </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579193884_24ba659165538b79a9af.jpg\" style=\"width: 592px;\" class=\"fr-fic fr-dib\"></span></span><br></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ta có cảm giác như đứng giữa trời Tây trước mẫu kiến trúc này </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579193899_9056c63e52c6183ec2c5.jpg\" style=\"width: 545px;\" class=\"fr-fic fr-dib\"></span></span><br></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiến trúc tân cổ điển tạo điểm nhấn cho mẫu thiết kế này</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Hy vọng rằng với những thông tin mà chúng tôi mới giới thiệu trên đây có thể giúp bạn đọc hiểu rõ các tiêu chuẩn cần thiết khi xây dựng khách sạn đạt tiêu chuẩn 3 sao. Nếu vẫn còn bất cứ thắc mắc nào khác quý khách hàng vui lòng liên hệ qua hotline để biết thêm chi tiết nhé.</span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n', NULL, 1, NULL, 1579193638, 1590043858, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(314, 'news', 16, 0, '1. Tiêu chuẩn xây dựng khách sạn 5 sao', '1-tieu-chuan-xay-dung-khach-san-5-sao', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Tiêu chuẩn xây dựng khách sạn 5 sao là loại hình công trình được xây dựng độc lập với những tiêu chí như: quy mô từ 100 phòng trở lên, đảm bảo nơi để xe và giao thông nội bộ, đảm bảo đầy đủ chất lượng và số lượng về cơ sở vật chất, trang thiết bị... để đáp ứng tốt nhất các dịch vụ phục vụ nhu cầu sinh hoạt, vui chơi giải trí và nghỉ dưỡng của các du khách. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img data-fr-image-pasted=\"true\" src=\"https://www.angiakhang.com/uploads/contents/1579247116_ac75d1dc7ddc90470c6c.png\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; border-style: none; -webkit-font-smoothing: antialiased; cursor: pointer; padding: 0px 1px; max-width: 100%; color: rgb(65, 65, 65); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: center; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; width: 609px;\" class=\"fr-fic fr-dib\"><br></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các tiêu chuẩn xây dựng khách sạn 5 sao</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tiêu chuẩn thiết kế khách sạn 5 sao bao gồm:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">1) Tiêu chuẩn vị trí, kiến trúc.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">a)Vị trí. </span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn 5 sao vị trí thuận lợi</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn có vị trí thuận lợi, thuận tiện giao thông, môi trường cảnh quan đẹp.</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">b)Thiết kế kiến trúc.</span></span></p>\n<ol>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có thiết kế kiến trúc phù hợp với yêu cầu kinh doanh, các khu vực dịch vụ được bố trí hợp lý.</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết kế kiến trúc đẹp, sang trọng.</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nội, ngoại thất được thiết kế, trang trí hợp lý, đẹp, sang trọng và hợp thẩm mỹ.</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đường đi trong khách sạn đảm bảo cho người khuyết tật sử dụng.</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khu vực buồng ngủ cách âm tốt.</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có mái che trước sảnh đón tiếp.</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vật liệu xây dựng đạt chất lượng tốt.</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có khu vực cửa ra vào dành riêng cho khách và nhân viên.</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Buồng ngủ cho người khuyết tật đi bằng xe đẩy, xe lăn.</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Toàn cảnh được thiết kế thống nhất.</span></span></li>\n<li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn có tầng đặc biệt phục vụ những dịch vụ đặc biệt (áp dụng đối với khách sạn và khách sạn nghỉ dưỡng).</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">2) Quy mô khách sạn.</span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tiêu chuẩn khách sạn 5 sao phải có tối thiểu 100 phòng. Diện tích phòng cụ thể như sau:</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng một giường đơn 24 m2.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng một giường đôi hoặc hai giường đơn 32 m2,</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng đặc biệt 56 m2.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng đặc biệt cao cấp 100 m2.</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">3) Không gian xanh.</span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn 5 sao có không gian xanh</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn 5 sao phải có cây xanh tại các khu vực công cộng. Đối với khách sạn nghỉ dưỡng phải có sân, vườn rộng.</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">4) Khu vực gửi xe.</span></span></p>\n<ol><li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khu vực gửi xe của khách sạn phải đủ diện tích để giữ xe cho khách của 50% tổng số phòng.</span></span></li></ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">5) Khu vực sảnh đón tiếp.</span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn phải có sảnh đón tiếp.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Diện tích 100 m2 (không áp dụng đối với khách sạn nổi).</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Diện tích 35 m2 (áp dụng đối với khách sạn nổi)</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng vệ sinh sảnh dành riêng cho nam và nữ.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng vệ sinh cho người khuyết tật đi bằng xe lăn, xe đẩy.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bar sảnh.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khu vực hút thuốc riêng.</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">6) Nhà hàng, bar. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn 5 sao có hệ thống nhà hàng, bar đạt tiêu chuẩn</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">7) Khách sạn 5 sao cần phải có:</span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">02 nhà hàng có phục vụ món Á, Âu.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">02 quầy bar.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Số ghế bằng 805 số giường.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng vệ sinh cho nam và nữ riêng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khu vực hút thuốc riêng.</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">8) Khu vực bếp</span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bếp phải thuận tiện cho việc vận chuyển thức ăn đến nhà hàng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Diện tích phải đảm bảo quy trình sơ chế, chế biến.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hệ thống thông gió hoạt động tốt.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có biện pháp ngăn chặn động vật, côn trùng gây hại.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tường phẳng, dễ làm sạch và không thấm nước.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trần bếp được làm từ vật liệu tiện cho vệ sinh và đảm bảo an toàn.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sàn phẳng, lát vật liệu chống trơn, dễ vệ sinh và làm sạch.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bếp Á - Âu.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bếp bánh.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khu vực soạn, chia thức ăn.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khu vực sơ chế nhiệt, nóng, nguội, lạnh được tách riêng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bếp phải có khu vực vận chuyển rác tách biệt, đảm bảo vệ sinh.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng vệ sinh cho nhân viên bếp ở bên ngoài khu vực bếp.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng đệm, đảm bảo cách âm, cách nhiệt, cách mùi giữa bếp và phòng ăn.</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> 9) Kho.</span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có các kho bảo quản nguyên vật liệu, thực phẩm riêng biệt.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kho để đồ dùng, thiết bị dự phòng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các kho lạnh được chia theo loại thực phẩm.</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">10) Phòng họp, phòng hội nghị, hội thảo.</span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sảnh chờ, đăng ký khách và giải khát giữa giờ.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">01 phòng hội nghị</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">02 phòng hội thảo</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">02 phòng họp</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cách âm tốt</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng vệ sinh cho nam và nữ riêng (áp dụng đối với phòng họp, hội nghị, hội thảo tách rời sảnh đón tiếp).</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">11) Khu vực dành cho cán bộ nhân viên của khách sạn.</span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khách sạn có đầy đủ phòng làm việc cho quản lý và các bộ phận chức năng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng trực buồng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng thay quần áo.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng vệ sinh, phòng tắm cho nam và nữ riêng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phòng ăn cho nhân viên.</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">12) Hành lang.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khu vực hành lang đi lại phải đảm bảo an toàn thoát hiểm.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">13) Yêu cầu về trang thiết bị và tiện nghi.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các trang thiết bị, nội ngoại thất, tiện nghi trong khách sạn 5 sao phải đồng bộ, hiện đại, sang trọng và được bài trí hợp lý.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">13) Sảnh đón tiếp. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">a) Khách sạn 5 sao có sảnh đón tiếp tiện nghi, hiện đại</span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khu vực quầy lễ tân phải được tổ chức thành các quầy phân theo chức năng, bao gồm: quầy đón tiếp, quầy đặt buồng và tổng đài điện thoại.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Internet không dây.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Điện thoại, máy vi tính.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bàn ghế tiếp khách</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bảng niêm yết giá dịch vụ và phương thức thanh toán.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bảng niêm yết tỷ giá ngoại tệ.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các thiết bị phục vụ thanh toán bằng thẻ tín dụng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khu vực cửa ra vào được bố trí thuận tiện.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khu vực giữ đồ và tài sản cho khách.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xe đẩy dành cho người khuyết tật.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giá để tạp chí, báo hoặc hệ thống đọc báo trực tuyến.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quầy thông tin, quan hệ khách hàng và hỗ trợ đón khách.</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">b) Phòng vệ sinh sảnh và các khu vực công cộng.</span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bồn cầu</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vòi nước di động cạnh bồn cầu</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bồn tiểu nam (phòng vệ sinh nam)</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giấy vệ sinh</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chậu rửa mặt, gương soi, vòi nước, xà phòng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Móc treo hoặc giá để đồ.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thùng rác có nắp</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết bị thông gió</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giấy hoặc khăn lau tay</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Máy sấy tay</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hệ thống ga và xi phông thoát sàn đảm bảo ngăn mùi hôi.</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">c) Phòng lưu trú.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">14) Buồng ngủ. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">a) Khách sạn 5 sao có buồng ngủ đầy đủ tiện nghi</span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tùy theo loại phòng mà sẽ có giường khác nhau như giường đơn, giường đôi, giường cỡ lớn, giường dành cho người khuyết tật.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chăn có vỏ bọc, đệm dày 20 cm, có ga bọc, gối có vỏ bọc</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tủ hoặc kệ đầu giường</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bảng điều khiển thiết bị điện đặt ở tủ đầu giường</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đèn đầu giường</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ổ cắm điện an toàn ở đầu giường, điện thoại</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tủ đựng quần áo có mắc treo quần áo</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mắc áo có nhiều loại để treo được các loại quần, áo - Bàn chải quần áo</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đường truyền internet không dây (wifi) tốc độ cao, đường truyền internet qua cáp tốc độ cao (internet cable)</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bàn làm việc trong buồng ngủ. (100% các phòng đều có)</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đèn bàn làm việc (áp dụng với khách sạn, khách sạn nghỉ dưỡng).</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ổ cắm điện an toàn ở bàn làm việc (áp dụng với khách sạn, khách sạn nghỉ dưỡng)</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cặp đựng tài liệu thông tin về khách sạn và hướng dẫn khách.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Gương soi cả người</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Rèm cửa sổ đủ chắn sáng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đèn đủ chiếu sáng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bàn ghế uống nước, cốc uống nước, tách uống trà, cà phê</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Dụng cụ mở bia, rượu</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hộp giấy ăn, bộ đồ ăn trái cây</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sọt hoặc túi đựng đồ giặt là</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sọt đựng rác</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết bị phát hiện khói báo cháy (smoke detector)</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hệ thống chữa cháy bằng nước tự động (sprinkler)</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Điều hòa không khí, tivi, tủ lạnh hoặc minibar (minibar có đặt các loại đồ uống, đồ ăn vặt, đồ ăn nhẹ)</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hướng dẫn sử dụng điện thoại và tivi</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giá để hành lý, dép đi trong phòng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giấy hoặc hộp mút lau giầy</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ấm đun nước siêu tốc</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Két an toàn cho 100 % số buồng ngủ</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Túi kim chỉ, máy sấy tóc, bàn là, cầu là</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thảm trải buồng ngủ hoặc sàn gỗ</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sơ đồ hướng dẫn thoát hiểm</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mắt nhìn gắn trên cửa, chuông gọi cửa</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chốt an toàn, ổ khóa từ dùng thẻ, cửa tự động đóng.</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">b) Phòng vệ sinh trong buồng ngủ.</span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tường phòng vệ sinh được làm bằng vật liệu không thấm nước.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sàn lát bằng vật liệu chống trơn</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ổ cắm điện an toàn</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chậu rửa mặt, bệ đặt chậu rửa mặt</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Gương soi, đèn trên gương soi, đèn trần</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vòi nước, vòi tắm hoa sen</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nước nóng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hệ thống sen cây tắm đứng phun mưa</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Móc treo quần áo, giá để khăn các loại</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bồn cầu, vòi nước di động cạnh bồn cầu</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giấy vệ sinh, thùng rác có nắp</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết bị thông gió</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các vật dụng cho khách như: xà phòng, khăn mặt, khăn tắm, khăn lau tay, bàn chải đánh răng, kém đánh răng, dầu gội đầu, sữa tắm, dầu xả, áo choàng sau tắm...</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Muối tắm</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Khăn chùi chân</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Điện thoại nối với buồng ngủ</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Dây phơi quần áo, cân sức khỏe</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hệ thống ga và xi phông thoát sàn đảm bảo ngăn mùi hôi</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đối với khách sạn 5 sao: 100% số buồng ngủ có phòng tắm đứng hoặc bồn tắm nằm có rèm che, trong đó 30 % số buồng ngủ có cả hai loại</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">c) Với phòng suite cao cấp, có thể có thêm:</span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bồn tắm tạo sóng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Gương cầu.</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">d) Phòng họp, hội thảo, hội nghị</span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bàn ghế, micro, đèn điện các loại</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Máy chiếu, màn chiếu</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trang thiết bị phục vụ họp, hội nghị, hội thảo.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sơ đồ, biển chỉ dẫn và đèn báo thoát hiểm</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Điều hòa không khí</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hệ thống thông gió tốt</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hệ thống camera.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thảm</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hệ thống chiếu sáng có tính thẩm mỹ và điều chỉnh được độ sáng</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">15)  Nhà hàng, bar, bếp</span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bàn ghế</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Dụng cụ và tủ đựng dụng cụ phục vụ ăn uống</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mặt bàn soạn chia, sơ chế, chế biến món ăn làm bằng vật liệu không thấm nước</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trang thiết bị, dụng cụ chế biến món ăn, đồ uống.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trang thiết bị phục vụ ăn tại buồng ngủ</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trang thiết bị phục vụ ăn uống của nhân viên</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trang thiết bị, dụng cụ chế biến bánh</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trang thiết bị, dụng cụ chế biến đồ nguội</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bồn rửa dụng cụ ăn uống</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bồn rửa cho sơ chế, chế biến riêng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hệ thống thoát nước chìm</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Dụng cụ và chất tẩy rửa vệ sinh, thùng rác có nắp</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ánh sáng hoặc chiếu sáng tốt, thông gió tốt</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các loại tủ lạnh bảo quản thực phẩm</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sơ đồ, biển chỉ dẫn và đèn báo thoát hiểm</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết bị chắn lọc rác, mỡ</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hệ thống hút mùi hoạt động tốt</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Điều hòa không khí cho nhà hàng</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thảm trải nhà hàng</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">16 )Hành lang, cầu thang</span></span></p>\n<ol>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sơ đồ, biển chỉ dẫn thoát hiểm</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đèn báo thoát hiểm ở hành lang</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các biển chỉ dẫn hướng và chỉ dẫn chức năng (phòng vệ sinh, văn phòng, số tầng, số phòng…)</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cửa thoát hiểm chống cháy</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thảm trải hành lang khu vực dịch vụ, buồng ngủ, hội nghị, hội thảo.</span></span></li>\n</ol>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">17) Thông gió, điều hòa không khí ở các khu vực</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hệ thống thông gió hoạt động tốt, đảm bảo thông thoáng, điều hòa không khí tại các khu vực dịch vụ. Bên cạnh đó xây dựng các cửa sổ để có hệ thống thông gió tự nhiên.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">18) Hệ thống lọc nước</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nước được lọc và đảm bảo uống được trực tiếp từ vòi nước</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">19) Thang máy</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif;\">Các khách sạn từ ba t', NULL, 1, NULL, 1579246635, 1590096966, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(315, 'news', 16, 1, '2. Một số mẫu khách sạn tiêu chuẩn 5 sao đẹp, sang trọng.', '2-mot-so-mau-khach-san-tieu-chuan-5-sao-dep-sang-trong', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><img src=\"/uploads/contents/1579246448_a0e60a6c0905680fd9a5.jpg\" style=\"width: 604px;\" class=\"fr-fic fr-dib\"></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu khách sạn 5 sao nghìn tỷ tại Sài Gòn</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579246461_bc4f35a39b90079368d5.jpg\" style=\"width: 606px;\" class=\"fr-fic fr-dib\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu khách sạn 5 sao tân cổ điển đẹp</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579246471_ae93780d5f2e56df5ed8.jpg\" style=\"width: 593px;\" class=\"fr-fic fr-dib\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu khách sạn 5 sao tiện nghi, hiện đại</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579246484_b769c69337133394a8e4.jpg\" style=\"width: 599px;\" class=\"fr-fic fr-dib\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu khách sạn 5 sao đẹp, sang trọng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1579246499_a3cce4f69f81113864c4.jpg\" style=\"width: 591px;\" class=\"fr-fic fr-dib\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu khách sạn 5 sao hiện đại tại Phú Quốc</span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><strong>&gt;&gt; Xem thêm:</strong> </span><a href=\"https://www.angiakhang.com/xay-dung-khach-san\"><span style=\"color: rgb(235, 107, 86); font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Đơn giá xây dựng khách sạn</span></a></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: left;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><strong>Rất mong được hợp tác cùng quý khách hàng.</strong></span></p>\n', NULL, 1, NULL, 1579246637, 1590096966, NULL),
(316, 'news', 17, 0, '1. Những rủi ro gặp phải khi chọn nhà thầu giá rẻ', '1-nhung-rui-ro-gap-phai-khi-chon-nha-thau-gia-re', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif;\">Tuổi thọ công trình không đảm bảo: Khi nhà thầu nhận thầu xây dựng với giá thấp để đảm bảo lợi nhuận có đơn vị sẽ cắt giảm vật tư hoặc dùng vật tư kém chất lượng, vật tư giả. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1590044130_0fdc071f0079e5b79969.png\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; border-style: none; -webkit-font-smoothing: antialiased; cursor: pointer; padding: 0px 1px; max-width: calc(100% - 10px); color: rgb(65, 65, 65); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300; letter-spacing: normal; orphans: 2; text-align: center; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; width: 631px;\" class=\"fr-fic fr-dii\"> </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng uy tín An Gia Khang</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu căn nhà của bạn xây dựng từ các vật tư kém chất lượng sẽ rất nhanh chóng bị hư hỏng, xuống cấp và thiếu an toàn. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kết cấu của một căn hộ đóng vai trò quan trọng và ảnh hưởng tới tính bền vững của căn nhà. Thế nhưng sẽ rất khó để bạn kiểm soát được chất lượng của phần thi công này. Với những căn nhà có kết cấu tốt tuổi thọ có thể kéo dài từ 70 đến 100 năm. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thế nhưng nếu như kết cấu kém thì chỉ sau khoảng từ 10 đến 30 năm là đã xuống cấp rồi. Bạn có thể sẽ mất nhiều chi phí để nâng cấp, sửa chữa. </span></span><span style=\"font-size: 14px;\"></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bạn có thể chọn nhà thầu xây dựng giá rẻ nhưng chi phí sửa nhà sau này cộng lại có thể còn cao hơn ban đầu bạn chọn nhà thầu uy tín. Do đó cần cân nhắc kỹ   để đảm bảo an toàn cho gia đình bạn nhé.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với những nhà thầu xây dựng giá rẻ chế độ bảo hành thường là không có hoặc có nhưng rất qua loa. Nhiều người khi xây dựng nhà thường quên mất vấn đề bảo hành, bảo dưỡng nhà. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tuy nhiên đó là một vấn đề khá quan trọng nó thể hiện trách nhiệm của nhà thầu với sản phẩm của chính mình. Có nhiều trường hợp nhà thầu thiếu kinh nghiệm xây dựng và thi công sai kỹ thuật khiến cho nhà nhanh bị xuống cấp và bạn phải tự sửa chữa.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu có thể bán toàn bộ hoặc một phần gói thầu và đùn đẩy trách nhiệm nếu vấn đề thi công, hoàn thiện phát sinh vấn đề trong khi thi công.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576123934_7653d983be0880907c0d.jpg\" style=\"width: 624px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đội ngũ công nhân thi công An Gia Khang</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Bỏ công trình khi còn đang thi công dở dang: những nhà thầu thiếu kinh nghiệm nhận thầu giá rẻ không có dự toán chi phí rõ ràng có thể làm phát sinh chi phí trong quá trình xây dựng. Do đó có thể dễ dàng phát sinh mâu thuẫn với chủ nhà khiến cho công trình bị tạm hoãn giữa chừng.</span></p>\n', NULL, 1, NULL, 1579569969, 1590044163, NULL),
(317, 'news', 17, 1, '2. Hiệp hội nhà thầu xây dựng Việt Nam', '2-hiep-hoi-nha-thau-xay-dung-viet-nam', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Hiệp hội xây dựng nhà thầu là tổ chức xã hội nghề nghiệp đại diện cho các nhà thầu trong lĩnh vực xây dựng.</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576123725_f3175496ad40a8b963b5.png\" style=\"width: 619px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hiệp hội nhà thầu xây dựng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hiệp hội nhà thầu xây dựng Việt Nam<strong> </strong>là tổ chức xã hội nghề nghiệp được thành lập theo quyết định số 13/1999/QĐ-TCCP của Bộ trưởng - Trưởng ban tổ chức cán bộ Chính phủ ngày 27 tháng 05 năm 1999. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hiệp hội ra đời nhằm tăng cường sự hợp tác giữa các nhà thầu trong nước để hỗ trợ lẫn nhau, cùng bảo vệ lợi ích chính đáng của doanh nghiệp trên cơ sở cạnh tranh lành mạnh và bình đẳng, cùng tồn tại, phát triển. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hơn nữa đây cũng là nơi mà các nhà thầu có thể gặp nhau để trao đổi và tạo điều kiện phát huy năng lực. Khắc phục được các khó khăn trong thực hiện dự án, quản lý điều hành và hỗ trợ lẫn nhau về kỹ thuật, pháp lý góp phần thúc đẩy sự phát triển của ngành xây dựng Việt nam. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576123752_22337fd61f0fc79db321.jpg\" style=\"width: 644px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đội ngũ công nhân thi công xây dựng An Gia Khang</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hiệp hội cũng là tổ chức nghề nghiệp tự nguyện của các doanh nghiệp ở tất cả các thành phần kinh tế cả nước gồm có lĩnh vực xây lắp công trình, trang trí nội thất, tư vấn, cung cấp thiết bị, vật tư, máy móc thi công, vật liệu xây dựng,….</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>&gt;&gt; Xem thêm:</strong> </span></span><a href=\"https://www.angiakhang.com/nha-thau\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"color: rgb(235, 107, 86);\">Nhà thầu xây dựng công nghiệp</span></span></span></a></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">[[contact_short_code]]</span></p>\n', NULL, 1, NULL, 1579570004, 1590044163, NULL),
(318, 'news', 18, 0, '1. Quy trình nhà thầu xây dựng thiết kế dự án cụ thể cho nhà đầu tư', '1-quy-trinh-nha-thau-xay-dung-thiet-ke-du-an-cu-the-cho-nha-dau-tu', '<p style=\"text-align: left;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng An Gia Khang, xin được chia sẽ quy trình nhà thầu thực hiện dự án như sau:</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Bước 1:</strong> Nhà thầu xây dựng tiếp nhận thông tin từ phía chủ đầu tư.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các chủ đầu tư có thể liên hệ với nhà thầu xây dựng Phúc Quốc thông qua điện thoại, bên nhà thầu sẽ trực tiếp tư vấn, tiếp nhận các yêu cầu của chủ đầu tư và hẹn lịch làm việc cụ thể chính xác với chủ đầu tư.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Bước 2:</strong> Bên thầu xây dựng sẽ trực tiếp đến khảo sát công trình và tiếp nhận các yêu cầu một cách cụ thể. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bộ phận kinh doanh cùng với bộ phận kĩ thuật của bên nhà thầu gặp trực tiếp để lấy thông tin yêu cầu từ khách hàng</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1574588552_7a7388c5f5e5d5fb7d06.jpg\" style=\"width: 600px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thi công đổ móng công trình tại Phú Quốc</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ở bước này, bộ phận kinh doanh cùng với bộ phận kĩ thuật của bên nhà thầu xây dựng sẽ gặp trực tiếp để lấy thông tin yêu cầu từ khách hàng, thu nhận các tài liệu liên quan đến dự án và khảo khảo sát công trình. Đồng thời bên phía nhà thầu xây dựng cung sẽ trao đổi sơ bộ và tư vấn cho chủ đầu tư về các khoảng công việc sẽ thi công trong xây dựng.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Bước 3:</strong> Nhà thầu xây dựng tiến hành bóc tách khối lượng, lập dự toán báo gia cho chủ đầu tư.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tại bước này, phòng kỹ thuật bên phía nhà thầu sẽ bắt đầu bóc tách khối lượng công việc của công trình từ đó lập dự toán thi công. Sau khi hoàn thành dự toán sẽ báo giá cho bên chủ đầu tư.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Bước 4:</strong> Hai bên kí kết hợp đồng xây dựng.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sau khi đã báo giá cho bên chủ đầu tư, nếu đồng ý, chủ thầu xây dựng sẽ sẽ soạn thảo hợp đồng, thương lượng các điều kiện, điều khoản có trong hợp đồng với chủ đầu tư. Nếu được duyệt qua hai bên sẽ ký hợp đồng.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Bước 5: </strong>Thực hiện hợp đồng. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng sẽ thi công theo đúng các nội dung đã kí kết trong hợp đồng với chủ đầu tư</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1574588595_15d92183fb61d715aa15.jpg\" style=\"width: 571px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bước đổ sàn nhà</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phía nhà thầu xây dựng sẽ bắt đầu tiến hành chuẩn bị nguồn nhân lực, máy móc và vật tư để thi công<span style=\"white-space:pre;\">    </span>dự án. Nhà thầu xây dựng sẽ thi công theo đúng các nội dung đã kí kết trong hợp đồng với chủ đầu tư. Ngoài ra, chủ thầu xây dựng cũng sẽ bố trí các kỹ sư giám sát chặt chẽ tiến độ của công trình , nghiệm thu các hạng mục.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Bước 6:</strong> Hai bên nghiệm thu công trình, tiến hành thanh lý hợp đồng.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng sẽ tiến hành làm hồ sơ hoàn thành công trình, dự án, biên bản nghiệm thu và bàn giao công trình đã hoàn thiện cho chủ đầu tư.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Bước 7:</strong> Thanh lý hợp đồng và bảo hành công trình theo như đúng kí kết trong hợp đồng.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chủ đầu tư có trách nhiệm thanh toán hết các khoản theo như đúng hợp đồng đã kí kết. </span></span><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nhà thầu xây dựng có trách nhiệm bảo hành công trình theo đúng quy định đã thỏa thuận.</span></p>\n', NULL, 1, NULL, 1579570779, 1590044290, NULL),
(319, 'news', 18, 1, '2. Kinh nghiệm chọn nhà thầu xây dựng', '2-kinh-nghiem-chon-nha-thau-xay-dung', '<p style=\"text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif;\"><span style=\"font-size: 14px;\">Xây nhà dường như là một trong những việc quan trọng đời người, chính vì thế mà khi quyết định xây nhà bạn cần phải suy nghĩ, tính toán kỹ lưỡng. vậy bạn cần phải làm gì trước khi đưa ra quyết định quan trọng đó?</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1574583325_6fb3b4fc68f6c83098e1.png\" style=\"width: 585px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công trình nhà thầu đang triển khai</span></span></p>\n<p style=\'margin:0in;margin-bottom:10.0pt;font-size:13px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;line-height:150%;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\"><strong>Bước 1:</strong> Điều quan trọng trước tiên khách hàng nên lập cho mình một bảng kế hoạch làm nhà thật rõ ràng cùng với việc chuẩn bị đầy đủ một khoản chi phí để công trình nhà có thể tiến hành thi công.</span></span></span></p>\n<p style=\'margin:0in;margin-bottom:10.0pt;font-size:13px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;line-height:150%;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\"><strong>Bước 2:</strong> Tiếp theo để có thể tiến hành xây dựng nhà quý khách hàng phải tiến hành chuẩn bị các loại giấy phép xây dựng nhà ở và phải hoàn thiện đầy đủ các hồ sơ nhằm mục đích cho việc xây nhà.</span></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1574583432_4a9570ae9869585c8e75.png\" style=\"width: 578px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giai đoạn đổ móng công trình</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bạn nên chọn nhà thầu xây dựng uy tín và thật kĩ lưỡng trước khi đưa ra quyết định xây công trình.</span></span></p>\n<p style=\'margin:0in;margin-bottom:10.0pt;font-size:13px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;line-height:150%;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\"><strong>Bước 3:</strong> Một việc quan trọng hơn hết mà bạn không nên bỏ qua chính là chọn cho mình một nhà thầu xây dựng tphcm uy tín, chuyên nghiệp để giúp bạn hoàn thiện ngôi nhà của mình. </span></span></span></p>\n<p style=\'margin:0in;margin-bottom:10.0pt;font-size:13px;font-family:\"Calibri\",\"sans-serif\";text-align:justify;line-height:150%;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\"><strong>Bước 4: </strong>Và bước cuối cùng trước khi tiến hành thi công là bạn nên chọn một ngày khởi công phù hợp, chuẩn bị mặt bằng rồi bàn giao lại cho đơn vị thi công từ<strong> </strong>nhà thầu xây dựng tại tphcm.</span></span></span></p>\n<p style=\"margin: 0in 0in 10pt; font-size: 13px; font-family: Calibri, sans-serif; text-align: center; line-height: 150%;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-height: 150%; color: black;\"><img src=\"/uploads/contents/1574586010_7845efe07b217ecf5b21.jpg\" style=\"width: 596px;\" class=\"fr-fic fr-dii\"></span></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng uy tín tphcm</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trong những bước trên, có lẽ bước mà chủ đầu tư cần đặc biệt quan tâm nhất đó chính là việc tìm kiếm một nhà thầu xây dựng tp HCM uy tín và chuyên nghiệp để giúp bạn hoàn thiện công trình một cách tốt nhất và chất lượng nhất.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Thế nhưng làm sao để có thể biết được đâu là một nhà thầu xây dựng chất lượng, uy tín? Điều này cũng không quá khó khăn đối với quý khách hàng nếu bạn chú ý những điểm sau.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Bạn nên gặp trực tiếp người điều hành của nhà thầu xây dựng để xem xét có nên bàn giao công trình cho họ hay không.</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1574586125_24c7edbf6a5f4e0fb645.png\" style=\"width: 595px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu xây dựng An Gia Khang tại Tphcm</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><strong>&gt;&gt; Xem thêm: </strong></span><a href=\"https://www.angiakhang.com/nang-luc-nha-thau\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px; color: rgb(235, 107, 86);\">Năng lực nhà thầu An GIa Khang</span></a></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một nhà thầu xây dựng có tốt hay không, có uy tín hay không sẽ căn cứ vào trách nhiệm, các yếu tố xây dựng nên thương hiệu và người chủ có trách nhiệm chính tham gia điều hành trực tiếp hay gián tiếp. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chính vì thế bằng cách gặp gỡ người điều hành của chủ thầu xây dựng sẽ giúp bạn có thể nhận biết một phần về họ và xem xét có nên hay không giao công trình cho họ.</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1574586296_045909b68d87b9821412.png\" style=\"width: 589px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đội ngũ công nhân đang thi công</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tiếp đến là yếu tố giá cả, không bàn đến sự đắt hay rẻ ở đây. Khách hàng khi tham khảo nhà thầu xây dựng tp HCM nên chú ý đến các chi tiết, từng hạng mục báo giá cả nhà thầu xem có rõ ràng không, có hợp lý không. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đừng quá tham rẻ, hãy chọn một mức giá hợp lý, có cơ sở rõ ràng, bởi ngày nay cũng có rất nhiều các nhà thầu chào giá vô cùng rẻ nhưng chất lượng sau đó, ai có thể đoán được?</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hãy tham khảo qua các công trình mà nhà thầu đó đã từng thi công qua, qua đó bạn sẽ biết được nhà thầu xây dựng đó có đúng thật sự tốt như đã quảng cáo, có thật sự chất lượng, uy tín hay không. </span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1574586335_536fc3fb835d926bcc11.jpg\" style=\"width: 568px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chủ đầu tư công trình An Gia Khang</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hãy chú ý, xem thật kĩ càng mẫu hợp đồng mà nhà thầy xây dựng đưa cho bạn để tham khảo. </span></span><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Chú ý đến mẫu hợp đồng mà nhà thầu xây dựng đưa cho bạn, qua mẫu hợp đồng đó bạn có thể biết được những quyền lợi và những ràng buộc của bạn với chủ thầu xây dựng đó. Cảm ơn các bạn đã xem bài viết trong chuyên mục: </span><a href=\"https://www.angiakhang.com/kinh-nghiem-hay\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px; color: rgb(209, 72, 65);\">Kinh nghiệm hay về xây dựng nhà</span></a></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n', NULL, 1, NULL, 1579570847, 1590044290, NULL),
(320, 'news', 19, 0, '1. 10 kinh nghiệm khi chọn xây nhà trọn gói', '1-10-kinh-nghiem-khi-chon-xay-nha-tron-goi', '<h3 style=\"text-align: left;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>1. Cần phải giám sát kỹ thuật thi công của nhà thầu</strong><br></span></span></h3>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quan trọng nhất là bạn nên tập đọc rồi nghiên cứu bản vẽ thiết kế để có thể tự giám sát. Cho dù công việc xây dựng là của nhà thầu nhưng bạn tuyệt đối không nên lơ là.</span></span></p>\n<ul><li><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong> Ví dụ như:</strong></span></span></li></ul>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phần móng bạn đề nghị đơn vị thi công phải định vị chuẩn coste và mốc theo như giấy phép xây dựng để tránh trường hợp bị tranh chấp do sai lệch với khu đất của người khác đồng thời thuận lợi cho việc hoàn công sau này. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bạn cũng cần phải kiểm tra kích thước của cốp pha móng như chiều cao, chiều dài hay chiều rộng. </span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576851407_a67d11329d233c200cb0.png\" style=\"width: 592px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có sự giám sát kỹ công trình</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với những ngôi nhà dùng móng băng hay móng đơn bạn cũng cần kiểm tra chiều sâu chôn móng. Thường là tính từ mặt đất tự nhiên khoảng cách của các thanh cốt thép bản vẽ móng và số lượng cố thép trong dầm móng đã được ghi trong bản vẽ kết cấu. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phần móng cọc cần phải giám sát từ khâu ép cọc, khoảng cách của cọc tối thiểu là 3D, cần kiểm tra khoảng cách thép ở trong đài đã đúng bản vẽ chưa, chiều cao đài cọc như thế nào. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Về phần thân nhà bạn có thể kiểm tra chiều cao các tầng qua bản vẽ và giám sát kỹ thuật cốt thép dầm, cầu thang, cột. Đồng thời kiểm tra kích thước hình học của cốp pha dầm và cột đảm bảo đúng như thiết kế. Chiều cao bậc cầu thang cần được xây đều đặn. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hệ thống điện nước âm phải lắp đặt chờ sẵn rồi mới đổ bê tông sau đó. Nhớ phải chống thấm ở sân thượng, sàn vệ sinh, mái. Với những công trình lợp mái ngói phải kiểm tra bằng cách tưới nước lên mái ngói để phát hiện ra chỗ thấm hay kiểm tra khi thấy có trời mưa…..</span></span></p>\n<h3 style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>2. Vật liệu xây nhà quyết định chất lượng căn nhà</strong></span></span></h3>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vật liệu xây dựng nhà trọn gói có thể bao gồm: xi măng, sắt thép, gạch, đá, cát, nước, điện, phụ gia chống thấm,… </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Và dù không can thiệp vào quá trình xây dựng của bên thi công nhưng bạn vẫn cần phải chú ý và nhận biết vật tư qua thương hiệu, logo, màu sắc để khi nhà thầu mang vật tư tới bạn vẫn có thể kiểm soát được chất lượng của chúng mà không mất quá nhiều thời gian.</span></span></p>\n<h3 style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>3. Chú ý kiểm tra chất lượng bê tông cốt thép</strong></span></span></h3>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bạn phải đảm bảo được rằng bê tông đúng chuẩn về khả năng chịu lực, cường độ. Đặc biệt với các công trình như nhà phố, biệt thự thì mác bê tông thường là #250. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Để biết bê tông có đúng mác hay không bạn có thể dựa vào phiếu giao nhận của nhà cung cấp và yêu cầu lấy mẫu thí nghiệm ở hiện trường rồi gửi đi thử mẫu nếu cảm thấy cần thiết. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với  cốt thép bạn cần dựa vào bản vẽ kết cấu để giám sát chất lượng thi công của nhà thầu. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Những yếu tố đặc biệt quan trọng trong thi công cốt thép đó là đặt đúng, cắt đủ, buộc chặt. các thanh thép đúng vị trí trong cấu kiện dầm, sàn, cột.</span></span></p>\n<h3 style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>4.  Chú ý chất lượng xây thô</strong></span></span></h3>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhớ chú ý để đảm bảo rằng vữa hồ xây thô cần đảm bảo thì công trình mới đảm bảo chất lượng được. Thường thì một bao xi măng trộn với khoảng 10 đến 11 thùng cát. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bạn cũng cần phải biết rằng  khi trộn hồ mà ít xi măng thì vữa không đủ kết dính. Thế nhưng nếu như trộn quá nhiều xi măng sẽ có thể gây nên hiện tượng nứt tường vì hồ quá già.</span></span></p>\n<h3 style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>5.  Kiểm soát bảng vật tư và vật dụng hoàn thiện</strong></span></span></h3>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trên bảng vật tư cần phải có các thông tin như nhà sản xuất, thương hiệu, quy cách, đơn giá, chủng loại,</span></span></p>\n<h3 style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>6.  Phát sinh và cách kiểm soát</strong></span></span></h3>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Điểm này rất quan trọng và cần đặc biệt chú ý là bởi khi bạn khoán thầu bằng hình thức xây nhà trọn gói thì vẫn có chi phí phát sinh. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ở giai đoạn xây thô phát sinh thường là do giá vật tư biến động nên bạn cần theo dõi thị trường ở những lúc tăng giá để kiểm soát được và thương lượng với bên công ty xây dựng.  </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Còn ở giai đoạn hoàn thiện các trường hợp phát sinh là do chọn vật liệu có giá cao hơn đơn giá mà nhà thầu đưa ra trong bản hợp đồng. Do đó bạn cần phải nhắm tới các vật dụng, vật liệu phù hợp với khả năng thanh toán. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhớ ưu tiên cho các vật dụng cơ bản rồi mới đến vật dụng trang trí nội thất. Hơn nữa bạn cũng nên tham khảo đơn giá xây dựng của các nhà thầu khác nhau để có cơ sở lựa chọn, so sánh nhà thầu hợp lý và uy tín.  </span></span></p>\n<h3 style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>7. Hợp đồng xây nhà trọn gói</strong></span></span></h3>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ở trong các bản hợp đồng xây nhà trọn gói tiến độ của công trình đã được quy định rõ ràng khi soạn thảo hợp đồng. Các hình thức phạt nếu như chậm tiến độ cũng phải liệt kê cụ thể trong hợp đồng nhằm tránh trường hợp chủ thầu kéo dài thời gian xây dựng mà không có phản hồi thỏa đáng với chủ đầu tư.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"> Không ít trường hợp chủ thầu vì nhận nhiều công trình cùng lúc khiến cho tiến độ thi công chậm trễ hoặc nhỏ giọt. Thậm chí có lúc chỉ có 1 vài công nhân làm việc. Chất lượng công trình khi thi công vì vậy mà không đảm bảo được. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Do đó ngoài việc quy định điều kiện phạt trong hợp đồng bạn cũng cần thường xuyên nhắc nhở và đốc thúc đơn vị thi công đảm bảo tiến độ và quy định xây dựng được quy định trong hợp đồng. </span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576851741_1b34e5ac297fbfd2374a.png\" style=\"width: 584px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu thiết kế hoàn chỉnh trong hợp đồng</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các thông tin trong hợp đồng cần phải trình bày rõ ràng và chi tiết không dùng các từ ngữ có ý nghĩa mơ hồ không có ý nghĩa cụ thể. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ví dụ như gạch ceramic hãng ABC hay “ tương đương”. Cụm từ tương đương có thể gây hiểu lầm và thậm chí mang đến rủi ro cho nhà đầu tư vì các vật liệu khác nhau thì giá trị hợp đồng cũng không giống nhau được.<br></span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhắc tới hợp đồng không thể bỏ qua vấn đề tạm ứng tiền. Nhiều chủ đầu tư thường tạm ứng cho bên thầu khoảng từ 40 đến 80% giá trị hợp đồng. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Do đó khi xảy ra các trường hợp sai thiết kế, làm ẩu hay gian lận chi phí đều không dám làm căng với nhà thầu vì đã xuống nhiều quá nhiều. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhiều chủ đầu tư thậm chí còn bị đe dọa chấm dứt hợp đồng gây tổn thất về chi phí cho nhà đầu tư.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Do vậy để ngăn tình huống này xảy ra nhà đầu tư cần chia nhỏ các đợt giao tiền như giao tiền theo tiến độ thi công. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ví dụ xong phần móng giao đợt một, xong phần cột tầng một giao đợt hai rồi xong phần sàn giao tiếp đợt ba. Chia nhỏ tiền giao có thể tránh rủi ro có thể xảy đến cho chủ đầu tư.</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1576851799_d0afaf2a82d85364500e.png\" style=\"width: 577px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cam kết bảo hành sau hoàn công</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hơn nữa nhà đầu tư có thể thanh lý hợp đồng khi chủ thầu không đạt chất lượng mà không phải chịu các tổn thất lớn.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các điều khoản đảm bảo an toàn cũng khá quan trọng để tránh chủ thầu gây cháy nổ, làm ẩu thậm chí tai nạn gây tử vong khi thi công. Bạn thử nghĩ về tình huống sự cố hay tai nạn gây tử vong thì có còn ai dám ở ngôi nhà đó nữa không. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Do đó điều khoản này cần có mức phạt cao nhất trong hợp đồng để tránh trường hợp nhà thầu gây mất an toàn khi thi công.</span></span></p>\n<h3 style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>8. Quy định  tiến độ trong hợp đồng</strong></span></span></h3>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tiến độ của công trình cần phải được quy định rõ ràng khi thảo hợp đồng cũng như phạt hợp đồng thế nào nếu như nhà thầu làm chậm tiến độ. Hiện nay trong giao dịch dân sự việc phạt hợp đồng là không giới hạn.</span></span></p>\n<h3 style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>9. Vật liệu</strong></span></span></h3>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trong hợp đồng cũng phải quy định rõ chủng loại của vật liệu hãng nào sản xuất, model bao nhiêu. Các thông tin này đều phải được quy định cụ thể không dùng các từ ngữ tương đương. </span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu có thể phải giải thích nghĩa cuẩ từ đó vào hợp đồng tránh tiền mất tật mang bởi vật liệu khác nhau có thể khiến giá hợp đồng khác đi rất nhiều.</span></span></p>\n<h3 style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>10. Điều khoản bảo hành</strong></span></span></h3>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Phần lớn các công trình thuộc dạng xây nhà ở trọn gói thường được bảo hành tổng thể 12 tháng, phần kết cấu bảo hành 5 năm, thấm dột bảo hành 2 năm.</span></p>\n', NULL, 1, NULL, 1579571711, 1590644923, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(321, 'news', 19, 1, '2. Các rủi ro có thể xây ra khi xây nhà trọn gói', '2-cac-rui-ro-co-the-xay-ra-khi-xay-nha-tron-goi', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif;\">Để tránh không bị thiệt thòi hoặc dễ mua bực vào người bạn cần biết được những rủi ro có thể gặp phải khi lựa chọn dịch vụ xây nhà trọn gói đó là:</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575450501_11bcb4cb1233e6a7d014.png\" style=\"width: 579px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Các lưu ý khi xây nhà trọn gói</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>a) Năng lực nhà thầu</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Cũng có những nhà thầu không có kinh nghiệm và chuyên môn không thể tư vấn về công năng sử dụng, pháp lý hay tính thẩm mỹ cho ngôi nhà của bạn, kinh nghiệm thi công an toàn và việc lựa chọn vật liệu xây dựng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575450551_1c28f1f523666b2d04af.jpg\" style=\"width: 584px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nên chọn các đơn vị uy tín để tránh rủi ro khi xây nhà trọn gói</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Muốn khắc phục được điều này bạn cần phải tìm hiểu kỹ một số vấn đề như thời gian thi công thực tế, thời gian thành lập công ty. Yêu cầu nhà thầu phải cung cấp được giấy phép đăng ký kinh doanh, bằng cấp nếu có hay chứng chỉ hành nghề. Đồng thời cảm nhận chuyên môn, hiểu biết của họ về lĩnh vực xây dựng.  </span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>b) Rủi ro về tài chính</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bạn cần phải ràng buộc chặt chẽ về điều khoản thanh toán tốt nhất là làm xong công đoạn trước rồi mới thanh toán tiền. Ví dụ nếu nhà thầu ép xong cọc, bạn thanh toán ép dọc. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu thi công xong phần móng thì mới thanh toán phần móng. Nhớ rằng trong bảng quy định vật tư đi kèm hợp đồng cần mô tả cụ thể chủng loại xuất xứ và tính năng kèm theo cả mức giá.</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>c)  Rủi ro về tiến độ</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có những nhà thầu không đủ nhân lực thực hiện dẫn tới công trình bị kéo dài thời gian thi công.</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>d) Rủi ro về chất lượng</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Để khắc phục vấn đề này bạn cần phải nhờ hoặc thuê người có chuyên môn xem xét bản vẽ, giám sát và theo dõi quá trình thi công. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trong hợp đồng cần có bảng quy định vật tư, chủng loại, xuất xứ của nhà sản xuất hay thông số cụ thể. Quy định bảo hành, thời gian và chi phí đảm bảo bảo hành cũng cần lưu ý đừng bỏ qua nhé.</span></span></p>\n<h3 style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>e) Rủi ro bán thầu</strong></span></span></h3>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có nghĩa là nhà thầu ký kết hợp đồng với bạn thế nhưng không tổ chức thi công, điều phối vật tư hay giám sát thi công mà sẽ khoán lại cho nhà thầu khác làm. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Không ít công ty làm PR khá rầm rộ, tự hào làm nhiều công trình mỗi năm nhưng bạn cần tìm hiểu thật kỹ rồi hãy đặt niềm tin nhé.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575450718_7f6c2863243a547c5365.png\" style=\"width: 602px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Công ty xây dựng nhà uy tín</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Những nhà thầu ít việc quá sẽ không có quá trình thi công bài bản, không đủ máy móc thiết bị, công nhân để thực hiện. Thế nhưng với những nhà thầu nhiều việc quá thì nhà bạn lại không được chăm sóc kỹ. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà thầu lớn hay nhỏ không quá quan trọng mà điều quan trọng chính là tâm huyết nghề nghiệp của họ với ngôi nhà bạn. Vì thế hãy chọn lựa những nhà thầu uy tín, toàn tâm toàn ý chung tay xây dựng mái ấm với bạn.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quý khách có thể xem thêm: </span></span><a href=\"https://www.angiakhang.com/xay-nha-tron-goi-tai-tphcm\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(226, 80, 65);\">xây nhà trọn gói tại tphcm</span></span></a></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">[[contact_short_code]]</span></span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Rất mong được hợp tác cùng quý khách hàng!.</span></p>\n', NULL, 1, NULL, 1579571822, 1590644923, NULL),
(322, 'category', 23, 2, '3. 15 mẫu nhà cấp 4 mái thái đẹp 2020', '3-15-mau-nha-cap-4-mai-thai-dep-2020', '<p style=\"text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><img src=\"/uploads/contents/1575604537_0d1265c16afeb46525eb.jpg\" style=\"width: 608px;\" class=\"fr-fic fr-dii\"></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà cấp 4 đẹp với thiết kế theo phòng cách hiện đại</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575604618_517247d6aafd796588f0.jpg\" style=\"width: 596px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà cấp 4 mái thái mái ngói đỏ rất ấn tượng</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575604656_53c21fae4ad599ad1183.jpg\" style=\"width: 573px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà cấp 4 mái thái theo kiểu hiện đại có nhà xe</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575604684_6a16d334b23de267a4fe.jpg\" style=\"width: 561px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà cấp 4 kiểu biệt thự</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575604707_1f09524e1e15fa3c4acd.jpg\" style=\"width: 560px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiểu nhà mái thái mới</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575604725_70fa7ab4936f8b52a75c.jpg\" style=\"width: 565px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiểu nhà mái thái có công viên</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575604751_2349ff2b0d1d07ac1620.jpg\" style=\"width: 563px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà cấp 4 mái thái có sân rộng</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575604778_aff916b339740ca330da.jpg\" style=\"width: 565px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà cấp 4 mái thái kiểu gia chủ</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575604806_a84468afc2947bd3018b.jpg\" style=\"width: 570px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhà cấp 4 kiểu biệt thự minni</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575604831_b70b700c51f8bca595cc.jpg\" style=\"width: 577px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà cấp 4 sang trọn nhất</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575604852_68cfe9291e93022640f0.jpg\" style=\"width: 586px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà cấp 4 ở nông thôn</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575604894_d85652c7f679a12e88e6.jpg\" style=\"width: 580px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiểu nhà cấp 4 có sân trước rộng</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575604923_4f155cc5ac60cc36b4cc.jpg\" style=\"width: 578px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà cấp 4 đại gia</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1575604963_894c464667bfd5976b06.jpg\" style=\"width: 582px;\" class=\"fr-fic fr-dii\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Mẫu nhà cấp 4 500tr</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Công ty xây dựng An Gia Khang</strong> tự tin là đơn vị thầu xây dựng chuyên thiết kế, thi công xây dựng các công trình nhà cấp 4 uy tín và chuyên nghiệp.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Chúng tôi sở hữu đội ngũ kỹ sư, kiến trúc sư giàu kinh nghiệm và chuyên nghiệp đảm bảo mang đến một chất lượng công trình tốt nhất cho quý khách hàng.</span></span></p>\n<ul>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Hợp đồng rõ ràng, có hỗ trợ và tư vấn nếu quý khách hàng có thắc mắc khi tham khảo qua hợp đồng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Luôn thanh lý hợp đồng đúng theo những gì nội dung đã cam kết. Thực hiện chính sách bảo hành công trình theo đúng quy định.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Giá thành đưa ra đảm bảo hợp lý, có cơ sở, phù hợp với mọi đối tượng khách hàng.</span></span></li>\n<li style=\"margin-top: 0in; margin-right: 0in; margin-bottom: 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Luôn luôn hỗ trợ và tư vấn miễn phí cho quý khách hàng thiết kế công trình sao cho tốt nhất và đẹp nhất.</span></span></li>\n</ul>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu quý khách hàng vẫn đang tìm kiếm một công ty xây dựng nhà cấp 4 uy tín, chuyên nghiệp, giá thành phù hợp hãy liên hệ ngay với chúng tôi để được tư vấn và hỗ trợ tốt nhất.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nếu quý khách hàng vẫn đang tìm kiếm một <strong>nhà thầu xây dựng uy tín</strong>, chuyên nghiệp, giá thành phù hợp hãy liên hệ ngay với chúng tôi.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quý khách có thể xem thêm: </span></span><a href=\"https://www.angiakhang.com/xay-nha-tron-goi-tai-tphcm\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(184, 49, 47);\">15 mẫu nhà phố đẹp 2020</span></span></a></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Chúng tôi hân hạnh được làm việc với quý khách!</span></strong></p>\n', NULL, 1, NULL, 1580228075, 1590049531, NULL),
(323, 'category', 3, 0, '1. Những công trình xây dựng tiêu biểu', '1-nhung-cong-trinh-xay-dung-tieu-bieu', '<p><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; display: inline !important; float: none;\">Công ty xây dựng An Gia Khang là một trong những công ty xây dựng uy tín và chất lượng tại Tphcm và Phú Quốc hiện nay. Với sự phát triển mạnh mẻ với đội ngũ nhân sự là những kiến trúc sư chuyên nghiệp nhiều năm kinh nghiệm, cùng với đội ngũ anh em công nhân đầy nhiệt quyết, gắn bó lâu dài trên những công trình dự án.</span></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n', NULL, 1, NULL, 1580456438, 1590065758, NULL),
(324, 'category', 62, 0, '1. Xây nhà cao tầng', '1-xay-nha-cao-tang', '<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Nhà cao tầng là một trong những ngôi nhà được thiết kế  và xây dựng với nhiều tầng và có chiều cao( có khi từ 10 trên 60 tầng). Vì vậy việc xây nhà cao tầng mang đến rất nhiều lợi ích cho người sử dụng, xây dựng nhà cao tầng giúp tiết kiệm diện tích mặt bằng sử dụng đất và giảm được rất nhiều chi phí xây dựng. </span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1582983186_e1c540d37847484f6a56.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thi công xây dựng nhà</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Một trung tâm thương mại lớn với nhiều cao ốc, nhà cao tầng lớn đem đến sự hấp dẫn và hoàng tráng của một thành phố. Và đây cũng là lý do nhiều nhà cao tầng được xây dựng ở các trung tâm lớn hiện nay. </span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đối với các khu đô thị lớn như : Thành Phố Hồ Chí Minh, Hà Nội, Đà Nẵng…nhà cao tầng được xây dựng mang đến tầm vóc của một đất nước phát triển.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trong quá trình thi công xây dựng nhà cao tầng, khác với những công trình qui mô nhỏ, vì vậy khi xây nhà cao tầng, cần có nhà thầu xây dựng có nhiều kinh nghiệm. Thiết kế và thi công xây dựng nhà cao tầng phải có kỹ thuật đặc biệt cao. Chi phí xây nhà cao tầng thường rất cao và tốn nhiều thời gian hơn các dự án qui mô nhỏ.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1582983217_9ee1d084210021388f2a.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thi công đổ sàn nhà cao tầng</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Có thể nói bản thân nhà cao tầng như một cổ máy lớn, nơi nhiều người lưu trú và làm việc, do đó nguồn năng lượng cung cấp cho tòa nhà rất là lớn, vì vậy hệ thống kỹ thuật, thiết bị phục vụ cho tòa nhà và con người sống phải được thiết kế và xây dựng hợp lý, một sự sai sót trong thiết kế nhà cao tầng có thể gây ảnh hưởng rất nghiệm trọng.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Để xây dựng nhà cao tầng nhà thầu xây dựng phải biết kết hợp các yếu tố: Vật lý học công trình, hệ kết cấu chịu lực và hoàn thiện công trình.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1582983273_ee7a59a1c9472faf2b53.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đổ bê tông cột</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Xây dựng nhà cao tầng có vai trò rất quan trọng đây là nơi có nhiều người cư trú làm việc, vì vậy trong qua trình thiết kế và thi công xây dựng nhà thầu cần phải đảm bảo yếu tố bền vững cho công trình, đóng góp cho môi trường sống hiện đại.</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1582983318_3eb3459e1ad8ab96f48c.jpg\" style=\"width: 662px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">An Gia Khang nhà thầu xây dựng nhiều kinh nghiệm</span></span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">An Gia Khang công ty chuyên thiết kế và thi công xây dựng nhà cao tầng tại Việt Nam. Công ty tự tin là nhà thầu xây dựng có nhiều năm kinh nghiệm đã và đang triển khai nhiều dự án xây dựng tầm quốc tế. Mọi thông tin quý khách hàng vui lòng liên hệ:</span></p>\n<p style=\"margin: 0in 0in 10pt; line-height: 115%; font-size: 15px; font-family: Calibri, sans-serif; text-align: center;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\"><strong>Nhà thầu xây dựng An Gia Khang</strong></span></p>\n<p><iframe width=\"100%\" height=\"360\" src=\"https://www.youtube.com/embed/3fQu0tIbMvQ\" frameborder=\"0\" allowfullscreen=\"\"></iframe></p>\n<p style=\'margin-top:0in;margin-right:0in;margin-bottom:10.0pt;margin-left:0in;line-height:115%;font-size:15px;font-family:\"Calibri\",\"sans-serif\";\'>[[contact_short_code]]</p>\n', NULL, 1, NULL, 1580457276, 1590571233, NULL);
INSERT INTO `object_content` (`id`, `object_name`, `object_id`, `order_no`, `title`, `slug`, `content`, `is_lock`, `author_id`, `modifier_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(326, 'category', 63, 0, '1. Mẫu hợp đồng xây nhà', '1-mau-hop-dong-xay-nha', '<p style=\'margin:0in;margin-bottom:6.0pt;font-size:13px;font-family:\"Times New Roman\",\"serif\";margin-top:6.0pt;margin-right:0in;margin-left:0in;text-align:center;\'><strong><span style=\"font-size: 14px; font-family: Arial, Helvetica, sans-serif;\">CỘNG HÒA XÃ HỘI CHỦ NGHĨA VIỆT NAM</span></strong></p>\n<p style=\'margin:0in;margin-bottom:6.0pt;font-size:13px;font-family:\"Times New Roman\",\"serif\";margin-top:6.0pt;margin-right:0in;margin-left:0in;text-align:center;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Độc lập - Tự do - Hạnh phúc<br> ----------</strong></span></span></p>\n<p style=\'margin:0in;margin-bottom:6.0pt;font-size:13px;font-family:\"Times New Roman\",\"serif\";margin-top:6.0pt;margin-right:0in;margin-left:0in;text-align:right;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><em>............, ngày...tháng...năm.....</em></span></span></p>\n<p style=\'margin:0in;margin-bottom:6.0pt;font-size:13px;font-family:\"Times New Roman\",\"serif\";margin-top:6.0pt;margin-right:0in;margin-left:0in;text-align:center;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>HỢP ĐỒNG THI CÔNG XÂY DỰNG CÔNG TRÌNH TRỌN GÓI</strong></span></span></p>\n<p style=\'margin:0in;margin-bottom:6.0pt;font-size:13px;font-family:\"Times New Roman\",\"serif\";margin-top:6.0pt;margin-right:0in;margin-left:0in;text-align:center;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">(Số: SGH-…-…….2016/HĐTCTG)</span></span></p>\n<p style=\'margin:0in;margin-bottom:6.0pt;font-size:13px;font-family:\"Times New Roman\",\"serif\";margin-top:6.0pt;margin-right:0in;margin-left:0in;text-align:justify;line-height:130%;\'><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><span style=\"line-