(331, 'category', 18, 3, '5. 10 Mẫu nhà thiết kế đẹp', '5-10-mau-nha-thiet-ke-dep', '<p><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Xin giới thiệu đến quý khách hãng những mẫu nhà phố và biệt thự đẹp được nhiều khách hàng ưa chuộng hiện nay.</span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1581614706_0b8182db172a090dc931.jpg\" style=\"width: 584px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố có Gara xe</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1581614734_f9c4f66e48e74ae48c9e.jpg\" style=\"width: 589px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự mini</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1581614759_c9d4aaf086d059258cb2.jpg\" style=\"width: 606px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thự ở Nông thôn</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1581614788_03a790c55d657f1d7dcc.jpg\" style=\"width: 605px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà cấp 4 mái thái ở Nông Thôn</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1581614818_b7ef24ae81c5e10f201f.jpg\" style=\"width: 611px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu biệt thư mini sân vườn</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1581614845_f412957461eaf17e9d4f.jpg\" style=\"width: 595px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố kiểu ốc xoắn</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1581614874_5185c6a93197a40a46c3.jpg\" style=\"width: 597px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà biệt thự kiểu mái thái</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1581614907_950e43ac1f7b55865587.jpg\" style=\"width: 584px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu kiểu nhà phố có sân rộng</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1581614942_131e36e6042c120514b3.jpg\" style=\"width: 608px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mẫu nhà phố sân rộng có gara</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1581615003_b5d8df6a01e9925fb94d.jpg\" style=\"width: 620px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Kiểu nhà biệt thự ở Nông Thôn</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Trên là 10 mẫu nhà được thiết kế theo nhiều phong cách hiện đại và cổ điển được nhiều khách hàng chọn lựa. Hãy chia sẽ nếu bạn cảm thấy hay cho nhé.</span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quý khách có thể xem thêm: <a href=\"https://www.angiakhang.com/nha-pho-1-tang-1-tret\"><span style=\"color: rgb(184, 49, 47);\">Nhà phố 1 tầng đẹp</span></a></span></span></p>\n<p><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">[[contact_short_code]]</span></span></p>\n<p><strong><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Rất hân hạnh được phục vụ quý khách.</span></strong></p>\n', NULL, 1, NULL, 1581615167, 1590049742, NULL),
(332, 'category', 15, 2, '3. Quy định về mật độ xây dựng biệt thự', '3-quy-dinh-ve-mat-do-xay-dung-biet-thu', '<p style=\"text-align: justify;\"><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">Quy định về mật độ xây dựng chính là một trong các tiêu chuẩn phải tuân theo khi muốn xây dựng bất cứ công trình kiến trúc nào, trong đó có biệt thự. </span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1584037023_d1a50fe836a9968bde5a.jpg\" style=\"width: 608px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quy định về xây dựng biệt thự</span></span></p>\n<h3 style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>a) Quy định về mật độ xây dựng biệt thự</strong></span></span></h3>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Theo quy định những chỉ tiêu kiến trúc công trình nhà liền kề, biệt thự ở trong các dự án nhà ở đã có quy hoạch chi tiết xây dựng tỷ lệ 1/500 và 1/2000 do  Sở QHKT TP Hồ Chí Minh ban hành. Cụ thể mật độ xây dựng biệt thự được quy định như sau:</span></span></p>\n<ul>\n<li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với biệt thự đơn lập không quá 50%</span></span></li>\n<li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với biệt thự song lập không quá 55%</span></span></li>\n</ul>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tầng cao tối đa của nhà biệt thự: xây dựng tối đa 3 tầng với tầng nửa hầm, tầng áp mái, mái che cầu thang, không gồm có tầng hầm và tầng lửng.</span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><img src=\"/uploads/contents/1584037082_97aae66a31135c7151f6.jpg\" style=\"width: 619px;\" class=\"fr-fic fr-dib\"></span></span></p>\n<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Biệt thự sân vườn công viên rộng</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Với những trường hợp lô đất tiếp giáp 2 đường trở lên thì mật độ xây dựng tăng lên không quá 5% trừ trường hợp lô đất dưới 50m2. Trong quy định cũng nêu rõ:</span></span></p>\n<ul>\n<li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phần nổi của tầng hầm không quá 1,2m so với cao độ vỉa hè</span></span></li>\n<li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Vị trí đường xuống hầm cách ranh giới lộ giới ít nhất là 3m</span></span></li>\n</ul>\n<h3 style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>b) Cách tính mật độ xây dựng biệt thự</strong></span></span></h3>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Căn cứ vào Quyết định số 04/2008/QĐ-BXD ngày 03/04/2008 của Bộ Xây dựng v/v Ban hành: “Quy chuẩn kỹ thuật Quốc gia về Quy hoạch xây dựng” (QCXDVN 01:2008/BXD). Sở Xây dựng đã thống nhất hướng dẫn về cách tính mật độ xây dựng công trình kiến trúc xây dựng.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Từ quy chuẩn này có thể giúp các cơ quan thẩm định, các đơn vị tư vấn thiết kế xây dựng và các đơn vị liên quan biết và thực hiện khi thẩm định, phê duyệt và cấp giấy phép xây dựng cho các công trình xây dựng có liên quan tới chỉ tiêu mật độ xây dựng công trình.</span></span></p>\n<h3 style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>c) Công thức tính mật độ xây dựng biệt thự như sau:</strong></span></span></h3>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mật độ xây dựng biệt thự (%) = Diện tích chiếm đất của biệt thự (m2) / Tổng diện tích lô đất xây dựng (m2) x 100%</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Trong đó:</strong></span></span></p>\n<ul>\n<li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Diện tích chiếm đất biệt thự được tính theo hình chiếu bằng của công trình biệt thự</span></span></li>\n<li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Diện tích chiếm đất của biệt thự không gồm có diện tích chiếm đất của các công trình khác như: bể bơi, tiểu cảnh trang trí, sân thể thao ngoài trời,…</span></span></li>\n</ul>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Ví dụ:</span></span></p>\n<ul>\n<li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Diện tích đất của nhà bạn là 10mx20m = 200m2.</span></span></li>\n<li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phần diện tích dùng xây biệt thự: 10mx14m = 140m2.</span></span></li>\n<li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phần sân trước chừa ra 4m: 10mx3m= 30m2</span></span></li>\n<li style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Phần sân sau chừa ra 2m: 10mx1m= 10m2</span></span></li>\n</ul>\n<p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Như vậy mật độ xây dựng biệt thự là: 140m2/200m2 x100 = 70%. Trong đó phần xây dựng là 70% (tương ứng 140m2),  phần chừa sân 30% (tương ứng 30m2).</span></span></p>\n', NULL, 1, NULL, 1581863617, 1590571782, NULL);
