(14, 1, 'Trang chủ - Top nhà tuyển dụng trả lương cao', 'tin-tuyen-dung-nam-noi-bat-trong-box-viec-lam-luong-cao-ben-duoi-trang-chu', NULL, '<p><strong><span style=\"color: rgb(56, 56, 56); font-family: geomanist; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; display: inline !important; float: none;\">Tin tuyển dụng nằm nổi bật trong box </span><span style=\"box-sizing: border-box; color: rgb(23, 144, 212); font-weight: 500; font-family: geomanist; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\">\"Việc làm lương cao\"</span><span style=\"color: rgb(56, 56, 56); font-family: geomanist; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; display: inline !important; float: none;\"> bên dưới trang chủ</span> </strong></p>', 'Ngon bổ rẻ', '', 'Vải siêu thấm', 'Từ quán ra đường', 10000, 10, 0, 1, 4, 1573665357, 1619168504, 1574786532),
(15, 1, 'Trang chủ - Hấp dẫn dành cho bạn', 'trang-chu-hap-dan-danh-cho-ban', NULL, '<p><span style=\"color: rgb(33, 33, 33); font-family: Roboto, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; display: inline !important; float: none;\">Tin tuyển dụng nằm nổi bật trong box </span><span style=\"box-sizing: border-box; color: rgb(23, 144, 212); font-weight: 500; font-family: Roboto, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\">\"Việc làm hấp dẫn\"</span><span style=\"color: rgb(33, 33, 33); font-family: Roboto, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; display: inline !important; float: none;\"> trung tâm trang chủ</span></p>', NULL, NULL, NULL, NULL, NULL, NULL, 0, 4, 4, 1619168553, 1619232276, NULL),
