(163, 'du-an-vi-du-trong-biet-thu-tang-1', 'App\\Models\\ProjectModel', 11, 'Project::detail', 'dự án ví dụ trong biệt thự tầng 1', NULL, NULL, '', '', '', 1, 1, 1573663417, 1573699749, NULL),
(167, 'gioi-thieu', 'App\\Models\\CategoryModel', 21, 'Category::index', 'Công ty An Gia Khang', '1574155533_0e7d0874a79da67eb716.jpg', NULL, 'Giới thiệu về công ty An Gia Khang', 'công ty xây dựng', 'Công ty xây dựng An Gia Khang', 1, 1, 1574155533, 1592224987, NULL),
(190, 'xay-nha-cap-4-mai-thai', 'App\\Models\\CategoryModel', 23, 'Category::index', 'Xây nhà cấp 4 mái thái', '1574350777_20222d2ccf75025438b9.jpg', NULL, 'Xây nhà cấp 4 mái thái [giá 600tr] phong cách hiện đại ở nông thôn', 'xây nhà cấp 4 mái thái, xây nhà trọn gói mái thái, xây nhà cấp 4, tư vấn xây nhà trọn gói.', 'Xây nhà cấp 4 mái thái theo phong cách hiện đại và cổ điển. Tư vấn báo giá xây nhà cấp 4 giá 400tr, 500tr hoặc có thể báo giá theo diện tích xây nhà.', 1, 1, 1574350777, 1590049531, NULL),
(207, 'xay-dung-khach-san', 'App\\Models\\CategoryModel', 25, 'Category::index', 'Xây khách sạn', '1582303439_81152b8f4397f6e80607.jpg', NULL, 'Xây dựng khách sạn 3, 4, 5, 6 sao theo tiêu chuẩn quy định Bộ Xây Dựng', 'xây dựng khách sạn, điều kiện xây dựng khách sạn, đơn giá xây dựng khách san 4 sao, đơn giá xây dựng khách sạn 3 sao, tiêu chuẩn xây dựng khách sạn. xây dựng khách sạn mini, đơn giá xây dựng khách sạn', 'Xây khách sạn là công trình kiến trúc kiên cố với nhiều tầng, nhiều phòng ngủ. Khách sạn thường được trang bị nhiều vật dụng tiện nghi phục vụ cho mục đích kinh doanh nghỉ dưỡng, dịch vụ ăn uống, lưu trú hay dịch vụ khác. Đơn giá xây dựng khách sạn 3, 4,5 sao đúng tiêu chuẩn quy định Bộ xây dựng.', 1, 1, 1574424941, 1590572217, NULL),
(208, 'xay-dung-nha-xuong', 'App\\Models\\CategoryModel', 26, 'Category::index', 'Xây nhà xưởng', '1575555367_bc40ede7ca36ed921d32.png', NULL, 'Xây dựng nhà xưởng công nghiệp uy tín và chất lượng tại tphcm', 'xây đựng nhà xường, công ty xây nhà xường, giá xây nhà xưởng, báo giá xây nhà xường, mẫu nhà xưởng đẹp, đơn giá xây dựng nhà xưởng,  chi phí xây dựng nhà xưởng, xây dựng nhà xưởng giá rẻ.', 'Xây dựng nhà xưởng là một trong những loại hình xây dựng nhà khung thép hay còn gọi là xây nhà tiền chế, với thiết kế chuẩn, kết cấu khung thép chất lượng, tuổi thọ nhà xưởng công nghiệp có thể lên tới 10-20 năm.  Công ty thiết kế và xây dựng An Gia Khang là một trong những nhà thầu xây dựng công nghiệp uy tín và chuyên nghiệp hiện nay. Tư vấn báo giá xây dựng nhà xưởng công nghiệp, quý khách vui lòng liên hệ: 0937 181 181 để được tư vấn chi tiết.', 1, 1, 1574425046, 1590570749, NULL),
(223, 'cong-trinh-tieu-bieu', 'App\\Models\\CategoryModel', 29, 'Category::index', 'Công trình tiêu biểu', '1574812796_50bd917208a7438396c9.jpg', NULL, 'Những công trình xây dựng tiêu biểu công ty xây dựng An Gia Khang', 'công trình xây dựng tiêu biểu, công ty xây dựng tại phú quốc, công ty xây dựng tại kiên giang', 'Công ty xây dựng tiêu biểu , công ty An Gia Khang trực tiếp thi thực hiện thi công thiết kế và xây dựng tại Tphcm và Phú Quốc . An Gia Khang là nhà thầu xây dựng uy tín tại tphcm và Kiên Giang.', 1, 1, 1574787192, 1580456554, NULL),
(229, 'cong-trinh-1', 'App\\Models\\CategoryModel', 35, 'Category::index', 'Công trình 1', '1574787897_b86b74c92d1a5f583551.jpg', NULL, '', '', '', 1, 1, 1574787424, 1574812625, NULL),
